insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (267, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금송이는 부채비율이 높은 A기업의 주식과 채권에 모두 투자하고 있습니다. A기업이 파산한다면, 일반적으로 투자원금 회수순서가 가장 늦은 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업의 우선주", "A기업의 보통주", "A기업의 담보부 채권", "A기업의 전환사채"]}]', '[{"type": "SOLUTION_TEXT", "data": "기업 파산 등에 따른 주주나 채권자의 자금회수 등의 권리행사 순서는 정해져 있습니다. 일반적으로 채권이 주식에 우선하는 권리를 가지며 주식 중에 우선주가 보통주에 우선하는 권리를 가집니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '기업의 안전가치 측정', null, null);
