insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (289, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금송이는 A기업 공모주에 투자하려고 하는데 \"따상이 기대된다\"라는 기사를 보았습니다. 올바른 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공모주란 기업이 주식시장에 상장하기 위하여, 일반인에게 배정하는 주식을 말한다.", "따상은 상장 첫날, 공모가 대비 두 배로 시작하여 가격제한폭(30%)까지 오르는 것을 말한다.", "대주주나 기관투자자 등은 의무보호예수기간 종료 후 지분을 대량 매각할 수도 있다.", "주식시장 상장 후 주가는 공모가 이하로 하락할 수 없다."]}]', '[{"type": "SOLUTION_TEXT", "data": "공모가는 기업공개 단계에서 정해지는 단일가격이지만, 상장 후에는 여러 상황에 따라 주가는 변동하게 됩니다. 이 때 공모가격보다 주가는 오를 수도 있고 떨어질 수도 있습니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '공모주 투자와 공모가', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (290, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["주식 시세, 호가 등에 대한 금송이의 설명으로 가장 올바르지 않는 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식 시세는 현재의 주식 가격을 말하며 시세표에는 종목명, 주가 등락 등 여러 정보가 일목요연하게 표시된다.", "거래량은 현재까지 매매된 주식의 수를 의미하며 해당 종목의 인기를 보여주기도 한다.", "호가창에서 현재 가격을 기준으로 매도호가는 위로 매수호가는 아래로 보여준다.", "체결강도는 매수체결과 매도체결 간의 비율로 산출되고 100%을 넘어가면 매도자가 매수자 보다 많다는 것을 의미한다."]}]', '[{"type": "SOLUTION_TEXT", "data": "시장이 시작할 때 거래 강도가 100%인데 이를 넘어가면 주식을 사는 사람이 파는 사람보다 더 많다는 것을 의미합니다. 반대로 100% 이하로 떨어지면 주식을 파는 사람이 더 많은 것을 뜻합니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주식의 기본분서과 기술적 분석', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (291, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금송이는 주식 차트 보는 법을 배우려고 합니다. 다음 설명 중 가장 올바르지 않는 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["단기 이동평균선이 중장기 이동평균선을 상향돌파 시 매도신호로 활용한다.", "주가가 저점에서 많이 오른 종목은 차익실현 등으로 주가의 변동 위험성이 커질 수 있다.", "파란색 막대를 음봉, 빨간색 막대를 양봉이라고 한다.", "차트로 알 수 있는 과거 흐름은 미래 결과를 보장하지 않는다."]}]', '[{"type": "SOLUTION_TEXT", "data": "단기 이동평균선이 중장기 이동평균선을 상향돌파할때는 매수신호로 활용합니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '골든크로스의 해석', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (292, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["다음 상황에 대한 설명으로 가장 올바르지 않는 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금송이는 A 증권회사의 수수료 무료 이벤트에 참여하였고 100만원을 국내 주식에 투자하여 10만원의 이익을 냈습니다. 그런데 결제날 10만원보다 적은 금액이 입금되었습니다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식거래는 유관기관 수수료, 증권사 위탁거래수수료, 세금 등 3가지 비용이 발생한다.", "한국거래소, 한국예탁결제원의 수수료가 차감되어 입금되었다.", "증권회사의 거래수수료가 부과될 수 있지만 증권회사 이벤트로 해당 비용은 무료이다.", "국내 주식은 매도하는 경우에만 교육세 0.23%가 부과된다."]}]', '[{"type": "SOLUTION_TEXT", "data": "주식 거래 시에 부과되는 세금은 증권거래세입니다. 주식을 살 때는 세금을 내지 않지만, 주식을 팔 때는 증권거래세를 내야 합니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주식 세금과 수수료', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (293, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["새내기 투자자인 금송이에게 가장 적합한 주식 투자 전략으로 가장 올바른 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["넓은 바다에서 낚시하듯 여러종목을 순환매하면서 투자한다.", "대형주를 선택한 후 전일 종가 대비 10%이하 가격에 예약매수를 걸어둔다.", "기업의 사업보고서를 확인하고 투자계획과 목표에 따라 계획적으로 분할매매를 한다.", "기업의 주식차트(일봉, 주봉, 월봉, 거래량) 변화와 외국인의 수급상황을 보고 판단을 한다."]}]', '[{"type": "SOLUTION_TEXT", "data": "주식시장에서는 계좌를 개설하는 순간부터 심리적 압박을 받을 수 있습니다. 빨리 주식을 사서 수익을 내야지 하는 일확천금의 조바심이 생겨 매매를 이성적으로 할 수 없게 될 수 있습니다. 이를 견제하기 위한 여러 방법 중 경험이 적은 투자자도 쉽게 실행할 수 있는 분할매수/분할매도 투자법이 있습니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '수익실현 방법', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (294, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["합리적인 투자자를 가정 시 다음 조건 아래에서 적절한 자산관리 방법으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["정기예금금리(무위험수익률): 1%", "위험보상율: 3%"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["여유자금을 정기예금에 투자한다", "여유자금을 기대수익률이 3%인 금융상품에 투자한다", "여유자금을 기대수익률이 5%인 금융상품에 투자한다", "여유자금을 기대수익률이 7%인 금융상품에 투자한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자할 자산으로부터 예상되는 수익률과 투자자가 만족할 만한 수익률을 비교하면 비교적 쉽게 투자 의사를 결정할 수 있다. 투자자는 특정한 투자 대상에서 ''기대되는 수익률(expected return)''이 일정 수준 이상이어야 투자한다. 이때 투자자를 만족시킬 수 있는 최소한의 수익률을 ‘요구 수익률(required return)’이라고 한다. 투자자들이 기대하는 최소한의 요구 수익률은 정기예금(무위험자산)의 이자율과 위험보상율을 합한 것이다. \n\n요구 수익률 = 이자율(무위험자산 수익률)+위험 보상률\n\n위 경우 무위험수익률(1%)과 위험보상률(3%)을 합한 요구수익률은 4%이다. 따라서 기대수익률이 4% 이상인 금융상품에 투자하거나, 아니면 무위험자산에 맡겨 두는 것이 낫다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '요구수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (295, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["3년 간 주식투자로 다음과 같은 현금흐름이 발생했을 때 보유기간 동안 수익률로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["20%", "25%", "30%", "35%"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자에서 발생하는 수익률은 다음과 같이 계산한다. 먼저 주식과 채권과 같은 투자 자산을 매도한 가격에서 이를 매입할 때 들어간 금액을 뺀 시세 차익과 채권 이자와 주식 배당처럼 투자 자산에서 발생한 현금흐름을 더한다. 그리로 수수료 등 비용을 차감한다. 이렇게 계산한 금액을 매입 금액으로 나눈 다음 100을 곱해 퍼센트(%)로 환산하면 수익률을 계산할 수 있다. \n\n수익률=(매도 금액-매입 금액±보유 기간 중 현금흐름-비용)/(매입 금액)  ×100\n          =  (120만원-100만원+10만원-5만원)/100만원  ×100 = 25% "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (296, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["1000만원을 연 복리 6%로 투자해서 2,000만원으로 만드는데 걸리는 시간으로 가장 올바른 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["6년", "8년", "10년", "12년"]}]', '[{"type": "SOLUTION_TEXT", "data": "72의 법칙은 복리를 전제로 자산이 2배로 늘어나는 데 걸리는 시간을 손쉽게 계산하는 방식이다. 72를 해당 수익률로 나누면 대략적으로 원금의 2배가 되는 기간을 산출할 수 있다.  \n\n72÷금리= 원금이 2배가 되는 시기(연)\n\n따라서 1000만원의 돈을 연 6%의 복리 상품에 투자하면 원금이 2배가 되는 데 걸리는 시간은 12년(72÷6=12)이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '72의 법칙 활용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (297, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["수익의 재투자를 통한 복리효과를 감안했을 때 수익률이 가장 높은 사람은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["강감찬씨는 1년을 투자해서 6% 수익을 냈다", "김유신씨는 2년을 투자해서 12% 수익을 냈다", "곽재우씨는 3년을 투자해서 18% 수익을 냈다", "이순신씨는 4년 동안 투자해서 24% 수익을 냈다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["강감찬", "김유신", "곽재우", "이순신"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자 대상 기간이 서로 다를 때는 서로 비교할 수 없다. 이때는 수익률을 1년을 기준으로 표준화 해서 비교하는 것이 일반적이다. 그리고 연율화 과정에서 재투자를 가정해 복리를 적용해 계산하는 것이 원칙이다. \n② 김유신씨가 2년 동안 낸 12% 수익을 연으로 환산하면 5.8%이다.\n③ 곽재우씨가 3년 동안 낸 18% 수익을 연으로 환산하면 5.8%이다.\n④ 이순신씨가 4년 동안 낸 24% 수익을 연으로 환산하면 5.5%이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '복리효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (298, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자 수익률을 계산할 때 고려해야 할 사항으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래가 빈번하면 수수료 부담이 커진다", "비과세 금융상품을 선택하면 수익률을 높일 수 있다", "기회비용은 명시적으로 계산할 수 없으므로 고려하지 않는다", "투자대상을 평가할 때는 수익과 함께 위험도 고려해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "수수료와 세금이 눈에 드러나는 비용이라면 그렇지 않은 것도 있다. 대표적인 예로 기회비용이 있다. 기회비용이란 어떤 행위를 하기 위해 포기해야 하는 다른 기회의 가치를 말하는데, 투자에도 그대로 적용된다. 예를 들어, 주식 투자를 하면서 많은 시간과 노력을 기울여 종목을 발굴할 것이다. 이런 시간과 노력이 얼마만큼 가치를 가질 것인지 명시적으로 계산하기는 어렵지만, 기회비용이 발생한 것만큼은 분명하다. 시간과 노력을 다른 곳에 기울일 수도 있기 때문이다. 투자 활동에 수반되는 기회비용을 줄이기 위한 방편으로 직접투자 대신 펀드와 위탁 판매와 같은 간접투자를 이용하기도 하는데, 이때는 투자 결정을 대신해 주는 대가로 운용 보수를 지불한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '수익률 결정요소로서 비용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (299, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자수익과 투자태도에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자태도를 감수하지 않으면 손실을 보지 않지만 수익도 얻을 수 없다", "기대수익이 10%라는 말은 항상 10% 수익을 얻는다는 뜻이다", "수익의 변동폭이 크면 투자태도가 크다고 할 수 있다", "투자에서 위험은 분산이나 표준편차로 측정할 수 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "기대 수익률이 연 10%라고 해서 그 주식에 투자하면 무조건 연 10%의 수익을 얻는다는 뜻은 아니다. 기대한 수익이 실현되지 않을 가능성, 투자 결과가 기대했던 수익과 다를 가능성을 투자태도라고 한다. 투자 위험은 투자에서 예상되는 미래 수익률의 분산이나 표준편차로 측정한다. 즉, 예상 수익률이 기대 수익률에서 크게 벗어나지 않으면 투자 위험이 적거나 없다고 하고, 예상되는 수익이 들쑥날쑥하면 투자태도가 크다고 말한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도에 대한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (300, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["다음 중 가장 유리한 투자대안으로 올바른 것은? (단, 모든 조건은 동일하다고 가정)"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자위험(표준편차) 10%, 기대수익률 5%", "투자위험(표준편차) 10%, 기대수익률 7%", "투자위험(표준편차) 10%, 기대수익률 10%", "투자위험(표준편차) 10%, 기대수익률 15%"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자위험이 동일하다면 수익률이 큰 자산을 선택한다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (301, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["다음과 같은 투자대안이 있을 때, 합리적 선택으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자대안 A: 기대수익률 5%, 투자위험 10%", "투자대안 B: 기대수익률 10%, 투자위험 10%", "투자대안 C: 기대수익률 10%, 투자위험 7%", "투자대안 D: 기대수익률 5%, 투자위험 7%"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자대안 A를 매수하고 B를 매도한다", "투자대안 B를 매도하고 C를 매수한다", "투자대안 C를 매수하고 D를 매도한다", "투자대안 A를 매도하고 D를 매수한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "기대수익이 동일하다면 투자위험이 적은 투자대안을 선택하고, 투자위험이 동일하다면 수익률이 큰 대안을 선택하는 것이 유리하다. A와 B는 투자위험이 동일하기 때문에 기대수익률이 큰 B를 매수하고 A를 매도하는 것이 유리하다.   "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (302, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자보호의 대상이 되는 사람 만을 <보기>에서 모두 고른 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["은행 예금에 가입 하려는 사람", "펀드에 가입한 사람", "보험계약 효력이 상실된 사람", "신용카드 이용자"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄷ", "ㄱ, ㄴ, ㄷ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융시장에서 금융상품 공급자가 금융회사라면, 금융상품에 대한 수요자를 금융 소비자라고 한다. 넓은 의미에서는 현재 금융상품을 이용하고 있는 사람뿐만 아니라 잠재적인 수요자까지도 포함한다. 은행 예금자, 금융투자회사의 투자자, 보험회사의 보험 계약자, 신용카드 회사의 신용카드 이용자와 같은 거래 당사자뿐만 아니라 앞으로 금융회사 상품이나 서비스를 이용하려는 사람도 금융 소비자에 포함된다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (303, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자보호에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자는 금융회사에 비해 정보력이 떨어지므로 보호해야 한다", "금융소비자가 금융회사에 비해 교섭력이 떨어지므로 보호해야 한다", "금융소비자보호는 금융회사에 대한 신뢰를 무너뜨려 금융시장 전체에 부정적 영향을 미친다", "금융기술 발달속도가 금융소비자의 금융역량 발전속도를 능가하고 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융 소비자는 금융회사에 비해 정보력과 교섭력 면에서 뒤처져 있다. 금융회사는 금융상품의 특성, 비용, 위험, 금융시장 상황 등에 대해 금융 소비자에 비해 훨씬 풍부하고 전문적인 정보를 가지고 있다. 금융회사가 이런 지위를 남용해 금융 소비자에게 피해를 끼치면 단순히 금융 소비자 개인의 피해로 그치지 않는다. 이는 금융회사에 대한 신뢰를 무너뜨려 결국에는 금융산업과 금융시장 전반에 부정적인 영향을 미친다. 따라서 금융 소비자 보호는 전체적인 금융 시스템의 안정을 위해서도 필요하다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (304, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자보호에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자의 지위를 향상시켜 금융회사와의 교섭력을 강화한다", "금융소비자가 피해를 입지 않도록 금융회사의 영업행위를 규제한다", "금융소비자에게 객관적이고 충분한 정보를 제공한다", "금융소비자보호는 정부기관만이 할 수 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융 소비자 보호는 정부 기관이 해야 할 일로 여기지만, 그렇지 않다. 정부의 역할이 큰 것은 사실이지만, 시장 참가자로서 금융회사와 금융 소비자가 참여하지 않으면 그 효과를 충분히 거두기 어렵다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (305, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융회사가 금융상품을 판매할 때 지켜야할 내용으로 가장 적절한 것은?  "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반투자자의 투자경험과 재산현황 등을 파악한다", "소비자에게 펀드를 판매하면서 단정적으로 수익률을 제시한다", "소비자에게 과도한 금전적 이익을 제공한다", "대출상품을 판매하면서 저축성 상품 가입을 권유한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융회사가 일반 투자자에게 투자를 권할 때는 면담이나 질문을 통해 투자자의 투자 목적, 재산 상황, 투자 경험 등을 파악하도록 한다.(적합성의 원칙)\n금융회사가 일반 금융 소비자에게 구매를 권유할 때는 금융상품의 중요 사항을 소비자가 이해할 수 있게끔 설명해야 한다. (설명의 의무) 금융회사가 금융 소비자에게 투자를 권유할 때, 금전적 이익 제공, 손실 보전의 약정과 같이 건전한 거래 질서를 해칠 우려가 있는 방법으로 사용해서는 안 된다. (부당 권유의 금지) 금융회사가 금융상품을 판매하는 과정에서 우월적인 지위를 이용해 금융 소비자의 의사에 반하여 금융상품을 가입하도록 요구해서는 안 된다. (구속성 금융상품 계약체결 금지)"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (306, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융상품의 약관에 대한 설명으로 가장 거리가 먼 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["약관의 뜻이 명확하지 않을 때는 금융회사에 유리하게 해석한다", "금융회사는 금융소비자에게 약관의 주요 내용을 설명해야 한다", "개별 약정이 있으면 약관에 우선한다", "금융소비자가 약관의 사본을 요구하면 교부해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융상품 약관은 금융회사가 불특정 다수의 금융 소비자를 대상으로 체결하는 금융상품 계약의 세부적인 내용을 담고 있다. 금융회사와 금융 소비자 간에 약관의 해석을 두고 분쟁이 발생하는 경우, 약관은 신의성실의 원칙에 따라 공정하게 해석되어야 하며, 금융 소비자에 따라 다르게 적용하거나 해석되어서는 안 된다. 또한 약관의 내용이나 뜻이 명확하지 않다면 금융 소비자에게 유리하게 해석해야 한다. 개별 약정이 있는 경우에는 그것이 약관에 우선해서 적용된다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
