insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (308, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융감독원에 접수된 민원과 분쟁조정절차에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융회사를 거치지 않은 민원은 민원인과 금융회사가 자율조정을 거친다", "금융회사를 거쳐 접수된 민원은 금융감독원이 직접 처리한다", "금융분쟁조정은 소송을 통해 해결되지 않은 민원을 해결하기 위한 제도다", "금융회사의 자율조정으로 민원이 해결되지 않으면 금융감독원이 직접 처리한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융 소비자가 금융 민원을 접수하면, 금융회사를 거치지 않은 민원은 우선 민원인과 금융회사 간에 자율 조정을 거친다. 이미 금융회사를 거친 민원이나 금융회사의 자율 조정을 통해 해결되지 않은 민원은 금융감독원이 직접 처리한다. 금융분쟁조정제도는 금융 소비자와 금융회사 간에 발생한 분쟁을 소송을 통하지 않고 원만하게 해결하기 위한 제도다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (309, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["제도권 금융상품에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은? "], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["투자손실이 발생 할 수 있는 금융투자상품은 제도권 금융상품이 아니다", "금융당국에 등록되거나 인가를 받은 금융회사를 제도권 금융회사라고 한다", "정식 인가를 받지 않은 금융회사도 제도권 금융상품을 판매할 수 있다", "제도권 금융상품은 투자자보호 절차에 따라서만 판매할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄴ, ㄹ"]}]', '[{"type": "SOLUTION_TEXT", "data": "ㄱ. 투자 결과에 따라 투자 손실이 발생할 수 있는 금융투자상품은 ‘제도권 금융상품’으로 분류한다.\nㄷ. ‘제도권 금융상품’ 관련 법령에 따라 등록되고 정식 인가를 받은 금융회사만 판매할 수 있기 때문에 인가를 받지 않은 금융회사는 판매할 수 없다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (310, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자상품에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래소 거래 여부에 따라 파생상품과 증권으로 분류한다", "금융상품은 원금손실가능성의 유무에 따라 금융투자상품과 비금융투자상품으로 나뉜다", "금융투자상품 중 원금초과손실 가능성이 없는 상품은 증권으로 분류한다", "거래소에서 거래되지 않는 파생상품을 장외파생상품이라고 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "원금초과손실 가능성의 유무에 따라 파생상품과 증권으로 분류한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (311, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["대표적인 금융투자상품에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은? "], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["주가연계증권(ELS) - 증권회사가 자기 신용으로 발행하는 파생결합증권의 일종으로, 특정 주가지수나 개별 종목의 주가에 연동하여 발행하며 상환일에 받을 금액이 결정된다", "파생결합증권(DLS) - 이자율, 환율 등이 사전에 약정된 범위내에서 변동하는 경우 약정된 수익율을 적용하여 산출한 금액을 투자자에게 지급하며, 투자 원금을 보장한다", "상장지수채권(ETN) - 금융회사가 자기 신용으로 발행한 채권으로 거래소에 상장되어 쉽게 매매가 가능하며 특정 지수의 수익을 보장한다", "상장지수 집합투자기구(ETF) - 증권회사가 투자자에게 일정 기간 동안 특정 종목의 주가 또는 주가지수를 사전에 정한 조건으로 매수하거나, 매도할 수 있는 권리를 부여한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄷ, ㄹ"]}]', '[{"type": "SOLUTION_TEXT", "data": "파생결합증권(DLS) – 파생결합증권은 투자 원금을 보장하지 않는다. 파생결합증권에서 원금을 보장해주는 상품은 파생결합사채(DLB)라고 한다. \n상장지수 집합투자기구(ETF) - 특정 주가지수 등의 가격 변동성을 추종할 수 있도록 자산을 구성하여 그 주가지수 수익률을 목표로 자산을 운용하는 펀드. (ㄹ) 보기의 설명은 주식워런트증권(ELW)에 대한 설명이다.\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융투자상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (312, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자의 기본적 권리에 대한 설명으로 가장 거리가 먼 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합리적인 금융 소비 생활을 위하여 필요한 교육을 받을 권리", "금융상품판매업자의 위법한 영업행위로 인한 재산상 손해로부터 보호받을 권리", "금융상품의 소비로 인한 피해에 대해서 신속∙공정한 절차에 따라 적절한 보상을 받을 권리", "금융회사 임직원의 권유로 금융상품에 가입한 경우 금융회사에 투자손실의 보전을 요구할 권리"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융소비자가 금융회사 임직원의 권유로 금융상품에 가입한 경우에도 금융상품 투자에 따른 책임은 금융소비자 본인에게 있다. (투자 결과에 대한 자기 책임의 원칙)"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '기본적 투자원칙 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (313, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 자신에게 적합한 금융투자상품을 선택하는 5단계를 순서대로 올바르게 나열한 것은? "], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["금융투자회사로부터 본인의 투자 성향에 맞는 금융투자상품을 권유 받는다", "본인의 투자 정보(투자목적, 재산상황, 투자경험 및 지식, 투자기간 등)를 금융투자회사에 정확히 제공한다", "원금을 초과해 손실을 입을 가능성이 있는 파생상품은 투자 손실에 특히 주의한다", "본인이 어떠한 투자 성향(안정형-안정추구형-위험중립형-적극투자형-공격투자형)을 갖고 있는지 제대로 파악한다", "본인이 투자할 금융투자 상품의 내용, 구조, 위험, 수수료 등에 대해 자세히 설명을 듣고 완전히 이해한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ - ㄹ - ㄴ - ㅁ - ㄷ", "ㄴ - ㄹ - ㄱ - ㅁ - ㄷ", "ㄹ - ㄴ - ㄱ - ㅁ - ㄷ", "ㅁ - ㄴ - ㄹ - ㄱ - ㄷ"]}]', '[{"type": "SOLUTION_TEXT", "data": "자신에게 맞는 금융투자상품을 고르기 위해서는 첫째, 본인의 투자정보를 금융투자회사에 정확히 제공하고, 둘째, 본인의 투자성향에 대해 제대로 파악하고, 셋째, 금융투자회사로부터 본인의 투자성향에 맞는 금융투자 상품을 권유받고, 넷째, 본인이 투자할 금융투자 상품에 대해 자세히 설명을 듣고 완전히 이해하고, 마지막으로 원금을 초과해 손실을 입을 가능성이 있는 파생상품은 투자 손실에 특히 주의해야 한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (314, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자보호의 필요성에 대한 설명으로 가장 거리가 먼 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선진국에서는 2011년 이후 본격적으로 금융소비자보호를 위해 노력하고 있다", "다양하고 복잡해지는 금융상품의 등장으로 금융소비자 보호의 필요성이 높아지고 있다", "금융회사는 금융상품의 제조∙판매 과정에서 금융소비자에 비해 유리하기 때문에 공정한 거래를 위해 금융소비자를 보호해야 한다", "모바일기기 사용 등이 익숙하지 않는 고령층에 대해서는 금융거래 사고방지를 위해 모바일 기기를 통한 금융서비스 이용을 제한하는 것이 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융 환경의 급속한 발전 및 변화 속도를 따라가지 못해 모바일 기기 등을 제공하는 금융서비스를 이용하지 못하는 고령층을 금융시장 안으로 포용하는 문제는 금융소비자보호를 통해 해결해 나가야 하는 과제이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (315, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자보호가 필요한 이유에 대한 설명으로 가장 거리가 먼 것은?  "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자는 금융상품을 제조∙판매하는 금융회사에 비해 상대적으로 정보력이 부족하다", "금융회사는 금융투자상품 판매시 어떠한 경우에도 금융소비자에게 투자 손실이 발생하지 않도록 노력해야 한다", "금융소비자는 정보를 받아들이고 활용하는데 한계가 있기 때문에 제한적인 합리성을 지닌 존재로 간주하고 보호해야 한다", "금융소비자는 금융회사와 금융상품 계약 시 전문성 및 교섭력에서 불리한 환경에 처할 수 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "➀ 정보의 비대칭성\n➁ 금융상품 투자시 손실은 투자자 본인의 책임이며, 금융회사가 투자 손실을 보전할 수 없다.  \n➂ 제한적 합리성\n➃ 교섭력의 평등성"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (316, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자보호법에 대한 설명으로 가장 거리가 먼 것은?   "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자보호법의 시행으로 은행업, 보험업, 금융투자업 등 각 업권별로 특화된 금융소비자보호 업무를 분리하여 수행할 수 있게 되었다", "금융소비자보호법은 2008년 키코(KIKO) 사태, 2011년 저축은행 사태를 거치면서 관련 법안이 발의되어 2021년부터 시행되었다", "금융소비자보호법은 금융상품판매업자의 영업 행위 준수 사항, 금융 분쟁조정 등 금융소비자 보호를 위한 여러 제도를 포함한다", "금융소비자는 금융소비자보호법에서 새롭게 도입된 금융소비자의 권리를 정확히 알고 이해할 필요가 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융소비자보호법은 서로 다른 금융업권에서 각기 다른 법령에 따라 수행해온 금융소비자보호업무를 일관된 제도로 추진하기 위한 목적으로 제정되었다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
