insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (319, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["사전적 금융소비자보호란 금융소비자가 금융상품 등의 계약으로 피해를 입지 않도록 사전에 금융회사가 준수하거나 지켜야 하는 방법과 절차 등을 말한다. 다음 중 사전적 금융소비자보호 방법과 절차로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융회사의 영업 행위 원칙 준수", "금융분쟁조정제도", "금융상품 약관 설명 및 교부", "금융정보 및 금융교육 제공"]}]', '[{"type": "SOLUTION_TEXT", "data": "금융분쟁조정제도는 금융소비자가 금융회사를 상대로 제기하는 금융분쟁 건에 대해 신청내용에 기초하여 사실관계 등을 확인하고, 이에 대해 합리적인 분쟁해결 방안이나 의견을 제시함으로써 당사자간의 합의를 유도하여 소송으로 가지 않고 분쟁을 원만하게 해결하는 제도로 대표적인 사후적 금융소비자보호 절차이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (320, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융회사가 금융소비자보호법에 따라 금융소비자에게 금융상품을 권유하고 판매하는 과정에서 준수해야 하는 일반적인 영업 행위 원칙에 대한 설명으로 거리가 가장 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융회사는 금융상품이나 금융상품 자문에 대해 금융소비자와 계약을 체결하고 권리를 행사하고 의무를 이행할 때 신의 성실의 원칙에 따라야 한다", "금융회사는 금융상품이나 금융상품 자문에 대한 계약을 체결하는 경우 정당한 사유 없이 금융소비자를 부당하게 차별해서는 안 된다", "금융회사는 관련 법령에 따라 사업내용, 재무상황, 영업실적 등 자사(自社)의 중요한 경영 내용을 정확하게 공시해야 한다", "금융회사는 임직원 및 금융상품 판매 대리 또는 중개업자가 업무를 수행할 때 법령을 준수하고 공정한 거래질서를 해하는 일이 일어나지 않도록 성실히 관리해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "➀ 신의 성실 의무\n➁ 차별 금지\n➂ 기업의 공시 의무에 대한 설명으로 일반적인 영업 행위 원칙과는 거리가 멀다. \n➃ 금융회사의 관리책임\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (321, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융회사의 금융상품 약관 설명 및 교부 의무에 대한 설명으로 가장 올바른 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["약관이란 여러 명의 당사자가 여러 명의 상대방과 계약을 체결하기 위해 일정한 형식으로 미리 마련한 계약의 내용을 말한다", "금융투자회사는 표준약관이 아닌 개별 약관을 제정해서 사용하는 경우 사후에 금융투자협회에 보고해야 한다", "금융회사가 금융소비자에게 약관이 제때에 교부되지 않거나 중요 내용의 설명을 소홀히 한 경우라도 해당 계약의 취소는 불가능하다", "약관은 모든 금융소비자에게 동일하게 해석되어야 하고, 약관 내용의 해석이 불명확한 경우 금융소비자에게 유리하게 해석되어야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "➀ 약관이란 계약의 한쪽 당사자가 여러 명의 상대방과 계약을 체결하기 위한 계약의 내용이다.\n➁ 개별 약관 제정의 경우 금융투자협회로 사전에 보고하여야 한다.\n➂ 약관 미교부 및 중요 내용 설명 소홀은 계약 취소의 사유가 될 수 있다. \n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (322, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자는 금융상품에 가입하기 전에 금융회사가 제공하는 금융상품에 대한 정보 등을 꼼꼼히 확인하고 이해해야 한다. 다음 중 금융소비자에게 불이익으로 작용할 수 있어 주의가 필요한 사항으로 가장 거리가 먼 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["원금손실 가능성 및 손실 가능 범위", "중도해지 시의 불이익", "금융소비자의 개인정보 열람 청구 절차", "기한이익 상실 사유"]}]', '[{"type": "SOLUTION_TEXT", "data": "개인정보 열람 청구권은 정보주체로서 개인의 권리를 보장하기 위한 권리로 금융소비자에게 불리한 사항으로 보기 어렵다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (323, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융소비자의 손실이나 피해 구제를 위한 금융 민원상담 및 민원신청, 분쟁조정에 대한 설명으로 가장 올바른 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["민원 접수는 우편 접수만 가능하며 인터넷, 팩스 등의 방법으로는 접수할 수 없다", "민원의 처리 기간은 통상 금융 민원신청의 경우 14일, 분쟁조정의 경우 30일 이내이다", "금융소비자가 민원접수 신청 후 민원 문제를 해결하지 못한 경우 민원상담을 신청하여 처리한다", "분쟁조정을 신청한 경우 금융소비자는 분쟁조정위원회의 조정안을 반드시 수용해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "➀ 민원 접수는 우편, 팩스, 인터넷 등을 통해 가능하다. \n➂ 민원상담 신청 후 해결하지 못한 경우 민원을 접수하여 처리한다. \n➃ 금융소비자는 분쟁조정위원회의 조정안을 최종 수용할지 여부를 결정할 수 있다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (324, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인정보보호법에 따라 금융회사가 준수해야 하는 개인정보보호 원칙에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인정보 처리방침은 금융회사의 내부 사정에 따라 공개하지 않을 수 있다", "개인정보의 처리 목적을 명확히 밝히고, 필요한 범위에서 최소한의 개인정보만을 수집해야 한다", "정보 주체의 사생활 침해를 최소화 할 수 있는 방법으로 개인정보를 처리해야 한다", "관련 법령에 따른 책임과 의무를 준수하고 정보주체의 신뢰를 얻기 위해 노력해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "개인정보보호법 제3조제5항에 따라 개인정보 처리지침 등 개인정보의 처리에 관한 사항은 반드시 공개해야 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인정보보호 체계 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (325, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자 사기 유형 및 이에 대한 대처요령에 대한 설명으로 가장 거리가 먼 것은?  "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["무인가 금융투자업자의 불법 영업행위 - 해당 업체가 제도권 금융회사인지 금융소비자 정보표털 파인에서 확인하고, 거래 중단 후 금융감독원에 상담한다", "불건건 유사투자자문행위 - 유사투자자문행위 관련 계약해지 및 자문수수료 환불은 한국소비자원으로 문의하면 피해구제를 받을 수 있다", "불법 유사수신행위 - 유사수신 피해를 입은 경우 설명회 자료, 거래내역, 녹취 파일 등 증빙자료를 확보하여 경찰에 신고하거나 금융감독원에 제보한다", "저금리 대환대출을 빙자한 전화상담 사기 - 개인정보를 이용한 사기 가능성이 높으므로 각별히 유의한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "유사투자자문행위 관련 분쟁은 한국소비자원으로 문의하면 분쟁 관련 도움을 받을 수 있지만, 직접적으로 피해구제를 받을 수 있는 것은 아니다. (미신고)유사투자자문업자의 리딩(leading)을 따라 매매를 하여 손실을 입은 경우 피해구제가 어렵다(투자자 자기책임원칙)"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (326, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자사기 피해 예방 요령에 대한 설명으로 가장 거리가 먼 것은?  "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["무작위로 발송되는 투자권유 이메일, 편지, 전화는 무시한다", "투자를 위해 제공받은 자료 외에 별도로 관련 자료 또는 정보를 관련 공공기관 또는 전문가를 통해 확인한다", "합법적인 투자권유인 경우에도 가족이나 관련 분야 경험자와 투자결정을 논의한다", "특약 등 중요한 내용은 반드시 계약서에 반영하여 문서로 남겨야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "합법적인 투자라도 ‘수익보장’, ‘임대보장’ 등 특별한 약속사항은 구두협의만으로는 법으로부터 보호를 받을 수 없기 때문에 구두합의 내용을 반드시 계약서에 포함시켜야 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (327, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자상품 투자 전 확인이 필요한 사항에 대한 설명으로 가장 거리가 먼 것은?  "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["계좌를 개설하기 전에 어떠한 금융투자상품을 거래하기 위한 목적으로 계좌를 만드는지 확인한다", "계좌를 개설한 후에 계좌설정 약관의 내용을 확인한다", "투자자는 금융회사의 확인절차를 통해 확인한 본인의 투자성향에 맞게 투자 계획을 수립해야 한다", "금융투자상품은 중도해지 시 위약수수료가 발생할 수 있으므로 투자기간 및 투자자금 사용 계획을 잘 세워 투자해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자자는 계좌 개설 전에 금융투자회사가 제공하는 서비스와 금융투자상품별 계좌 설정 약관의 내용을 확인하고 동의해야 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
