insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (331, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객 A는 증권회사 직원 B에게 주식매매를 포괄적으로 위임하고 매매 결과를 주1회 보고받는 형태로 거래하고 있었다. 어느 날 증권회사 직원 B는 ◯◯카드 주식 9,000주 가량을 매수하고, 이를 고객 A에게 보고하자 A는 이를 질책하면서 앞으로 동 종목을 매수하지 말 것을 지시하였으나 이후 직원 B는 이를 어기고 동 종목을 다시 매수하였고 그 사실을 고객 A에게 보고하지 않음"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "SOLUTION_TEXT", "data": "고객 A는 직원 B에게 계좌의 운용을 포괄적으로 일임하였거나, 적어도 묵시적으로 사후 승인한 것으로 보인다. 그러나 고객 A가 직원 B에게 ◯◯카드 주식을 매수하지 말 것을 명확하게 지시함으로써 포괄적 대리권을 제한하였음에도 불구하고 직원 B는 동 주식을 매수하였고, 이후 고객 A에게 매수 사실을 보고하지 아니하였으므로 이는 임의매매에 해당함"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
