insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (333, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객 A는 증권회사 직원 B에게 자신의 계좌관리를 맡겨 직원 B에게 종목추천 및 주문집행을 담당하게 함. 이후, 직원B는 ☆와 ★종목에 대해 호재성 재료가 있음을 언급하며 순차적으로 매수를 추천하였는데 ☆의 경우 당시 공시 사항을 근거로 한 추천이었음에 반해 ★의 경우 단순한 시장풍문사항에 불과하였음. 결국, 고객A는 직원B의 추천에 따라서 ☆와 ★종목을 매입하였으나 두 종목 모두 주가가 하락하였고 특히 ★종목의 경우 직원B가 언급하였던 호재성 재료(풍문)는 현실화되지 않았음"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "SOLUTION_TEXT", "data": "직원 B가 시장에 공시된 사항을 근거로 매수를 추천하는 경우 매수추천이 아무런 근거 없는 본인의 단정적 판단에만 기인한 것이라 보기 어려우나, 당시 풍문에 불과한 사항에 대해 별도의 확인노력 없이 매수를 추천한 것이 사실이고, 그 과정에서 풍문이라는 사실에 대한 언급없이 마치 조만간 공시될 것처럼 고객 A를 오인케 한 표현을 사용한 행위는 단정적·합리적 근거없는 판단제공 행위로서 부당권유행위에 해당될 여지가 있음"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (334, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객 A는 △△은행과 금융거래를 하며 그동안의 거래로 친분을 쌓은 담당직원 B로부터 △△은행이 판촉행사 중인 고위험상품인 주가연계펀드(ELF)에 대해 투자권유를 받음. 직원 B는 고객 A가 평소에 은행예금 등 안정추구적인 투자성향임을 이미 알고 있었음에도 불구하고, 또한 직원 B 자신도 동 상품에 대해서 많은 시간동안 학습하지 않아 충분히 인지하지 못한 채 동 상품에 대하여 설명 및 적극적으로 투자를 권유하면서 상품의 수익구조 등에 대해서도 사실과 다르게 설명함"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "SOLUTION_TEXT", "data": "담당직원B는 고객 A와의 금융거래로 고객의 투자성향에 대해서 익히 파악하고 있었음에도, 직원 자신조차도 제대로 이해하지 못한 고위험의 펀드상품을 투자권유하였고, 동 펀드상품의 수익구조에 대해서도 사실과 다르게 왜곡하여 설명하여 고객 A에게 투자상품에 대한 위험성에 관한 올바른 인식형성을 방해하고, 과대한 위험성을 수반한 거래를 적극적으로 권유하여 고객보호의무 위반한 사실이 인정됨"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
