insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (341, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["디지털 전환에 성공한 기업에 대한 설명이다. <보기>의 (  ) 안에 들어갈 단어를 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["디지털 전환 작업을 추진하는 주체가 (   )이며, 강력한 카리스마로 조직과 문화의 패러다임 변화를 주도한다", "고객이 온라인, 오프라인, 모바일 등 어떠한 (   )에서 접근하든 고객에게 최상의 서비스를 경험할 수 있도록 옴니 (   ) 전략에 집중한다", "해결하려는 문제와 목표를 명확하게 세우고, 우선 구체적인 한두 가지 디지털 전환 프로젝트에서 시작한다", "참조: 《디지털 금융의 이해와 활용》, 김수진 저(수정 인용)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["CEO, 채널, 채널", "고객, 상황, 채널", "CEO, 상황, 멀티", "고객, 채널, 고객"]}]', '[{"type": "SOLUTION_TEXT", "data": "첫째, 디지털 전환 작업을 추진하는 주체가 CEO이며, 강력한 카리스마로 조직과 문화의 패러다임 변화를\n주도한다. 둘째, 고객이 온라인, 오프라인, 모바일 등 어떠한 채널에서 접근하든 고객에게 최상의 서비스를 경험할 수\n있도록 옴니 채널 전략에 집중한다. 셋째, 해결하려는 문제와 목표를 명확하게 세우고, 우선 구체적인 한두 가지 디지털 전환 프로젝트에서 시\n작한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털전환 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (342, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["인공지능에 대한 설명으로 올바른 것만을 <보기>에서 모두 고른 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["인공지능은 학습, 추론 및 언어 이해 능력 등 인간이 가진 지적 능력을 컴퓨터 프로그램으로 실현하는 기술이다", "인공지능은 사람의 지능과 마찬가지로, 문제 해결 능력, 학습 능력, 범용성의 3가지 특징을 갖고 있다", "인공지능이 가장 넓은 개념이고, 인공지능을 구현하는 방법 중 하나가 머신 러닝이다", "딥러닝은 머신러닝의 방법 중 하나로 인공 신경망(Artificial Neural Network) 알고리즘을 활용하는 기술 중 하나이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "SOLUTION_TEXT", "data": "인공지능은 학습, 추론 및 언어 이해 능력 등 인간이 가진 지적 능력을 컴퓨터 프로그램으로 실현하는 기술이다. 인공지능은 사람의 지능과 마찬가지로, 문제 해결 능력, 학습 능력, 범용성(패턴 인식, 추론 능력)의 세 가지 특징을 갖고 있다. 인공지능과 관련하여 머신 러닝(Machine Learning, 기계 학습)과 딥 러닝(Deep Learning,심층 학습)을 혼용하여 사용하는 경향이 있는데, 인공지능이 가장 넓은 개념이고, 인공지능을 구현하는 방법 중 하나가 머신 러닝이며, 딥 러닝은 머신 러닝의 방법 중 하나로 인공 신경망(Artificial Neural Network) 알고리즘을 활용하는 기술 중 하나이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (343, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (  ) 안에 들어갈 말을 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["과거 : 규칙&데이터 → 전통적 프로그래밍 → 해답", "현재, 미래 : 데이터&해답 → (    ) → 규칙", "(   ) = 알고리즘 + (    ) + 발달된 하드웨어"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["빅데이터, 딥러닝, 인공지능", "인공지능, 딥러닝, 머신러닝", "머신러닝, 딥러닝, 빅데이터", "딥러닝, 인공지능, 채널"]}]', '[{"type": "SOLUTION_TEXT", "data": "수백 장의 아기 사진을 학습시키고 무작위로 수천 장의 사진을 제시하면 학습을 통해 아기 사진만 분류하는 것이 머신 러닝에 해당한다. 반면 컴퓨터에서 스스로 다양한 아기 사진을 찾아보고 아기에 대해 학습한 뒤, 새로운 아기를 보았을 때 아기라고 인식하는 것이 딥 러닝에 해당한다. 따라서 딥러닝을 위해서는 고도로 발달한 하드웨어와 빅데이터는 필수요소인 셈이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
