insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (345, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["로보어드바이저의 장점에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["차별화된 고비용 서비스", "데이터 기반의 객관적인 추천", "시간과 장소에 구애받지 않는 편리함", "인간의 개입이 초래할 수 있는 실수 예방"]}]', '[{"type": "SOLUTION_TEXT", "data": "기존 개인형 맞춤 자산관리 서비스는 고액자산가에게만 제공하던 금융서비스였다. 하지만 로보어드바이저는 기존 대면서비스에 비해 물리적 제약이 거의 없기 때문에 서비스 가입에 높은 제약을 둘 필요가 없다. 따라서 가입 최소금액을 상당히 낮게 책정할 수수료도 대면서비스에 비해 절반이상 저렴하다. 그렇기 때문에 모든 고객에게 평등하면서도 일반적인 서비스가 제공되는 것이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (346, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["모바일 기기 금융거래 이용자 안전거래 10계명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융거래 비밀번호 안전하게 관리하기", "모바일기기 수리 전 중요정보 삭제하기", "일회용 비밀번호(OTP) 사용하기", "공용 무선랜(Wi-Fi) 사용하기"]}]', '[{"type": "SOLUTION_TEXT", "data": "출처가 불분명하거나 보안설정이 없는 무선랜(Wi-Fi)를 사용하면 모바일 기기의 도용의 우려가 있으니 금융거래 시 반드시 이러한 무선랜의 사용을 자제하여야 한다. 그 외에 모바일기기의 잠금기능을 설정하기, 비밀번호는 수시로 변경하기, 보안 업데이트를 정기적으로 수행하기, 바이러스 검사하기 등을 들 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 금융 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (347, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["사이버 보안에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["CCTV는 사이버 보안의 일종이다", "사이버 보안은 시스템, 네트워크 및 프로그램을 디지털 공격에서 보호하는 방식이다", "사이버 보안은 악의적인 로그인과 코드로부터 자산을 보호하는 것에 초점이 맞춰진 활동이다", "사이버 보안 관리는 금융회사나 일반 기업이 해야 하지만, 디지털 금융 소비자도 사이버 공격에 대한 이해도를 높여 이로 인한 피해 예방에 노력해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "사유지침입이나 절도로부터 방어하는 보안은 사이버 보안에 대비되는 물리적 보안에 해당한다. 아무리 첨단기술을 바탕으로 하더라도 CCTV나 적외선 감지기들은 사이버 보안의 범주에 들지 않는다. 왜냐하면 사이버보안이란 컴퓨터와 네트워크, 데이터를 악의적인 전자적 공격으로부터 보호하는 전반적인 활동을 일컫기 때문이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (348, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인정보보호법상 개인정보에 해당하는 것으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["성명, 주민번호 등을 통하여 개인을 알아볼 수 있는 정보", "해당 정보만으로는 특정 개인을 알아볼 수 없더라도 다른 정보와 쉽게 결합하여 알아볼 수 있는 정보", "개인이 소속된 법인 또는 단체의 정보", "가명처리를 통해 추가정보의 결합 없이는 특정 개인을 알아볼 수 없는 정보"]}]', '[{"type": "SOLUTION_TEXT", "data": "개인정보의 주체는 자연인이어야 하며, 법인 또는 단체의 정보는 이에 해당하지 않는다. 반면 성명, 주민번호 등 개인을 알아 볼 수 있는 정보와 결합하면 쉽게 알 수 있는 정보 등을 가명 처리함으로써 원래의 상태로 복원하기 위한 추가 정보의 사용, 결합없이는 특정 개인을 알아볼 수 없는 정보인 가명정보도 보호해야할 개인정보에 해당한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인정보보호 체계 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (349, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>는 디지털 금융 사기의 종류와 의미를 설명한 글이다. (   )에 들어갈 내용을 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(    )은 문자메시지(SMS)와 (    )의 합성어이다", "(    )은 악성코드에 감염된 사용자의 PC를 조작하여 금융 정보를 빼내는 것을 가리킨다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["스미싱(Smishing), 파밍(Pharming), 피싱(Phishing)", "스미싱(Smishing), 피싱(Phishing), 파밍(Pharming)", "파밍(Pharming), 스미싱(Smishing), 피싱(Phishing)", "파밍(Pharming), 피싱(Phishing), 스미싱(Smishing)"]}]', '[{"type": "SOLUTION_TEXT", "data": "스미싱 : 문자메시지(SMS)와 피싱(Phishing)의 합성어로, 문자메시지에 포함된 인터넷 주소를 클릭하면 악성코드가 스마트폰에 설치되어 피해자도 모르는 사이에 소액결제 피해가 발생하거나 개인금융정보가 탈취된다.\n파밍 : 악성코드에 감염된 사용자의 PC를 조작하여 금융 정보를 빼내는 것을 가리킨다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (350, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인정보 유출 예방법에 대한 설명으로 올바른 것만을 <보기>에서 모두 고른 것은?."], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["주소 변경 시 거래 금융회사에 즉시 통보하여 거래 내역, 잔고, 약관의 변경 등 거래와 관련한 중요한 변동 사항을 받아볼 수 있도록 조치한다", "금융거래 시 사용하는 각종 비밀번호는 고유하고 타인이 추측하기 어려운 문자로 지정하여 사용한다", "비밀번호는 분실 예방을 위해 종이나 PC 등에 기록한다", "공용으로 사용하는 PC방 등에서는 개인정보를 조회하지 않는다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄴ, ㄹ", "ㄱ, ㄷ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "SOLUTION_TEXT", "data": "비밀번호는 분실 시 금융사고의 위험이 있기 때문에 종이나 PC 등에 남기지 말아야 한다. 그리고 정기적으로 변경하여야 하며 그외의 개인정보 유출 예방책으로 시스템 접속 상태에서 자리를 비울 때는 반드시 로그아웃하여야 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인정보보호 체계 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (351, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["홍길동씨가 인터넷 링크가 포함된 지인의 문자를 받았을 경우 취해야 할 행동으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["문자내용을 꼼꼼히 확인 후 링크에 들어가서 내용을 파악한 후 지인에게 확인전화를 한다", "미확인 앱이 함부로 설치되지 않도록 스마트폰의 보안 설정을 강화한다", "이동통신회사 고객센터에 전화하거나 인터넷 홈페이지를 통하여 소액결제를 원천 차단한다", "백신프로그램을 설치하고 주기적으로 업데이트한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "출처가 확인되지 않은 문자메시지의 인터넷 주소는 절대로 클릭을 하여서는 안 된다. 부인이나 부모, 자식 등 아무리 가까운 사람일지라도 내용을 확인하기 전에 반드시 문자를 보낸 사람에게 확인을 한 후, 맞는 경우에만 클릭을 하는 습관을 들여야 한다. 이미 피해를 본 상태라면 인식을 하자마자 다음의 조처를 해야 한다. 제일 먼저 경찰청 및 사이버수사대나 금융감독원 등에 피해 사실을 신고해야 한다. 그리고 수상한 인터넷 주소를 클릭해 의문의 프로그램이 설치됐다면 서둘러서 핸드폰 통신사를 통해 공장 초기화를 진행하는 것이 좋다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (352, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자계획을 수립할 때 고려해야 할 사항만을 모두 고른 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["투자자의 위험 감내 수준", "투자기간", "자산배분", "제약조건"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ, ㄷ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자 관리는 크게 투자 계획(Plan)을 수립하고 실행하고(do) 통제(see)하는 3단계를 거친다. 투자 계획 수립은 먼저 투자 목표를 수립하는 것에서 시작하는데, 이때 투자자의 위험 감내 수준, 투자 기간, 제약 조건을 함께 고려해야 한다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자계획 수립 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (353, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["통합적인 투자관리가 이루어지는 과정을 순서대로 올바르게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["투자계획", "자산배분", "포트폴리오 평가", "종목선정"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ-ㄴ-ㄷ-ㄹ", "ㄱ-ㄷ-ㄴ-ㄹ", "ㄱ-ㄴ-ㄹ-ㄷ", "ㄱ-ㄹ-ㄴ-ㄷ"]}]', '[{"type": "SOLUTION_TEXT", "data": "Plan(투자계획)-Do(자산배분 및 종목선정)-See(포트폴리오 평가)의 순서로 투자관리가 이루어진다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자관리 프로세스 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (354, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["일반적으로 장기 투자성과에 가장 큰 영향을 미치는 것으로 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분", "종목선정", "투자타이밍", "리밸런싱"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자 전략을 실행하는 과정에서 투자자는 자산 배분, 종목 선정, 투자 시기 포착이라는 3가지 문제에 직면한다. 이중 투자성과에 가장 큰 영향을 미치는 것은 자산배분이다. 1974~1983년 91개의 대규모 연금 플랜을 연구한 결과, 자산 배분이 포트폴리오 운용 성과에 91.5%의 영향을 미치는 것으로 나타났고, 증권 선택(2.7%)과 시장 예측(1.8%)이 미치는 영향은 미미했다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 중요성 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (355, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자전술을 수립하는 과정에 대한 설명으로 (    ) 안에 들어갈 말을 순서대로 올바르게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["먼저 주식과 채권 등 자산에 대한 투자 비율을 정하고 구체적인 종목을 선정하는 것을 (    )이라고 하고, 반대로 투자 종목을 먼저 선정하고 자산 배분을 나중에 하는 것을 (    )이라고 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상향식, 하향식", "하향식, 상향식", "적극적, 소극적", "소극적, 적극적"]}]', '[{"type": "SOLUTION_TEXT", "data": "자산 배분이 전략을 수립하는 것이라면, 종목 선정과 투자 타이밍을 선정하는 것은 구체적인 전술이다. 먼저 주식과 채권 등 자산에 대한 투자 비율을 정하고 구체적인 종목을 선정하는 것을 하향식(Top Down)이라고 하고, 반대로 투자 종목을 먼저 선정하고 자산 배분을 나중에 하는 것을 상향식(Bottom Up)이라고 한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자전술 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (356, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["소극적 투자관리에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매수와 매도를 최소화해서 거래 비용을 줄인다", "증권시장에는 이미 이용할 수 있는 모든 정보가 반영되어 있다", "증권시장에서 과대평가 또는 과소평가된 증권을 찾기 어렵다", "다소 위험을 감수하더라도 초과 수익을 획득할 수 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이라고 여긴다. 증권시장에는 이용할 수 있는 모든 정보가 반영되어 있으므로 과대 또는 과소 평가된 증권을 찾기 어렵다. 그래서 투자 관리의 초점을 초과 수익을 획득하는 것보다는 투자 위험을 최소화하는 데 두고 시장수익률을 추구한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (357, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["증권시장이 효율적이라고 전제하는 소극적 투자 관리 전술에 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식을 매수한 다음 장기보유한다", "인덱스펀드에 투자한다", "시장대표지수를 추종하는 상장지수펀드(ETF)에 투자한다", "미래 성장이 예상되는 일부 종목에 집중투자한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "전략적 차원에서 결정된 자산 배분의 범위 내에서, 최적의 투자 대안을 선정하는 전술적 차원에서의 대응은 적극적 투자 관리와 소극적 투자 관리로 나눌 수 있다. 이중 소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이기기 때문에 과대평가 또는 과소평가 된 증권을 찾기 어렵다고 생각한다. 구체적인 종목을 분석하거나 독자적으로 판단하지 않고, 시장 전체의 일반적인 예측을 그대로 받아들여 정보 비용을 줄이고 매수와 매도를 최소화해 거래 비용을 줄인다. 단순 매수 후 보유하는 전략, 인덱스펀드와 ETF를 활용한 투자, 정액 분할 투자가 여기에 해당된다. 일부 종목에 집중투자하는 것은 적극적 투자관리 전략에 해당한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (358, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["홍길동씨는 주식시장에 이용할 수 있는 모든 정보가 반영되어 있다고 생각하지 않는 투자자이다. 홍길동씨의 생각에 부합하는 투자 전술로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식시장의 이례적인 현상을 활용해 투자한다", "적정한 투자타이밍을 찾기 위해 노력한다", "특정 산업이나 소수 종목에 집중 투자한다", "주식을 매수한 다음 장기보유 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "적극적 투자 관리(Active Investment Style)는 다소 위험을 감수하더라도 시장의 평균을 넘어서는 초과 수익을 추구한다. 증권시장이 비효율적이라고 전제하고, 위험이 동일한 증권들 중에서 수익이 높은 증권을 선택하거나, 기대 수익이 동일한 증권 중에서 위험이 낮은 증권을 선택하는 방법이다. 적극적 투자 관리 방법으로는 소주종목이나 특정 산업에 집중적으로 투자하거나, 투자 타이밍을 포착하거나, 시장의 이례적인 현상을 이용해 종목을 선정하는 것이 대표적이다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (359, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["\"계란을 한 바구니에 담지 말라\"는 격언과 관련 있는 투자 방법으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["분산투자", "집중투자", "거치식투자", "마켓타이밍 전략"]}]', '[{"type": "SOLUTION_TEXT", "data": "분산투자라의 중요성을 표현한 대표적인 말이다. 분산투자는 계란을 옮길 때 많은 바구니를 사용한다는 뜻 이상이다. 다른 것들과 분명히 구별되는 바구니를 찾는 것이 중요해졌다. 각 바구니의 독특한 수익률 형태가 다른 바구니의 손익을 부분적으로 상쇄해서 전체 포트폴리오의 변동성을 낮추기 때문이다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (360, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["한 해의 절반은 건기이고, 나머지 절반은 우기인 섬나라에서 당신이 우산을 판매하고 있다고 가정해 보자. 날씨에 따른 매출액 변동을 줄이려면 어떤 제품을 추가해서 판매하는 것이 좋을까?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["아이스크림", "우산", "비옷", "장화"]}]', '[{"type": "SOLUTION_TEXT", "data": "분산투자효과를 이해하고 있는지 묻는 질문이다. 날씨가 제품 판매량에 영향을 미친다. 우기에는 우산, 우의, 장화 판매가 늘어나겠지만, 건기에는 판매가 급감한다. 대신 아이스크림 판매가 늘어날 수 있다. 커피 판매량은 날씨에 크게 영향을 받지 않는다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
