insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (363, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["분산투자 효과에 대한 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["분산투자를 하면 전쟁이나 천재지변에 따른 위험도 줄일 수 있다", "보유 종목 수를 계속 늘린다고 투자 위험이 계속 줄어들지는 않는다", "분산투자를 해도 노사문제와 같은 기업 고유의 위험은 줄어들지 않는다", "보유 종목 수를 늘려 나갈수록 위험 감소 폭은 계속해서 커진다"]}]', '[{"type": "SOLUTION_TEXT", "data": "포트폴리오 내에 편입된 종목 수를 늘리면 계속해서 위험이 줄어들지는 않는다. 보유 종목 수를 추가할수록 위험이 줄어들기는 하지만, 감소의 폭은 줄어든다. 통상 포트폴리오에 30개 종목을 편입하면 비체계적 위험은 대부분 사라지며, 더 분산투자를 해도 위험은 거의 줄어들지 않는다. 따라서 포트폴리오를 구성할 때 기대 수익률과 표준편차, 자산 간의 상관관계를 고려하여 적절한 분산으로 가장 최적의 포트폴리오를 구성할 수 있도록 조율해야 한다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
