insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (469, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["형식적으로는 나중에 이자를 붙여 다시 매입하는 조건으로 채권을 매각하는 것이지만 실질적으로는 보유중인 채권을 담보로 자금을 빌리는 단기금융거래를 의미하는 용어로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["콜(call)", "환매조건부채권(RP)", "양도성예금증서(CD)", "기업어음증권(CP)"]}]', '[{"type": "SOLUTION_TEXT", "data": "① 콜은 금융기관(주로 은행) 사이에서 1일 또는 단기자금을 융통할 때 쓰이는 거래를 뜻한다. 과거 기관들 사이에 전화(call)를 통하여 금리가 결정되어 거래되었기에 붙여진 이름이다. \n③  CD는 양도성예금증서, 즉 증서로 거래될 수 있는 정기예금이다.\n④ CP는 기업에서 발행하는 기업어음을 뜻한다.\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 분류 이해', null, null);
