insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (489, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["이자소득에 대한 총 세율이 20%라고 할 때, 다음 중 세후 실효수익률이 가장 높은 채권은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["발행금리가 0%인 시장금리 5.5%의 1년 만기 채권", "발행금리가 3%인 시장금리 6%의 1년 만기 채권", "발행금리가 6.5%인 시장금리 6.5%의 1년 만기 채권", "발행금리가 8%인 시장금리 7%의 1년 만기 채권"]}]', '[{"type": "SOLUTION_TEXT", "data": "① 세후 실효수익률 = 5.5%-(0%×0.2) = 5.5%\n② 세후 실효수익률 = 6%-(3%×0.2) = 5.4%\n③ 세후 실효수익률 = 6.5%-(6.5%×0.2) = 5.2%\n④ 세후 실효수익률 = 7%-(8%×0.2) = 5.4%"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 수익률 이해', null, null);
