insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (491, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["일반적으로 경기가 좋아지면 주가는 상승하나 채권 가격이 하락하고, 반대로 경기가 나빠지면 주가는 하락하나 채권 가격이 상승한다. 이는 경기라는 변수에 대해서 주식과 채권 가격이 (         )이기 때문이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상관계수가 1", "상관계수가 0", "상관계수가 마이너스", "상관계수가 플러스"]}]', '[{"type": "SOLUTION_TEXT", "data": "상관계수는 -1부터 +1까지 산출되며, +1은 완전히 같은 방향으로 움직이는 것을, -1은 완전히 반대 방향으로 움직이는 것을 의미한다. 상관계수가 마이너스 라는 것은 대체로 반대방향으로 움직인다는 것을 의미한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상관계수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (492, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["경제지표에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["GDP(국내총생산)와 경기종합지수는 일반적으로 금융시장 참여자가 중요하게 생각하는 경제지표이다", "분기별로 발표되는 GDP수치는 금리나 주가에 즉각적인 영향을 미친다", "경기종합지수는 경제의 각 부문을 대표하는 경제지표들을 가공, 종합하여 하나의 지표로 나타난 것이며 선행지수, 동행지수, 후행지수로 나누어진다", "실업률, 전경련BSI(기업경기실사지수), 통계청CSI(소비자전망지수) 등도 금융시장에서 관심을 가지는 경제지표이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "금리나 주가에 직접적인 영향을 미치는 것은 GDP 지표 발표치 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '경제지표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (493, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["물가와 채권 금리의 관계에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["향후 물가가 크게 오를 것으로 예상되면 돈에 대한 수요가 증가해 시중 금리는 오르게 된다", "물가가 기대치보다 크게 오르게 되면 물가 안정을 제1의 목표로 삼는 한국은행은 기준금리를 올리는 등 시중 자금을 흡수해 돈의 공급을 줄이는데, 이는 시장 금리를 상승시키는 원인이 된다", "물가 상승으로 한국은행의 기준금리 인상 등 통화긴축이 예상될 경우 단기적으로 주가에 긍정적인 영향을 끼친다", "소비자 물가 지수는 한국은행의 통화 정책 결정 시 중요한 참고가 되기에 금융시장에 미치는 영향이 큰 편이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "물가 지수의 변화가 채권시장에 끼치는 영향은 직접적인 데 반해 주식시장에 끼치는 영향은 다소 간접적이다. 물가 상승으로 한국은행의 기준금리 인상 등 긴축 통화가 예상될 경우 단기적으로 주가에는 좋지 않은 영향을 끼칠 수 있다. 하지만 장기적으로 물가 수준의 상승은 곧 기업이 보유하고 있는 자산 가격의 상승, 기업이 창출하는 명목 이익의 상승으로 연결되므로 주가에 긍정적으로 작용한다는 분석이 많다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '물가의 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (494, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 상황들이 시중금리에 어떤 영향을 미치게 되는지 금리의 방향을 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금리 하락, 상승, 상승, 상승", "금리 하락, 하락, 상승, 상승", "금리 하락, 상승, 하락, 상승", "금리 하락, 상승, 하락, 하락"]}]', '[{"type": "SOLUTION_TEXT", "data": "기준금리 상향 등을 통한 통화 긴축정책은 시중 유동성을 흡수하며 금리상승을유발하며 확장적 재정정책 (경기부양 재정정책)은 국채발행과 경기부양 가능성 확대로 금리상승을 유발한다. 주식시장 상승은 경기지표의 호전과 채권시장 자금의 주식시장 이동을 통해 금리상승을 유발한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금리변동요인 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (495, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["증시주변 자금에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자들이 가장 관심 깊게 지켜보는 증시주변자금 지표는 고객예탁금과 신용잔고의 추이인데 일부 전문가는 고객예탁금과 신용잔고를 합한 총계 금액을 종합 증시주변자금으로 계측한다", "고객예탁금은 언제든지 주식매입 자금으로 동원될 수 있는 대기자금 성격이 강하기에 증가할수록 주식시장에 호재로 해석하는 전문가들이 많다", "신용잔고는 언젠가 주식의 매도 등을 통해 상환해야 하기에 일정 기간내 주식 매도가 발생할 가능성이 매우 높은 일종의 매도대기물량으로 계산된다", "주식 공매도는 과열된 주가를 정상화시키기도 하며 증권시장의 유동성을 높이기도 하는 순기능이 있지만 불법적인 매매를 위해 주식 공매도가 실행되는 역기능도 존재한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "일부 전문가는 고객예탁금에서 신용잔고를 차감한 금액을 종합 증시주변자금으로 계측하기도 한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '증시주변자금 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (496, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["환율과 경제에 관한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A국 상품의 경쟁력에 비해 A국의 통화가치가 하락하면 무역수지를 포함한 경상수지나 국제수지의 흑자 증가로 A국의 경제성장에 플러스 요인이 된다", "다른 나라 통화들에 비해 A국의 통화가치가 하락한다는 것은 다른 나라들의 입장에서는 A국에서 생산되는 상품 또는 주식 등 A국 자산들의 가격이 떨어진다는 것과 같다", "A국 상품의 경쟁력에 비해 A국의 통화가치가 상승하면 국부의 가치 증가로 경제성장에 플러스 요인이 된다", "1997년 IMF 외환위기 전까지만 하더라도, 미국과 일본의 플라자 합의로 한국이 수혜를 입었던 3년(1986~1988년)을 제외하고는, 한국은 수십년간 고질적으로 무역수지 적자를 지속하였다"]}]', '[{"type": "SOLUTION_TEXT", "data": "수 많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다. 이른바 전 세계적으로 확산되어 있는 소리없는 전쟁, 환율전쟁이 그것이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '환율의 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (497, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["자본시장과 거시경제 변수에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시중금리 수준이 높아지면 더 많은 대출이 일어나 시중에 통화량(유동성)이 더욱 풍부해진다", "한국은행은 기준금리 결정, 시중 은행의 지급준비율 결정, 통안채 발행량 조정의 3가지 주요 방법으로 시중 통화량을 조절한다", "실업률은 한 나라의 경제 활동을 포괄적으로 나타내며 1년에 1번 연말에 발표된다", "수많은 국가들이 자국의 통화가치를 강하게 하여 국가의 달러 환산 부(富)가 높아지도록 유도하고 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "시중 금리 수준이 높아지면 대출이 줄어들어 시중 유동성이 줄어들게 된다. 실업률은 매월 발표된다. 수많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거시경제변수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (498, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["행동편향을 통한 실수가 금융투자상품과 서비스를 선택할 때 빈번히 발생하는 이유에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자상품에 대한 경험과 기회가 적다", "많은 금융투자 의사결정이 감정적으로 이루어진다", "투자의사결정의 실수가 있었다면 이를 반복하지 않기 위해 노력한다", "금융투자자들이 금융상품을 복잡하게 여겨 제대로 알고 의사결정을 하려 하지 않는다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 금융상품에 대한 학습경험의 기회가 적기 때문에 행동편향에 따라 행동한다.\n지문 2의 경우, 많은 금융투자 의사결정이 비용 대비 효용을 따지기보다는 스트레스, 걱정, 손실이나 후회에 대한 두려움에 떠밀려 의사결정을 한다.\n지문 3의 경우, 금융소비자가 행동편향에 따라 투자의사를 결정하면 혹독한 대가를 치룬 후 후회하는 모습을 반복할 수 있다. 이런 투자의사결정의 실수를 극복하기 위해서 내가 가진 행동편향을 이해할 필요가 있다.\n지문 4의 경우, 대부분의 금융투자자들은 금융투자상품을 복잡하게 여겨서 제대로 알고 의사결정을 하려는 시간과 노력을 기울이지 않고 행동편향에 따라 선택한다.\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 발생이유 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (499, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["다음 신문 기사의 내용을 통해 유추해 볼 수 있는 김씨의 행동편향으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["<기사> 투자 경험이 없는 김씨는 어느 날 친구로부터 주식으로 돈을 벌었다는 얘기에 귀가 솔깃했다. 좋은 종목 있으면 추천해달라고 친구에게 요청했고, 친구는 증권 브로커 B씨를 소개해주었다. 이씨는 전문가처럼 주식에 관해 유창하게 설명해 주었고 바이오 종목 1개를 추천했다. 김씨는 1천만원을 투자했고, 이익이 났다. 김씨는 투자에 대해 자신감이 붙었고 금액도 1억원으로 늘리고 투자종목도 늘렸다. 그러나 몇 개 종목에서 손해가 나기 시작했고 원금까지 손실을 입기 시작했다. 덜컥 겁이나 이제라도 그만해야 싶었지만 손실이 커져 그만할 수 없었다. 급기야 원금을 반이나 날린 후에 진작 그만두었어야 한다고 후회했다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향", "사후확증 편향", "손실회피 편향", "자기과신 편향"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 투자 경험이 없는 김씨가 친구가 수익이 났다는 얘기만을 듣고 귀가 솔깃해 추천을 권유한 것은 군중심리 편향에 가깝다.\n지문 2의 경우, 사후확증 편향은 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이미 그렇게 될 줄 알고 있었던 것처럼 믿게 되는 오류로 상기 기사내용과는 거리가 멀다.\n지문 3의 경우, 원금까지 손실을 입기 시작한 이후 그만하지 못한 것은 손실을 회피하려는 편향 때문이다.\n지문 4의 경우, 투자에 대한 경험이 없었으나 초기 투자 성공으로 투자에 대한 자신감이 붙어 투자 금액을 쉽게 늘린 것은 자기과신 편향의 영향으로 볼 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (500, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["한 설문조사 결과 운전자의 88%가 자신이 평균보다 운전을 잘한다고 생각하며 그 중 80% 이상이 자신의 운전실력이 상위 30% 안에 든다고 생각한다고 한다. 이렇듯 사람들은 본인에 대한 과도한 믿음, 즉 자기과신을 하는 경향이 강하다. 이러한 자기과신 편향이 투자의사결정에 영향을 주었을 때의 반응으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그 팀장 설명도 그럴싸한데 다가 지난 번에도 그 말대로 되더라니까. 이번에도 맞을 거야", "처음에는 조금 불안하기도 했는데 매달 꼬박 꼬박 수익금이 들어오더라. 그래서 더 돈을 넣으려고. 너도 해봐", "낮은 금리의 퇴직연금 포트폴리오를 조정해야지 조정해야지 생각만하고 계속 그대로 놨둬 기회를 놓쳐버렸어", "내가 1년 간 지켜봤는데 이 주식은 계속 오르지 않고 올랐다가 내려갔다 반복만 해. 이 정도 내려가면 다시 올라가니까 지금 사야해"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 자기자신 뿐만 아니라 타인의 말을 그대로 믿고 행동하는 것도 그런 대상을 내가 잘 골랐다는 자기과신 편향의 영향이다. \n지문 2의 경우, 폰지 사기의 전형적인 수법으로 몇번의 수익금 수령이 초기 불안을 없애고 자기과신을 통해 더 많은 돈을 투자한다.\n지문 3의 경우, 현상유지 편향이 영향을 주었을 때의 행동으로 조정할 의사가 있거나 조정해야 할 상품을 그대로 유지하게 한다.\n지문 4의 경우, 자기과신 편향은 이렇다 할 대표성이 없는 몇차례의 경험을 통해 더욱 단단해져 이번에도 지난번처럼 될 거라 믿고 행동하게 만든다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (501, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["다음 투자 격언은 어떤 행동편향에 대한 주의를 주고 있는가?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["우리가 위험에 빠지는 이유는 무언가를 몰라서가 아니다. 확실히 안다고 착각해서다. - 마크 트웨인"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "손실회피 편향", "자기과신 편향"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1의 경우, 사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급하는 것을 말한다.\n지문2의 경우, 사람들이 합리적이거나 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는 편향이다.\n지문3의 경우, 사람들이 이득보다 손실이 더 크게 느껴, 이익 증가보다 손실 증가에 민감하게 느끼는 것을 말한다.\n지문4의 경우, 사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이 믿고 행동한다. 이러한 자기과신 편향은 자신이 가지고 있는 능력이나 정보, 지식 수준을 실제보다 더 높거나 정확하다고 믿게 만들어 섣불리 위험한 투자를 할 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (502, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["군중심리는 주식시장이 급격히 오르거나 떨어질 때 자주 작동하여 시장을 과열시키거나 쉽게 얼어붙게 한다. 이러한 군중심리에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["남들이 주식으로 돈을 벌었다는 소식이 들리면 소외감이 커져 군중심리가 발동한다", "경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 현혹되지 않는다", "군중심리에 빠지는 것은 다른 이들과 다른 결정을 내리는 것보다, 그들과 함께하는 것이 더 마음이 편하고 쉽기 때문이다", "사회에 순응하고 싶어하는 인간의 본능 때문에 대다수와 다르게 행동함으로써 군중에서 소외되고 싶지 않아 군중심리가 생긴다"]}]', '[{"type": "SOLUTION_TEXT", "data": "군중심리의 기저에는 사회에 순응하고 군중에서 소외되고 싶지 않은 인간의 본능이 깔려 있다. 이에 따라 남들이 투자를 통해 돈을 벌었다거나 하면 거기서 소외되지 않게 투자에 뛰어든다. 경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 더 취약하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (503, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 빠르게 직관적으로 생각하는 시스템1과 느리고 노력이 필요한 시스템2가 그것이다. 사람들은 금융투자 의사결정 시 후자를 사용한다고 착각하지만 전자를 사용해 결정하는 경우가 많다. 금융투자 시 시스템1이 이익과 손실상황에서 보이는 성향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["이익상황에서는 위험회피성향을, 손실상황에서는 위험회피성향을 보인다", "이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보인다", "이익상황에서는 위험보유성향을, 손실상황에서는 위험보유성향을 보인다", "이익상황에서는 위험보유성향을, 손실상황에서는 위험회피성향을 보인다"]}]', '[{"type": "SOLUTION_TEXT", "data": "향후 주식시장에 대한 긍정적인 전망이 우세할 때 이미 수익이 난 계좌를 보유한 투자자는 긍정적인 전망에도 불구하고 이익을 실현하고 싶어한다. 미래의 불확실성이 싫기 때문에 당장의 이익을 챙겨야 할 것 같아서이다. 반대로 주식시장에 대한 부정적인 전망이 우세 할 때 이미 손실이 난 계좌를 보유한 투자자는 시장상황이 부정적이어서 지금의 손실규모가 더 커질 수 있음에도 사람들은 손실을 확정 짓지 않고 위험을 보유하려고 한다. 이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보이고 있는 것이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '손실회피성향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (504, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["합리적인 투자자라면 시장 상황이나 미래 가치 등을 판단하여 보유하고 있는 주식의 매도 여부를 결정한다. 그러나 일반적으로 그 종목의 적정가치를 따져보려 하지않고 자신이 구입한 가격만을 최우선 결정요인으로 판단하는 경우가 많다. 이러한 편향을 일컫는 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향", "기준점 편향", "시기추론 편향", "도박사의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "사람들은 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는데 이를 기준점 편향이라한다. 기준점 편향을 앵커링효과라고 도 한다.  앵커링효과에서 앵커(Anchor)는 배를 정박시킬 때 배가 떠내려가지 않도록 하는 닻을 의미하며 앵커링효과는 배를 정박시키는 닻을 내리듯 사람의 생각이나 판단이 그 기준점을 벗어나지 않는 것을 말한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준점편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (505, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융상품 투자 시 ''과거의 운용실적이 미래의 수익을 보장하지 않습니다''라는 문구는 다음 중 어떤 행동편향을 방지하기 위한 것인가?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["손실회피 편향", "사후확증 편향", "매몰비용의 오류", "과거추세지속가정 편향"]}]', '[{"type": "SOLUTION_TEXT", "data": "과거의 운용실적이 미래의 수익을 보장하지 않는다는 것은 투자의사 결정을 할 때, 과거의 수익만을 보고 앞으로도 그런 수익이 지속될 것이라고 판단하는 것은 위험하다는 것을 알리는 것이다. 과거의 수익은 미래의 수익을 담보하는 대표성이 있는 것이 아닌데 앞으로도 과거처럼 수익이 날거라고 믿고 행동하는 과거추세지속가정 편향을 막기위한 것이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '과거추세지속가정편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (506, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자를 하기 전에 빠지기 쉬운 대표적인 행동편향 만을 짝지어 놓은 것으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향, 심적회계, 현재중시 편향", "군중심리 편향, 확증편향, 손실회피 편향", "사후확증 편향, 심적회계, 현상유지 편향", "사후확증 편향, 손실회피 편향, 현재중시 편향"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자를 하기 전에 빠지기 쉬운 대표적인 행동편향은 군중심리 편향(주위에서 투자를 통해 돈을 벌었다는 소식을 들었을 때 투자를 하지않은 자신만 바보가 된 거 같은 기분이 들어 투자시장에 뛰어든다), 심적회계(예상치 못했던 성과급이나 연말정산 후 세금 환급액이 들어오면 없던 돈인 셈치고 평상시 위험하다고 생각했던 주식에 몰빵 투자한다), 현재중시 편향(자신의 미래 재무목표에 맞는 투자가 아니라, 즉각적인 욕망을 충족하려는 충동에 따라 단기 고수익만을 목표로 투자한다)이다.\n손실회피 편향이나 확증편향, 사후확증 편향은 투자 후 빠지기 쉬운 대표적인 행동편향이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (507, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서는 방어형 투자자들이 빠지기 쉬운 대표적인 행동편향 중 하나를 설명하고 있다. 이에 해당하는 행동편향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["저금리시대가 지속되면서 합리적인 금융투자를 통해 미래 자산을 만들어 가야하지만 위험 추구 성향이 낮은 방어형 투자자들은 기본적으로 손실을 두려워해 투자를 꺼려한다. 이들도 저금리 기조가 고착화되어 안정형 상품으로는 미래에 자신이 쓸 자산을 만들기 어려운 것을 인정하면서도 투자에는 여전히 소극적이다. 이는 변화를 좋아하지 않고 불확실한 것을 꺼리기 때문이다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "도박사의 오류", "현상유지 편향", "매몰비용의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "현상유지 편향의 주된 원인은 변화에 대한 거부감이다. 이런 편향은 금융투자 관성으로 이어지는데, 저금리 시대에도 과거의 관행을 그대로 유지하며 안정형 상품을 유지하고 있는 것이 대표적인 사례이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (508, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["형동경제학에서 기존 전통경제학과 거리를 두고 있는 내용에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인간의 선택이 온전히 합리적이지 않다고 주장한다", "불확실한 상황에서 인간의 의사결정은 여러 심리적 편향에 흔들린다", "인간은 돈과 관련된 선택과 행동에서는 합리적이지 않은 모습이 더욱 두드러진다", "모든 경제주체는 처한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다"]}]', '[{"type": "SOLUTION_TEXT", "data": "전통적인 주류 경제학은 우리가 경제적 또는 재무적 의사결정을 할 때 ‘인간은 합리적’이라는 가정에서 출발한다. 모든 경제주체는 다양한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다는 것이다. 반면 행동경제학은 그 간의 전통 경제학과 달리 인간의 선택이 온전히 합리적이진 않다고 주장한다. 더욱이 돈과 관련된 선택과 행동에서는 이런 합리적이지 않은 모습이 더욱 두드러진다. 행동경제학은 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번한 것을 실험을 통해 확인했다. 불확실한 상황에서 인간의 의사결정은 어림짐작이나 주먹구구식으로 여러 심리적 편향에 흔들린다는 것을 밝혀냈다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (509, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["행동경제학의 대표적 이론으로, 주식시장에 대한 긍정적인 전망이 우세하여도 이미 수익이 난 계좌의 이익을 실현하고 싶어하거나, 반대로 주식시장에 대한 부정적인 전망이 우세하여도 이미 손실이 난 계좌를 그대로 보유하고자 하는 편향을 설명하는 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전망이론", "게임이론", "기대효용이론", "기대-가치이론"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1의 경우, 전망이론은 행동경제학자 대니얼 카너먼과 아모스 트버스키가 주장한 이론으로 사람들이 이익보다 손실에 더 민감하여 이익상황에서는 안전한 선택을, 손실상황에서는 위험한 선택을 하는 것을 밝혀냈다. \n지문2의 경우, 게임이론은 여러 경제주체가 서로를 이기기 위해 경쟁하는 상황에서 그 결론이 어떻게 도출되는지를 설명하기 위한 이론이다. ''게임''이란 이름이 붙은 것은 경쟁자들이 서로를 이기기 위해 전략을 구사하는 상황이 승부를 내는 게임을 연상시키기 때문이다.\n지문3의 경우, 기대효용이론은 행동이 초래하는 결과가 불확실한 상황에서 합리적인 경제주체의 판단은 결과에 관한 효용의 기대치에 입각하여 이루어진다는 이론이다.\n지문4의 경우, 기대-가치이론은 기대와 가치가 동기의 결정요인이라고 가정한다. 어떤 행동을 하는 동기가 생기는 것은 기대요인과 가치요인이 함께 있어야 한다는 것이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전망이론 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (510, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자를 하다보면 큰 경제 이슈가 발생했을 때, 미디어에 수많은 소위 전문가라는 사람들이 나와 이런 일이 일어날 줄 이미 오래전부터 알고 있었다는 듯이 이렇게 된 원인을 아주 논리적으로 해석하고 설명하는 것을 볼 수 있다. 이처럼 어떤 일이 발행하였을 때 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이전부터 그렇게 될 줄 알고 있었던 것처럼 믿게 된다는 것을 설명하는 행동편향은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "군중심리 편향", "현재중시 편향", "사후확증 편향"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1의 경우, 기준점 편향은 앵커링 효과라고도 하는데, 배가 떠내려가지 않도록 닻을 내리듯이 생각이나 판단이 기준점을 벗어나지 않는 말한다.\n지문2의 경우, 군중심리 편향은 군중심리에 빠져 다른 금융투자자들의 행동이 합리적인지 따져보지도 않고 막연히 그 행동을 따라하는 것을 말한다.\n지문3의 경우, 현재중시 편향이란 미래에 비해 현재를 중시하는 것으로 즉각적인 욕망을 충족하려는 충동에 따라 행동하는 것을 말한다.\n지문4의 경우, 사후확증 편향은 투자 선택에 있어서 마치 내가 이럴 줄 알았다는 착각을 불러 일으킨다. 그러나 합리적인 투자를 위해서는 그것이 사전이 아닌 사후 진단이라는 것을 기억해야할 필요가 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (511, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["자기과신 편향을 가지고 있는 사람들이 이에 따른 투자 실패 경험이 있을 때에도 같은 실수를 반복하는 이유를 설명하는 편향으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["내 잘못이 아니라 시장 탓으로 돌린다", "내 잘못이 아니라 상황 탓으로 책임을 회피한다", "투자 실패한 경험은 잊고 성공한 경험만 기억한다", "위험요소를 상대적으로 높게 평가한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이라고 믿고 행동한다. 이를 ‘자기과신 편향’이라고 한다. 특정 금융투자상품 또는 투자 정보에 대한 자신의 판단이 다른 사람들의 평가나 판단보다 더 정확하다고 생각하고 과몰입할 뿐 아니라 위험요소는 상대적으로 낮게 평가한다. 투자 위험을 가중시키는 일이다. 사람들이 투자 실패 경험을 반복하는 이유는 지나친 자기과신으로 실수를 외부 탓으로 돌리거나 기억 자체를 잊어버리려고 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (512, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 공통으로 들어갈 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["2001년 바버와 오딘 교수는 주식투자에 있어서 남녀간 차이가 뚜렷이 존재한다는 연구 결과를 발표했다. 이 연구를 통해 남자가 여자보다 (     )편향이 높고, 주식투자에 있어서 남자는 (     )편향으로 인한 과다한 거래로 여자보다 수익률이 낮다고 밝혔다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기과신", "시기추론", "매몰비용", "현재중시"]}]', '[{"type": "SOLUTION_TEXT", "data": "UC-Davis 바버(Brad Barber) 교수와 UC-Berkeley의 오딘(Terrance Odean) 교수는 1991년부터 1997년까지 6년간 무려 35,000 가구의 주식계좌를 살펴본 후 주식투자에 있어서 남녀간 성(性)적 차이가 뚜렷이 존재한다는 연구 결과를 발표했다.\n남자가 여자보다 자기과신 편향이 높아 주가의 상승과 하락을 예상할 수 있다는 확신으로 빈번한 매매를 했을 확률이 높다. 이로 인한 결과는 거래비용 증가와 수익률 악화로 이어질 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (513, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["군중심리 편향은 투자 뿐만 아니라 소비 등 다양한 분야의 인간 행동에서 일어난다. 군중심리에서 비롯된 행동을 설명하는 용어로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["모방소비", "편승효과", "스노브 효과", "밴드왜건 효과"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1의 경우, 모방소비란 다른 사람의 소비 행동을 그대로 따라하는 소비를 말하는 것으로 군중심리에 영향을 받는다.\n지문2의 경우, 편승효과란 소비자가 어떤 재화를 소비할 때, 다른 소비자들이 많이 소비하는 소비 형태를 따라감으로써 수요가 증가하는 현상을 뜻하는 경제학 이론으로 군중심리의 한 형태이다. 지문3의 경우, 스노브 효과(snob effect)는 다른 사람들이 어떤 상품을 많이 소비하고 있기 때문에, 자신은 그 재화의 소비를 중단하거나 줄이는 것을 말하는 것으로 군중심리에 반대되는 개념이다. 지문4의 경우, 밴드왜건 효과는 서커스나 퍼레이드 행렬의 선두에서 요란한 퍼포먼스를 펼치며 행렬을 인도하는 마차(밴드왜건, Bandwagon)의 뒤를 따라 사람들의 긴 행렬이 줄줄이 이어지는 모습을 빗대어 표현한 것으로 군중심리를 설명한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (514, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["사후확증 편향은 인간이 기억을 재구성하는 특성 때문에 발생한다. 사람들의 기억을 되돌아 볼 때 불완전한 부분을 자신이 믿고 싶은 것으로 메꾸는 경향이 있는 것이다. 금융투자시 이러한 사후확증 편향에 빠지지 않기 위한 태도로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자 과정에서 자신이 저지른 실수를 인정한다", "금융투자에서 필요한 것은 자신감이라는 것을 명심한다", "투자 과정에서 있었던 실수를 통해 배우려는 태도를 취한다", "금융투자시 나의 선택이 틀릴 수 있음을 받아들이고 대비책을 세워 놓는다"]}]', '[{"type": "SOLUTION_TEXT", "data": "사후확증 편향에서 벗어나기 위해서는 투자 과정에서 자신이 저지른 실수를 인정하고 실수를 통해 배우려는 태도가 필요하다. 또한 나의 선택이 틀릴 수 있음을 받아들이고 대비책을 세워 놓을 필요가 있다. 금융투자자들의 지나친 자신감은 반복된 실수를 가져올 수 있다는 것을 명심하고 자신의 장점과 단점을 현실적으로 직시하는 노력이 필요하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (515, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["각 행동편향 및 이에 빠지지 않기 위한 행동을 짝지은 것으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["손실회피 편향: 이익과 손실을 똑같은 가치로 평가하려고 지속적으로 노력한다", "군중심리 편향: 지나친 자신감은 반복된 실수를 가져올 수 있다는 것을 명심한다", "심적회계: 돈에 관한 행위를 할 때는 사용처에 상관없이 동일한 잣대를 가지려고 노력한다", "기준점 편향: 처음 산 가격이나 초기에 알게 된 정보에만 의존하지 않고 다양한 기준으로 투자 대상을 살펴보려 노력한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문2의 경우, 사후확증 편향에 빠지지 않기 위한 행동이다. 군중심리에 빠지지 않기 위해서는 군중에서 한걸음 떨어져 다른 금융투자자들이 왜 그런 행동을 보이는지 충분히 따져 본 후 선택하는 태도가 필요하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (516, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["각 행동편향 및 이에 빠지지 않기 위한 행동을 짝지은 것으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향 : 늘 반대 의견을 구하려고 노력한다", "시기추론 편향 : 보유한 포트폴리오의 점검을 통해 부적한 부분이 있는지 파악하고 이를 방치하고 있었는지 판단한다", "매몰비용의 오류 : 투자가 잘못되어 가고 있다는 것을 발견했다면 과거에 들어간 비용에서 벗어나 합리적인 결정을 내리려고 노력한다", "도박사의 오류 : 독립적인 확률적 사건에 섣부른 기대를 반영하여 인과관계가 있을 것으로 보고 의사결정을 하는 행동을 삼가해야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문2의 경우, 현상유지 편향에 빠지지 않기 위한 행동이다. 시기추론 편향에 빠지지 않기 위해서는 앞으로의 10년을 생각하면 아주 멀게 느껴지지만, 지난 10년을 생각해보면 똑같은 10년이지만 꽤 빨리 흘렀다고 느낀다. 이렇듯 자신의 미래를 역방향으로 예측하면 그 시간을 짧게 느끼고 미래에 대비하여 장기 투자해야 한다는 필요성도 더 절실히 느낄 수 있다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (517, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서 설명하는 행동편향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["원금 손실의 위험 때문에 공격적인 투자를 못하던 보수적인 투자자도 투자를 통해 얻은 수익금만을 투자할 때는 태도가 달라진다. 수익금은 어차피 내 돈이 아니었으니 날려도 된다는 태도로 매우 공격적인 투자를 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "손실회피 편향", "도박사의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "심적회계에 대한 설명이다.  사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급한다. 이를 ‘심적 회계(Mental Accounting)’라고 하는데, 사람 마음 속에서 돈에 대한 기준이 항목에 따라 다르다는 것이다. 어디에 쓰는 돈이건 본질적으로 가치가 같다는 점에서 편향이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '심적회계편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (518, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서 A에게 예상되는 행동편향으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 올해 유망 종목이라고 해서 샀는데 오르긴 커녕 아직도 20%나 손해가 나있어.", "B : 이제라도 정리해야 되는 거 아냐?", "A : 아 몰라~. 어여튼 원금 회복되기 전에는 못팔아. 지금도 돈생기면 조금씩 사고 있는데 언젠가는 오르겠지. 아우 내가 정말 미쳐!"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "현재중시 편향", "손실회피 편향", "매몰비용의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 투입 원금이 기준점으로 잡히는 기준점 편향 때문에 주식을 매도하려고 할 때 현재 상황이나 미래 가치 보다 구입했던 가격이 최우선 기준점이 되어 매도 가격에 영향을 미친다.\n지문 2의 경우, 현재중시 편향이란 미래에 비해 현재를 중시하는 것으로 즉각적인 욕망을 충족하려는 충동에 따라 행동하는 것을 말하는 것으로 해당 대화 내용과는  거리가 멀다.\n지문 3의 경우, 사람들은 이익보다 손실이 더 크게 느끼는데 이에 따라 손실 난 투자를 그대로 유지하려고 하는 경향이 있다.\n지문 4의 경우, 매몰비용의 오류는 가지고 있는 주식을 보유할지, 매각할지를 결정할 때 주식의 가격이 앞으로 어떻게 될 지를 고려하는 것이 아니라 들어간 돈 즉 매입가를 가장 중요시 여기는 편향을 말한다.\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현재중시편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (519, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서 투자자들에게 영향을 미치는 행동편향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 내가 최근에 소개 받은 단톡 주식카페가 있는데 거기서 추천한 종목이 하나 있는데 같이 투자할래?", "B : 정말? 핫한 주식 맞아?", "A : 어 카페 회원들 다 극찬하고 있어! 지금도 좀 올랐는데 앞으로 더 좋을 거래!", "B : 그래? 내가 아는 사람도 대박 종목 하나 잡아서 크게 벌었다던데 그런 종목인가 보다. 하자! 하자!", "A : 넌 친구 잘 둔지 알아"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "군중심리 편향", "도박사의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "군중심리에 빠져 다른 투자자들의 행동이 합리적인지 따져보지도 않고 막연히 그 행동을 따라하는 것을 말한다. 투자 초보자들이 겪는 가장 일반적인 행동편향 중 하나이며, 집단적인 관심이 커질 경우 버블을 만들기도 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (520, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서 B씨에게 예상되는 행동편향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 그 때 샀던 바이오주 지금도 잘나가? 그 대표라는 사람 말대로 신약 허가도 받았고?", "B : 아니 지금은 좀 떨어졌어.", "A : 왜? 신약 허가 못 받았어?", "B : 응. 그래도 괜찮아 조만간 다시 진행할꺼래.", "A : 그 대표 말과 달랐구나. 큰소리 치더만.", "B : 야 니가 뭘 안다고 그래~ 원래 다 그런 거거든 그리고 이 회사는 정말 믿을 만한 회사야. 알지도 못하면서."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증 편향", "시기추론 편향", "현상유지 편향", "도박사의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "확증편향에 빠진 금융투자자는 기존의 믿음에 부합하는 정보만 찾고 그 정보를 더 중요하게 여기고, 모순된 정보의 중요도는 무시하거나 임의로 낮춘다. 또 정보를 자기 믿음에 맞게끔 재해석하기도 하는데, 심지어 모순된 정보 마저도 아전인수식으로 자기 믿음에 유리하게 해석한다. 자기 믿음에 부합하는 정보만 기억하고, 모순된 정보는 잊어버리거나 자기 믿음에 부합한다고 기억해 버리는 것이다. 이는 상황을 직시하지 못하게 하여 그릇된 선택을 하게 만들 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '확증편향 이해', null, null);
