insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (522, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 오버나이트 테스트와 관련된 행동편향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["당신이 보유한 종목들이 투자 손해를 보고 있다. 만약 누군가가 오늘 당신이 보유한 모든 종목을 팔아 현금화해준다면 다음날 그 현금으로 당신이 가지고 있던 그 종목을 다시 매수할 것인가? 만약 다시 매수하지 않을 거라면 그런 종목을 지금은 왜 그대로 가지고 있는가? 오버나이트 테스트는 내가 보유하고 있는 종목들의 가치가 오르거나 내린 주식이 아닌 현금일때도 그 주식을 매수할 것인가를 생각해보고 계속 보유여부를 합리적으로 따져 보는 것이다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향", "현재중시 편향", "시기추론 편향", "매몰비용의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "매몰비용의 오류에서 벗어나는 방법은 투자가 잘못되어 가고 있다는 것을 발견했다면 재빠르게 정신을 차리고 되돌아서는 것뿐이다. 매몰비용을 포기하고 합리적인 결정을 내려야 하는데 투자에서는 오버나이트 테스트를 해보는 것도 좋은 방법 중 하나이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (523, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서 D씨에게 예상되는 핸동편향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["내가 몇 달간 쭉 지켜 지켜봤는데 이 주식은 3일 연속 하락하면 그 다음에는 꼭 오르더라고 그래서 지금 사야해"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["현상유지 편향", "손실회피 편향", "도박사의 오류", "매몰비용의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "도박사의 오류란 서로 독립적인 확률적 사건이 서로 확률에 영향을 미친다는 착각에 기인한 오류이다. 주가가 오르거나 내릴 확률은 반반일 뿐인데 며칠 연속 떨어졌으니 오를 때가 되었다거나 반대로 며칠 연속 올랐으니 오늘은 떨어질거라 판단하는 것이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '도박사의오류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (524, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["현상유지 편향이 영향을 미친 사례로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["퇴직연금(DC)의 경우 최초 운용지시를 한 상품으로 그대로 두거나 방치하는 경우가 많다", "퇴직을 앞둔 직장인들의 가장 큰 후회 중 하나는 노후 준비를 소홀하게 했다는 것이다", "손실이 난 투자 포트폴리오를 그대로 가져가면서, 그 손실이 늘어나도 팔지 않는 경향이 있다", "저금리시대가 지속되고 있는데도 가계금융 자산의 70% 이상이 원리금보장형으로 그대로 운용되고 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "현상유지 편향은 변화를 좋아하지 않고 불확실 것과 마주하는 것을 꺼려하는 행동 상태를 말한다. 금융회사가 추천한 상품을 가입한 후 재평가하지 않고 처음 그대로 방치하거나, 손실난 포트폴리오를 귀찮아서 그대로 가져가기도 한다. \n지문 2의 경우, 시기추론 편향의 영향으로 사람들은 보통 먼 미래의 일은 추상적이고 낙관적으로 생각하는데 이로 인해 노후 준비도, 장기투자도 쉽게 하지 못한다.\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (525, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자를 하고 난 후, 수익이 났을 때 빠지기 쉬운 대표적인 행동편향 만을 짝지어 놓은 것으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기과신 편향, 현상유지 편향, 확증편향", "자기과신 편향, 손실회피 편향, 확증편향", "손실회피 편향, 현상유지 편향, 확증편향", "자기과신 편향, 사후확증 편향, 확증편향"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자가 성공적이어서 수익이 났다면 앞으로 더 큰 성과를 낼 수 있다는 자신감을 얻는다. 이런 경우 보통 투자 금액이 이전보다 훨씬 더 커진다.(자기과신 편향)\n또, 이런 성과가 나올 수밖에 없었던 이유를 갖다 붙인다. 결과가 이렇게 될 줄 알고 있었다고 느끼며 행동에 확신이 붙는다. 주변 사람들에게 자신의 성공기를 확신에 찬 모습으로 떠벌리고 다닌다.(사후확증 편향) 만일 자신의 선택이 아니라 주변 사람의 추천을 통해 얻은 수익이라면 그 대상에 대한 신뢰가 매우 커져 그 사람 말만 계속 믿고 투자하려 한다.(확증편향)"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (526, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자시 자신의 투자성향을 확인하고 그에 맞게 포트폴리오를 구성하는 것은 매우 중요하다. 공격형 투자자들이 빠지기 쉬운 대표적인 행동편향 만을 짝지어 놓은 것으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향, 자기과신 편향, 도박사의 오류", "확증편향, 현상유지 편향, 매몰비용의 오류", "손실회피 편향, 도박사의 오류, 기준점 편향", "현상유지 편향, 자기과신 편향, 매몰비용의 오류"]}]', '[{"type": "SOLUTION_TEXT", "data": "공격형 투자자들은 투자성향상 주식 같은 위험자산에 큰 고민없이 선뜻 투자한다. 그리고 자신의 선택에 대해 확신을 가지며 자신의 생각과 다른 의견에는 잘 귀기울이지 않는다.(확증편향) 이런 행동이 수익으로 이어졌다면 자신의 투자 감각을 과신하며 모든 게 자신의 능력 때문이라고 확신한다.(자기과신 편향) \n주식투자를 쉽게 생각하며 분산하지 않고 몰빵 투자를 하는 등 더 용감한 투자를 하기도 한다. 다른 사람들이 수익을 내고 있다는 소리를 전해 들으면 그 상품에 대해서도 자산의 능력을 믿고 쉽게 투자를 고려한다.(군중심리 편향) \n만약 성과를 내지 못하고 손실을 본다 해도 손실의 원인을 분석하기보다는 손해를 만회하고자 하는 욕구가 더 강해지며 다음 번에는 실패하지 않을 거라 믿는다.(도박사의 오류)"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (527, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["행동편향을 극복하기 위한 행동으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 자신이 이성적인 존재임을 믿고 선택한다", "금융투자시 자신이 겪었던 행동편향이 무엇인지 확인한다", "금융투자시 투자 원칙과 기준을 세우고 투자 과정을 객관적으로 기록한다", "금융상품과 서비스에 대한 의사결정을 해야 할 때 행동편향의 영향을 받고 있는지 스스로 점검해 본다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 행동경제학에서는 인간의 선택이 온전히 이성적이지 않고 앞뒤가 맞지 않거나 목적에도 부합하지 않는 등 일관성이 없다고 보았다.\n행동편향을 극복하기 위해서는 행동편향이 무엇인지 인지하고 자신이 겪었던 행동편향을 확인하며, 행동편향에 흔들리지 않도록 투자 원칙과 기준을 세우고 투자 과정을 기록하며, 의사결정시 행동편향을 영향을 받고 있는지 스스로 확인해야 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (528, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["행동편향 극복을 위한 올바른 자세로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["하이투자태도-하이리턴을 명심한다", "투자과정을 기록하고 모니터링 한다", "과거의 실패는 잊고 새로운 마음으로 투자한다", "자산배분과 리밸런싱 등 투자원칙을 세우고 기록한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 기대수익률에 대한 투자 위험을 인지함으로서 자기과신이나 확증편향에 빠진 맹목적인 투자를 지양하게 할 수 있다. \n지문 2의 경우, 투자과정을 기록함으로서 자신의 감정적인 선택의 결과를 확인할 수 있고 이를 통해 반복된 실수를 피할 수 있다.\n지문 3의 경우, 투자는 과거의 실수를 반복하지 않기 위한 리뷰가 필요하다. 새로운 기대만을 가지고 임하는 투자는 실패를 반복할 수 있다.\n지문 4의 경우, 자산배분의 원칙을 세우고 리밸런싱 기준을 통해 투자를 관리하는 것은 행동편향의 영향에서 벗어나 초심을 유지할 수 있게 도와준다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (529, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["행동편향 극복을 위한 ''투자과정 기록 및 모니터링''에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기억은 주관적이어서 왜곡되기도 하지만 기록은 객관적이다", "어떤 생각으로 투자 대상을 선택하였고 어떻게 관리할 계획인지 기록한다", "투자결과 중 성공한 투자만을 골라 이유를 평가하고 기록하여 성공전략에 집중한다", "기록내용을 모니터링 하여 다음 투자시 자신의 행동편향에 휘둘리지 않고 합리적인 의사 결정을 하는데 이용한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "자신의 투자 경험을 기록하는 것은 반복된 실수를 피하는 가장 효과적인 방법이다. 기억은 지극히 주관적이어서 왜곡되기도 하지만, 이에 비해 기록은 객관적이다. 자신이 어떤 생각으로 투자 대상을 선택하였고 어떻게 관리를 할 계획인지를 기록한다. 성공한 투자의 성공 이유는 물론 실패한 투자의 실패 원인이 무엇이었는지 냉정하게 평가하고 기록해 둔다. 이는 다음 투자시 자신의 행동편향에 휘둘리지 않고 합리적인 의사 결정을 하는데 지속적으로 도움을 준다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (530, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (     ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(    )은 사람들이 경제적 또는 재무적 의사결정을 할 때 인간은 합리적이라는 가정에서 모든 경제 주체는 이성적인 선택을 한다고 전제한다. 그러나 (    )은 인간의 선택이 온전히 합리적이진 않으며, 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번함을 실험을 통해 확인했다.  현재는 금융 시장을 이해하고 올바른 선택을 하기 위해서는 (    )이 얘기하는 인간의 행동편향을 이해하고 극복하고자 노력하는 것이 필요하다는 인식이 점점 더 확산되고 있다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["행동경제학, 전통경제학, 전통경제학", "행동경제학, 전통경제학, 행동경제학", "전통경제학, 행동경제학, 전통경제학", "전통경제학, 행동경제학, 행동경제학"]}]', '[{"type": "SOLUTION_TEXT", "data": "전통경제학은 우리가 경제적 또는 재무적 의사결정을 할 때 ‘인간은 합리적’이라는 가정에서 출발한다. 모든 경제주체는 다양한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다는 것이다.  행동경제학은 그 간의 전통 경제학과 달리 인간의 선택이 온전히 합리적이진 않다고 주장한다. 더욱이 돈과 관련된 선택과 행동에서는 이런 합리적이지 않은 모습이 더욱 두드러진다. 행동경제학은 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번한 것을 실험을 통해 확인했다. 불확실한 상황에서 인간의 의사결정은 어림짐작이나 주먹구구식으로 여러 심리적 편향에 흔들린다는 것을 밝혀냈다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
