insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (532, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["합리적 투자의사 결정을 위하여 행동편향을 대하는 태도로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 인간은 행동편향에 빠지기 쉬운 존재임을 인정한다", "오랜기간 형성된 행동편향일수록 단기에 고칠 수 있다", "행동편향으로 인해 투자 의사결정시 합리적이지 않은 선택을 할 수 있음을 인지한다", "금융투자에 대한 기대만이 아니라 변동성 등 투자의 위험을 충분히 인지하고 대비하는 투자 태도를 갖는다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문 2의 경우, 오랜 기간 동안 형성된 자신이 행동편향을 바로 잡는 것 역시 오랜 시간이 소요될 수 있다.일상생활하는 가운데 의사결정시마다 자신의 행동편향들을 의식하고 점검하려는 관심과 노력을 지속적으로 기울여 자신이 행동편향들을 극복해 나가려는 마음가짐과 자기성찰이 필요하다.\n우리가 편향에 빠지기 쉬운 존재임을 인정하고 받아들이는 것이 중요하다. 사람들의 의사결정은 계획적이고 신중하기보다 직관적이고 자동적으로 이루어지는데, 그런 행동이 믿음처럼 좋은 결과를 만들어 내지 못하고 손해로 이어진다. 이런 편향에 빠지는 것은 인간이기에 당연한 것임을 받아들인다. 자신이 편향에 얼마나 취약하고 또 그 결과가 자신에게 손해를 준다는 것을 인지하는 겸손한 태도를 취하는 것이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (533, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자회사에 해당하는 것으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["증권회사", "선물회사", "자산운용회사", "상호저축은행"]}]', '[{"type": "SOLUTION_TEXT", "data": "우리나라 금융회사는 은행, 비은행 금융회사, 보험회사, 금융투자회사, 기타 금융회사로 분류된다. 이중에 금융투자회사는 증권회사, 선물회사, 자산운용회사, 투자자문회사, 부동산신탁회사, 종합금융회사가 있다. 상호저축은행은 비은행 금융회사다.\n지문1 증권회사는 대표적인 금융투자회사다.\n지문2 선물회사는 금융투자업의 투자중개업(주로 선물)을 주로 하는 금융투자회사다.\n지문3 자산운용회사는 금융투자업 중 집합투자업을 주로 하는 금융투자회사다.\n지문4 상호저축은행은 비은행 금융회사로 비은행 금융회사에는 상호저축은행, 여신금융전문회사, 대부업자, 상호금융이 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (534, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["금융투자회사의 6가지 기본업무 중 ‘개인투자자가 주식과 채권 등을 사고 팔수 있도록 역할’을 하는 업무로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자매매업", "투자중개업", "신탁업", "투자자문업"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 투자매매업은 금융투자회사가 ‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문2 개인투자자가 투자하기를 원하는 주식, 채권 등을 단순하게 사고 팔 수 있도록 ‘중개’하는 역할이다. 중개는 시장에서 사자와 팔자를 연결하는 기능만 한다는 것이다. 따라서 중개기능을 수행한 금융투자회사에 가격체결 수준을 이유로 문제를 제기 할 수 없다. \n지문3 신탁업은 위탁자가 법률행위에 의하여 자기재산을 수탁자에게 이전하고, 수탁자는 그 재산을 신탁목적에 따라 관리 또는 처분하며, 그 이익을 일정한 수익자에게 귀속시키는 제도다.\n지문4 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자업 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (535, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["집합투자업에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["집합투자업은 펀드운용을 주된 업무로 한다", "자산운용사 중에는 특정 종류의 자산을 사모로만 운용할 수 있는 회사도  있다", "집합투자업을 영위하는 회사들은 회사명에 OO자산운용 또는 OO투자신탁운용 등을 주로 사용한다", "개인투자자 입장에서 볼 때 가입중인 펀드를 어느 자산운용사가 운용하는지는 전혀 중요하지 않다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 집합투자업은 펀드 운용이 주요 업무다.\n지문2 사모전문집합투자기구를 운용할 수 있는 사모전문운용사를 말한다.\n지문3 자산운용사가 주로 사용하는 명칭은 OO자산운용, OO투자신탁운용이다. \n지문4 모든 투자자는 자신이 투자하고 있는 펀드를 운용하는 자산운용사가  어디인지 알고 자산운용사에 대한 평가를 펀드의 평과와 동시에 해야 한다.  펀드투자의 첫걸음은 투자한 펀드를 운용하는 자산운용사에 대한 판단이다.  따라서 전혀 중요하지 않은 것이 아니라, 정말 중요하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '집합투자업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (536, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 내용으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(          )은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자일임업", "투자자문업", "투자매매업", "집합투자업"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자일임업은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임 받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)이다.\n지문2 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다.\n지문3 투자매매업은 금융투자회사가‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문4 집합투자업은 우리가 일반적으로 알고 있는 펀드운용을 하는 것이다. 정확하게는 ‘2인 이상의 투자자로부터 모은 금전 등을 투자자로부터 일상적인 운용지시를 받지 아니하면서 재산적 가치가 있는 투자대상자산을 취득, 처분 그 밖의 방법으로 운용하고 그 결과를 투자자에게 배분하여 귀속하는 것’을 말한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (537, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 내용으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금융투자회사가 투자일임업을 수행할 때 사용하는 계좌를 일반적으로 (       )라고 부른다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["DB(Defined Benefit)", "DC(Defined Contribution)", "Wrap Account", "IRP(Individual Retirement Pension)"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 확정급여형 퇴직연금(Defined Benefit)은 기존의 퇴직금제도와 유사하게근속연수에 평균임금을 곱하여 퇴직금이 산정되는 구조다. \n지문2 확정기여형 퇴직연금(Defined Contribution)은 퇴직금을 직원의 퇴직연금계좌로 지급하는 구조로 운용수익률에 따라 퇴직금의 규모가 변동한다.\n지문3 Wrap Account = Wrap(감싸다) + Account(계좌) 고객의 자산현황, 투자목표, 위험허용도 등을 반영하여 가장 적합한 포트폴리오를 제공하고 그에 따른 고객의 의사결정에 따라 일임받은 투자권한 내에서 주문집행, 결제 등의 업무를 처리하고 사전에 정한 일정비율의 수수료를 받는 종합자산관리서비스\n지문4 개인형 퇴직연금(Individual Retirement Pension)은 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (538, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["생애 처음으로 투자를 시작하는 개인투자자 A의 행동 중 현행 제도와 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["우체국을 방문해서 주식매매 계좌를 신규개설 하였다", "증권회사에 가서 펀드매매 계좌를 개설하고 펀드를 매수하였다", "증권회사 앱을 다운받아 비대면 계좌를 개설하고 주식을 매수하였다", "은행을 방문해서 개설한 주식매매 계좌에 100만원을 입금하고, 은행직원에게 주식 매수 주문을 하였다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 우체국에서 주식매매를 위한 위탁계좌를 개설할 수 있다.\n지문2 증권회사는 펀드 판매사로서 펀드 매매를 위한 계좌개설과 펀드 매수를 중개할 수 있다.\n지문3 증권회사의 비대면 채널을 통한 주식 매매 거래의 전형이다.\n지문4 은행은 증권회사를 대신해서 주식매매 계좌의 개설을 대행해 줄 수 있다. 그러나 주식매수 주문은 은행에서는 할 수 없고, 해당 계좌의 원 소속인 증권사를 통해서 매매 주문을 할 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (539, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 A가 온라인에서 본 유사투자자문업자의 행위 중 현행 자본시장법에 비추어 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유사투자자문업자는 모두 불법으로 법적 근거가 없다", "안내문에는 불특정 다수인을 대상으로 발행되는 간행물, 전자우편 등에 의한 조언이 가능하다고 한다", "금융감독원에 신고된 유사투자자문업자는 월 30만원 이하로 일대일 투자자문을 하는 것은 합법적이라고 한다", "자신에게 돈을 맡기면 매월 5%씩 안정적인 수익률을 보장한다는 광고와 함께 자신의 계좌수익률 사진을 보내왔다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 유사투자자문업은 자본시장법 101조(유사투자자문업의 신고)에 따라 금융위원회에 신고한 자는 합법적인 유사투자자문업자다. 관련 규정을 어긴 자만 불법이다.\n지문2 자본시장법 101조(유사투자자문업의 신고)의 규정으로 맞는 말이다.\n지문3 1대1 투자자문은 불법이며 불특정 다수를 상대로 조언을 해야 합법이다.\n지문4 유사투자자문업자의 일임계약은 불법이고, 수익률 약정 등도 과장광고로 금지대상이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '유사투자자문 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (540, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["공모주와 스팩(SPAC)투자에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공모주 청약은 증권회사 계좌없이 은행에서도 할 수 있다", "공모주 청약은 IPO(Initial Public Offering)를 주관하는 주관사나 공동주관사를 통해서만 청약이 가능하다", "스팩이 만기 시점까지 합병하지 못하는 경우에는 투자자에게 투자금의 원금과 이자를 반환하는 수익구조를 가진다", "스팩은 공모를 통해 M&A자금을 먼저 마련하며 상장한 후 다 른회사와 합병하는 것을 유일한 사업목적으로 하는 Paper Company다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 공모주 청약을 위해서는 금융투자회사를 통해서 청약을 하고, 배정받는 주식을 보유하거나 매매를 통해 이익을 얻을 수 있다. 따라서 증권회사의 계좌는 반드시 있어야 청약이 가능하다.\n지문2 주관사나 공동주관사를 통해서만 해당 공모주의 청약을 할 수 있기 때문에 해당 공모주를 주관(또는 공동주관)하는 증권사의 계좌가 필요하다.   \n* IPO(Initial Public Offering) 또는 상장(Listing) : 기업이 발행한 주권이 한국거래소 (KRX)가 정하는 요건에 따른 증권시장에서 거래가 될 수 있는 자격을 부여 받는 것\n지문3 스팩의 큰 특징중의 하나로 합병이 불발될 때는 원금과 이익금을 돌려받는 구조다.\n지문4 스팩은 합병을 목적으로 하는 명목상의 회사다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공모주 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (541, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["채권 투자를 원하는 개인투자자 A가 하는 행동으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["B은행 채권을 직접 매수하기 위해 B은행에 예금계좌를 개설했다", "증권회사 영업점을 방문해서 만기가 2년 남은 B은행 채권을 매수했다", "장내 국채시장에 들어가서 30년 만기 국채를 500만원씩 두 번 분할 매수했다", "증권회사 HTS를 통해 장내 채권시장에서 장외채권인 사모사채 1억원을 매수하였다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 채권매매를 직접하기 위해서는 증권회사의 위탁계좌를 개설해야 한다. 은행을 방문해서 신탁을 통해 매매를 지시할 수는 있으나, 이때도 예금계좌로 활용할 수는 없다.\n지문2 증권회사 영업점을 활용한 장외시장을 통한 채권매매의 전형적인 경우다.\n지문3 장내 국채시장은 국채전문딜러로 지정된 증권회사, 기관만이 참여 가능한 시장이다. 개인이 참여할 수도 없고 거래 단위로 주로 100억 단위\n지문4 증권사 HTS로 거래하는 장내 채권시장은 상장채권만을 거래할 수 있고 사모사채인 장외채권은 매매할 수 없다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권거래 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (542, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["펀드에 투자를 원하는 투자자 A가 알고 있는 펀드에 대한 설명으로 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드 기준가를 산출하는 등 신탁 회계를 담당하는 곳이 사무수탁회사다", "집합투자기구 평가회사를 통해 펀드 평가정보를 얻을 수 있다", "펀드는 판매하는 곳에서 직접 운용하기 때문에 어느 금융회사에서 가입하는지가 아주 중요하다", "펀드는 운용재산을 보관하는 수탁회사가 별도로 있어 자산운용과 자산보관이 분리되어 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 사무수탁회사는 펀드회계를 담당하는 회사다.\n지문2 펀드 평가사를 통해 객관적인 펀드 정보를 얻을 수 있다.\n지문3 펀드는 가입을 하는 판매사와 운용을 하는 운용회사가 분리되어 있다. 가입하는 곳에서 운용한다는 것은 틀린 말이다.\n지문4 수탁회사의 기능에 대한 올바른 설명이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (543, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 내용으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(       )는 Wrap Account의 일종으로 하나의 계좌에서 다양한 일임운용서비스를 통합적으로 제공하는 금융투자상품이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["UMA(Unified Managed Accounts)", "CMA(Cash management account)", "ELS(Equity Linked Securities)", "DLS(Derivative Linked Securities)"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 UMA(Unified Managed Accounts)에 대한 설명이다.\n지문2 CMA(cash management account) 어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD), 국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품이다. \n지문3 ELS(Equity Linked Securities) 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (544, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 A의 거래형태에 대한 설명으로 사실과 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["증권회사 MTS로 위탁계좌에 있는 돈으로 ETF를 매수하였다", "ETF 매수를 위해 은행을 방문하여 특정금전신탁에 가입하여 운용지시를 하였다", "은행에서 특정금전신탁으로 매수하는 ETF나 증권회사를 통해 매수하는 ETF는 동일한 것으로 매매비용도 동일하다", "증권회사를 통해 ETF를 매매할때는 실시간 매매가 가능하지만 은행의 특정금전신탁을 통한 ETF매매는 계좌별 실시간 매매는 어렵다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 증권회사 위탁계좌와 MTS를 활용한 직접매매형태다.\n지문2 ETF에 투자하는 특정금전신탁에 가입하는 형태로 은행의 신탁을 통해서 ETF를 거래할 수 있는 방법이다.\n지문3 특정금전신탁을 통해서 매매하던지 직접 매매하던지 매매하는 ETF는 동일하다. 그러나 투자비용에서는 위탁계좌를 통한 매매와 특정금전신탁을 통한 매매는 큰 차이가 있다. 특정금전신탁이 수취하는 신탁보수가 위탁계좌에는 없고, 매매수수료도 차이가 난다.\n지문4 증권회사를 통한 ETF매매는 한국거래소에 직접 주문을 내지만, 특정금전신탁을 통한 매매는 고객의 운용지시와 특정금전신탁의 주문이 증권사를 통해서 이루어 지기 때문에 아무리 빨라도 실시간 매매라고 할 수는 없다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
