insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (546, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["증권회사의 비대면 계좌를 개설하기 위해 필요한 것으로 가장 거리가 먼것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["본인 명의의 스마트폰", "주민등록증 또는 운전면허증", "실명확인이 가능한 본인명의 금융기관 계좌", "증권회사 HTS(Home Trading System)가 설치된 PC"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 본인명의의 스마트 폰이 필요하다.\n지문2 신분증은 실명확인을 위한 신분증 촬영을 위해 필요하다.\n지문3 실명확인을 위해 필요하다.\n지문4 HTS는 개설된 계좌를 사용하여 거래에 필요한 프로그램이다. 비대면계좌를 개설하기 위해 반드시 필요한 것은 아니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '계좌개설절차 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (547, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["주식매매를 위한 대기자금도 단기 실세금리 수준의 이자율을 받을 수 있도록 비대면 주식계좌 개설시 함께 계좌를 개설하는 단기 금융투자상품은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ELS(Equity Linked Securities)", "CMA(Cash management account)", "DLS(Derivative Linked Securities)", "IRP(Individual Retirement Pension)"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 ELS(Equity Linked Securities)는 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문2 CMA(cash management account)어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD)·국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품\n지문3 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 개인형 퇴직연금 IRP (Individual Retirement Pension)개인별로 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'CMA 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (548, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["MTS(Mobile Trading System)을 활용한 주식매매에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["MTS 로그인을 위해서는 공동인증서만을 사용할 수 있다", "비밀번호 대신 지문으로 거래가 가능한 증권회사도 있다", "비대면계좌 개설시 모바일 OTP를 발급 받는 경우 OTP기기를 따로 가지고 다닐 필요가 없다", "한국거래소가 부여한 코드를 입력하지 않아도 종목명 입력이나 음성 검색이 가능하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 MTS 로그인은 공동인증서 뿐만 아니라 개별금융회사의 인증서( 예:카카오페이인증, KB모바일 인증 등)나 간편인증, 생체인증으로 로그인이 가능하다.\n지문2 MTS 거래시에도 비밀번호 대신 지문으로 거래가 가능하다.\n지문3 모바일 OTP 서비스를 이용하는 경우 별도의 OTP기기는 필요하지 않다.\n지문4 주식종목의 검색방법은 직접입력 또는 음성입력을 통해 검색이 가능하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
