insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (560, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인전문투자자에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인전문투자자가 직접 금융투자협회에 신청해야 한다", "개인전문투자자는 필수요건을 갖추고 선택요건 중 한 가지 이상을 충족하면 된다", "자산요건은 부채를 차감한 순재산이 5억원 이상일 것이다", "소득요건은 본인의 직전연도 소득금액이 1억원 이상이거나 부부 합산 직전연도 소득합계액이 1.5억원 이상일 것이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 개인전문투자자 신청 서류를 금융투자회사(주로 증권회사)에 제출한다. \n각 금융투자회사는 자체심사를 통해 자격부여 여부를 판단한다. \n지문2 필수요건과 선택요건이 있다. 선택요건은 소득요건, 전문가요건, 자산요건이 있다.\n지문3 자산요건은 순재산 5억원 이상(부부합산 필수, 거주 부동산관련 금액 제외)으로 부채를 차감한 순재산 5억 이상이다.\n지문4 소득요건은 본인의 직전연도 소득금액이 1억 원 이상이거나 부부가 합산해서 직전연도 소득합계액이 1.5억 원 이상이면 충족된다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (561, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(     )이란 가치평가방법 등에 대한 투자자의 이해가 어려운 상품으로서 최대 원금손실 가능성이 일정수준(20%~30%)이상이며 투자자보호가 필요한 상품을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["저난도 금융투자상품", "고난도 금융투자상품", "고수익 금융투자상품", "중위험 금융투자상품"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문2 2019년 11월에 금융감독원이 발표한 ‘고위험 금융상품 투자자 보호 강화를 위한 종합개선방안’에 따르면, 고난도 금융투자상품은 다음과 같이 규정된다. ‘고난도 금융투자상품’은 파생상품 내재 등으로 가치평가방법 등에 대한 투자자의 이해가 어려운 상품으로서, 최대 원금손실 가능성이 일정수준 (20%~30%)이상인 상품이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (562, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["고난도 금융투자상품에 대한 설명으로 <보기>의 (     ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 (     )된다는 사실을 알아야 한다. 물론 해당 금융투자회사는 이러한 사항에 대한 통지가 (     ) 사항이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["철회, 의무", "확정, 의무", "철회, 선택", "확정, 선택"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1  투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 (철회)된다는 사실을 알아야 한다. 물론 해당 금융투자회사는 이러한 사항에 대한 통지가 (의무화)되어 있다. 그리고 숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유 종용하는 행위가 금지되어 있어 \n오로지 투자자의 의지로 청약의 승낙 여부를 판단해야 한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (563, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 A가 금융투자회사에서 고난도 금융투자상품를 가입할 때 경험하는 상황으로 사실과 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["숙려기간 내에 청약 승낙 의사표시를 해야 가입할 수 있다", "만 65세 미만의 일반투자자에게는 녹취숙려제도를 선택적으로 적용할 수 있다", "숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유·종용하는 행위가 금지되어 있다", "투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 철회된다는 사실을 알아야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 숙려기간 내에 청약 승낙 의사표시를 해야 가입할 수 있다. \n지문2 고난도 금융투자상품에 대해서는 만 65세 미만의 일반투자자에게도 녹취 · 숙려제도를 의무화 하고 있다. \n지문3 숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유·종용하는 행위가 금지되어 있다. \n지문4 투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 철회된다는 사실을 알아야 한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '고난도상품 이해', null, null);
