insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (569, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 A가 새내기 펀드투자자로서 바람직한 행동으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["요즘 유행하는 펀드가 무엇인지 검색하고 가입했다", "최근 한달 간 수익률이 제일 높은 펀드를 알아보고 가입했다", "신문에 나오는 펀드이름을 보고 스마트폰을 이용해서 가입했다", "금융투자회사 창구에서 나의 투자성향에 맞는 펀드를 추천받고 투자설명서로 자세한 설명을 듣고 난 후 투자여부를 결정했다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 유행하는 펀드라고 해서 모든 투자자에게 적합한 것은 아니다.\n지문2 단기 수익률이 높은 펀드를 무작정 따라가는 투자선택보다는 위험 대비 수익률을 확인하는 절차가 필요하다.\n지문3 언론에 자주 소개되는 이름난 펀드가 꼭 좋은 펀드일 수는 없다.\n지문4 초보 투자자 일수록 창구에서 자신의 투자스타일과 위험성향에 맞는 펀드를 추천받아 자세한 설명을 듣고 난 후에 투자여부를 결정하는 것이 바람직하다. 이러한 경험을 한 후에 온라인 가입을 통해 투자비용을 아끼는 방향으로 발전하는 것이 좋다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 매매 이해', null, null);
