insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (571, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["포트폴리오 매니저에게는 운용의 목표와 기준이 되는 지표이고, 투자자의 입장에서는 해당 펀드의 운용결과가 기준대비 얼마나 효율적이었는지 판단하는 기준이 되는 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["위험", "수익률", "벤치마크", "미실현이익"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문3 포트폴리오 매니저가 포트폴리오를 운용할 때는 운용의 목표와 기준을 정해서 운용한다. 이때 이 운용의 목표와 기준이 되는 기준지표가 우리가 흔히 이야기하는 벤치마크다. 포트폴리오 매니저에게는 운용의 목표와 기준이 되는 기준지표지만, 투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단 하는 기준이 된다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (572, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자실적 평가에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["벤치마크는 포트폴리오 매니저에게 운용의 목표와 기준이 되는 기준지표다", "투자자의 입장에서는 해당 펀드의 운용결과가 기준지표 대비 얼마나 효율적이었는지를 판단하는 기준이 된다", "벤치마크는 투자전략을 효과적으로 실행했는지 여부를 판단하는 기준이 되는 독립적인 수익률로 정의할 수 있다", "펀드를 기준으로 벤치마크는 각 펀드의 투자설명서에 내용이 기재되어 있고, 상황에 따라 유연하게 변경 가능하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 벤치마크는 포트폴리오 매니저에게 운용의 목표와 기준이 되는 기준지표가 맞다. \n지문2 투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단하는 기준으로 활용된다.\n지문3 벤치마크는 ‘투자전략을 효과적으로 실행했는지 여부를 판단하는 기준이 되는 독립적인 수익률’로 정의할 수 있다. \n지문4 펀드를 기준으로 벤치마크는 각 펀드의 투자설명서에 내용이 기재되어 있고, 임의로 변경할 수 없다. 이는 투자설명서 정정사항에 해당하여 정당한 절차를 거친 이후에 효력을 갖는다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (573, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["벤치마크(기준지표) 중에 섹터/스타일 지수의 성격의 지수로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["대형주지수", "중소형지수", "가치주지수", "종합주가지수"]}]', '[{"type": "SOLUTION_TEXT", "data": "벤치마크의 종류인 시장지수와 섹터/스타일지수의 구분에 대한 문제이다.\n지문1 섹터/스타일 지수\n지문2 섹터/스타일 지수\n지문3 섹터/스타일 지수\n지문4 종합주가지수는 대표적인 시장지수의 일종이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (574, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자실적을 평가하는 지표중에 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 (         ) 라고 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["수익률", "벤치마크", "위험조정 성과지표", "절대수익 추구 성과지표"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 단순수익률은 위험요소를 고려하지 않는 수익률 기준이다.\n지문2 벤치마크는 수익률을 비교평가하는 기준으로 위험요소인 변동성을 별도록 고려하지는 않는다.\n지문3 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 위험조정 성과지표(RAPM : Risk –Adjuisted Performance)라 한다. 선택하는 수익률과 위험지표와 위험을 조정하는 방식에 따라 단위 위험당 초과수익률과 위험조정 수익률로 나눌 수 있다. \n지문4 절대수익 추구 성과지표라는 용어는 없으나, 투자자가 목표로 하는 절대수익률을 기준으로 할 수는 있다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (575, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["샤프비율(Sharpe Ratio)에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["샤프비율이 낮을수록 성과가 양호하다고 본다", "샤프비율은 투자수익률 대비 변동성 비율로 투자성과를 측정한다", "포트폴리오가 부담한 위험 1단위당 초과수익의 크기를 나타낸다", "비교대상 포트폴리오의 샤프비율이 둘 다 (-)값이 나온다면 어느 한쪽이 더 우수하다고 판단하기 어렵다"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 샤프비율이 높을수록 성과가 양호하다고 본다.\n지문2 샤프비율 = (포트폴리오 기대수익률 - 무위험자산수익률) / 포트폴리오 수익률의 표준편차\n지문3 포트폴리오가 부담한 위험 한 단위당 초과수익의 크기를 나타낸다. \n지문4 예를 들어 두 포트폴리오를 비교할 때 두 포트폴리오의 샤프비율이 둘 다 (-)의 값을 보인다면 어느 쪽이 더 우수하다는 판단은 무의미하다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (576, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(      )은 포트폴리오 수익률을 위험으로 나누어(벤치마크 대비 초과 수익률을 추적오차의 표준편차로 나눈 값) 산출하는 지표다. 벤치마크 대비 안정적 수익을 보는 지표인데 이 지표가 높을수록 성과가 우수한 포트폴리오로 볼 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["정보비율", "투자비율", "위험비율", "수익비율"]}]', '[{"type": "SOLUTION_TEXT", "data": "정보비율은 포트폴리오 수익률을 위험으로 나누어 산출하는 지표이다(BM 대비 초과 수익률을 추적오차의 표준편차로 나눈 값)\n\n정보비율(IR)=초과 수익(value add)/위험(risk)\n\n벤치마크 대비 안정적 수익이 얼마나 되는지 보는 지표로, 정보비율이 높을수록 성과가 우수한 포트폴리오로 볼 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (577, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 A는 동일한 위험을 부담하고 얻은 초과수익이 높은 펀드를 선호한다고 할 때 선택하는 상품으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A펀드", "B펀드", "C펀드", "D펀드"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자자 A는 정보비율이 높은 펀드를 선호한다. 이는 동일한 위험을 부담하고 얻은 초과수익이 높은 펀드에 선호가 있고 이런 펀드는 벤치마크 대비 안정적인 수익률을 내는 상품이라고 할 수 있다.\n\n\n\n지문1 정보비율이 제일 높은(1.67) 펀드 ㉠이 정답이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (578, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(      )는 부담한 위험수준에 대해 요구되는 수익률보다 펀드가 얼마나 더 높은 수익률을 달성했는가를 나타내는 지표다. 우수한 포트폴리오의 경우에는 지속적으로 (      )을 보이고 반대로 성과가 열등한 포트폴리오의 경우에는 지속적으로 (     )을 보인다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["젠센의 알파, 음(-)의 알파값, 양(+)의 알파값", "젠센의 알파, 양(+)의 알파값, 음(-)의 알파값", "인덱스 알파, 음(-)의 알파값, 양(+)의 알파값", "인덱스 알파, 양(+)의 알파값, 음(-)의 알파값"]}]', '[{"type": "SOLUTION_TEXT", "data": "젠센의 알파는 부담한 위험수준에 대해 요구되는 수익률보다 펀드가 얼마나 더 높은 수익률을 달성했는가를 나타내는 지표다. 우수한 포트폴리오의 경우에는 지속적으로 양(+)의 알파값을 보인다. 반대로 성과가 열등한 포트폴리오의 경우엔 지속적으로 음(-)의 알파값을 보인다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (579, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["펀드 실질투자 수익률 보고서에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "ㄱ.", "dataType": "text", "data": ["비용차감 전 수익률과 세부내역을 확인할 수 있다", "입금과 출금이 고려된 자산의 투자수익률을 각종 발생비용 내역을 고려한 수익률을 확인할 수 있다", "투자원금에 대한 정보는 확인할 수 없다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ", "ㄴ, ㄷ", "ㄱ, ㄴ", "ㄱ, ㄴ, ㄷ"]}]', '[{"type": "SOLUTION_TEXT", "data": "펀드 실질 투자수익률 보고서를 통해 비용차감 전 수익률과 세부내역을 확인 가능하다. 입금과 출금이 고려된 자산의 투자수익률을 각종 발생비용 내역을 고려한 수익률을 확인 가능하다. 펀드 실질 투자수익률 보고서는 펀드에 대한 기본정보와 함께 원금과 입출금 내역 및 보수/수수료를 감안한 누적수익율과 연환산 수익율을 제공한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자관리 방법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (580, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["상장기업의 주식을 취득해서 보유하는 경우에 발생하는 주주의 권리로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주총회에 참석할 권리", "배당가능이익에 대해 배당을 받을 권리", "매년 정기적으로 보유하고 있는 주식에 비례해서 이자를 받을 권리", "회사의 경영에 중대한 영향을 미치는 행위에 대한 다수 주주의 결정에 반대해서 주식매수청구권을 행사할 권리"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 주총에 참석해서 주요 의사결정에 참여하는 것은 주주의 기본적인 권리에 속한다.\n지문2 주주로서 배당가능이익에 대해 주총의 의결에 따라 배당을 받을권리가 있다.\n지문3 이자를 받을 권리는 채권을 보유한 채권자의 권리이다. 주주의 권리는 아니다.\n지문4 주식매수청구권에 대한 권리도 주주의 권리에 속한다. 주로 소액주주에게 해당한다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (581, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["주주로서 행사할 수 있는 권리에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합병승인의 특별결의에 반대하면 주식매수청구권을 청구할 수 있다", "주식 매수가격은 주주와 해당 법인간의 협의에 의해서 결정하는 것이 원칙이다", "주식매수청구권은 소액주주의 경우 의사표시가 없어도 적용을 해주기 때문에 그냥 기다리기만 하면 된다", "주식매수 청구를 받은 법인은 매수의 청구기간이 종료하는 날부터 1월 이내에 해당 주식을 매수하여야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "상법상 주식매수청구권이 인정되는 경우는\n① 회사의 영업양도 등을 함에 있어 필요한 특별결의\n② 합병승인의 특별결의\n③ 분할 합병승인의 특별결의\n④ 주식의 포괄적 교환승인의 특별결의\n⑤ 주식의 포괄적 이전승인의 특별결의의 경우이다. 이런 경우에 반대하는 주주는 회사에 대하여 주식매수청구권을 행사 할 수 있도록 상법에 규정하고 있다. 이런 규정은 다수가 찬성하는 중대한 사안이라도 반대하는 주주에게 보유주식을 회사에게 적정가격으로 팔 수 있는 권리를 부여한 것이다. \n지문3 주식매수청구권은 주주의 일방적인 의사표시에 의하여 성립한다. 의사표시가 없으면 주식매수청구권을 행사하지 않는 것으로 간주된다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주총 특별결의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (582, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(     )은 펀드투자자에게 중대한 영향을 미치는 사항을 투자자가 직접 결정하기 위해  하는 회의로 투자설명서 중 기타 투자자 보호를 위해 필요한 사항에 내용이 기재되어 있는 회의를 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자회의", "임원회의", "수익자총회", "대의원 대회"]}]', '[{"type": "SOLUTION_TEXT", "data": "펀드의 수익자 총회 관련 사항은 투자설명서 제 5부 기타 투자자 보호를 위해 필요한 사항에 내용이 기재되어 있으며 수익자 총회는 수익자 전체로 구성된다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (583, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>에서 설명하고 있는 수익자총회 관련 제도로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["수익자는 수익자총회에 출석하지 아니하고 서면에 의하여 의결권을 행사 할 수 있다. 이 경우 수익자는 서면에 의결권 행사의 내용을 기재하여 수익자총회일 전날까지 자산운용회사에 제출해야 한다. 다만, 제도에서 규정하는 요건을 모두 충족하는 경우에는 수익자총회에 출석한 수익자가 소유한 수익증권의 총 좌수의 결의 내용에 영향을 미치지 아니하도록 의결권을 행사한 것으로 본다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["간주의결권 행사", "유사의결권 행사", "주식매수청구권 행사", "주식매도청구권 행사"]}]', '[{"type": "SOLUTION_TEXT", "data": "간주의결권 행사를 위한 요건\n① 수익자에게 법 시행령 제221조 제6항에 따라 집합투자규약에 적힌 내용을 알리는 서면, 전화·전신·팩스, 전자우편 또는 이와 비슷한 전자통신의 방법으로 의결권행사에 관한 통지가 있었으나 의결권이 행사되지 아니하였을 것\n② 간주 의결권행사의 방법이 집합투자규약에 기재되어 있을 것 \n③ 수익자총회에서 의결권을 행사한 수익증권의 총 좌수가 발행된 수익증권의 총 좌수의10분의 1 이상 일 것\n④ 그밖에 수익자의 이익보호와 수익자 총회결의의 공정성 등을 위하여 간주의결권 행사의 결과를 금융위원회가 정하여 고시하는 바에 따라 수익자에게 제공하는 것을 따를 것\n\n지문3 주식매수청구권제도는 회사의 해산이나 합병, 영업양수/양도 둥의 결의 등 회사의 경영에 중대한 영향을 미치는 행위에 대한 다수주주의 결정에 반대하는 소수 주주를 보호하기 위하여 회사가 반대주주의 보유주식매수를 청구할 수 있는 권리를 부여한 제도다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (584, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["상장회사의 상장폐지 사유로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["파산신청", "주가급등", "고의중과실로 공시의무 위반", "외부감사인의 감사의견 부적정"]}]', '[{"type": "SOLUTION_TEXT", "data": "지문1 상장법인이 파산신청을 하면 관리종목으로 지정된다. 그리고 법원의 파산선고 결정이 나면 즉시 상장폐지 된다.\n지문2 단순 주가급등으로는 상장폐지 대상이 될 수 없다.\n지문3 상장법인이 최근 1년간 공시의무 위반 누계 벌점이 15점 이상인 경에는 관리종목으로 지정된다. 관리종목 지정 후 최근 1년간 누계벌점이 15점 이상 추가되었을 경우와 고의, 중과실로 공시의무를 위반했을 경우에는 상장폐지 조건에 해당된다.\n지문4 반기 검토보고서 상 감사의견이 ‘부적정’ 또는 ‘의견거절’인 경우 관리종목으로 지정된다. 2년 연속으로 감사보고서 상 감사의견이 감사범위 제한 한정인 경우에는 상장폐지 절차가 개시된다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장폐지 이해', null, null);
