insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (592, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 투자자 A의 실제 투자사례에서 발생 가능한 투자태도로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["2021년 1월 투자자 A는 증권사 직원을 찾아가 보유하고 있던 원금 5천만원에 대한 투자상담을 받고 다음과 같은 투자 포트폴리오를 구성하였다. (참고로 원금 5천만원 중 2천만원은 1년 후 전세 보증금을 올려줄 때 사용될 자금이다)", "<투자자 A의 투자내역>", "2천만원 - 향후 실적 기대감이 높은 IT섹터 B기업 주식", "2천만원 - 부동산 펀드 (만기 3년)", "1천만원 -  최근 지인을 통해 소개 받은 주식 종목(합병 기대감으로 최근 주가가 30% 상승중인 종목)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유동성 투자태도", "군중심리 투자태도", "직접적 시장위험", "원금초과손실위험"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자자 A의 투자 포트폴리오를 분석해 보면, 먼저 시장의 움직임에 따른 통제 불가능한 간접적 시장위험 뿐만 아니라, 전체 자산 중 60% 이상을 특정 주식종목에 분산하지 않고 집중투자함에 따른 직접적 시장위험에 함께 노출되어 있다. 또한 전체 자금 중 2천만원이 만기 3년짜리 부동산 펀드에 묶임으로써 1년 후 자금 필요시점에서의 가용 자금이 3천만원으로 줄어 들었고, 3천만원이 모두 주식자산에 집중됨으로써 증시 조정에 따른 수익률 하락 시 유동성 투자태도에 직면할 가능성도 있다. 마지막으로 철저한 분석없이 지인의 소개에 따른 투자로 인해서 군중심리 투자태도에도 노출되었다고 볼 수 있다.\n"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도 종류 숙지', null, null);
