insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (599, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자의 자산배분 실행 과정에 대한 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자는 단기적인 시장에 적극적으로 대응하는 전술적 자산배분 보다는 장기적인 투자목표에 기반한 전략적 자산배분에 더 쉽게 접근하기 때문에 전략적 자산배분에 편중되는 경향이 있다", "개인투자자의 비합리적 투자는 전략적 자산배분의 결여와 전술적 자산배분에 편중되는 데에 그 원인이 있다고 할 수 있다", "개인투자자가 자산배분전략을 실행함에 있어 가장 중요한 것은 위험과 관계없이 높은 기대수익률을 가진 자산을 선별하여 포트폴리오를 구성하는 것이라고 할 수 있다", "개인투자자의 자산배분전략은 기본적인 투자전략의 목표나 기간이 기관투자자와 다를 수 밖에 없으므로 기관투자자의 자산배분전략은 전혀 도움이 되지 않는다"]}]', '[{"type": "SOLUTION_TEXT", "data": "개인 투자자의 경우 기본적으로 마켓타이밍에 기반하여 투자하는 경향이 많아 전술적 자산배분에 대한 의존이 높은 편이고 이로 인해 비합리적 투자가 이루어지는 경우가 많다. 개인 투자자가 포트폴리오를 구성할 때는 수익률에만 의존하는 것이 아니라 위험 대비 기대수익률을 평가하는 것이 필요하고, 이를 위해서는 벤치마킹할 수 있는 기관 투자자나 전문가의 자산배분전략이나 포트폴리오를 참고하는 것이 좋다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (600, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 증권사 시장전망에 동의한 투자자가 취할 수 있는 자산배분전략으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "- 2020년 3월부터 시작된 코로나 바이러스의 영향으로 글로벌 경기는 극심한 침체국면에 빠짐", "- 경기 회복을 위해 미국을 비롯한 주요국들은 금리인하 등 통화완화정책과 함께 막대한 유동성 공급을 통한 재정정책을 펼침", "- 이로 인해 글로벌 경기는 점진적인 경기회복 추이를 보이고 있으나 그 부작용으로 인플레이션(물가상승)이 발생할 가능성이 높아짐"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그동안 시장에 공급된 막대한 유동성으로 인해 향후 인플레이션 가능성이 매우 커진 상황이기 때문에 인플레이션 국면에 유리한 원자재 투자 비중을 늘린다", "완전히 해결되지 않은 코로나 바이러스 국면에서 안전자산이면서도 물가상승에 대비가 가능한 물가연동채권(TIPS)의 투자 비중을 늘린다", "완전히 해결되지 않은 코로나 바이러스 국면과 높은 시장변동성에 대응하기 위해 안전자산인 국채 투자비중을 늘린다", "그동안 시장에 공급된 유동성이 향후 경기 회복을 위한 다양한 사회간접시설 확충 사업 등에 쓰일 것으로 예상하여 인프라펀드에 대한 투자비중을 늘린다"]}]', '[{"type": "SOLUTION_TEXT", "data": "경기 둔화 국면에서 중앙은행은 금리인하를 통한 통화정책을 사용하고, 아울러 정부는 적극적인 재정정책을 통해 유동성을 공급하여 경기를 회복시키게 된다. 이로 인해 다양한 자산가격은 상승하게 되며 경기도 점진적으로 회복하는 효과가 있으나, 시장에 풀린 유동성으로 인해 인플레이션 문제가 부각되며 시간이 지나면 금리인상이나 유동성 축소와 같은 국면이 전개될 가능성이 높다. 따라서 경기둔화국면이 완화되면서 인플레이션 가능성이 높은 시장상황에서는 금리상승에 취약한 채권이나 주식의 비중을 줄이고 인플레이션 헤지 기능이 있는 원자재, 인프라, 물가연동채권 등의 비중을 확대하는 전략이 필요하다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (601, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["경기순환 국면과 그에 따른 전술적 자산배분전략으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 경기둔화국면에서 유동성 공급으로 인해 물가상승이 예상되면 인플레이션 헤지를 위한 자산인 원자재와 회사채에 대한 투자비중 확대가 필요하다", "일반적으로 경기호조가 지속되고 이로 인한 물가상승이 예상되면 인플레이션에 대비할 수 있는 자산인 원자재와 주식 등 위험자산에 대한 투자비중 확대가 필요하다", "일반적으로 경기둔화가 심화되고 물가가 하락하면 안전자산 성격의 국고채 및 현금에 대한 투자비중 확대가 필요하다", "일반적으로 경기호조가 지속되는 동시에 물가가 하락하면 위험자산 성격의 주식이나 회사채에 대한 투자비중 확대가 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "일반적으로 경기둔화국면에서 유동성 공급으로 인해 물가상승이 예상되는 국면에서는 인플레이션 헤지를 위한 자산인 원자재나 인프라에 대한 투자 비중을 확대해야 하며, 향후 물가상승으로 인한 부작용을 억제하기 위해 금리인상 등의 정책이 예상되기 때문에 채권 등의 자산비중을 줄이는 것이 필요하다. 다만 채권 중에서도 인프레이션 헤지 기능이 있는 물가연동채권(TIPS)의 경우는 예외이다.  "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전술적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (602, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자가 주식 포트폴리오 운용을 구성하는 목적 및 방법에 관한 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 이유는 주식이 가진 높은 내재 변동성에 따른 투자태도를 줄이기 위해서이다", "개인 투자자가 주식 포트폴리오를 구성할 때 단기적인 시장의 움직임보다는 장기적인 관점에서 구성하고 포트폴리오의 변경은 최소화해야 한다", "개인 투자자가 주식 포트폴리오를 구성하는 경우 시장상황이나 대내외 변수와 같은 거시적인 요인들을 배제하고 투자 대상 개별주식의 내재 가치와 시장가격에 의존하는 전략이 필요하다", "개인 투자자가 주식을 활용하여 포트폴리오를 구성할 때 가장 중요한 것은 투자태도 감소 보다는 수익 극대화에 있다고 할 수 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "주식은 대표적인 위험자산으로 높은 수익을 기대할 수 있는 대신 단기적으로 변동성이 매우 크고 가격을 예측하기 어렵다는 특징이 있다. 따라서 투자자는 주식 포트폴리오를 구성할 때 투자태도 관리에 최우선 순위를 두고 단기적으로 시장의 변화에 적극적으로 대응하는 것이 필요하며, 기대 수익률 뿐만 아니라 기업이 가진 내재가치와 함께 다양한 대내외 변수들을 함께 고려하는 것이 필요하다. 결국 개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 이유는 주식이 가진 고유의 내재 변동성과 개별주식 투자에 따른 투자태도를 줄이는데 목적이 있다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
