insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (606, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자의 주식 포트폴리오 전략에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["적극적 포트폴리오 관리전략은 시장지수 대비 높은 성과를 내기 위해 다양한 유형의 주식에 적극적으로 최대한 분산하여 포트폴리오를 구축한다", "적극적 포트폴리오 관리전략은 특정 트렌드나 테마를 좇아 포트폴리오를 구성하는 경향이 많아 포트폴리오의 변동성이 높아질 수 있다", "소극적 포트폴리오 관리전략은 안정적인 주식 포트폴리오를 유지하고자 하는 투자자에게 적합한 전략으로 시장지수를 추종하는 포트폴리오를 구축하고 이를 추종하는 방식이다", "소극적 포트폴리오 관리전략은 주식시장의 특정 지수를 초과하는 수익을 내는 것이 목적이 아니라 주식시장 자체의 성장에 따른 수익을 추종하는 전략이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "적극적 포트폴리오 관리전략은 투자하고자 하는 시장지수를 선정하고 지수의 수익률을 상회하는 성과를 목표로 포트폴리오를 관리하게 되며, 따라서 특정 섹터나 종목에 대한 자산배분이나 투자비중 유지 보다는 시장의 트랜드나 테마를 좇아 포트폴리오를 구성하여 투자하는 전략이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (607, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["핵심-위성 포트폴리오 관리전략에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["소극적 포트폴리오 관리전략과 적극적 포트폴리오 관리전략의 장점을 섞어서 투자효과를 높이는 목적으로 사용한다", "적극적 포트폴리오 관리전략의 비중은 70~80%, 소극적 포트폴리오 관리전략의 비중을 20~30% 의 비중으로 섞어서 수익률을 적극적으로 개선시키는 전략이다", "핵심-위성 포트폴리오 관리전략은 변동성을 적절한 수준에서 관리하면서도 시장지수보다 높은 수익률을 내고자 하는 목적으로 많이 사용된다", "투자자는 핵심-위성 포트폴리오 관리전략을 수행함에 있어 초기 설정한 투자 비중을 꾸준히 유지하는 것이 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "핵심-위성 포트폴리오 관리전략은 시장지수를 안정적으로 추종할 수 있는 대표종목으로 전체 포트폴리오 중 70~80%를 구성하고, 나머지 20~30%의 비중은 시장의 트렌드를 추종하여 단기적으로 시장을 주도하는 테마 섹터나 종목으로 포트폴리오를 관리하는 방법이다. 즉 소극적 포트폴리오 관리전략의 비중을 70~80%로, 적극적 포트폴리오 관리전략의 비중을 20~30%로 유지하는 전략이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (608, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 증권사 시장전망에 동의한 투자자가 핵심-위성 포트폴리오 관리전략에 따라 구성해 볼 수 있는 포트폴리오로 가장 적합한 것은? (단, 투자자는 안정적 투자성향으로 가정)"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식시장 전망 :", "코로나 발생 이후 글로벌 경기침체가 심화되었고 이에 대한 각국 중앙은행 및 행정부의 금리인하 및 양적완화 정책이 실시됨. 이에 따른 대규모 유동성 공급은 글로벌 경제를 회복국면으로 이끌고 있고, 이에 따라 기업들의 실적도 반등 추세를 이어감. 특히 코로나로 인한 재택근무 확대 및 비대면 비즈니스 활동의 증가로 인해 반도체 및 언텍트 테마 관련업종의 실적 향상이 두드러질 것으로 기대됨. 다만 코로나 이슈가 여전히 진행 중이고, 변이 바이러스의 등장 등 시장에 변수가 여전히 많아 신중한 투자가 필요함"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장 대표지수 성과추종형 인덱스펀드 투자 70% + 반도체업종 관련주식 투자 30%", "반도체업종 관련주식투자 70% + 시장 대표지수 성과추종형 인덱스펀드 투자 30%", "언택트테마 관련주식 투자 70% + 반도체업종 관련주식 투자 30%", "반도체업종 관련주식 투자 70% + 언택트테마 관련주식 투자 30%"]}]', '[{"type": "SOLUTION_TEXT", "data": "핵심-위성 포트폴리오 관리전략은 시장지수를 안정적으로 추종할 수 있는 대표종목으로 전체 포트폴리오 중 70~80%를 구성하고, 나머지 20~30%의 비중은 시장의 트렌드를 추종하여 단기적으로 시장을 주도하는 테마 섹터나 종목으로 포트폴리오를 관리하는 방법이다. 투자자 A는 기존 주식투자 경험이 없는 투자자로서 전체 자산 중 일부 자산을 주식 포트폴리오에 투자하는 만큼 소극적 포트폴리오 관리전략의 비중을 70~80%로, 적극적 포트폴리오 관리전략의 비중을 20~30%로 유지하는 핵심-위성 전략이 바람직하다. 따라서 시장 대표지수를 추종하는 인덱스펀드에 대한 투자를 70% 수준으로 하고, 향후 시장 전망이 우호적인 반도체 또는 언텍트 관련 종목에 대한 투자를 30% 수준으로 하는 것이 바람직하다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
