insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (612, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["채권의 특징과 그에 따른 포트폴리오 구성방법으로 가장 거리가 먼 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권은 다른 자산 대비 거래 단위가 크고, 장외시장에서 거래되는 비중이 높아 개인 투자자는 채권에 투자하는 펀드나 ETF와 같은 간접투자상품을 통해 포트폴리오를 구성하는 것이 효율적이다", "채권은 발행 주체나 조건에 따라 매우 다양한 금리와 수익률 구조를 가지고 있기 때문에 포트폴리오를 구성하기에 효율적인 자산이다", "모든 채권은 안전자산의 성격을 갖고 있기 때문에 포트폴리오의 안정성을 높이는 특징이 있으나 수익률 측면에서는 다른 자산대비 매력도가 떨어지는 단점이 있다", "채권은 종류에 따라 매우 다양한 수익구조와 변동성이 있을 수있으므로 다양한 종류의 채권에 분산투자함으로써 자산배분 효과를 높일 수 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "채권은 발행 주체나 조건에 따라 매우 다양한 수익구조를 가지고 있으며 신용등급이 낮은 하이일드 채권이나 이머징국가에서 발행된 국채 등은 주식이나 부동산 등 다른 자산과 비교해서도 높은 금리와 매력적인 수익률을 보유하고 있다고 할 수 있다. 다만 이러한 채권은 상대적으로 높은 변동성을 가지고 있기 때문에 안전자산으로 분류하기는 어렵다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (613, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["채권 포트폴리오의 수익률 및 변동성에 가장 큰 영향을 미치는 금리의 움직임과 이에 따른 포트폴리오 구성방법에 대한 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["향후 시장금리 상승으로 채권가격 하락이 예상되면 듀레이션이 짧은 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 하락으로 채권가격 하락이 예상되면 듀레이션이 긴 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 상승으로 채권가격 상승이 예상되면 듀레이션이 긴 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 하락으로 채권가격 상승이 예상되면 듀레이션이 짧은 채권으로 포트폴리오를 구성하는 것이 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "채권 포트폴리오는 시장금리와 매우 높은 상관관계를 갖고 있기 때문에 투자자는 시장금리의 움직임에 따라 채권 포트폴리오의 듀레이션을 조절해야 한다. 일반적으로 듀레이션이 긴 채권은 금리민감도가 높고 변동성이 큰 반면 듀레이션이 짧은 채권은 상대적으로 변동성이 낮다. 따라서 투자자는 시장금리 상승으로 인해 채권가격이 하락하는 구간에서는 듀레이션이 짧은 채권으로 포트폴리오를 구성하고, 반대로 시장금리 하락으로 인해 채권가격이 상승하는 구간에서는 듀레이션이 긴 채권 포트폴리오로 구성해야 한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (614, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 증권사 시장전망에 동의한 투자자가 구성해 볼 수 있는 채권 포트폴리오의 내용으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "2020년 3월부터 시작된 코로나 바이러스의 영향으로 글로벌 경기는 극심한 침체국면에 빠짐. 경기 회복을 위해 미국을 비롯한 주요국들은 금리인하 등 통화완화정책과 함께 막대한 유동성 공급을 통한 재정확대정책을 펼침.", "이로 인해 글로벌 경기는 점진적인 경기회복 추이를 보이고 있으나, 그 부작용으로 인플레이션(물가상승)이 발생할 가능성이 높아진 상황임"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인플레이션이 예상되는 국면에서 향후 중앙은행의 금리인상이 예상되기 때문에 듀레이션이 짧은 채권의 비중을 늘린다", "금리의 변동성이 높아질 가능성이 증가하는 상황이므로 듀레이션이 짧은 채권의 비중을 늘린다", "글로벌 경기가 점진적인 회복추이를 보일 가능성이 높기 때문에 듀레이션이 짧은 고금리 채권의 비중을 늘린다", "글로벌 경기가 점진적인 회복추이를 보일 가능성이 높기 때문에 만기가 긴 채권의 비중을 늘려서 수익률을 높인다"]}]', '[{"type": "SOLUTION_TEXT", "data": "경기침체구간에서는 경기 회복을 위해 금리인하 등의 통화완화정책과 함께 유동성공급을 늘리는 등 적극적인 재정확대정책이 나오게 된다. 이에 따라 경기는 점차 회복세를 보이지만 이후 시중에 풀린 유동성에 따른 부작용으로 시장은 인플레이션 투자태도에 직면하게 되고, 이에 대한 억제책으로 금리인상과 함께 유동성 축소가 진행되게 된다. 문제에서 제시된 시장상황은 경기회복과 함께 향후 인플레이션 억제를 위한 금리인상 및 유동성 축소가 예상되는 국면이므로 이러한 경우 투자자는 만기와 듀레이션이 짧은 채권의 비중을 늘려 변동성을 축소해야 하고, 경기 회복에 따른 수혜를 받을 수 있는 고금리 채권의 비중을 늘리는 방향으로 채권 포트폴리오 전략을 가져가는 것이 필요하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (615, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["글로벌 경기의 흐름과 채권 가격의 영향에 따른 채권 포트폴리오 관리방향으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["글로벌 시장에 위기 상황이 발생하면 시장에서는 안전자산에 대한 선호현상이 강해지고, 이에 따라 선진국 국채의 가격이 상승할 가능성이 높으므로 포트폴리오에서 비중을 늘려야 한다", "글로벌 경기가 호조세를 지속하며 안정세가 지속되는 구간에서는 안전자산 성격의 국채 보다는 금리가 높은 하이일드 채권의 비중을 늘리는 전략이 필요하다", "글로벌 경기가 호조세를 보이며 물가상승이 출현하는 구간에서는 금리인하 가능성이 높으므로 듀레이션이 긴 채권의 비중을 늘리는 전략이 필요하다", "글로벌 경기가 침체국면을 지속하는 구간에서는 금리인하 가능성이 높으므로 듀레이션이 긴 채권의 비중을 늘리는 전략이 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "다양한 채권의 종류 중에서 특히 안전자산으로서의 성격이 강한 선진국 국채나 우량회사채 등은 글로벌 시장에 위기상황이 발생하거나, 글로벌 경기침체가 지속되는 구간에서 수요가 많아지며 가격이 상승하는 특징이 있기 때문에 이러한 구간에서 비중을 늘리는 전략이 필요하다. 반면 글로벌 경기가 호조세를 지속하는 구간에서는 상대적으로 금리가 높은 위험자산 성격의 하이일드 채권의 비중을 높이는 전략이 필요하다. 아울러 경기가 침체국면을 지속하는 구간에서는 향후 금리인하 등의 정책이 나올 가능성이 높기 때문에 이에 따른 롱듀레이션 채권 비중을 늘리는 전략이 필요한 반면, 글로벌 경기의 호조세가 지속되며 인플레이션 우려가 가시화 되는 구간에서는 향후 금리 상승 가능성이 높기 때문에 듀레이션이 짧은 채권의 비중을 늘리는 전략이 필요하다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (616, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자가 주식과 채권을 활용하여 포트폴리오를 구성하는 전략에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식과 채권은 전통적으로 낮은 상관관계를 가지고 있는 자산이기 때문에 두 자산을 적절한 비중으로 혼합하여 투자하면 포트폴리오의 분산투자효과를 높일 수 있다", "상관관계가 낮은 주식과 채권에 분산하여 투자하면 경제환경변수에 따른 간접적 시장투자태도를 줄일 수 있다", "상관관계가 낮은 주식과 채권에 분산하여 투자하면 각각의 자산이 보유한 고유의 투자태도인 직접적 시장투자태도를 줄일 수 있다", "너무 낮은 금리의 채권으로 주식이 보유한 변동성을 상쇄시키기 힘든 국면에서는 채권보다는 고배당주로 관심을 돌려는 것도 좋은 전략이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "주식과 채권은 대표적인 위험자산과 안전자산으로 두 자산간 상관관계는 마이너스를 보이거나 매우 낮다. 따라서 두 자산간 분산투자를 통해 투자자는 포트폴리오의 변동성을 낮추고 분산투자 효과를 높일 수 있다. 두 자산간 분산투자를 통해 투자자는 포트폴리오의 대외적인 시장변수, 즉 간접적 시장투자태도를 감소시키는 효과를 얻을 수 있다. 그러나 이러한 주식과 채권에 대한 분산투자가 각각의 자산이 보유하고 있는 직접적 시장투자태도를 줄이는 데 영향을 미칠 수는 없다. 주식과 채권 각각의 직접적 시장투자태도를 줄이기 위해서는 주식과 채권자산을 각각 분산투자하여 해결할 수 있다.  "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 관리전략 이해', null, null);
