insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (634, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자가 투자자산 포트폴리오를 구성할 때, 대체투자상품과 전통자산투자상품의 비중을 조절하는 방법으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 개인 투자자는 과거 투자 경험에 대한 의존도가 높아 포트폴리오가 특정자산에 편중되는 경향이 많아 시장 변동성에 그대로 노출되어 있는 전통자산과 시장 변동성과 연관성이 낮은 대체투자상품을 함께 섞어서 포트폴리오를 구성할 필요가 있다", "대체투자상품을 포트폴리오에 활용할 때는 전통자산에서 커버하지 못하는 투자태도-리턴 프로파일 상의 영역을 채워 투자하는 것이 좋다", "전통자산과 대체투자자산의 가격간 상관관계를 인지하고 시장 국면에 따라 유기적으로 비중을 조절하는 것이 필요하다", "전통자산과 대체투자자산은 상관관계가 낮아 포트폴리오 효과가 크기 때문에 초기에 비중을 균등하게 배분하여 투자할 필요가 있고, 이러한 비중을 절대적으로 유지하는 것이 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "일반적으로 개인 투자자는 과거의 투자경험에 대한 의존도가 높아 포트폴리오가 특정 자산에 편중되는 경향이 많이 나타나기 때문에 시장의 변동성에 그대로 노출되는 경우가 많다. 따라서 개인 투자자가 투자 포트폴리오에 대체투자상품을 활용하는 목적은 전통자산에서 커버하지 못하는 투자태도-리턴 프로파일 상의 영역을 채워, 보다 안정적인 자산관리를 하는데 있다. 따라서 투자자는 전통자산과 대체투자자산간의 상관관계를 파악하고, 시장국면에 따라 비중을 유기적으로 조절하는 것이 필요하다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (635, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["<보기>의 증권사 시장전망에 동의한 투자자가 취해볼 수 있는 전통자산과 대체투자상품 간 리밸런싱 내용으로 가장 올바른 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "현재 시장은 풍부한 유동성으로 인해 경기 호황국면을 이어가고 있고, 이에 맞춰 기업실적은 호조세를 이어가고 있음. 반면 물가는 상승세를 이어가며 인플레이션에 대한 우려가 제기되는 국면임"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식비중 확대, 채권비중 축소, 부동산비중 축소, 원자재 비중 확대", "주식비중 축소, 채권비중 확대, 부동산비중 축소, 원자재 비중 확대", "주식비중 확대, 채권비중 확대, 부동산비중 확대, 원자재 비중 확대", "주식비중 축소, 채권비중 축소, 부동산비중 축소, 원자재 비중 축소"]}]', '[{"type": "SOLUTION_TEXT", "data": "현재 시장국면은 경기호황국면이 이어지면서 기업실적의 호조세가 지속되고 이로 인한 주식시장의 활황이 지속될 수 있는 환경이다. 따라서 주식의 비중은 확대하는 것이 필요하다. 반면에 인플레이션에 대한 우려로 인해 향후 중앙은행의 정책금리 인상과 이로 인한 시장금리 상승이 예상되는 국면이기 때문에 채권의 비중은 축소하는 것이 필요하다. 대체투자상품 중 금리와 역의 상관관계에 있는 부동산 역시 비중을 축소하는 것이 바람직하며, 반면 인플레이션 헤지기능을 갖고 있는 원자재의 비중은 확대함으로써 인플레이션에 따른 부작용을 완화시킬 수 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 관리전략 이해', null, null);
