insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (637, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["상장지수펀드(ETF)에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상장지수펀드는 낮은 비용, 높은 유동성, 투명한 운용 등의 특징을 갖추고 있어 개인 투자자가 포트폴리오 운용을 하는데 있어 효율을 높일 수 있는 대표적인 상품이다", "상장지수펀드는 주식, 채권 등과 같은 전통자산 뿐만 아니라 부동산, 원자재, 인프라 등 대체투자상품 등을 기초로 하여 만들 수 있어 활용도가 높은 상품이다", "상장지수펀드는 특정지수를 벤치마크로 선정하고, 이 지수를 추종하여 수익을 내는 패시브 형태의 상품만 존재한다", "최근에는 다양한 상장지수펀드를 편입하여 자산배분효과를 높이는 EMP (ETF Managed Portfolio)와 같은 상품도 등장하며 더욱 활용도를 높여주고 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "상장지수펀드는 특정지수를 벤치마크로 선정하고 이 지수를 추종하여 수익을 내는 패시브 펀드로 처음 등장하였으나, 최근에는 액티브 전략으로 운용되는 상품이 등장하며 빠르게 성장하고 있는 추세이다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (638, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자가 자산배분전략과 포트폴리오를 구성하는데 활용할 수 있는 간접투자수단에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 자산배분전략과 포트폴리오를 구성하는데 있어 비용 측면과 효율성 측면에서 비교하면 직접 자산을 편입하는 것이 가장 유리하다", "펀드나 신탁과 같은 간접투자상품은 동일한 자산에 투자하더라도 운용주체의 성향에 따라 운용전략에 차이가 많을 수 있기 때문에 신중한 선택이 필요하다", "로보어드바이저 상품은 개인 투자자의 성향을 분석하여 이에 맞는 자산배분전략과 포트폴리오를 구성하고 관리하는 상품으로 최근 활용도가 증가하고 있다", "로보어드바이저와 같은 투자자 맞춤형 상품은 자산배분전략과 포트폴리오 운용에 있어 활용도가 높으나 고비용이나 성과의 불확실성 등의 단점도 있다"]}]', '[{"type": "SOLUTION_TEXT", "data": "개인 투자자가 간접투자상품을 활용하여 포트폴리오를 구성하는 것은 시장에서 직접 자산을 편입하여 포트폴리오를 구축하는 것 보다 훨씬 편리하고 효율적이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (639, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["자산배분전략과 포트폴리오 구성에 앞서 수행되어야 하는 투자목표설정에 대한 설명으로 가장 거리가 먼 것은? "], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 자산배분전략과 포트폴리오를 구성함에 있어 수익실현 기준 및 로스컷 기준 등 명확한 목표를 수립하는 것이 매우 중요하다", "자산배분의 안정성과 투자 수익성을 함께 추구하는 것을 목표로 하는 전략을 베타-알파 투자전략이라 하고, 이 전략은 장기적이고 안정적 운용을 목표로 하는 베타 자산에 70~80%를 투자하고, 나머지 자산을 추가수익 가능성이 높은 자산에 투자하는 전략이다", "자산배분전략을 충실하게 반영하여 안정적인 성과유지를 목표로 하는 전략을 베타 투자전략이라고 하고, 이 전략은 최초 설정한 자산군별 비중을 큰 변화 없이 유지하는 전략이다", "시장상황에 따른 적극적인 투자수익 추구를 목표로 하는 전략을 알파 투자전략이라고 하고, 이 전략은 전체 자산을 추가수익 가능성이 높은 고위험-고수익 자산에 투자하는 전략이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "자산배분전략과 포트폴리오 운용을 수행하는 목적은 특정 자산에 몰빵투자를 하거나 단기적인 시장의 움직임만을 추종하여 투자함으로써 발생할 수 있는 위험을 줄이고 안정적인 자산관리를 도모하기 위함이다. 따라서 다양한 자산배분전략과 포트폴리오 구성의 목표 중 특정 자산이나 전략에 전체 자산을 투자하는 것은 성립되지 않는다. 알파투자전략의 경우에는 베타투자자산의 비중을 50% 수준 이내에서 유지하고, 나머지 비중은 시장의 단기변화를 효과적으로 포착할 수 있는 알파투자 전략에 투자하는 전략을 말한다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (640, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자가 투자목표를 정하고 포트폴리오를 관리할 때의 유의사항으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 투자목표를 수립할 때는 가급적 목표수익률을 높게 설정하고 시장상황에 따라 목표수익률을 조절해 가는 것이 필요하다", "포트폴리오를 관리함에 있어 기준이 되는 수익률은 전 운용기간에 걸친 평균수익률을 사용하여야 한다", "포트폴리오를 관리함에 있어 유동성 투자태도에 대한 점검이나 긴급상황에 대비한 유동성 관리는 필수적이다", "포트폴리오의 수익률은 특정시점에만 관리하는 것이 아니라 시장환경의 변화에 따라 지속적으로 관리하는 것이 중요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자에 대한 목표 수익률은 적정한 수준으로 설정하는 것이 좋다. 목표수익률이 과도할수록 투자자는 특정 자산에 집중해서 투자하는 경향이 생기게 되고 이로 인해 안정적인 자산관리가 어려워 질 수밖에 없다. 자산배분전략과 포트폴리오 운용은 일시적인 높은 수익이 아니라 꾸준하고 안정적인 수익을 지속적으로 가져감으로써 안정적인 자산관리를 할 수 있는데 목적이 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
