insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (642, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자자 목표기반 자산배분전략 및 포트폴리오 구성 단계에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["첫번째 단계: 구체적이고 현실적인 투자 목표를 수립해야 하며 구체적으로 명확한 투자기간과 미래에 필요한 자금 규모 등과 같은 현실적인 목표를 수립한다", "두번째 단계: 세금제도에 기반한 세후 수익률과 함께 투자시 소요되는 투자비용을 고려하여 포트폴리오에 넣을 수 있는 투자 후보군을 구축한다", "세번째 단계: 투자자산의 위험 대비 기대수익을 감안하여 목표에 맞는 투자군을 선택해야 하며 이때 투자자는 자산군의 최근 성과를 기준으로 투자군을 선택한다", "네번째 단계: 투자자의 제약 조건을 고려해서 시나리오 분석을 진행하고 세번째 단계에서 선택된 투자군 내에 있는 자산 중에서 실제 투자자산을 선택하여 투자 포트폴리오를 구성한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자자 목표기반 자산배분전략과 포트폴리오를 구성 절차 중 세번째 단계에서는 투자자산의 위험 대비 기대수익을 감안하여 목표에 맞는 투자군을 선택하게 되며 투자자는 자산군의 최근 성과만을 기준으로 판단하는 것이 아니라 장기간의 평균수익률을 감안하는 것이 중요하다. 따라서 투자자는 장기간의 역사적 성과에 기반해서 다양한 자산들의 위험대비 기대수익을 참고하여 투자하는 것이 필요하다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
