insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (644, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["주식거래 계좌를 개설하기 위해 진행한 투자성향 테스트의 결과가 <보기>와 같을 때, 투자자의 투자성향으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["연령대 : 40대", "투자 가능 기간 : 3년 이상", "투자 경험 : ELW, 선물옵션, 시장수익률 이상의 수익을 추구하는 주식형펀드, 파생상품에 투자하는 펀드, 주식 , 선물거래에 대한 투자 경험", "금융상품 투자에 대한 지식수준 : 높은 수준", "전체 금융자산 중 투자예상 자금의 비중 : 10% 이내", "수입 현황 : 현재 일정한 수입이 발생하고 있으며 향후 현재 수준을 유지하거나 증가할 것으로 예상", "원금 손실 감수 수준 : 기대수익이 높다면 위험이 높아도 상관하지 않음"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공격투자형", "위험중립형", "안정추구형", "적극투자형"]}]', '[{"type": "SOLUTION_TEXT", "data": "국내 금융회사에서 공통적으로 활용하는 투자성향테스트는 총 7개의 질문에 대한 답변으로 투자자 성향을 구분하구 있으며 7개 항목별 점수를 합산하여 ① 20점 이하 : 안정형, ② 20~40점 : 안정추구형, ③ 40~60 : 위험중립형, ④ 60~80 : 적극투자형, ⑤ 80~100 : 공격투자형 으로 분류하고 있다. \n투자자 A의 답변을 종합해 보면 가장 높은 점수대의 답변으로 공격 투자형에 해당한다. "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자성향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (645, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자성향에 따른 위험자산과 안전자산에 대한 배분비율 구성 이후 실제 포트폴리오에 투자자산을 편입하는 과정에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["실제 포트폴리오를 구성하는 과정은 투자자의 목표를 달성하기에 가장 유리한 자산을 담는 과정으로 과거 성과에 기반하여 자산을 선별해야 한다", "실제 포트폴리오를 구성하는 과정에서 가장 중요한 것은 투자 이후 전개될 시장환경에 대한 예측과 이에 따른 투자자산을 선별하는 과정이다", "개인 투자자가 실제 포트폴리오를 구성하는데 있어 가장 유용하게 참고할 수 있는 대표적인 지표로 경기순환주기에 따른 자산가격 동향이 있다", "개인 투자자가 실제 포트폴리오를 구성한 이후에도 경기주기나 시장환경은 계속해서 변화하기 때문에 실제 구성된 포트폴리오는 지속적인 리밸런싱을 통해 우량한 포트폴리오를 유지하여야 한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "실제 포트폴리오를 구성하는 과정은 다양한 안전자산과 위험자산군 내에서 나의 목표를 달성하기에 가장 유리한 자산을 담는 과정으로 이 과정에서 가장 중요한 것은 투자 이후 전개될 시장환경에 대한 예측이다. 아울러 개인 투자자가 향후 시장환경을 예측하기에 가장 유용하게 참고할 만한 지표로는 경기순환주기에 따른 자산가격 동향이 있다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자성향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (646, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["경기순환주기별 자산가격 동향에 따라 투자자산의 비중을 관리하는 과정에 대한 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["경기 침체의 막바지 단계인 경우 투자자는 안전자산인 채권투자 비중을 늘리는 것이 필요하다", "호황의 막바지 단계인 경우 투자자는 위험자산인 주식투자 비중을 늘리는 것이 필요하다", "경기 침체의 초기 단계인 경우 투자자는 안전자산인 채권투자 비중을 늘리는 것이 필요하다", "경기 호황의 초기 단계인 경우 투자자는 안전자산인 채권투자 비중을 늘리는 것이 필요하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "개인 투자자가 향후 시장환경을 예측하기에 가장 유용하게 참고할 만한 지표로는 경기순환주기에 따른 자산가격 동향이 있으며 투자자는 현재가 아니라 다음 진행될 경기주기를 예상하고 실제 포트폴리오를 선택하고 비중을 조절하는 전략이 필요하다. 즉 현재 경기 상황이 침체 국면인 경우에는 향후 경기회복국면을 예상하고 이에 맞는 자산을 선택하는 것이 필요하고, 현재 경기 상황이 호황국면인 경우에는 향후 전개될 경기위축상황을 예상하여 이에 맞는 자산을 선택하는 것이 필요하다. 다만 이 경우 현재 경기 상황이 호황 또는 위축의 초입인지 막바지인지에 대한 판단이 필요하다.  "}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '경기에 따른 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (647, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["최근 국내 개인들의 자산관리 방법의 변화에 대한 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["저금리 여파로 인해 은행예금으로 개인들의 자금이 몰리는 추세이다", "전국적으로 부동산투자 열풍이 지속되고 있다", "금융투자상품에 대한 관심과 투자규모가 증가하고 있다", "은행예금이나 적금도 금융투자상품 중 하나이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "정부의 각종 규제와 높은 세금 등으로 인해 부동산투자에 대한 관심도 줄어들었으며, 저금리 여파로 은행의 예금이나 적금의 이자수입도 줄어든 상황에서 국내 개인들의 자산관리 방법이 금융투자상품으로 옮겨가고 있는 상황이다.\n\n지문1의 경우, 고금리상황은 현재의 저금리 상황과 대치되는 설명이다.\n지문2의 경우, 현재 부동산투자의 열풍이 사라진 상황과 대치되는 설명이다.\n지문3의 경우, 최근 개인투자자들 사이에 점증하고 있는 금융투자상품에 대한 관심과 점증하고 있는 투자규모를 적절하게 설명하고 있다.\n지문4의 경우, 은행예금이나 적금은 원금손실의 가능성이 없으므로 ‘금융투자상품’으로 분류되지 않고 단순히 ‘금융상품’으로 구분된다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융상품 선택 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (648, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자수익과 투자위험의 관계에 대한 일반적인 설명으로 가장 적절한 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["특별하게 규정할 만한 관계가 없다", "정비례 관계이다", "반비례 관계이다", "수직적 관계이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "투자수익과 투자위험은 서로 정비례 관계가 형성된다. 즉, 고수익을 올리기 위해서는 고위험 상품에 투자해야 하고(고위험-고수익) 투자위험을 줄이려고 한다면 저수익 상품에 투자하는 수 밖에 없다(저위험-저수익). 기본적으로 위험도 적으면서 투자수익까지 많은 투자상품은 없다고 봐야 한다.\n\n지문1의 경우, 투자수익과 투자위험은 일반적으로 정비례 관계로 규정된다.\n지문2의 경우, 투자수익과 투자위험은 일반적으로 정비례 관계로 규정된다.\n지문3의 경우, 투자수익과 투자위험은 반비례 관계가 아니다.\n지문4의 경우, 투자수익과 투자위험은 수직적 관계가 아니다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '기본적 투자원칙 이해', null, null);
