insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (652, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인투자자의 재무상태를 파악하기 위해 재무상태표를 준비하는 방법에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인자산의 정리 및 기록은 유동성이 높은 순서대로 하는 것이 적합하다", "개인부채는 단기부채와 장기부채로 구분하여 정리하고 기록하는 것이 적합하다", "단기부채란 일반적으로 상환기간이 2년 이내인 부채를 말한다", "유동성이 높다는 것은 현금화를 비교적 빨리 시킬 수 있다는 것을 말한다"]}]', '[{"type": "SOLUTION_TEXT", "data": "단기부채란 일반적으로 상환기간이 1년 이내인 부채를 말한다\n\n지문1의 경우, 개인자산의 정리 및 기록은 유동성이 높은 순서대로 하는 것이 적합하기 때문에 맞는 설명이다.\n지문2의 경우, 개인부채는 단기부채와 장기부채로 구분하여 정리하고 기록하는 것이 적합하기 때문에 맞는 설명이다.\n지문3의 경우, 단기부채란 일반적으로 상환기간이 1년 이내인 부채를 말하기 때문에 틀린 설명이다.\n지문4의 경우, 유동성이란 현금화를 시키는 과정에서 신속하면서 동시에 자산가치의 훼손을 최소화 정도를 의미한는 것으로서 유동성이 높다는 것은 현금화를 빨리 시킬 수 있다는 의미이기 때문에 맞는 설명이다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인자산관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (653, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["개인의 현금흐름표에 포함되는 비상자금에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 향후 6개월 동안 소요될 것으로 예상되는 자금이다", "일반적으로 주택마련자금이나 자녀교육비를 의미한다", "주로 기본생활비를 말한다", "기타 예비자금도 포함시키는 것이 적절하다"]}]', '[{"type": "SOLUTION_TEXT", "data": "주택마련자금이나 자녀교육비는 비상자금의 성격과는 거리가 멀다\n\n지문1의 경우, 비상자금은 예상치 않은 사태에 대비하기 위한 자금을 말하는 것으로 일반적으로 6개월 간의 생활비를 말하기 때문에 적합한 설명이다.\n지문2의 경우, 주택마련자금이나 자녀교육비 등은 예상치 않은 사태라고 말하는 것은 적절하지 않기 때문에 틀린 설명이다.\n지문3의 경우, 비상자금은 예상치 않은 사태에 대비하기 위한 향후 6개월 정도 소요될 기본생활비를 말하기 때문에 맞는 설명이다.\n지문4의 경우, 비상자금은 예상치 않은 사태에 대비하기 위한 향후 6개월 정도 소요될 기타 예비자금도 포함시키는 것이 적절하기 때문에 맞는 설명이다."}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인자산관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (654, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": ["투자수익률과 투자기간의 계산에 대한 설명으로 가장 거리가 먼 것은?"], "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["연 평균 2% 투자수익이 발생하면 투자원금이 2배 되는데 약36년이 걸린다", "연 평균 12%의 투자수익이 발생하면 투자원금이 2배 되는데 약6년 걸린다", "72의 법칙은 투자수익률이나 투자기간을 계산할 수 있는 간편한 방법이다", "72의 법칙은 연평균 특정 투자수익률이 발생한다는 가정 하에서 투자원금이 4배로 되기 까지 걸리는 기간을 계산하는 방법이다"]}]', '[{"type": "SOLUTION_TEXT", "data": "72법칙은 연평균 특정 투자수익률이 발생한다는 가정 하에서 투자원금이 2배로 늘어나기 까지 걸리는 기간을 계산하는데 사용할 수 있는 간편한 방법이다\n\n지문1의 경우, 72/2%=36년으로 맞는 계산이다.\n지문2의 경우, 72/12%=6년으로 맞는 계산이다.\n지문3의 경우, 72법칙은 투자수익률이나 투자기간을 계산하는데 있어서 개인투자자들이 간단하게 활용할 수 있는 방법이기 때문에 맞는 설명이다.\n지문4의 경우, 72법칙은 연평균 특정 투자수익률이 발생한다는 가정 하에서 투자원금이 2배로 늘어나기 까지 걸리는 기간을 계산하는 방법이기 때문에 틀린 설명이다"}, {"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '72의 법칙 활용', null, null);
