insert into "ETEST"."UK_MASTER" ("UK_ID", "UK_NAME", "UK_DESCRIPTION", "TRAIN_UNSEEN", "PART", "EXTERNAL_LINK" ) values (404, '상한가/하한가', '주식시장에서 전일 종가 대비해서 가격제한폭 상한까지 주가가 오른 경우를 상한가라 한다. 가격제한폭 하한까지 주가가 떨어진 경우를 하한가라 한다.', '1', '필요자금 마련방법', null);
insert into "ETEST"."UK_MASTER" ("UK_ID", "UK_NAME", "UK_DESCRIPTION", "TRAIN_UNSEEN", "PART", "EXTERNAL_LINK" ) values (405, '따상', '신규 상장 종목이 첫 거래일에 공모가 대비 두 배로 시초가가 형성된 뒤 가격제한폭까지 올라 마감하는 것을 뜻한다. 따상을 기록하게 되면 당일 수익률이 공모가의 160%에 달한다.', '1', '필요자금 마련방법', null);
insert into "ETEST"."UK_MASTER" ("UK_ID", "UK_NAME", "UK_DESCRIPTION", "TRAIN_UNSEEN", "PART", "EXTERNAL_LINK" ) values (406, '주문과 호가', '통상 같은 의미로 혼용되고 있으나, 주문은 위탁자(투자자)가 주식거래를 위해 증권사에 주식을 주문하는 것을 말하는데 비해, 호가는 증권사가 위탁자로부터 받은 주문을 주식시장에 제출하는 것을 뜻한다.', '1', '필요자금 마련방법', null);
insert into "ETEST"."UK_MASTER" ("UK_ID", "UK_NAME", "UK_DESCRIPTION", "TRAIN_UNSEEN", "PART", "EXTERNAL_LINK" ) values (407, '지정가 주문', '지정가는 희망하는 가격에 주문을 내는 것이다. 가격 수준에 따라서 바로 주문이 체결될 수 있고, 가격이 맞지 않으면 체결되지 않을 수도 있다. 원하는 가격이 아니면 거래가 성사되지 않기 때문에 꼭 원하는 가격에 거래하고 싶은 경우에만 사용한다. 대부분의 일반투자자가 주식 주문에서 지정가를 사용한다. 해외 주식의 경우, 지정가 예약 주문을 할 수 있다. 지정가 예약 주문은 당일에만 유효하고, 실제 주문 처리 및 체결은 해외 주식시장이 개장되어야 한다.', '1', '필요자금 마련방법', 'https://kofia.waplstudy.com/api/file/books/CB1/index.html#p=931');
insert into "ETEST"."UK_MASTER" ("UK_ID", "UK_NAME", "UK_DESCRIPTION", "TRAIN_UNSEEN", "PART", "EXTERNAL_LINK" ) values (408, '시장가 주문', '말 그대로 시장에서 지금 거래되는 가격을 말한다. 원하는 가격보다 거래의 성사가 중요한 경우에 사용할 수 있다. 예를 들어, 가격이 오를 것으로 예상되는 종목을 사야 할 때 사자의 가격을 지정하지 않고 주문 수량만을 입력하고 시장가 주문을 내면, 지금 시장에서 거래되는 가격으로 즉시 체결될 수 있다. 다만 이때 체결 가격은 화면에 보이는 현재가와 다를 수 있으니 주의해야 한다. 대부분의 금융투자회사는 지정가를 기본으로 세팅하고 있고, 시장가는 한 번 더 클릭해야 사용할 수 있도록 하고 있다. 지정가 사용이 기본인 셈이다.', '1', '필요자금 마련방법', 'https://kofia.waplstudy.com/api/file/books/CB1/index.html#p=931');
