insert into "ETEST"."UK_MASTER" ("UK_ID", "UK_NAME", "UK_DESCRIPTION", "TRAIN_UNSEEN", "PART", "EXTERNAL_LINK" ) values (585, '가치변동요인으로서 금리', '제대로 된 금융투자는 제대로 금리를 이해해야만 가능하다.', '1', '금융투자상품 가치변동요인', 'https://kofia.waplstudy.com/api/file/books/CB1/index.html#p=404');
