insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (296, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "1000만원을 연 복리 6%로 투자해서 2,000만원으로 만드는데 걸리는 시간으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["6년", "8년", "10년", "12년"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "72의 법칙은 복리를 전제로 자산이 2배로 늘어나는 데 걸리는 시간을 손쉽게 계산하는 방식이다. 72를 해당 수익률로 나누면 대략적으로 원금의 2배가 되는 기간을 산출할 수 있다.  \n\n72÷금리= 원금이 2배가 되는 시기(연)\n\n따라서 1000만원의 돈을 연 6%의 복리 상품에 투자하면 원금이 2배가 되는 데 걸리는 시간은 12년(72÷6=12)이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '72의 법칙 활용', null, null);
