insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (250, 'MULTIPLE_CHOICE', '[{"data": "당신은 TV방송, 신문기사, 투자 카페 또는 SNS 등에서 특정 종목을 많은 투자자들이 사고 있다는 정보를 접한 후 해당 주식을 매수한 경험이 있으십니까?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그 정보만으로 다른 사람을 따라 종목을 매수한 적은 없다.", "많은 투자자가 사고 있다는 종목을 매수한 적이 있다."]}]', '[]', null, '성향', null, null, '김진균', TO_DATE('2020/01/07 01:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, TO_DATE('2021/09/17 18:12:32', 'YYYY/MM/DD HH24:MI:SS'), 'Schwab Brokerage (Quiz)', null, 'ㅊㄱ ㅈㅅㅅㅈㅇ ㅅㅎㅇ ㅂㅇㅎㅇ ㄱㅈㅇ ㅌㅈㄱㅎㅇ ㅈㄱㅎㄱ ㅇㅅㄴㄷ. ㄷㅅㅇ ㅇㄸㄱ ㅎㄷㅎㅅㄴㄲ? ㅈㄱ ㅁㅍㄴ ㅂㅎㅈ ㅇㅇㅇㅁㄹ ㅎㅈ ㅍㅌㅍㄹㅇㄹ ㅇㅈㅎㄷ. ㅈㅂㄹ ㅇㄷㅇㅌㅎㅇ ㅎㅎ ㅈㅅㅅㅈㅇ ㅂㄷㅇㄹㅂㅌ ㅍㅌㅍㄹㅇㄹ ㅂㅇㅎㄱ ㅇㅎ ㅈㅅㅇ ㅈㅈㅈㅎㄷ. ', null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (299, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자수익과 투자태도에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자태도를 감수하지 않으면 손실을 보지 않지만 수익도 얻을 수 없다", "기대수익이 10%라는 말은 항상 10% 수익을 얻는다는 뜻이다", "수익의 변동폭이 크면 투자태도가 크다고 할 수 있다", "투자에서 위험은 분산이나 표준편차로 측정할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "기대 수익률이 연 10%라고 해서 그 주식에 투자하면 무조건 연 10%의 수익을 얻는다는 뜻은 아니다. 기대한 수익이 실현되지 않을 가능성, 투자 결과가 기대했던 수익과 다를 가능성을 투자태도라고 한다. 투자 위험은 투자에서 예상되는 미래 수익률의 분산이나 표준편차로 측정한다. 즉, 예상 수익률이 기대 수익률에서 크게 벗어나지 않으면 투자 위험이 적거나 없다고 하고, 예상되는 수익이 들쑥날쑥하면 투자태도가 크다고 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도에 대한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (298, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자 수익률을 계산할 때 고려해야 할 사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래가 빈번하면 수수료 부담이 커진다", "비과세 금융상품을 선택하면 수익률을 높일 수 있다", "기회비용은 명시적으로 계산할 수 없으므로 고려하지 않는다", "투자대상을 평가할 때는 수익과 함께 위험도 고려해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "수수료와 세금이 눈에 드러나는 비용이라면 그렇지 않은 것도 있다. 대표적인 예로 기회비용이 있다. 기회비용이란 어떤 행위를 하기 위해 포기해야 하는 다른 기회의 가치를 말하는데, 투자에도 그대로 적용된다. 예를 들어, 주식 투자를 하면서 많은 시간과 노력을 기울여 종목을 발굴할 것이다. 이런 시간과 노력이 얼마만큼 가치를 가질 것인지 명시적으로 계산하기는 어렵지만, 기회비용이 발생한 것만큼은 분명하다. 시간과 노력을 다른 곳에 기울일 수도 있기 때문이다. 투자 활동에 수반되는 기회비용을 줄이기 위한 방편으로 직접투자 대신 펀드와 위탁 판매와 같은 간접투자를 이용하기도 하는데, 이때는 투자 결정을 대신해 주는 대가로 운용 보수를 지불한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '수익률 결정요소로서 비용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (261, 'MULTIPLE_CHOICE', '[{"data": "금송이는 투자카페에서 향후 5년 동안 연100%의 투자수익을 보장하는 종목 추천을 받았습니다. 금송이의 반응으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["즉시 투자한다.", "해당 종목에 대한 조사와 투자를 병행한다.", "카페의 다른 회원이 투자하는 경우에 투자한다.", "투자수익은 보장될 수 없음을 알기 때문에 추천을 무시한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자수익을 보장하거나 위험이 전혀 없다는 보장 등은 투자사기의 전형적인 신호입니다. 이런 추천은 무시해야 합니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '높은 수익률을 보장하는 종목추천 의심', null, null);
