insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (239, 'MULTIPLE_CHOICE', '[{"data": "당신은 주식투자에서 어느 정도의 연간 손실을 감내하실 수 있습니까?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["30% 이상의 원금손실", "20~29%의 원금손실", "10~19%의 원금손실", "10% 미만의 원금손실"]}]', '[]', null, '성향', null, null, '김진균', TO_DATE('2020/01/07 01:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, TO_DATE('2021/09/17 18:12:32', 'YYYY/MM/DD HH24:MI:SS'), ' Grable & Lytton 투자위험수용성향 문항', null, 'ㄷㅅㅇ ㄷㅇ ㅈ ㅎㄴㄹ ㅅㅌㅎ ㅅ ㅇㅅㄴㄷ. ㅇㄴ ㄱㅇ ㅅㅌㅎㅅㄱㅅㄴㄲ? 5%ㅇ ㅎㄹㄹ 5,000ㅁㅇ ㅇㄷ 25%ㅇ ㅎㄹㄹ 1,000ㅁㅇ ㅇㄷ 50%ㅇ ㅎㄹㄹ 500ㅁㅇ ㅇㄷ 250ㅁㅇㅇ ㅎㅅㅎ ㅇㄷ ', null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (295, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "3년 간 주식투자로 다음과 같은 현금흐름이 발생했을 때 보유기간 동안 수익률로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["20%", "25%", "30%", "35%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "투자에서 발생하는 수익률은 다음과 같이 계산한다. 먼저 주식과 채권과 같은 투자 자산을 매도한 가격에서 이를 매입할 때 들어간 금액을 뺀 시세 차익과 채권 이자와 주식 배당처럼 투자 자산에서 발생한 현금흐름을 더한다. 그리로 수수료 등 비용을 차감한다. 이렇게 계산한 금액을 매입 금액으로 나눈 다음 100을 곱해 퍼센트(%)로 환산하면 수익률을 계산할 수 있다. \n\n수익률=(매도 금액-매입 금액±보유 기간 중 현금흐름-비용)/(매입 금액)  ×100\n          =  (120만원-100만원+10만원-5만원)/100만원  ×100 = 25% "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (294, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "합리적인 투자자를 가정 시 다음 조건 아래에서 적절한 자산관리 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["정기예금금리(무위험수익률): 1%", "위험보상율: 3%"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["여유자금을 정기예금에 투자한다", "여유자금을 기대수익률이 3%인 금융상품에 투자한다", "여유자금을 기대수익률이 5%인 금융상품에 투자한다", "여유자금을 기대수익률이 7%인 금융상품에 투자한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "투자할 자산으로부터 예상되는 수익률과 투자자가 만족할 만한 수익률을 비교하면 비교적 쉽게 투자 의사를 결정할 수 있다. 투자자는 특정한 투자 대상에서 ''기대되는 수익률(expected return)''이 일정 수준 이상이어야 투자한다. 이때 투자자를 만족시킬 수 있는 최소한의 수익률을 ‘요구 수익률(required return)’이라고 한다. 투자자들이 기대하는 최소한의 요구 수익률은 정기예금(무위험자산)의 이자율과 위험보상율을 합한 것이다. \n\n요구 수익률 = 이자율(무위험자산 수익률)+위험 보상률\n\n위 경우 무위험수익률(1%)과 위험보상률(3%)을 합한 요구수익률은 4%이다. 따라서 기대수익률이 4% 이상인 금융상품에 투자하거나, 아니면 무위험자산에 맡겨 두는 것이 낫다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '요구수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (270, 'MULTIPLE_CHOICE', '[{"data": "A기업 주가는 5천원, B기업은 주가는 1만원입니다. 금송이는 규모가 큰 기업에 투자하기 위해 가격이 비싼 B기업 주식을 사려합니다. 다음 중 가장 올바른 조언은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["B기업은 주식이 2배나 비싸니 A기업 보다 규모가 큰게 맞다.", "기업규모는 주식가격으로 알 수 없고 시가총액을 확인해야 한다.", "기업규모는 주가에 반비례하니 A기업이 B기업보다 더 크다.", "기업규모는 기업에서 발표하는 재무제표의 주석으로 알 수 있다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "기업규모는 자본금, 시가총액 등으로 구분합니다. 시가총액의 경우, 분류 3개월전부터 일평균 시가총액을 기준으로 시가총액 상위 100위까지를 ''대형주''로, 상위 101위부터 300위까지를 ''중형주''로, 나머지 종목을 ''소형주''로 분류합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주가를 기초로 한 시장가치 산출', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (271, 'MULTIPLE_CHOICE', '[{"data": "기업의 자산·이익·자본·배당에 해당하는 재무정보와 주가를 이용한 기업평가방법에 대한 금송이의 설명 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["PBR이 1보다 작다면, 기업의 청산 가치가 현재의 주가보다 크다.", "PER이 3배라면, 1주당 이익보다 3배 높은 가격으로 주식이 거래되고 있다는 것을 의미한다.", "ROE는 기업 경영효율성을 측정 하는 것으로 낮을수록 좋다는 의미이다.", "배당수익률은 1주당 배당금을 주가로 나눈 값으로, 배당금이 주가의 몇% 인가를 나타낸다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "ROE(자기자본이익률)는 높을수록 경영효율성 및 사업실적이 좋다는 것을 의미합니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주가를 기초로 한 비교가치 산출', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (272, 'MULTIPLE_CHOICE', '[{"data": "PER은 주당수익 대비 주가가 몇 배인지를 보여주는 지표입니다. 금송이는 PER이 5배인 A기업에 투자하려 합니다. 이 업종 PER은 10배, 타업종 B기업 PER은 20배입니다. 금송이에게 해 줄 조언 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["PER은 현재 주가와 과거 실적으로 산출되는 지표이므로, 추정 PER보다 투자에 활용도가 높다.", "PER이 높은 주식 보다는 낮은 주식에 투자하는 것을 저PER주 투자라 한다.", "저PER의 원인이 일시적인 가격하락이 아니고 실적하향인 경우를 가치함정이라고 한다.", "PER은 업종마다 평균이 달라 업종이 다른 A,B 기업을 단순 비교하는 것은 적절치 않다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "PER은 현재의 주가와 과거 실적으로 산출된 EPS(주당순이익)로 산출되어 기업가치를 평가하는데 한계가 있습니다. 이를 보완하기 위한 추정EPS로 구한 추정PER이 투자에 활용도가 더 높을 수 있습니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '시장가치의 한계', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (273, 'MULTIPLE_CHOICE', '[{"data": "금송이는 A주식에 투자하여 배당금을 받고자 합니다. 다음의 경우 금송이가 배당금을 받으려면 언제까지 주식을 사야 할까요?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["<3분기 배당일정>", "배당락일 : 9월 29일(수)", "배당기준일 : 9월 30일(목)", "배당지급일 : 11월18일(목)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["9월 28일(화)", "9월 29일(수)", "9월 30일(목)", "11월 18일(목)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "배당금을 받으려면 배당기준일에 반드시 해당 주식을 보유하고 있어야 합니다. 또한 주식 거래의 결제(T+2일 결제) 특성상 영업일 기준으로 최소 2일 전에는 주식을 매수해야 합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '배당의 개념', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (274, 'MULTIPLE_CHOICE', '[{"data": "금송이는 배당금을 받기 위해 A기업에 투자하려 합니다. 배당에 대해 금송이에게 해 줄 수 있는 설명으로 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["당기순이익 가운데 주주에게 지급하는 것을 배당이라고 한다.", "주당배당금 5백원, 액면가 5천원이면 실질 배당수익률은 10%이다.", "높은 배당률은 기업의 재투자가 부족하다는 뜻일 수 있다.", "예금 금리가 1%일 때, 3~5% 이상의 배당수익률이면 고배당이라고 한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "실질 배당수익률은 주당배당금과 주가로 계산됩니다. 즉 주당배당금/주가 입니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '명목배당율과 시가배당율', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (275, 'MULTIPLE_CHOICE', '[{"data": "금송이는 투자할 주식을 고르기 위해 조언을 구합니다. 종목고르기 방법 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기업의 수익 증대, 낮은 부채, 자산 규모 등을 확인하는 것은 종목고르기의 기초 요소이다.", "모멘텀 투자할 때는 종목의 고평가 여부를 판단하는 주요 지표로 P/E 비율을 이용한다.", "가치 투자할 때는 기업의 펀더 멘털을 분석하여 저평가 여부를 판단한다.", "기업의 양적요인과 경영진, 고객 충성도 등도 파악하여 종합적인 미래성장성을 판단해야 한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "P/E(Price/Earning) 비율은 가치투자에서 종목의 고평가 여부를 판단하는 주요 지표로 사용됩니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '배당 귀족', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (276, 'MULTIPLE_CHOICE', '[{"data": "당신은 금송이에게 단기 주가변동 이유를 설명합니다. 가장 올바르지 않은 설명은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장 전체의 가격 변화가 클 때, 많은 주식은 같은 방향으로 움직일 수 있다.", "통상, 채권 이자율이 높아지면 주식투자의 매력도 높아진다.", "원자재 기반의 제품을 제공하는 기업의 주가는 원자재 가격에 따라 크게 영향받을 수 있다.", "경제지표, 기준금리 등의 발표는 주식투자 여부를 결정하는데 영향을 끼칠 수 있다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 안전자산 성격인 채권의 이자율이 높아지면 위험자산인 주식의 매력도는 상대적으로 낮아집니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '과거 실적이 미래 수익의 관계', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (277, 'MULTIPLE_CHOICE', '[{"data": "다음 내용을 보고 금송이에게 해줄 수 있는 설명으로 가장 옳지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["21년 9월 현재, 코스피 시가총액 상위 기업에는 삼성전자 등이 있으며 이를 대형주라 하고 시가총액이 작은 기업을 소형주라고 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["통상 코스피의 대형우량주는 성장성,재무안정성,사업수익성을 모두 갖춘 종목으로 여긴다.", "단기적으로 소형주 주가변동성은 대형주의 주가변동성과 비교했을 때 차이가 없다.", "현재 영업이익은 작지만 성장성이 큰 주식은 가치주보다 가격변동성이 더 크다.", "통상 IT기업에 대한 투자는 자산가치주 보다는 기대수익과 위험수준이 높다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "일반적으로 소형주의 주가변동성은 대형주에 비해 큽니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '대형주, 소형주, 가치주, 성장주의 기업적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (278, 'MULTIPLE_CHOICE', '[{"data": "금송이는 투자하고 있던 A기업에서 자사주(자기회사 주식)를 매입한다는 공시를 보게 되었습니다. 금송이의 다음 판단 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업이 자기회사 주식을 매입하는 것으로 단기적으로 주가에 부정적인 영향을 준다.", "자기회사 주식을 소각하는 경우는 주식수가 줄어 A기업의 1주당 이익이 커진다고 판단한다.", "A기업이 사들인 자기회사 주식은 원칙적으로 6개월이내에 매도할 수 없다.", "A기업의 매출과 순이익 등 사업실적 증가 여부를 추가로 확인해야 한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "자사주 매입은 주주친화적인 행동으로 분류되며, 단기적으로 주가에 긍정적인 영향을 미칩니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '자사주 매입으로 인한 개별 변동요인', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (279, 'MULTIPLE_CHOICE', '[{"data": "금송이는 A기업의 투자정보를 모으던 중 최근 분기에 발표한 기업실적보고서를 보게 되었습니다. 이와 대한 설명으로 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 기업의 실적발표에 투자자는 높은 관심을 보인다.", "발표된 기업실적은 시장 예측치와도 비교해야 한다.", "예측치보다 저조한 기업실적은 주가에 부정적인 영향을 준다.", "기업실적 예측치는 주식시장을 운영하는 거래소가 발표한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "기업의 실적예측치는 증권사(애널리스트들)가 경제와 산업 상황을 고려해 산정하여 발표하며, 다른 증권사의 전망치를 평균한 것을 컨센서스라고 합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '기업은 비즈니스 유형별로 분류', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (280, 'MULTIPLE_CHOICE', '[{"data": "금송이는 최근 경영진을 교체하고 신상품을 출시하는 A기업에 투자를 고려하고 있습니다. 다음 판단 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매출, 이익 등 숫자로 표현되는 양적 요인외에 질적 요인도 주가에 영향을 미칠 수 있다.", "경영진의 교체는 주가에 영향을 미치는 요인이 될 수 있다.", "팬덤과 같은 강한 고객충성도는 주가에 긍정적인 영향을 미칠 수 있다.", "기업의 회계부정행위는 주가에 작은 영향을 미칠 수 있다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "기업의 회계부정행위는 투자자에게 치명적인 위험이다. 주가는 하락하고 주식시장에서 거래정지가 되고, 종국에는 무상감자 또는 상장폐지도 될 수 있습니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '기업이 속한 업종의 특징 파악', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (281, 'MULTIPLE_CHOICE', '[{"data": "금송이는 주식투자를 위해 산업의 특징과 트렌드를 조사하려고 합니다. 다음 중 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["산업에 대한 구체적인 정보는 증권사 애널리스트 보고서에 담겨 있다.", "자신이 종사하는 업종 등 익숙한 산업을 먼저 조사한다.", "인구구조의 변화는 유아용품, 교육, 헬스케어 산업 등에 영향을 미친다.", "전자 산업은 삼성전자 주가가 오르면 다른 전자회사 주가도 오른다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "과거에는 특정 종목이 오르면 그 산업에 있는 주식이 다 오르는 경우가 많았습니다. 삼성전자가 오르면 다른 전자회사 주가가 아무 이유 없이 같이 올랐습니다. 기업 내용을 고려하지 않던 때의 얘기인데, 지금은 그런 경우가 거의 없습니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '경기방어업종의 특징', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (282, 'MULTIPLE_CHOICE', '[{"data": "안전지향 투자자인 금송이가 다음 상황에서 판단하는 내용 중 가장 합리적인 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금송이는 1,000만원을 예금과 배당주에 50:50 포트폴리오를 구성하고 목표 수익률은 3%로 정하고 있습니다. 주가는 일정한데 예금 금리가 1%에서 2%로 오른다고 합니다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["이자가 늘어나는 것으로만 해석하고 다른 행동은 하지 않는다.", "예금을 늘리기 위해서 주식의 일부를 처분한다.", "주식에 더 투자하기 위하여 예금의 일부를 해지한다.", "주식 투자를 위하여 예금의 전부를 해지한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "안정지향 투자자의 포트폴리오 목표 수익률을 3%로 정하고 있는 가운데 예금금리 인상으로 예금수익이 증가한다면 위험자산인 주식을 줄이고 안전자산인 예금으로 이동하는게 합리적입니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '금리와 경제성장과의 관계', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (283, 'MULTIPLE_CHOICE', '[{"data": "금송이의 다음 질문에 대한 올바른 답변으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["요즘 경기는 너무 나쁜데 주식시장은 계속 오르는 상황이 이해가 되지 않습니다. 왜 그런가요?"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["경기는 회복기, 호황기, 후퇴기, 침체기 순으로 순환하고 주식시장은 경기상황에 앞서 움직인다.", "통계청은 경기종합지수를 발표하고, 경기선행지수가 상승하면 경기가 좋아질 것으로 예상한다.", "KOSPI지수는 경기선행지수 중 하나로, 약 6개월 앞서 움직인다.", "경기 방어주는 경기 변동에 따라 기업 실적과 주가가 크게 달라지는 주식으로 자동차,철강 등이 해당한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "경기 방어주는 경기와 상관없이 움직이는 주식입니다. 전기, 가스, 철도 같은 공공재와 의약품, 식료품 같은 생필품을 만드는 회사가 여기에 해당합니다. 반면 경기 민감주는 경기 변동에 따라 기업 실적이 크게 달라지는 주식입니다. 자동차, 철강, 항공운수, 석유화학, 건설, IT가 이에 해당합니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주도 상품의 변화와 경기 판단', null, null);
