insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (285, 'MULTIPLE_CHOICE', '[{"data": "주식의 매매에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매매일과 결제일이 다르다.", "PC 뿐만 아니라 스마트폰으로 매매할 수 있다.", "시장가주문을 내는 경우 가격의 입력이 따로 필요하지 않다.", "주식계좌를 개설하기 위해서는 반드시 증권사를 방문해야 한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "주식계좌 개설은 증권사 방문뿐만아니라 비대면, 온라인으로도 가능합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주식 매매과정의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (286, 'MULTIPLE_CHOICE', '[{"data": "금송이는 주식매매를 통해 다음과 같은 결과를 얻었습니다. 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["100만원을 A사 주식에 투자한 후 한달 뒤 매도하여 매매수수료와 증권거래세를 지불하고 1만원의 수익을 실현하였습니다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["10%의 수익을 실현하였다.", "약 12%/연 수익을 실현한 셈이다.", "약 20%/연 수익을 실현한 셈이다.", "약 50%/연 수익을 실현한 셈이다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "한달에 1%의 수익을 올린 것은 연 기준으로 환산하면 약 12%의 수익을 올린 것과 같은 셈입니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '복리와 수익률의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (287, 'MULTIPLE_CHOICE', '[{"data": "다음 상황에 있는 금송이에게 기업 공시 정보를 설명한 것으로 가장 올바르지 않은 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자의 대가 피터린치는 일상에서 황금알을 낳는 성장기업을 찾아라.고 하였습니다.", "금송이는 아파트 주차장에 주차된 파란색 번호판 차량 중 가장 많이 주차된 차종을 생산한 기업의 공시정보를 알아보려 합니다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융감독원의 전자공시사이트에서 공시정보를 확인할 수 있다.", "공시정보 중에 많이 보는 것이 재무제표이고, 주식투자자라면 꼭 확인해야 하는 정보이다.", "공시에는 해당 회사가 산업에서 차지하는 시장점유율 등의 정보도 담겨져 있다.", "증자/감자, 부도, 최대주주 변경 등의 공시는 정기공시에 해당하며 정해진 날에 공시한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "수시공시 사항은 증자나 감자, 전환사채, 신주인수권부사채, 교환사채 발행, 부도, 최대주주 변경 등이 해당합니다. 주요경영사항이 발생한 당일 또는 다음날까지 금융위원회와 한국거래소에 신고해야 합니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '공시정보 확인', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (288, 'MULTIPLE_CHOICE', '[{"data": "다음 그래프에서 52주 고가와 저가를 바르게 표시한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["(고가) 5월 / (저가) 6월", "(고가) 1월 / (저가) 7월", "(고가) 4월 / (저가) 10월", "(고가) 12월 / (저가) 1월"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "52주 고가는 1월이고 52주 저가는 7월 입니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '1년동안의 주식 가격 확인', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (289, 'MULTIPLE_CHOICE', '[{"data": "금송이는 A기업 공모주에 투자하려고 하는데 \"따상이 기대된다\"라는 기사를 보았습니다. 올바른 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공모주란 기업이 주식시장에 상장하기 위하여, 일반인에게 배정하는 주식을 말한다.", "따상은 상장 첫날, 공모가 대비 두 배로 시작하여 가격제한폭(30%)까지 오르는 것을 말한다.", "대주주나 기관투자자 등은 의무보호예수기간 종료 후 지분을 대량 매각할 수도 있다.", "주식시장 상장 후 주가는 공모가 이하로 하락할 수 없다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "공모가는 기업공개 단계에서 정해지는 단일가격이지만, 상장 후에는 여러 상황에 따라 주가는 변동하게 됩니다. 이 때 공모가격보다 주가는 오를 수도 있고 떨어질 수도 있습니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '공모주 투자와 공모가', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (290, 'MULTIPLE_CHOICE', '[{"data": "주식 시세, 호가 등에 대한 금송이의 설명으로 가장 올바르지 않는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식 시세는 현재의 주식 가격을 말하며 시세표에는 종목명, 주가 등락 등 여러 정보가 일목요연하게 표시된다.", "거래량은 현재까지 매매된 주식의 수를 의미하며 해당 종목의 인기를 보여주기도 한다.", "호가창에서 현재 가격을 기준으로 매도호가는 위로 매수호가는 아래로 보여준다.", "체결강도는 매수체결과 매도체결 간의 비율로 산출되고 100%을 넘어가면 매도자가 매수자 보다 많다는 것을 의미한다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "시장이 시작할 때 거래 강도가 100%인데 이를 넘어가면 주식을 사는 사람이 파는 사람보다 더 많다는 것을 의미합니다. 반대로 100% 이하로 떨어지면 주식을 파는 사람이 더 많은 것을 뜻합니다."}]', '상', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주식의 기본분서과 기술적 분석', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (291, 'MULTIPLE_CHOICE', '[{"data": "금송이는 주식 차트 보는 법을 배우려고 합니다. 다음 설명 중 가장 올바르지 않는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["단기 이동평균선이 중장기 이동평균선을 상향돌파 시 매도신호로 활용한다.", "주가가 저점에서 많이 오른 종목은 차익실현 등으로 주가의 변동 위험성이 커질 수 있다.", "파란색 막대를 음봉, 빨간색 막대를 양봉이라고 한다.", "차트로 알 수 있는 과거 흐름은 미래 결과를 보장하지 않는다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "단기 이동평균선이 중장기 이동평균선을 상향돌파할때는 매수신호로 활용합니다."}]', '하', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '골든크로스의 해석', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (292, 'MULTIPLE_CHOICE', '[{"data": "다음 상황에 대한 설명으로 가장 올바르지 않는 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금송이는 A 증권회사의 수수료 무료 이벤트에 참여하였고 100만원을 국내 주식에 투자하여 10만원의 이익을 냈습니다. 그런데 결제날 10만원보다 적은 금액이 입금되었습니다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식거래는 유관기관 수수료, 증권사 위탁거래수수료, 세금 등 3가지 비용이 발생한다.", "한국거래소, 한국예탁결제원의 수수료가 차감되어 입금되었다.", "증권회사의 거래수수료가 부과될 수 있지만 증권회사 이벤트로 해당 비용은 무료이다.", "국내 주식은 매도하는 경우에만 교육세 0.23%가 부과된다."]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "주식 거래 시에 부과되는 세금은 증권거래세입니다. 주식을 살 때는 세금을 내지 않지만, 주식을 팔 때는 증권거래세를 내야 합니다."}]', '중', '지식', null, null, '김진균', TO_DATE('2020/01/07 02:00:00', 'YYYY/MM/DD HH24:MI:SS'), '이득수', TO_DATE('2021/10/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, '자체 제작', '주식 세금과 수수료', null, null);
