insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (297, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "수익의 재투자를 통한 복리효과를 감안했을 때 수익률이 가장 높은 사람은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["강감찬씨는 1년을 투자해서 6% 수익을 냈다", "김유신씨는 2년을 투자해서 12% 수익을 냈다", "곽재우씨는 3년을 투자해서 18% 수익을 냈다", "이순신씨는 4년 동안 투자해서 24% 수익을 냈다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["강감찬", "김유신", "곽재우", "이순신"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자 대상 기간이 서로 다를 때는 서로 비교할 수 없다. 이때는 수익률을 1년을 기준으로 표준화 해서 비교하는 것이 일반적이다. 그리고 연율화 과정에서 재투자를 가정해 복리를 적용해 계산하는 것이 원칙이다. \n② 김유신씨가 2년 동안 낸 12% 수익을 연으로 환산하면 5.8%이다.\n③ 곽재우씨가 3년 동안 낸 18% 수익을 연으로 환산하면 5.8%이다.\n④ 이순신씨가 4년 동안 낸 24% 수익을 연으로 환산하면 5.5%이다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '복리효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (374, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "ETF(상장지수펀드)에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["액티브 운용전략을 대표하는 금융상품이다", "주식시장에 상장되어 있어 주식처럼 사고 팔 수 있다", "추종하는 지수와의 추적오차를 최소화 하려고 노력한다", "투자자 입장에서는 거래비용이 저렴하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "주식시장에 상장되어 거래되는 ETF는 추종하는 지수와의 추적 오차를 최소화하는 것을 목표로 운용하는데, 패시브 운용전략을 대표하는 금융상품이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'ETF 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (375, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "적립식 투자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매달 일정한 금액을 투자해 주식을 매입하는 투자방법이다", "투자기간 동안 평균 가격으로 주식을 매입할 수 있다", "주가가 높을 때 상대적으로 적은 주식을 매입하게 된다", "항상 거치식 투자보다 나은 성과를 얻는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "매달 일정한 금액으로 주식을 매입하면, 주가가 높을 때는 적은 수의 주식을 사게 되고 주가가 낮을 때는 많이 매입하게 된다. 이렇게 해서 오랜 기간 투자하면 투자 기간 동안 평균 가격으로 주식을 매입할 수 있다. 이를 ‘매입 단가 평준화 효과(Cost Averaging Effect)’라고 한다. 투자기간 동안 주가가 계속 상승하는 때에는 목돈을 한꺼번에 투자하는 거치식투자보다 수익률이 좋지 못하다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적립식투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (376, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "4달 동안 매달 10만원을 투자해서 다음과 같은 방식으로 A회사 주식을 매입했을 때, 주당 평균매입단가로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["858원", "954원", "1,011원", "1,054원"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "4달 동안 40만 원을 투자해서 466주를 매입한 셈이다. 이렇게 되면 1주당 평균 매입 단가는 858원이므로 A보다 싸게 주식을 매입한 셈이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '코스트애버리징 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (377, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자기간 동안 주가가 지속적으로 상승하거나 하락할 때 적립식과 거치식 투자 성과에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주가가 지속 상승하면, 거치식이 적립식보다 투자성과가 우수하다", "주가가 지속 하락하면, 거치식이 적립식보다 투자성과가 우수하다", "시장상황과 무관하게 적립식이 거치식보다 항상 나은 투자성과를 보인다", "시장상황과 무관하게 거치식이 적립식보다 항상 나은 투자성과를 보인다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자기간 동안 주가가 지속적으로 하락하는 상황에서는 적립식 투자를 해도 손실을 피할 수 없다. 다만 목돈을 한꺼번에 투자는 것보다 손실 규모를 줄일 수 있다. 반대로 주가가 지속적으로 상승하는 경우에는 초기에 목돈을 투자는 거치식이 적립식보다 나은 성과를 보인다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적립식투자 장점 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (378, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자금 중 일부를 부채로 조달하는 레버리지투자의 효과에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기대수익과 함께 위험도 감소한다", "기대수익과 함께 위험도 증가한다", "기대수익은 증가하지만 위험은 감소한다", "기대수익은 감소하지만 위험은 증가한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "금융에서는 실제 가격 변동보다 몇 배나 많은 투자 수익이 발생하는 현상을 레버리지 효과라고 한다. 레버리지 효과가 발생하려면 투자 자금 중에 일부를 자기 자본이 아닌 부채로 조달해야 한다. 분산투자를 하면 위험이 줄지만 기대 수익도 줄어든다. 이와 반대로 레버리지를 이용해 기대수익과 함께 위험을 높이는 투자 전략도 있다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '레버리지투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (379, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자기 돈 40만원과 대출금 60만원을 합쳐 만든 투자자금 100만원으로 주식을 100만원어치 구입한 다음, 130만원에 매도했을 때, 투자수익률로 가장 적절한 것은? (단, 이자비용 10만원 발생)", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["20%", "25%", "30%", "50%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "부채를 활용해서 투자하면 그렇지 않았을 때보다 수익률을 상승시키는 효과가 있다. 부채를 활용하지 않았다면 100만원을 투자해서 130만원에 팔았기 때문에 수익률이 30%다. 하지만 부채를 활용해서 수익률을 50%로 올릴 수 있었다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '레버리지투자 이해', null, null);
