insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (301, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "다음과 같은 투자대안이 있을 때, 합리적 선택으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자대안 A: 기대수익률 5%, 투자위험 10%", "투자대안 B: 기대수익률 10%, 투자위험 10%", "투자대안 C: 기대수익률 10%, 투자위험 7%", "투자대안 D: 기대수익률 5%, 투자위험 7%"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자대안 A를 매수하고 B를 매도한다", "투자대안 B를 매도하고 C를 매수한다", "투자대안 C를 매수하고 D를 매도한다", "투자대안 A를 매도하고 D를 매수한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "기대수익이 동일하다면 투자위험이 적은 투자대안을 선택하고, 투자위험이 동일하다면 수익률이 큰 대안을 선택하는 것이 유리하다. A와 B는 투자위험이 동일하기 때문에 기대수익률이 큰 B를 매수하고 A를 매도하는 것이 유리하다.   "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (302, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호의 대상이 되는 사람 만을 <보기>에서 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["은행 예금에 가입 하려는 사람", "펀드에 가입한 사람", "보험계약 효력이 상실된 사람", "신용카드 이용자"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄷ", "ㄱ, ㄴ, ㄷ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "금융시장에서 금융상품 공급자가 금융회사라면, 금융상품에 대한 수요자를 금융 소비자라고 한다. 넓은 의미에서는 현재 금융상품을 이용하고 있는 사람뿐만 아니라 잠재적인 수요자까지도 포함한다. 은행 예금자, 금융투자회사의 투자자, 보험회사의 보험 계약자, 신용카드 회사의 신용카드 이용자와 같은 거래 당사자뿐만 아니라 앞으로 금융회사 상품이나 서비스를 이용하려는 사람도 금융 소비자에 포함된다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (303, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자는 금융회사에 비해 정보력이 떨어지므로 보호해야 한다", "금융소비자가 금융회사에 비해 교섭력이 떨어지므로 보호해야 한다", "금융소비자보호는 금융회사에 대한 신뢰를 무너뜨려 금융시장 전체에 부정적 영향을 미친다", "금융기술 발달속도가 금융소비자의 금융역량 발전속도를 능가하고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "금융 소비자는 금융회사에 비해 정보력과 교섭력 면에서 뒤처져 있다. 금융회사는 금융상품의 특성, 비용, 위험, 금융시장 상황 등에 대해 금융 소비자에 비해 훨씬 풍부하고 전문적인 정보를 가지고 있다. 금융회사가 이런 지위를 남용해 금융 소비자에게 피해를 끼치면 단순히 금융 소비자 개인의 피해로 그치지 않는다. 이는 금융회사에 대한 신뢰를 무너뜨려 결국에는 금융산업과 금융시장 전반에 부정적인 영향을 미친다. 따라서 금융 소비자 보호는 전체적인 금융 시스템의 안정을 위해서도 필요하다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (304, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자의 지위를 향상시켜 금융회사와의 교섭력을 강화한다", "금융소비자가 피해를 입지 않도록 금융회사의 영업행위를 규제한다", "금융소비자에게 객관적이고 충분한 정보를 제공한다", "금융소비자보호는 정부기관만이 할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "금융 소비자 보호는 정부 기관이 해야 할 일로 여기지만, 그렇지 않다. 정부의 역할이 큰 것은 사실이지만, 시장 참가자로서 금융회사와 금융 소비자가 참여하지 않으면 그 효과를 충분히 거두기 어렵다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (305, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융회사가 금융상품을 판매할 때 지켜야할 내용으로 가장 적절한 것은?  ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반투자자의 투자경험과 재산현황 등을 파악한다", "소비자에게 펀드를 판매하면서 단정적으로 수익률을 제시한다", "소비자에게 과도한 금전적 이익을 제공한다", "대출상품을 판매하면서 저축성 상품 가입을 권유한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "금융회사가 일반 투자자에게 투자를 권할 때는 면담이나 질문을 통해 투자자의 투자 목적, 재산 상황, 투자 경험 등을 파악하도록 한다.(적합성의 원칙)\n금융회사가 일반 금융 소비자에게 구매를 권유할 때는 금융상품의 중요 사항을 소비자가 이해할 수 있게끔 설명해야 한다. (설명의 의무) 금융회사가 금융 소비자에게 투자를 권유할 때, 금전적 이익 제공, 손실 보전의 약정과 같이 건전한 거래 질서를 해칠 우려가 있는 방법으로 사용해서는 안 된다. (부당 권유의 금지) 금융회사가 금융상품을 판매하는 과정에서 우월적인 지위를 이용해 금융 소비자의 의사에 반하여 금융상품을 가입하도록 요구해서는 안 된다. (구속성 금융상품 계약체결 금지)"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (306, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융상품의 약관에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["약관의 뜻이 명확하지 않을 때는 금융회사에 유리하게 해석한다", "금융회사는 금융소비자에게 약관의 주요 내용을 설명해야 한다", "개별 약정이 있으면 약관에 우선한다", "금융소비자가 약관의 사본을 요구하면 교부해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "금융상품 약관은 금융회사가 불특정 다수의 금융 소비자를 대상으로 체결하는 금융상품 계약의 세부적인 내용을 담고 있다. 금융회사와 금융 소비자 간에 약관의 해석을 두고 분쟁이 발생하는 경우, 약관은 신의성실의 원칙에 따라 공정하게 해석되어야 하며, 금융 소비자에 따라 다르게 적용하거나 해석되어서는 안 된다. 또한 약관의 내용이나 뜻이 명확하지 않다면 금융 소비자에게 유리하게 해석해야 한다. 개별 약정이 있는 경우에는 그것이 약관에 우선해서 적용된다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (307, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["원칙적으로 금융감독원은 금융소비자가 제기한 금융민원을 (   ) 일 이내에 처리해야 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["3일", "7일", "14일", "21일"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "금융 소비자가 금융회사와의 거래에서 생긴 문제를 우선 금융 상담 통해 해결할 수 있지만, 상담으로 해결하기 어려운 문제는 금융감독원에 민원을 신청해서 처리할 수 있다. 현재 금융 민원 처리 기간은 분쟁 조정은 30일, 기타 금융 민원은 14일 이내다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (308, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융감독원에 접수된 민원과 분쟁조정절차에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융회사를 거치지 않은 민원은 민원인과 금융회사가 자율조정을 거친다", "금융회사를 거쳐 접수된 민원은 금융감독원이 직접 처리한다", "금융분쟁조정은 소송을 통해 해결되지 않은 민원을 해결하기 위한 제도다", "금융회사의 자율조정으로 민원이 해결되지 않으면 금융감독원이 직접 처리한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "금융 소비자가 금융 민원을 접수하면, 금융회사를 거치지 않은 민원은 우선 민원인과 금융회사 간에 자율 조정을 거친다. 이미 금융회사를 거친 민원이나 금융회사의 자율 조정을 통해 해결되지 않은 민원은 금융감독원이 직접 처리한다. 금융분쟁조정제도는 금융 소비자와 금융회사 간에 발생한 분쟁을 소송을 통하지 않고 원만하게 해결하기 위한 제도다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (309, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "제도권 금융상품에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자손실이 발생 할 수 있는 금융투자상품은 제도권 금융상품이 아니다", "금융당국에 등록되거나 인가를 받은 금융회사를 제도권 금융회사라고 한다", "정식 인가를 받지 않은 금융회사도 제도권 금융상품을 판매할 수 있다", "제도권 금융상품은 투자자보호 절차에 따라서만 판매할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄴ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "ㄱ. 투자 결과에 따라 투자 손실이 발생할 수 있는 금융투자상품은 ‘제도권 금융상품’으로 분류한다.\nㄷ. ‘제도권 금융상품’ 관련 법령에 따라 등록되고 정식 인가를 받은 금융회사만 판매할 수 있기 때문에 인가를 받지 않은 금융회사는 판매할 수 없다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (310, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자상품에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래소 거래 여부에 따라 파생상품과 증권으로 분류한다", "금융상품은 원금손실가능성의 유무에 따라 금융투자상품과 비금융투자상품으로 나뉜다", "금융투자상품 중 원금초과손실 가능성이 없는 상품은 증권으로 분류한다", "거래소에서 거래되지 않는 파생상품을 장외파생상품이라고 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "원금초과손실 가능성의 유무에 따라 파생상품과 증권으로 분류한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (315, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호가 필요한 이유에 대한 설명으로 가장 거리가 먼 것은?  ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자는 금융상품을 제조∙판매하는 금융회사에 비해 상대적으로 정보력이 부족하다", "금융회사는 금융투자상품 판매시 어떠한 경우에도 금융소비자에게 투자 손실이 발생하지 않도록 노력해야 한다", "금융소비자는 정보를 받아들이고 활용하는데 한계가 있기 때문에 제한적인 합리성을 지닌 존재로 간주하고 보호해야 한다", "금융소비자는 금융회사와 금융상품 계약 시 전문성 및 교섭력에서 불리한 환경에 처할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "➀ 정보의 비대칭성\n➁ 금융상품 투자시 손실은 투자자 본인의 책임이며, 금융회사가 투자 손실을 보전할 수 없다.  \n➂ 제한적 합리성\n➃ 교섭력의 평등성"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (311, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "대표적인 금융투자상품에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주가연계증권(ELS) – 증권회사가 자기 신용으로 발행하는 파생결합증권의 일종으로, 특정 주가지수나 개별 종목의 주가에 연동하여 발행하며 상환일에 받을 금액이 결정된다", "파생결합증권(DLS) – 이자율, 환율 등이 사전에 약정된 범위내에서 변동하는 경우 약정된 수익율을 적용하여 산출한 금액을 투자자에게 지급하며, 투자 원금을 보장한다", "상장지수채권(ETN) – 금융회사가 자기 신용으로 발행한 채권으로 거래소에 상장되어 쉽게 매매가 가능하며 특정 지수의 수익을 보장한다", "상장지수 집합투자기구(ETF) – 증권회사가 투자자에게 일정 기간 동안 특정 종목의 주가 또는 주가지수를 사전에 정한 조건으로 매수하거나, 매도할 수 있는 권리를 부여한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "파생결합증권(DLS) – 파생결합증권은 투자 원금을 보장하지 않는다. 파생결합증권에서 원금을 보장해주는 상품은 파생결합사채(DLB)라고 한다. \n상장지수 집합투자기구(ETF) - 특정 주가지수 등의 가격 변동성을 추종할 수 있도록 자산을 구성하여 그 주가지수 수익률을 목표로 자산을 운용하는 펀드. (ㄹ) 보기의 설명은 주식워런트증권(ELW)에 대한 설명이다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융투자상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (312, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자의 기본적 권리에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합리적인 금융 소비 생활을 위하여 필요한 교육을 받을 권리", "금융상품판매업자의 위법한 영업행위로 인한 재산상 손해로부터 보호받을 권리", "금융상품의 소비로 인한 피해에 대해서 신속∙공정한 절차에 따라 적절한 보상을 받을 권리", "금융회사 임직원의 권유로 금융상품에 가입한 경우 금융회사에 투자손실의 보전을 요구할 권리"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "금융소비자가 금융회사 임직원의 권유로 금융상품에 가입한 경우에도 금융상품 투자에 따른 책임은 금융소비자 본인에게 있다. (투자 결과에 대한 자기 책임의 원칙)"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '기본적 투자원칙 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (313, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 자신에게 적합한 금융투자상품을 선택하는 5단계를 순서대로 올바르게 나열한 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금융투자회사로부터 본인의 투자 성향에 맞는 금융투자상품을 권유 받는다", "본인의 투자 정보(투자목적, 재산상황, 투자경험 및 지식, 투자기간 등)를 금융투자회사에 정확히 제공한다", "원금을 초과해 손실을 입을 가능성이 있는 파생상품은 투자 손실에 특히 주의한다", "본인이 어떠한 투자 성향(안정형-안정추구형-위험중립형-적극투자형-공격투자형)을 갖고 있는지 제대로 파악한다", "본인이 투자할 금융투자 상품의 내용, 구조, 위험, 수수료 등에 대해 자세히 설명을 듣고 완전히 이해한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ – ㄹ – ㄴ – ㅁ – ㄷ", "ㄴ – ㄹ – ㄱ – ㅁ – ㄷ", "ㄹ – ㄴ – ㄱ – ㅁ – ㄷ", "ㅁ – ㄴ – ㄹ – ㄱ – ㄷ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "자신에게 맞는 금융투자상품을 고르기 위해서는 첫째, 본인의 투자정보를 금융투자회사에 정확히 제공하고, 둘째, 본인의 투자성향에 대해 제대로 파악하고, 셋째, 금융투자회사로부터 본인의 투자성향에 맞는 금융투자 상품을 권유받고, 넷째, 본인이 투자할 금융투자 상품에 대해 자세히 설명을 듣고 완전히 이해하고, 마지막으로 원금을 초과해 손실을 입을 가능성이 있는 파생상품은 투자 손실에 특히 주의해야 한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (314, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호의 필요성에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선진국에서는 2011년 이후 본격적으로 금융소비자보호를 위해 노력하고 있다", "다양하고 복잡해지는 금융상품의 등장으로 금융소비자 보호의 필요성이 높아지고 있다", "금융회사는 금융상품의 제조∙판매 과정에서 금융소비자에 비해 유리하기 때문에 공정한 거래를 위해 금융소비자를 보호해야 한다", "모바일기기 사용 등이 익숙하지 않는 고령층에 대해서는 금융거래 사고방지를 위해 모바일 기기를 통한 금융서비스 이용을 제한하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "금융 환경의 급속한 발전 및 변화 속도를 따라가지 못해 모바일 기기 등을 제공하는 금융서비스를 이용하지 못하는 고령층을 금융시장 안으로 포용하는 문제는 금융소비자보호를 통해 해결해 나가야 하는 과제이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (316, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호법에 대한 설명으로 가장 거리가 먼 것은?   ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융소비자보호법의 시행으로 은행업, 보험업, 금융투자업 등 각 업권별로 특화된 금융소비자보호 업무를 분리하여 수행할 수 있게 되었다", "금융소비자보호법은 2008년 키코(KIKO) 사태, 2011년 저축은행 사태를 거치면서 관련 법안이 발의되어 2021년부터 시행되었다", "금융소비자보호법은 금융상품판매업자의 영업 행위 준수 사항, 금융 분쟁조정 등 금융소비자 보호를 위한 여러 제도를 포함한다", "금융소비자는 금융소비자보호법에서 새롭게 도입된 금융소비자의 권리를 정확히 알고 이해할 필요가 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "금융소비자보호법은 서로 다른 금융업권에서 각기 다른 법령에 따라 수행해온 금융소비자보호업무를 일관된 제도로 추진하기 위한 목적으로 제정되었다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (317, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호법에 따른 금융회사의 금융상품 6대 판매 원칙 중 <보기>에서 설명하는 원칙으로 가장 올바른 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금융회사는 일반 금융소비자에 대해서 계약 체결 전 금융소비자의 정보(재산 상황, 투자 목적 및 경험 등)를 파악하고, 이를 서명 등의 방법으로 확인받아 이를 기록·유지하고, 확인한 내용을 금융소비자에게 지체 없이 제공하여야 한다", "금융회사는 파악한 정보를 기초로 일반 금융소비자에게 적합하지 않은 계약 체결을 권유하지 않아야 하는 의무를 지닌다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["설명의무원칙", "적정성 원칙", "적합성 원칙", "부당권유 행위 금지 원칙"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "적합성 원칙에 대한 설명이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (318, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자보호법에서 금융소비자보호를 위해 새로 도입한 제도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["청약철회권", "위법계약해지권", "과징금", "약관설명 및 교부"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "약관설명 및 교부는 금융소비자보호법 시행 이전에 도입된 것으로 금융소비자보호법에서 새로 도입된 제도로 보기 어렵다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (319, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "사전적 금융소비자보호란 금융소비자가 금융상품 등의 계약으로 피해를 입지 않도록 사전에 금융회사가 준수하거나 지켜야 하는 방법과 절차 등을 말한다. 다음 중 사전적 금융소비자보호 방법과 절차로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융회사의 영업 행위 원칙 준수", "금융분쟁조정제도", "금융상품 약관 설명 및 교부", "금융정보 및 금융교육 제공"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "금융분쟁조정제도는 금융소비자가 금융회사를 상대로 제기하는 금융분쟁 건에 대해 신청내용에 기초하여 사실관계 등을 확인하고, 이에 대해 합리적인 분쟁해결 방안이나 의견을 제시함으로써 당사자간의 합의를 유도하여 소송으로 가지 않고 분쟁을 원만하게 해결하는 제도로 대표적인 사후적 금융소비자보호 절차이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (320, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융회사가 금융소비자보호법에 따라 금융소비자에게 금융상품을 권유하고 판매하는 과정에서 준수해야 하는 일반적인 영업 행위 원칙에 대한 설명으로 거리가 가장 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융회사는 금융상품이나 금융상품 자문에 대해 금융소비자와 계약을 체결하고 권리를 행사하고 의무를 이행할 때 신의 성실의 원칙에 따라야 한다", "금융회사는 금융상품이나 금융상품 자문에 대한 계약을 체결하는 경우 정당한 사유 없이 금융소비자를 부당하게 차별해서는 안 된다", "금융회사는 관련 법령에 따라 사업내용, 재무상황, 영업실적 등 자사(自社)의 중요한 경영 내용을 정확하게 공시해야 한다", "금융회사는 임직원 및 금융상품 판매 대리 또는 중개업자가 업무를 수행할 때 법령을 준수하고 공정한 거래질서를 해하는 일이 일어나지 않도록 성실히 관리해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "➀ 신의 성실 의무\n➁ 차별 금지\n➂ 기업의 공시 의무에 대한 설명으로 일반적인 영업 행위 원칙과는 거리가 멀다. \n➃ 금융회사의 관리책임\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (326, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자사기 피해 예방 요령에 대한 설명으로 가장 거리가 먼 것은?  ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["무작위로 발송되는 투자권유 이메일, 편지, 전화는 무시한다", "투자를 위해 제공받은 자료 외에 별도로 관련 자료 또는 정보를 관련 공공기관 또는 전문가를 통해 확인한다", "합법적인 투자권유인 경우에도 가족이나 관련 분야 경험자와 투자결정을 논의한다", "특약 등 중요한 내용은 반드시 계약서에 반영하여 문서로 남겨야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "합법적인 투자라도 ‘수익보장’, ‘임대보장’ 등 특별한 약속사항은 구두협의만으로는 법으로부터 보호를 받을 수 없기 때문에 구두합의 내용을 반드시 계약서에 포함시켜야 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (321, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융회사의 금융상품 약관 설명 및 교부 의무에 대한 설명으로 가장 올바른 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["약관이란 여러 명의 당사자가 여러 명의 상대방과 계약을 체결하기 위해 일정한 형식으로 미리 마련한 계약의 내용을 말한다", "금융투자회사는 표준약관이 아닌 개별 약관을 제정해서 사용하는 경우 사후에 금융투자협회에 보고해야 한다", "금융회사가 금융소비자에게 약관이 제때에 교부되지 않거나 중요 내용의 설명을 소홀히 한 경우라도 해당 계약의 취소는 불가능하다", "약관은 모든 금융소비자에게 동일하게 해석되어야 하고, 약관 내용의 해석이 불명확한 경우 금융소비자에게 유리하게 해석되어야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "➀ 약관이란 계약의 한쪽 당사자가 여러 명의 상대방과 계약을 체결하기 위한 계약의 내용이다.\n➁ 개별 약관 제정의 경우 금융투자협회로 사전에 보고하여야 한다.\n➂ 약관 미교부 및 중요 내용 설명 소홀은 계약 취소의 사유가 될 수 있다. \n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (322, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자는 금융상품에 가입하기 전에 금융회사가 제공하는 금융상품에 대한 정보 등을 꼼꼼히 확인하고 이해해야 한다. 다음 중 금융소비자에게 불이익으로 작용할 수 있어 주의가 필요한 사항으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["원금손실 가능성 및 손실 가능 범위", "중도해지 시의 불이익", "금융소비자의 개인정보 열람 청구 절차", "기한이익 상실 사유"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "개인정보 열람 청구권은 정보주체로서 개인의 권리를 보장하기 위한 권리로 금융소비자에게 불리한 사항으로 보기 어렵다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (323, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융소비자의 손실이나 피해 구제를 위한 금융 민원상담 및 민원신청, 분쟁조정에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["민원 접수는 우편 접수만 가능하며 인터넷, 팩스 등의 방법으로는 접수할 수 없다", "민원의 처리 기간은 통상 금융 민원신청의 경우 14일, 분쟁조정의 경우 30일 이내이다", "금융소비자가 민원접수 신청 후 민원 문제를 해결하지 못한 경우 민원상담을 신청하여 처리한다", "분쟁조정을 신청한 경우 금융소비자는 분쟁조정위원회의 조정안을 반드시 수용해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "➀ 민원 접수는 우편, 팩스, 인터넷 등을 통해 가능하다. \n➂ 민원상담 신청 후 해결하지 못한 경우 민원을 접수하여 처리한다. \n➃ 금융소비자는 분쟁조정위원회의 조정안을 최종 수용할지 여부를 결정할 수 있다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (324, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인정보보호법에 따라 금융회사가 준수해야 하는 개인정보보호 원칙에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인정보 처리방침은 금융회사의 내부 사정에 따라 공개하지 않을 수 있다", "개인정보의 처리 목적을 명확히 밝히고, 필요한 범위에서 최소한의 개인정보만을 수집해야 한다", "정보 주체의 사생활 침해를 최소화 할 수 있는 방법으로 개인정보를 처리해야 한다", "관련 법령에 따른 책임과 의무를 준수하고 정보주체의 신뢰를 얻기 위해 노력해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "개인정보보호법 제3조제5항에 따라 개인정보 처리지침 등 개인정보의 처리에 관한 사항은 반드시 공개해야 한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인정보보호 체계 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (325, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자 사기 유형 및 이에 대한 대처요령에 대한 설명으로 가장 거리가 먼 것은?  ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["무인가 금융투자업자의 불법 영업행위 – 해당 업체가 제도권 금융회사인지 ‘금융소비자 정보표털 파인’에서 확인하고, 거래 중단 후 금융감독원에 상담한다", "불건건 유사투자자문행위 – 유사투자자문행위 관련 계약해지 및 자문수수료 환불은 한국소비자원으로 문의하면 피해구제를 받을 수 있다", "불법 유사수신행위 - 유사수신 피해를 입은 경우 설명회 자료, 거래내역, 녹취 파일 등 증빙자료를 확보하여 경찰에 신고하거나 금융감독원에 제보한다", "저금리 대환대출을 빙자한 전화상담 사기 – 개인정보를 이용한 사기 가능성이 높으므로 각별히 유의한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "유사투자자문행위 관련 분쟁은 한국소비자원으로 문의하면 분쟁 관련 도움을 받을 수 있지만, 직접적으로 피해구제를 받을 수 있는 것은 아니다. (미신고)유사투자자문업자의 리딩(leading)을 따라 매매를 하여 손실을 입은 경우 피해구제가 어렵다(투자자 자기책임원칙)"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (327, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자상품 투자 전 확인이 필요한 사항에 대한 설명으로 가장 거리가 먼 것은?  ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["계좌를 개설하기 전에 어떠한 금융투자상품을 거래하기 위한 목적으로 계좌를 만드는지 확인한다", "계좌를 개설한 후에 계좌설정 약관의 내용을 확인한다", "투자자는 금융회사의 확인절차를 통해 확인한 본인의 투자성향에 맞게 투자 계획을 수립해야 한다", "금융투자상품은 중도해지 시 위약수수료가 발생할 수 있으므로 투자기간 및 투자자금 사용 계획을 잘 세워 투자해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "투자자는 계좌 개설 전에 금융투자회사가 제공하는 서비스와 금융투자상품별 계좌 설정 약관의 내용을 확인하고 동의해야 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '금융소비자보호법 이해', null, null);
