insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (329, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자상품 매매주문 시 주의할 사항에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식을 거래하는 경우 금융감독원 전자공시 등을 통해 주식 상장법인이 공시한 주요 경영사항을 수시로 확인할 필요가 있다", "영업점 직원을 통해 매매 주문을 내는 경우 녹취가 되는 영업점 전화번호를 이용하며, 통화중인 경우에는 영업점 직원 개인 핸드폰으로 주문을 내도록 한다", "매매 주문을 낸 이후에는 반드시 주문의 이행상황과 매매체결내역을 확인한다", "영업점에서 주문전표를 이용하는 경우 주문수량 및 가격은 투자자 본인이 직접 작성한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "영업점 직원을 통한 매매 주문은 반드시 녹취가 되는 영업점 전화번호를 이용하되, 직원 개인 핸드폰으로는 절대로 주문을 내지 않도록 한다. 향후 매매주문 관련 분쟁 발생 시 녹취 기록은 사실 관계 확인에 꼭 필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (330, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "모바일 기기를 통해 주문을 내는 경우에 대한 주의사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["모바일기기나 인터넷에 본인의 금융정보를 저장하지 않는다", "금융거래시 휴대폰 문자안내서비스(SMS), 일회용비밀번호(OTP)를 이용한다", "금융회사가 안내하는 공식 배포처를 확인하여 금융서비스를 이용한다", "보안프로그램을 설치한 경우에는 공공장소에서 무료 와이파이를 사용해도 안전하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "공공장소의 무료 와이파이는 사용시 해킹 위험이 높으므로 보안 프로그램을 설치한 경우에도 사용을 피하는 것이 바람직하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사전적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (331, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객 A는 증권회사 직원 B에게 주식매매를 포괄적으로 위임하고 매매 결과를 주1회 보고받는 형태로 거래하고 있었다. 어느 날 증권회사 직원 B는 ◯◯카드 주식 9,000주 가량을 매수하고, 이를 고객 A에게 보고하자 A는 이를 질책하면서 앞으로 동 종목을 매수하지 말 것을 지시하였으나 이후 직원 B는 이를 어기고 동 종목을 다시 매수하였고 그 사실을 고객 A에게 보고하지 않음"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "고객 A는 직원 B에게 계좌의 운용을 포괄적으로 일임하였거나, 적어도 묵시적으로 사후 승인한 것으로 보인다. 그러나 고객 A가 직원 B에게 ◯◯카드 주식을 매수하지 말 것을 명확하게 지시함으로써 포괄적 대리권을 제한하였음에도 불구하고 직원 B는 동 주식을 매수하였고, 이후 고객 A에게 매수 사실을 보고하지 아니하였으므로 이는 임의매매에 해당함"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (332, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객이 증권사 직원에게 주식 매매를 일임한 기간의 일부 기간에 월매매회전율이 약 1,400%에 달하였고, 특별히 단기매매를 하였어야 할 사유도 없었음. 또한 고객이 특정 종목에 대해 “당일 전부 처분” 지시에 했음에도 불구하고 직원은 지정가주문으로 일부 수량만 매도함"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "매매회전율이 약 1,400%에 달하고, 고객의 요청 등 단기매매를 했어야 할 특별한 사정이 없었으므로 과다매매로 볼 수 있다. 또한 고객의 “당일 전부 처분” 지시에 대해 직원이 주문을 지정가주문이 아닌 시장가주문으로 하였더라면 전량 매도가 가능한 상황이었으나 지정가주문으로 고객의 “당일 전부 처분” 지시를 따르지 못한 것은 전문가로서 선관주의의무에 충실했다고 보기 어려움"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (333, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객 A는 증권회사 직원 B에게 자신의 계좌관리를 맡겨 직원 B에게 종목추천 및 주문집행을 담당하게 함. 이후, 직원B는 ☆와 ★종목에 대해 호재성 재료가 있음을 언급하며 순차적으로 매수를 추천하였는데 ☆의 경우 당시 공시 사항을 근거로 한 추천이었음에 반해 ★의 경우 단순한 시장풍문사항에 불과하였음. 결국, 고객A는 직원B의 추천에 따라서 ☆와 ★종목을 매입하였으나 두 종목 모두 주가가 하락하였고 특히 ★종목의 경우 직원B가 언급하였던 호재성 재료(풍문)는 현실화되지 않았음"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "직원 B가 시장에 공시된 사항을 근거로 매수를 추천하는 경우 매수추천이 아무런 근거 없는 본인의 단정적 판단에만 기인한 것이라 보기 어려우나, 당시 풍문에 불과한 사항에 대해 별도의 확인노력 없이 매수를 추천한 것이 사실이고, 그 과정에서 풍문이라는 사실에 대한 언급없이 마치 조만간 공시될 것처럼 고객 A를 오인케 한 표현을 사용한 행위는 단정적·합리적 근거없는 판단제공 행위로서 부당권유행위에 해당될 여지가 있음"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (334, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 사례에 해당하는 금융분쟁 유형으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["고객 A는 △△은행과 금융거래를 하며 그동안의 거래로 친분을 쌓은 담당직원 B로부터 △△은행이 판촉행사 중인 고위험상품인 주가연계펀드(ELF)에 대해 투자권유를 받음. 직원 B는 고객 A가 평소에 은행예금 등 안정추구적인 투자성향임을 이미 알고 있었음에도 불구하고, 또한 직원 B 자신도 동 상품에 대해서 많은 시간동안 학습하지 않아 충분히 인지하지 못한 채 동 상품에 대하여 설명 및 적극적으로 투자를 권유하면서 상품의 수익구조 등에 대해서도 사실과 다르게 설명함"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["임의매매", "일임매매", "부당권유", "불완전판매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "담당직원B는 고객 A와의 금융거래로 고객의 투자성향에 대해서 익히 파악하고 있었음에도, 직원 자신조차도 제대로 이해하지 못한 고위험의 펀드상품을 투자권유하였고, 동 펀드상품의 수익구조에 대해서도 사실과 다르게 왜곡하여 설명하여 고객 A에게 투자상품에 대한 위험성에 관한 올바른 인식형성을 방해하고, 과대한 위험성을 수반한 거래를 적극적으로 권유하여 고객보호의무 위반한 사실이 인정됨"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (335, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자율적 분쟁조정제도의 장점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["소송수행으로 인한 추가적인 비용부담 없이 최소한의 시간 내에 합리적으로 분쟁을 처리할 수 있다", "복잡한 금융 관련 분쟁에 대한 해당 분야 전문가의 견해를 들을 수 있다", "개인투자자 측면에서 확인하기 어려운 금융투자회사의 보유자료 등을 조정기관을 통해 간접적으로 확인할 수 있다", "분쟁조정기관은 기존 판례 및 선례, 법이론을 바탕으로 결과를 도출하기 때문에 항상 실제 소송과 같은 결과를 기대할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "분쟁조정기관은 기존의 판례 및 선례, 법이론을 바탕으로 가장 보편타당한 결과를 도출하기 위해 노력하나, 실제 소송 수행결과와 반드시 같은 결과가 나올 것으로 단정할 수는 없다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (336, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자율적 분쟁조정제도의 단점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자율적인 분쟁해결제도의 특성상 양당사자의 합의가 도출되지 아니하면 분쟁처리가 지연될 수 있다", "각각의 자율 분쟁조정기구 판단기관에 따라 결과의 차이가 있을 수도 있다", "한 분쟁조정기구의 조정결과를 받아들이지 못한다고 해서 다른 분쟁조정기구에 다시 분쟁조정을 신청하는 것은 허용되지 않는다", "분쟁조정기구에서 제시한 조정안에 대한 수용 여부는 당사자가 판단해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "분쟁조정기구마다 특징이 다르며, 예를 들어 한국거래소 또는 금융투자협회 분쟁조정기구를 거쳤더라도 조정 결과를 수긍할 수 없다면 금융감독원 분쟁조정기구를 통해 다시금 분쟁조정을 할 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '사후적 금융소비자보호 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (337, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "디지털금융 트렌드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["구체적이고 적시적인 초개인화", "혁신성과 안정성을 모두 충족하는 서비스", "시간 제약이 없는 비접촉식 결제", "타인과 구별되는 차별적인 금융서비스 소비"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "현재 금융업의 디지털 트렌드는 인터넷과 모바일 통신의 발달로 인하여 사람들에게 보다 구체적이고 적시적인 초개인화 경험, 혁신과 안정성을 충족하는 서비스 경험 기회, 시간의 제약이 없는 비접촉식 결제 경험, 평등하고 가치있는 금융서비스 소비 등을 지향한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 금융 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (338, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "온라인뱅킹에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["스마트폰의 대중화로 인하여 지금은 모바일 뱅킹 이용추세가 점증하고 있다", "처음에 온라인뱅킹은 인터넷뱅킹, 홈뱅킹 등과 같은 의미로 사용하였다", "온라인뱅킹 이용금액 중에서 모바일뱅킹 이용금액이 대부분을 차지한다", "모바일뱅킹 이용의 증가로 창구거래 비중은 감소하는 추세이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "2021년 4월 5일에 한국은행에서 발행한 “2020년중 국내은행 인터넷뱅킹서비스 이용현황” 에 따르면 인터넷뱅킹 이용실적 중 모바일뱅킹의 비중이 건수 기준으로 77.5%인 것에 비해 금액 기준으로는 15.4%에 지나지 않는다. 이에 대한 요인으로 모바일뱅킹 이용 금액의 한도가 제한되어 있고, 고액의 경우에는 대면채널을 선호하는 금융회사의 특성과 더불어, 마지막으로 이용자의 보안에 대한 우려 등을 들 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 금융 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (339, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "증권거래에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["한국거래소에 따르면 2020년에 비대면 거래의 비중이 처음으로 80%를 넘어섰다", "MTS 비중이 HTS의 비중을 앞서고 있다", "전국의 증권회사의 지점 수는 매년 일정한 수준을 유지하고 있다", "MTS거래가 증권거래의 대세가 되어가는 상황이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "IMF이후인 1999년 3월 866개이던 증권사 전국 지점수는 2010년말 1,790개로 정점을 찍은 후 매년 5% 안팎으로 줄어들어 결국 2020년말 현재 기준 20여년전 수준인  860개에 이르렀다. 이러한 급격한 점포수는 비대면채널의 강화로 지속할 것으로 예상하고 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 금융 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (340, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "사이버 증권거래에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전산 장애에 대비해 전화 및 자동응답시스템(ARS) 등 대체주문수단을 확보한 후 거래한다", "HTS 프로그램 오작동 등 매매 주문에 이상이 있다면, 즉시 사용을 중단하고 콜센터에 주문 내용 및 포지션을 명확히 알리고 비상 주문을 요구한다", "HTS를 사용하여 매매거래를 하는 동안 컴퓨터 부하를 최대한 줄일 수 있도록 다른 활성창을 닫고 실행한다", "PC의 바이러스 감염에 대비하여 백신 프로그램의 업데이트는 가급적 자제한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "PC의 백신 프로그램을 업그레이드를 자주 하여 최신 버전을 유지하여야 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 금융 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (345, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "로보어드바이저의 장점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["차별화된 고비용 서비스", "데이터 기반의 객관적인 추천", "시간과 장소에 구애받지 않는 편리함", "인간의 개입이 초래할 수 있는 실수 예방"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "기존 개인형 맞춤 자산관리 서비스는 고액자산가에게만 제공하던 금융서비스였다. 하지만 로보어드바이저는 기존 대면서비스에 비해 물리적 제약이 거의 없기 때문에 서비스 가입에 높은 제약을 둘 필요가 없다. 따라서 가입 최소금액을 상당히 낮게 책정할 수수료도 대면서비스에 비해 절반이상 저렴하다. 그렇기 때문에 모든 고객에게 평등하면서도 일반적인 서비스가 제공되는 것이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (341, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "디지털 전환에 성공한 기업에 대한 설명이다. <보기>의 (  ) 안에 들어갈 단어를 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["디지털 전환 작업을 추진하는 주체가 (   )이며, 강력한 카리스마로 조직과 문화의 패러다임 변화를 주도한다", "고객이 온라인, 오프라인, 모바일 등 어떠한 (   )에서 접근하든 고객에게 최상의 서비스를 경험할 수 있도록 옴니 (   ) 전략에 집중한다", "해결하려는 문제와 목표를 명확하게 세우고, 우선 구체적인 한두 가지 디지털 전환 프로젝트에서 시작한다", "참조: 《디지털 금융의 이해와 활용》, 김수진 저(수정 인용)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["CEO, 채널, 채널", "고객, 상황, 채널", "CEO, 상황, 멀티", "고객, 채널, 고객"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "첫째, 디지털 전환 작업을 추진하는 주체가 CEO이며, 강력한 카리스마로 조직과 문화의 패러다임 변화를\n주도한다. 둘째, 고객이 온라인, 오프라인, 모바일 등 어떠한 채널에서 접근하든 고객에게 최상의 서비스를 경험할 수\n있도록 옴니 채널 전략에 집중한다. 셋째, 해결하려는 문제와 목표를 명확하게 세우고, 우선 구체적인 한두 가지 디지털 전환 프로젝트에서 시\n작한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털전환 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (342, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "인공지능에 대한 설명으로 올바른 것만을 <보기>에서 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["인공지능은 학습, 추론 및 언어 이해 능력 등 인간이 가진 지적 능력을 컴퓨터 프로그램으로 실현하는 기술이다", "인공지능은 사람의 지능과 마찬가지로, 문제 해결 능력, 학습 능력, 범용성의 3가지 특징을 갖고 있다", "인공지능이 가장 넓은 개념이고, 인공지능을 구현하는 방법 중 하나가 머신 러닝이다", "딥러닝은 머신러닝의 방법 중 하나로 인공 신경망(Artificial Neural Network) 알고리즘을 활용하는 기술 중 하나이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "인공지능은 학습, 추론 및 언어 이해 능력 등 인간이 가진 지적 능력을 컴퓨터 프로그램으로 실현하는 기술이다. 인공지능은 사람의 지능과 마찬가지로, 문제 해결 능력, 학습 능력, 범용성(패턴 인식, 추론 능력)의 세 가지 특징을 갖고 있다. 인공지능과 관련하여 머신 러닝(Machine Learning, 기계 학습)과 딥 러닝(Deep Learning,심층 학습)을 혼용하여 사용하는 경향이 있는데, 인공지능이 가장 넓은 개념이고, 인공지능을 구현하는 방법 중 하나가 머신 러닝이며, 딥 러닝은 머신 러닝의 방법 중 하나로 인공 신경망(Artificial Neural Network) 알고리즘을 활용하는 기술 중 하나이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (343, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (  ) 안에 들어갈 말을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["과거 : 규칙&데이터 → 전통적 프로그래밍 → 해답", "현재, 미래 : 데이터&해답 → (    ) → 규칙", "(   ) = 알고리즘 + (    ) + 발달된 하드웨어"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["빅데이터, 딥러닝, 인공지능", "인공지능, 딥러닝, 머신러닝", "머신러닝, 딥러닝, 빅데이터", "딥러닝, 인공지능, 채널"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "수백 장의 아기 사진을 학습시키고 무작위로 수천 장의 사진을 제시하면 학습을 통해 아기 사진만 분류하는 것이 머신 러닝에 해당한다. 반면 컴퓨터에서 스스로 다양한 아기 사진을 찾아보고 아기에 대해 학습한 뒤, 새로운 아기를 보았을 때 아기라고 인식하는 것이 딥 러닝에 해당한다. 따라서 딥러닝을 위해서는 고도로 발달한 하드웨어와 빅데이터는 필수요소인 셈이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (344, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "빅데이터에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기존의 응용 프로그램으로 효과적으로 처리할 수 있는 양의 데이터를 말한다", "비정형의 데이터는 빅데이터가 될 수 없다", "대용량의 데이터 규모, 빠른 속도, 높은 다양성을 지닌 정보 자산이다", "기존 3V인 데이터 규모(Volume), 속도(Velocity), 다양성(Variety)에 정확성(Veracity)과 장소(Venue)를 더해서 빅데이터 5V라고 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "① 빅데이터는 기존의 응용 프로그램으로는 효과적으로 처리할 수 없는 엄청난 양의 데이터를 말한다.\n② 빅데이터는 비정형의 방대한 양의 데이터뿐만 아니라 정형의 데이터를 포함한 모든 데이터를 설명하는 데 사용되는 전문 용어이다.\n④ 장소(Venue)가 아니라 가치(Value)를 더해서 빅데이터 5V라고 한다.\n《디지털금융의 이해와 활용》, 김수진 저 참고"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (346, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "모바일 기기 금융거래 이용자 안전거래 10계명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융거래 비밀번호 안전하게 관리하기", "모바일기기 수리 전 중요정보 삭제하기", "일회용 비밀번호(OTP) 사용하기", "공용 무선랜(Wi-Fi) 사용하기"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "출처가 불분명하거나 보안설정이 없는 무선랜(Wi-Fi)를 사용하면 모바일 기기의 도용의 우려가 있으니 금융거래 시 반드시 이러한 무선랜의 사용을 자제하여야 한다. 그 외에 모바일기기의 잠금기능을 설정하기, 비밀번호는 수시로 변경하기, 보안 업데이트를 정기적으로 수행하기, 바이러스 검사하기 등을 들 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 금융 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (347, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "사이버 보안에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["CCTV는 사이버 보안의 일종이다", "사이버 보안은 시스템, 네트워크 및 프로그램을 디지털 공격에서 보호하는 방식이다", "사이버 보안은 악의적인 로그인과 코드로부터 자산을 보호하는 것에 초점이 맞춰진 활동이다", "사이버 보안 관리는 금융회사나 일반 기업이 해야 하지만, 디지털 금융 소비자도 사이버 공격에 대한 이해도를 높여 이로 인한 피해 예방에 노력해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "사유지침입이나 절도로부터 방어하는 보안은 사이버 보안에 대비되는 물리적 보안에 해당한다. 아무리 첨단기술을 바탕으로 하더라도 CCTV나 적외선 감지기들은 사이버 보안의 범주에 들지 않는다. 왜냐하면 사이버보안이란 컴퓨터와 네트워크, 데이터를 악의적인 전자적 공격으로부터 보호하는 전반적인 활동을 일컫기 때문이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '디지털 관련개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (348, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인정보보호법상 개인정보에 해당하는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["성명, 주민번호 등을 통하여 개인을 알아볼 수 있는 정보", "해당 정보만으로는 특정 개인을 알아볼 수 없더라도 다른 정보와 쉽게 결합하여 알아볼 수 있는 정보", "개인이 소속된 법인 또는 단체의 정보", "가명처리를 통해 추가정보의 결합 없이는 특정 개인을 알아볼 수 없는 정보"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "개인정보의 주체는 자연인이어야 하며, 법인 또는 단체의 정보는 이에 해당하지 않는다. 반면 성명, 주민번호 등 개인을 알아 볼 수 있는 정보와 결합하면 쉽게 알 수 있는 정보 등을 가명 처리함으로써 원래의 상태로 복원하기 위한 추가 정보의 사용, 결합없이는 특정 개인을 알아볼 수 없는 정보인 가명정보도 보호해야할 개인정보에 해당한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인정보보호 체계 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (349, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>는 디지털 금융 사기의 종류와 의미를 설명한 글이다. (   )에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(    )은 문자메시지(SMS)와 (    )의 합성어이다", "(    )은 악성코드에 감염된 사용자의 PC를 조작하여 금융 정보를 빼내는 것을 가리킨다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["스미싱(Smishing), 파밍(Pharming), 피싱(Phishing)", "스미싱(Smishing), 피싱(Phishing), 파밍(Pharming)", "파밍(Pharming), 스미싱(Smishing), 피싱(Phishing)", "파밍(Pharming), 피싱(Phishing), 스미싱(Smishing)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "스미싱 : 문자메시지(SMS)와 피싱(Phishing)의 합성어로, 문자메시지에 포함된 인터넷 주소를 클릭하면 악성코드가 스마트폰에 설치되어 피해자도 모르는 사이에 소액결제 피해가 발생하거나 개인금융정보가 탈취된다.\n파밍 : 악성코드에 감염된 사용자의 PC를 조작하여 금융 정보를 빼내는 것을 가리킨다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (350, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인정보 유출 예방법에 대한 설명으로 올바른 것만을 <보기>에서 모두 고른 것은?.", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주소 변경 시 거래 금융회사에 즉시 통보하여 거래 내역, 잔고, 약관의 변경 등 거래와 관련한 중요한 변동 사항을 받아볼 수 있도록 조치한다", "금융거래 시 사용하는 각종 비밀번호는 고유하고 타인이 추측하기 어려운 문자로 지정하여 사용한다", "비밀번호는 분실 예방을 위해 종이나 PC 등에 기록한다", "공용으로 사용하는 PC방 등에서는 개인정보를 조회하지 않는다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄴ, ㄹ", "ㄱ, ㄷ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "비밀번호는 분실 시 금융사고의 위험이 있기 때문에 종이나 PC 등에 남기지 말아야 한다. 그리고 정기적으로 변경하여야 하며 그외의 개인정보 유출 예방책으로 시스템 접속 상태에서 자리를 비울 때는 반드시 로그아웃하여야 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인정보보호 체계 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (351, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "홍길동씨가 인터넷 링크가 포함된 지인의 문자를 받았을 경우 취해야 할 행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["문자내용을 꼼꼼히 확인 후 링크에 들어가서 내용을 파악한 후 지인에게 확인전화를 한다", "미확인 앱이 함부로 설치되지 않도록 스마트폰의 보안 설정을 강화한다", "이동통신회사 고객센터에 전화하거나 인터넷 홈페이지를 통하여 소액결제를 원천 차단한다", "백신프로그램을 설치하고 주기적으로 업데이트한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "출처가 확인되지 않은 문자메시지의 인터넷 주소는 절대로 클릭을 하여서는 안 된다. 부인이나 부모, 자식 등 아무리 가까운 사람일지라도 내용을 확인하기 전에 반드시 문자를 보낸 사람에게 확인을 한 후, 맞는 경우에만 클릭을 하는 습관을 들여야 한다. 이미 피해를 본 상태라면 인식을 하자마자 다음의 조처를 해야 한다. 제일 먼저 경찰청 및 사이버수사대나 금융감독원 등에 피해 사실을 신고해야 한다. 그리고 수상한 인터넷 주소를 클릭해 의문의 프로그램이 설치됐다면 서둘러서 핸드폰 통신사를 통해 공장 초기화를 진행하는 것이 좋다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자사기 예방', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (352, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자계획을 수립할 때 고려해야 할 사항만을 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자자의 위험 감내 수준", "투자기간", "자산배분", "제약조건"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ, ㄷ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "투자 관리는 크게 투자 계획(Plan)을 수립하고 실행하고(do) 통제(see)하는 3단계를 거친다. 투자 계획 수립은 먼저 투자 목표를 수립하는 것에서 시작하는데, 이때 투자자의 위험 감내 수준, 투자 기간, 제약 조건을 함께 고려해야 한다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자계획 수립 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (353, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "통합적인 투자관리가 이루어지는 과정을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자계획", "자산배분", "포트폴리오 평가", "종목선정"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ-ㄴ-ㄷ-ㄹ", "ㄱ-ㄷ-ㄴ-ㄹ", "ㄱ-ㄴ-ㄹ-ㄷ", "ㄱ-ㄹ-ㄴ-ㄷ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "Plan(투자계획)-Do(자산배분 및 종목선정)-See(포트폴리오 평가)의 순서로 투자관리가 이루어진다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자관리 프로세스 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (354, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "일반적으로 장기 투자성과에 가장 큰 영향을 미치는 것으로 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분", "종목선정", "투자타이밍", "리밸런싱"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자 전략을 실행하는 과정에서 투자자는 자산 배분, 종목 선정, 투자 시기 포착이라는 3가지 문제에 직면한다. 이중 투자성과에 가장 큰 영향을 미치는 것은 자산배분이다. 1974~1983년 91개의 대규모 연금 플랜을 연구한 결과, 자산 배분이 포트폴리오 운용 성과에 91.5%의 영향을 미치는 것으로 나타났고, 증권 선택(2.7%)과 시장 예측(1.8%)이 미치는 영향은 미미했다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 중요성 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (355, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자전술을 수립하는 과정에 대한 설명으로 (    ) 안에 들어갈 말을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["먼저 주식과 채권 등 자산에 대한 투자 비율을 정하고 구체적인 종목을 선정하는 것을 (    )이라고 하고, 반대로 투자 종목을 먼저 선정하고 자산 배분을 나중에 하는 것을 (    )이라고 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상향식, 하향식", "하향식, 상향식", "적극적, 소극적", "소극적, 적극적"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "자산 배분이 전략을 수립하는 것이라면, 종목 선정과 투자 타이밍을 선정하는 것은 구체적인 전술이다. 먼저 주식과 채권 등 자산에 대한 투자 비율을 정하고 구체적인 종목을 선정하는 것을 하향식(Top Down)이라고 하고, 반대로 투자 종목을 먼저 선정하고 자산 배분을 나중에 하는 것을 상향식(Bottom Up)이라고 한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자전술 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (356, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "소극적 투자관리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매수와 매도를 최소화해서 거래 비용을 줄인다", "증권시장에는 이미 이용할 수 있는 모든 정보가 반영되어 있다", "증권시장에서 과대평가 또는 과소평가된 증권을 찾기 어렵다", "다소 위험을 감수하더라도 초과 수익을 획득할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이라고 여긴다. 증권시장에는 이용할 수 있는 모든 정보가 반영되어 있으므로 과대 또는 과소 평가된 증권을 찾기 어렵다. 그래서 투자 관리의 초점을 초과 수익을 획득하는 것보다는 투자 위험을 최소화하는 데 두고 시장수익률을 추구한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (357, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "증권시장이 효율적이라고 전제하는 소극적 투자 관리 전술에 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식을 매수한 다음 장기보유한다", "인덱스펀드에 투자한다", "시장대표지수를 추종하는 상장지수펀드(ETF)에 투자한다", "미래 성장이 예상되는 일부 종목에 집중투자한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "전략적 차원에서 결정된 자산 배분의 범위 내에서, 최적의 투자 대안을 선정하는 전술적 차원에서의 대응은 적극적 투자 관리와 소극적 투자 관리로 나눌 수 있다. 이중 소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이기기 때문에 과대평가 또는 과소평가 된 증권을 찾기 어렵다고 생각한다. 구체적인 종목을 분석하거나 독자적으로 판단하지 않고, 시장 전체의 일반적인 예측을 그대로 받아들여 정보 비용을 줄이고 매수와 매도를 최소화해 거래 비용을 줄인다. 단순 매수 후 보유하는 전략, 인덱스펀드와 ETF를 활용한 투자, 정액 분할 투자가 여기에 해당된다. 일부 종목에 집중투자하는 것은 적극적 투자관리 전략에 해당한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (358, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "홍길동씨는 주식시장에 이용할 수 있는 모든 정보가 반영되어 있다고 생각하지 않는 투자자이다. 홍길동씨의 생각에 부합하는 투자 전술로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식시장의 이례적인 현상을 활용해 투자한다", "적정한 투자타이밍을 찾기 위해 노력한다", "특정 산업이나 소수 종목에 집중 투자한다", "주식을 매수한 다음 장기보유 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "적극적 투자 관리(Active Investment Style)는 다소 위험을 감수하더라도 시장의 평균을 넘어서는 초과 수익을 추구한다. 증권시장이 비효율적이라고 전제하고, 위험이 동일한 증권들 중에서 수익이 높은 증권을 선택하거나, 기대 수익이 동일한 증권 중에서 위험이 낮은 증권을 선택하는 방법이다. 적극적 투자 관리 방법으로는 소주종목이나 특정 산업에 집중적으로 투자하거나, 투자 타이밍을 포착하거나, 시장의 이례적인 현상을 이용해 종목을 선정하는 것이 대표적이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (359, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "\"계란을 한 바구니에 담지 말라\"는 격언과 관련 있는 투자 방법으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["분산투자", "집중투자", "거치식투자", "마켓타이밍 전략"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "분산투자라의 중요성을 표현한 대표적인 말이다. 분산투자는 계란을 옮길 때 많은 바구니를 사용한다는 뜻 이상이다. 다른 것들과 분명히 구별되는 바구니를 찾는 것이 중요해졌다. 각 바구니의 독특한 수익률 형태가 다른 바구니의 손익을 부분적으로 상쇄해서 전체 포트폴리오의 변동성을 낮추기 때문이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (360, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "한 해의 절반은 건기이고, 나머지 절반은 우기인 섬나라에서 당신이 우산을 판매하고 있다고 가정해 보자. 날씨에 따른 매출액 변동을 줄이려면 어떤 제품을 추가해서 판매하는 것이 좋을까?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["아이스크림", "우산", "비옷", "장화"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "분산투자효과를 이해하고 있는지 묻는 질문이다. 날씨가 제품 판매량에 영향을 미친다. 우기에는 우산, 우의, 장화 판매가 늘어나겠지만, 건기에는 판매가 급감한다. 대신 아이스크림 판매가 늘어날 수 있다. 커피 판매량은 날씨에 크게 영향을 받지 않는다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (361, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "분산투자를 통해서 줄일 수 있는 위험으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["글로벌 경제위기", "천재지변", "전쟁", "분식회계"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자 위험은 크게 체계적 위험과 비체계적 위험으로 나뉜다. 체계적 위험은 세계적인 경제 위기나 천재지변, 전쟁 등과 같이 모든 투자 자산의 가치에 동시에 영향을 미치는 위험을 말한다. 따라서 체계적 위험은 분산투자를 해도 사라지지 않는다. 비체계적 위험이란 개별 기업의 독특한 요소 때문에 발생하는 위험으로, 성격이 다른 기업이나 자산에 투자하면 비체계적 위험을 회피하거나 그 손실을 상쇄할 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (362, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "분산투자를 통해서 줄일 수 있는 위험으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전쟁", "천재지변", "체계적 위험", "비체계적 위험"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자 위험은 크게 체계적 위험과 비체계적 위험으로 나뉜다. 체계적 위험은 세계적인 경제 위기나 천재지변, 전쟁 등과 같이 모든 투자 자산의 가치에 동시에 영향을 미치는 위험을 말한다. 따라서 체계적 위험은 분산투자를 해도 사라지지 않는다. 비체계적 위험이란 개별 기업의 독특한 요소 때문에 발생하는 위험으로, 성격이 다른 기업이나 자산에 투자하면 비체계적 위험을 회피하거나 그 손실을 상쇄할 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '체계적 위험 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (363, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "분산투자 효과에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["분산투자를 하면 전쟁이나 천재지변에 따른 위험도 줄일 수 있다", "보유 종목 수를 계속 늘린다고 투자 위험이 계속 줄어들지는 않는다", "분산투자를 해도 노사문제와 같은 기업 고유의 위험은 줄어들지 않는다", "보유 종목 수를 늘려 나갈수록 위험 감소 폭은 계속해서 커진다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "포트폴리오 내에 편입된 종목 수를 늘리면 계속해서 위험이 줄어들지는 않는다. 보유 종목 수를 추가할수록 위험이 줄어들기는 하지만, 감소의 폭은 줄어든다. 통상 포트폴리오에 30개 종목을 편입하면 비체계적 위험은 대부분 사라지며, 더 분산투자를 해도 위험은 거의 줄어들지 않는다. 따라서 포트폴리오를 구성할 때 기대 수익률과 표준편차, 자산 간의 상관관계를 고려하여 적절한 분산으로 가장 최적의 포트폴리오를 구성할 수 있도록 조율해야 한다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (364, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "비체계적인 위험에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개별 기업의 독특한 요소 때문에 발생하는 위험이다", "성격이 다른 자산이나 기업에 투자하면 피할 수 있다", "비체계적 위험을 떠안으면 추가적인 보상을 받을 수 있다", "개별 기업의 주가를 시장과 다른 방향으로 움직이는 위험이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "비체계적인 위험은 적절한 분산투자를 통해 쉽게 제거할 수 있기 때문에, 비체계적인 투자태도를 떠안아도 추가적인 보상을 받을 수는 없다. 투자자들이 보상받을 수 있는 투자태도는 체계적인 위험 뿐이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '비체계적 위험 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (365, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "장기투자 효과에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자 자산이 가진 변동성은 보유기간에 따라 달라진다", "단기적으로 채권 가격의 변동성이 주식보다 훨씬 적다", "투자 기간을 늘리더라도 주식의 변동성은 줄어들지 않는다", "투자 기간이 길면 포트폴리오 내 주식 비중을 크게 가져가도 된다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "주식과 채권의 위험은 보유 기간에 영향을 받는다. 단기 투자를 할 때 주식은 채권보다 훨씬 더 위험한 투자 자산이다. 그러나 보유 기간이 15년 이상으로 길어지면 연평균 수익률의 표준편차가 장단기 채권보다 작아진다. 30년 이상이면 주식의 위험은 장단기 채권의 절반 수준이 된다. 투자할 때 장기적인 관점이 중요한 것은 바로 이런 사실 때문이다. 투자 기간이 늘어날수록 포트폴리오 내에서 주식이 차지하는 비중도 커져야 한다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '장기투자 효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (366, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자 전략과 전술을 수립할 때 고려하여야 할 사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일정한 위험 수준에서 투자수익을 최대화한다", "일정한 투자 수익이라면 위험을 최소화한다", "투자 관리는 계획, 실행, 사후 통제 순으로 진행한다", "투자자산의 위험은 투자 기간과 무관하게 일정하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자 자산의 수익과 위험은 절대적이지 않으며 투자 기간에 따라 달라진다. 단기적으로 보면 채권 가격의 변동성은 주식보다 낮지만 기대 수익률도 낮다. 주식의 경우 단기적인 변동성은 채권보다 높지만 장기적으로는 채권보다 낮은 변동성을 보인다. 그런데도 수익은 채권보다 훨씬 높다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자전략전술 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (367, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "글로벌 분산투자를 통해서만 줄일 수 있는 위험으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["부존자원이 많지 않은 국가의 원자재 가격 상승 위험", "노사분쟁이 자주 발생하는 기업이 가진 위험", "회계 투명성이 떨어지는 기업의 분식회계 위험", "산업재해 자주 발생하는 기업이 가진 위험"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "특정 국가가 가진 고유의 위험은 글로벌분산투자를 통해 줄일 수 있다. 노사분쟁, 분식회계, 산업재해와 같은 비체계적 위험은 국내 분산투자를 통해서도 줄일 수 있다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '글로벌 분산투자 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (373, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "패시브 운용전략을 잘 대표하는 상품은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["헤지펀드", "인덱스펀드", "배당주펀드", "원자재펀드"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "패시브 운용 전략을 대표하는 금융상품으로는 인덱스펀드가 있다. 인덱스펀드와 ETF는 시장을 대표하는 지수를 선정하고, 지수의 수익률과 펀드의 수익률 간에 편차가 발생하지 않도록 조정하는 것 말고는 특별한 노력을 기울이지 않는다. 따라서 액티브 운용 전략에 비해 수수료와 운용 비용이 덜 들어간다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '패시브 운용전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (368, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 투자위험을 해소하기 위한 대안으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["전 세계 증시에서 우리나라 증시가 차지하는 비중은 채2%가 안 된다", "수출 주도로 성장하는 국내 경제는 환율 변동에 취약하다", "우리나라는 부존 자원이 부족해 원자재 가격의 상승에 취약하다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["수출 주도로 성장하는 국내 기업에 집중투자한다", "내수 중심으로 성장하는 국내 기업에 집중투자한다", "환율 변동의 영향을 덜 받는 기업을 골라 투자한다", "글로벌 기업의 주식과 채권에 분산투자 해 변동성을 낮춘다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자자가 저지르는 흔한 실수가 있다면, 글로벌 투자의 기회를 충분히 활용하지 않는 것이다. 전 세계 증시에서 국내 증시가 차지하는 비중은 2%가 채 안 된다. 그러니 국내 자산에만 투자하는 것은 조그마한 바구니에 재산을 전부 쓸어 담는 셈이다. 국내 경제는 내수보다는 수출 중심으로 성장하기 때문에 환율 변동에 크게 영향을 받는 데다, 부존 자원이 많지 않아 원자재 가격의 상승에 취약할 수밖에 없다. 이러한 국가가 가진 고유의 위험을 줄이려면 투자 자금을 적극적으로 해외로 분산하는 것이 좋다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '글로벌 분산투자 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (369, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드를 활용해 간접투자 할 때 나타나는 장단점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전문가에게 투자를 맡기면 자기 일에 집중할 수 있다", "적은 금액으로도 분산투자 할 수 있다", "추가적인 비용이 발생한다", "자기가 원하는 종목을 적시에 매수할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "간접 투자의 가장 큰 장점은 전문가에게 투자를 맡기고 자신의 일에 집중할 수 있다는 점이다. 물론 전문가를 활용하려면 비용을 지불해야 한다. 또한 소액으로 분산투자가 가능한 것도 장점이다. 간접 투자는 자산을 위탁하는 것이기 때문에 투자자의 의도와 다르게 운용될 수도 있다. 시장이 급등하더라도 내가 자금을 맡긴 펀드를 운용하는 매니저가 주도 주식을 갖고 있지 않으면 성과가 시장 수익률에도 못 미칠 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '간접투자 장단점 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (370, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "직접 투자의 장단점에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자신이 원할 때 투자종목을 사고 팔 수 있다", "자신에게 적합한 포트폴리오를 스스로 구성한다", "전문가의 도움 없이 투자할 수 있다", "간접투자보다 항상 우수한 성과를 보인다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "직접 투자의 가장 큰 장점은 자신에게 적합한 포트폴리오를 스스로 꾸릴 수 있다는 점이다. 자신의 취향에 맞는 종목을 골라, 사고 싶을 때 사고 팔고 싶을 때 판다. 내 돈을 스스로 관리한다는 점에서 자부심도 느낄 수 있다. \n하지만 일반인들 중에 충분한 투자 경험과 역량을 갖춘 이는 많지 않은 데다, 단기간에 전문성을 갖추기도 어렵다. 투자 역량과 경험을 충분히 갖춘 투자자라고 해도 의사결정을 내리려면 필요한 정보를 취득하고 분석하는 데 많은 시간이 든다. 전문성을 갖추지 않은 채 투자 대안을 충분히 검토하지 않고 감정에 치우쳐 매매하면 수익은 내지 못하고 수수료만 지불할 가능성이 높다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '직접투자 장단점 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (371, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에 해당하는 투자방법은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["증권시장의 흐름을 파악해 포트폴리오 내 주식과 채권 비중을 조절한다", "시장 흐름을 주도하는 섹터와 테마에 속하는 주식의 비중을 늘린다", "시장 평균보다 높은 수익이 기대되는 종목에 집중투자한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["액티브투자", "패시브투자", "적립식투자", "포트폴리오투자"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "액티브 운용 전략은 시장 평균 수익률을 넘는 수익을 얻기 위해 투자방법이다. 증권시장의 흐름을 판단해 포트폴리오의 주식과 채권의 비중을 조절하거나, 시장 상황에 따라 주식 내에서 섹터와 테마의 비중을 조정하기도 한다. 시장보다 상대적으로 더 높은 수익이 기대되는 종목을 선정해서 집중 투자하는 방법도 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '액티브운용 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (372, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "패시브 운용전략에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["마켓 타이밍에 따라 증권을 매매한다", "비교적 비용이 저렴하다", "펀드간 수익률 차이가 크다", "비교 대상이 되는 지수보다 높은 수익을 추구한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "액티브 운용 전략에서는 시장 평균 수익률보다 높은 수익을 얻기 위해 적극적으로 노력하므로, 매매를 자주 하고 수수료와 운용 비용이 많이 들어간다. 하지만 패시브 운용 전략은 시장을 이기려고 노력하지 않으며 시장이 움직이는 대로 따라가기 때문에 비용이 적게 들어간다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '패스브운용 이해', null, null);
