insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (384, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "정부, 공공기관, 기업 등이 외부에서 자금을 빌릴 때 발행하는 차용증서로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식", "채권", "선물", "옵션"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "정부, 공공기관, 특수법인, 주식회사 형태를 갖춘 기업도 외부에서 자금을 빌릴 때가 있는데, 이때 발행하는 차용증서를 채권이라고 한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (385, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "채권을 발행할 수 있는 주체로 가장 거리가 먼 것은", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["정부", "공기업", "주식회사", "개인사업자"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "정부, 공공기관, 특수법인, 주식회사 형태를 갖춘 기업도 외부에서 자금을 빌릴 때가 있는데, 이때 발행하는 차용증서를 채권이라고 한다. 다만 정부, 지방정부, 공공기관, 공기업 및 상법상의 주식회사만이 채권을 발행할 수 있다는 점에서, 개인도 발행할 수 있는 차용증서와 차이가 난다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (386, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "채권에 특징에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["정부, 공기업, 주식회사 등 발행 주체가 다양하다", "발행된 채권은 증권시장에서 사고 팔 수 있다", "증권이 아닌 통장 형태로 거래된다", "만기는 하루짜리 초단기물부터 영구채까지 다양하다"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "정부, 지방정부, 공공기관, 공기업 및 상법상의 주식회사만이 발행할 수 있다는 점, 자본시장법이 정하는 바에 따라 증권시장에서 거래된다는 점, 다수의 투자자에 의해 자금이 조달된다는 점 등에서 개인의 차용증과 구별된다. 그리고 채권은 은행 예금같이 꼬박꼬박 이자를 지급하는 금리상품으로, 통장이 아닌 증서의 형태다. 하루짜리 초단기물에서 영구채권에 이르기까지 만기가 다양하다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 특징 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (387, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "채권의 특징에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["채무자가 지급해야 할 자와 상환금액이 확정되어 있다", "적자가 발생한 해에는 채권 이자를 지급하지 않을 수 있다", "원리금을 상환해야 하는 기간이 정해져 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄱ, ㄴ, ㄷ"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "채권의 이자는 주식 배당과는 달리 수익이 발생하든 말든 지급해야 한다. 주식은 순이익이 발생하는지 여부와 그 규모에 따라 배당을 달리할 수 있지만, 채권은 적자가 발생해도 이미 확정된 이자는 지급해야 한다. 이때 액면가에 대해 약정한 이자율을 연율로 표시한 것을 표면금리(coupon rate)라고 한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 특징 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (388, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "채권에 투자할 때 고려해야 할 위험으로 적절한 것만을 <보기>에서 모두 고른 것은? ", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["시중금리 변화에 따라 채권가격이 상승하거나 하락할 수 있다", "발행자의 재무상태가 악화되면 약속한 원금과 이자를 못 받을 수 있다", "채권 발행물량이 적으면 현금화하기 어려울 수도 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄱ, ㄴ, ㄷ"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "채권투자에 따르는 위험도 있다. 첫째, 채권 가격의 변동에 따른 시장 위험이다. 일반적으로 채권 가격은 시장금리와 발행 기관의 신용에 따라 변동한다. 따라서 시장 가격이 매입 가격보다 낮아지면 자본을 손해 볼 가능성이 있다. 둘째, 채권을 발행한 기관의 경영 또는 재무 상태가 악화되어서 약속한 이자와 원금을 늦게 받거나 못 받게 되는 채무불이행이다. 셋째, 채권의 발행 물량이 적거나 유통 시장이 발달하지 않았다면 채권을 현금화하기 어려운 유동성 위험도 존재한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 특징 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (389, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["두 사람 이상의 투자자로부터 투자자금을 모은다", "투자자로부터 일상적인 운용지시를 받는다", "수익금을 투자금액에 비례해서 배분한다", "주식, 채권, 통화, 원자재 등 다양한 자산에 투자할 수 있다"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "펀드란 2명 이상의 투자자가 투자 자금을 모은 후 수익이 예상되는 곳에 투자하여 돈을 벌고, 그 수익금을 투자한 금액에 비례하여 나누어 돌려주는 금융상품이다. 그리고 투자자로부터 일상적인 운용지시를 받지 않는다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (390, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "펀드를 만들어 운용하고 체계적으로 관리하는 금융회사는? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산운용회사", "보험회사", "증권회사", "수탁회사"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "펀드를 만들어 운용하는 곳은 자산운용회사로, 그곳에 속한 투자 전문가가 어느 주식이나 채권에 얼마만큼 투자할지 운용 전략을 세워 체계적으로 관리한다. 은행, 증권, 보험사 등은 펀드 판매 회사로, 투자자에게 펀드 투자를 권유하고 투자 계약을 체결하는 역할을 담당한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (391, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "펀드투자 시 투자자가 납입한 대금을 관리하는 역할을 하는 금융회사는?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산운용회사", "펀드판매회사", "자산보관회사(수탁회사)", "사무관리회사"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "펀드판매회사가 투자자에 판매한 수익증권 대금은 펀드를 설정하고 운용하는 자산운용사로 들어가는 것이 아니라, 자산보관회사(수탁회사)에서 관리한다. 자산운용회사가 파산할 경우에 투자자가 투자한 돈을 보호하기 위해서다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (392, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "주식이나 외환 등 현물 자산에서 파생된 것으로서, 표준화되어 있는 상품을 미리 정한 가격으로 미래에 거래를 정산하는 금융투자상품은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선물", "옵션", "펀드", "스왑"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "파생상품은 주식이나 외환 등 현물 자산에서 파생되어 생긴 상품으로, 미래에 자산을 매매할 수 있는 계약이라고 할 수 있다. 당장 거래가 이루어지는 현물(Spot)과 달리 미래에 거래를 정산하는 파생상품을 선물(Futures)이라고 한다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (393, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "주식과 파생상품을 결합해 만든 금융상품으로 가입시점에 비해 주가가 큰 폭으로 하락하지만 않으면 수익을 얻을 수 있는 금융투자상품은", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ETF", "ISA", "ELS", "EMP"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "주가연계파생결합증권(Equity Linked Securities)을 ELS라고 한다. ELS는 주식과 파생상품이 결합된 금융투자 상품으로, 주식투자로는 얻을 수 없는 수익 구조로 설계되었다. 주가가 하락할 때 수익이 늘어나거나 가입 시점에 비해 평가 시점의 주가가 크게 하락하지 않으면 수익을 지급하는 방식이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (394, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "전통 투자자산과 비교해서 대체투자 자산이 갖는 특성으로 가장 적절한 것은?   ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주로 장내에서 거래되고, 유동성이 높다", "투자 기간이 아주 짧다", "전통 투자자산과 상관관계가 낮다", "거래방식이 표준화되어 있다"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "대체투자자산은 전통적인 자산과는 상관관계가 낮다. 대체투자는 전통적인 투자 방식에 비해 투자 기간은 길고 유동성이 낮다. 전통적인 투자는 주로 장내에서 거래되고 거래 방식도 표준화되어 있는 경우가 많다. 하지만 대체투자는 유통 시장이 형성되어 있지 않아서 주로 장외에서 거래되기 때문에 유동성이 떨어지고 투자 기간도 길어지는 것이다. 또한 투자 대상과 거래 방법 등이 표준화되어 있지 않기 때문에 운용하는 데 많은 비용이 들어간다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '대체투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (395, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "대체투자와 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["헤지펀드", "사모펀드(PEF)", "벤처투자", "상장지수펀드(ETF)"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "대체투자로는 전통적 자산과 상관관계가 낮고 포트폴리오 효과가 좋은 원자재 투자가 있다. 다양한 자산에 다양한 전략을 사용하는 헤지펀드도 대체투자에 속한다. 성장 동력을 잃은 회사에 지분을 내고 경영에 직접 참여하여 기업 가치를 제고하는 경영 참여형 사모펀드(PEF)도 중요한 대체투자다. 성장 잠재력은 있으나 현재의 가치를 평가하기는 어려운 벤처 투자도 대체투자에서 차지하는 비중이 늘고 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '대체투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (396, 'SHORT_ANSWER_CORRECT_ANSWER', '[{"data": "DLS(파생연계증권)의 기초자산으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금리", "주식", "환율", "원유"]}]', '[{"type": "SHORT_ANSWER_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "파생결합증권은 결합되는 자산이 주식일 경우에는 ELS, 주식 이외의 자산이면 DLS로 분류한다. DLS에는 펀드나 채권 금리, 환율, 원유 등 다양한 기초 자산과 결합된 상품이 있다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (397, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주당장부가치비율(또는 주당순자산가치, PBR)에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주가를 주당순자산으로 나눈 값이다", "이 지표가 1보다 낮다면 청산가치가 현재 주가보다 높다는 의미이다", "성장성이 높은 기업은 일반적으로 이 지표가 높게 산출된다", "보유하고 있는 공장이나 부동산 등 자산이 많아질수록 이 지표는 낮게 산출된다", "이 지표의 절대적인 적정수준은 존재하지 않는다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄷ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄷ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "PBR은 주가를 주당순자산으로 나눈 값으로, 기업이 청산된다고 가정할 때 주주가 건질 수 있는 청산가치의 규모를 주가와 비교하는 지표이다. PBR이 1보다 낮다는 것은 청산가치가 주가보다 크다는 것을 의미하며, 대부분 자산이 많은 기업들의 PBR이 상대적으로 낮은 편이다. 보유 자산은 적지만 성장성에 대한 기대가 높아 주가가 비싸게 형성되는 기업의 PBR은 상대적으로 높은 편이다. PER과 마찬가지로 PBR 역시 절대적인 적정 수준은 존재하지 않으면 상대적 비교에 유용하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식가치지표의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (398, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "A주식의 현재 가격이 10,000원이다. 투자자 甲은 A주식의 가격이 상승할 것으로 예상하고 행사가격 12,000원의 Call옵션을 계약당 1,000원에 매수하였다. 옵션 만기일에 주가가 9,000원으로 하락하였다면, 투자자 A의 수익은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["1,000원 수익", "2,000원 수익", "2,000원 손실", "1,000원 손실"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "Call옵션 매수자의 손익을 파악하는 문제로, 실제 손익은 옵션매수비용까지 고려해야 한다는 점을 이해해야 한다.\nCall옵션 매수자의 만기 시점 수익은 다음과 같다.\nMax{ (기초자산가격 – 행사가격), 0} – 옵션매수비용\n투자자 A씨가 행사가격 12,000원 Call옵션을 1,000원 가격에 매수하였다. 만기에 기초자산 가격이 행사가격보다 낮은 9,000원이므로, Call옵션 매수 손익은 옵션매수 비용 1,000원으로 한정된다. \nMax{ (9,000원 – 12,000원), 0} – 1,000원 = -1,000원 손실\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (399, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "B기업의 부채비율(레버리지비율)이 500%라고 할 때, 부채비율을 줄일 수 있는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["차입금을 갚는다", "사채를 발행한다", "유상증자를 실시한다", "기존 회사채를 상환한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "부채비율을 줄이기 위해서는 부채를 낮추거나 자본을 늘려야 한다. \n지문 1에서 부채 항목인 차입금을 갚으면 부채가 줄어들어 부채비율을 줄인다.\n지문 2에서 타인자본인 사채를 발행하면, 부채항목이 늘어나기 때문에 부채비율을 늘리는 조치이다.\n지문 3의 유상증자를 실시하는 것은 자기자본인 자본을 증액하는 것이므로, 부채비율을 줄인다.\n지문 4에서 기존 발행된 회사채를 상환하여 부채항목을 줄일 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '부채와 자본 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (408, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "상장기업이 주주와 이해관계자, 그리고 일반인을 대상으로 주가에 영향을 미칠만한 사실을 공개적으로 밝히는 행위는?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공시", "공표", "공개", "매매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "기업이 이해관계자와 일반인에게 주가에 영향을 미칠 만한 각종 사실을 공개적으로 밝히는 행위다. 공시는 사실성과 즉시성을 갖춰야만 투자자에게 올바른 정보를 신속하게 전달할 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '유통시장 제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (400, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식선물거래와 주식옵션거래의 손익관계에 대한 설명으로 가장 거리가 먼 것은? (단, 현재 주가를 기준으로 선물계약을 체결하고, 현재 주가가 옵션행사가격이라고 가정)\n", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선물 매수와 Call옵션 매수는 모두 주가 상승시 수익을 얻는다", "선물 매도와 Put옵션 매도는 모두 주가 하락시 수익을 얻는다", "Call옵션 매수와 Put옵션 매도는 모두 주가 상승시 수익을 얻는다", "Put옵션 매수와 Call옵션 매도는 모두 주가 하락시 수익을 얻는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "주식선물 매수는 주가 상승시 수익을 얻으며, 선물 매도는 주가 하락시 수익을 얻는다.\nCall옵션 매수는 주가 상승시 수익을 얻으며, Call옵션 매도는 주가 상승시 손실을 본다\nPut옵션 매수는 주가 하락시 수익을 얻으며, Put옵션 매도는 주가 하락시 손실을 본다\n지문 1에서 선물 매수와 콜옵션 매수는 주가 상승시 수익을 얻는 구조이다.\n지문 2에서 선물 매도는 주가 하락시 수익을 얻지만, 풋옵션 매도는 주가 하락시 손실을 본다.\n지문 3에서 Call옵션 매수는 주가 상승시 수익을 얻으며, Put옵션 매도는 주가 상승시 옵션 매도로 얻은 Put옵션 매도프리미엄이 수익으로 확보된다.\n지문 4에서 Put옵션 매수는 주가 하락시 수익을 얻으며, Call옵션 매도는 주가 하락시 옵션 매도로 얻은 Call옵션 매도프리미엄이 수익으로 확보된다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (401, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "A기업(자기자본 1,000억원)이 독자적으로 개발한 신기술이 반도체 생산의 핵심기술로 채택되면서 매출과 영업이익이 큰 폭으로 늘어났다. 매출액은 전년도 1,000억원의 2배가 되었으며, 영업이익도 전년도 180억원에서 올해 400억원으로 급신장하였다. 당기순이익은 올해 200억원을 기록하였으며, 배당금은 50억원을 지급하였다. 이때, A기업의 재무비율에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["올해 매출액 대비 영업이익률은 20%이다", "전년대비 매출액증가율은 200%이다", "배당성향은 250%이다", "자기자본 이익률은 20%이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "재무비율은 기업의 재무제표에서 확인할 수 있는 주요 지표의 비교를 통해 해당 기업의 수익성/성장성/안정성 등을 파악할 수 있다.\n지문 1의 매출액 대비 영업이익률은 올해 매출액 2000억원과 영업이익 400억원의 비율이다\n지문 2의 매출액증가율은 (당기매출액-전기매출액)/전기매출액X100으로 100% 신장이 맞다\n지문3의 배당성향은 배당금/순이익으로 나눈 값으로, 배당금 50억원과 당기순이익 200억원의 비율이다.\n지문4의 자기자본이익률은 순이익/자기자본으로 산출하며, 당기순이익 200억원과 자기자본 1000억원의 비율이다\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/16 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무비율의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (402, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "A기업은 스타트업에서 시작하여 한국거래소에 상장한 기업이다. 매출이 꾸준하게 발생하고 있지만, 부채가 많아 실제 당기순이익은 많지 않은 편이다. A기업을 평가할 때 주목해야 할 재무비율로 가장 거리가 먼 것은?\n", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["부족한 자본에도 불구하고 투자와 연구개발을 위해 타인자본을 활용해야 하는 기업의 경우 부채비율이 높다고해서 부정적으로만 평가할 필요는 없다", "부채로 인해 발생하는 이자비용이 영업이익을 초과하는 경우 이자보상배율이 1보다 크다", "매출액 증가율이 매년 우상향하는 경우 기업의 규모가 성장하고 있다고 평가할 수 있다", "유동자산보다 유동부채가 많은 경우 유동비율이 1보다 작다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "성장기업임에도 불구하고 부채가 많을 경우 기업의 안정성 측면에서 유의할 필요가 있다. 성장기업의 경우 부채비율이 무조건 높다고 부정적으로만 평가할 필요는 없다. 매출규모와 성장성을 같이 판단해야 해당 기업을 제대로 평가할 수 있다.\n지문 2의 이자보상배율의 산식은 (영업이익/이자비용)*100이기 때문에, 이자비용이 영업이익을 초과한다면 이자보상배율은 1을 하회한다.\n지문 3에서 매출액이 매년 증가하는 기업이라면 성장성 측면에서 긍정적인 기업이다. \n지문 4의 유동비율의 산식은 (유동자산/유동부채)*100이므로, XYZ의 유동비율은 1 이하로 산출될 수 있다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/17 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무비율의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (403, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "배당에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["배당은 기업의 잉여자본금을 주주에게 환원하는 대표적 행위이다", "배당수익률이 높다는 것은 주가 대비 지급되는 주당 배당금이 높거나 반대로 주당 배당금 대비 주가가 낮은 경우이다", "성장단계의 기업은 배당보다 투자에 집중하기 때문에 배당지급이 없거나 배당 지급 규모가 적을 수 있다", "배당수익률이 10%라면 현재 주가가 향후 10% 상승할 가능성이 높다고 평가할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "배당은 기업의 잉여자본을 주주에게 환원하는 경제행위이며, 주주는 기업에게 이익배당을 청구할 수 있는 권리가 있다. 성장단계의 기업은 배당보다 투자에 집중하는 경향이 높다. \n지문 2와 4의 배당수익률 산식은 (주당배당금/주가)*100이므로, 배당수익률이 높다면 배당금이 많거나 주가가 낮은 경우이다. 배당수익률은 배당 규모를 판단하는 지표이며, 주가의 예상 등락을 평가할 수 있는 가격지표로 활용할 수 없다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/18 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '배당 관련용어 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (404, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>와 같은 상태에 있는 Put옵션에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["Put옵션의 프리미엄은 현재 3만원이다", "Put옵션의 행사가격은 10만원이며, 기초자산의 현재 가격은 8만원이다", "Put옵션의 만기는 2개월 후이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["해당 Put옵션은 내가격(ITM) 상태이다", "해당 Put옵션의 시간가치는 1만원이다", "해당 Put옵션의 내재가치는 3만원이다", "해당 Put옵션의 프리미엄은 기초자산 가격이 하락할 수록 올라갈 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "Put옵션의 행사가격은 10만원이데, 기초자산 가격은 이보다 낮은 8만원이다. 따라서 해당 Put옵션은 내가격(ITM) 상태이다. Max{(10만원-8만원), 0}  해당 Put옵션의 프리미엄은 3만원으로, 이중 시간가치는 1만원, 내재가치는 2만원이다. 내재가치는 (행사가격-기초자산가격)의 차이이며, 시간가치는 옵션의 프리미엄에서 내재가치를 제외한 부분이다. Put옵션의 일반적인 특징은 기초자산 가격이 하락할수록 옵션 프리미엄은 상승한다는 점이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (405, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "최신 VR기술을 보유한 신생기업이 임직원에게 보상 차원에서 근무 연수에 따라 일정량의 회사 주식을 지급하는 스톡옵션 결정을 내렸다. 임직원들은 미리 정해진 가격으로 회사 주식을 살 수 있는 권리를 갖게 된 것이다. 경제적 실질이 스톡옵션과 유사한 금융투자상품은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["우선주", "액면분할", "ETN", "Call옵션"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "스톡옵션은 기업의 임직원에게 미리 정해진 가격으로 회사 주식을 살 수 있는 권리이다. 주가가 오를수록 스톡 옵션을 보유한 임직원이 얻을 수 있는 이익도 늘어난다. 때문에 임직원들에게 스톡 옵션은 회사의 성과를 공유할 수 있는 동기를 부여하며, 임직원 입장에서는 보너스로 인식된다. 지문 1의 우선주는 의결권이 제한된 주권이다. 지문 2의 액면분할은 액면가를 일정 배율로 나누는 행위이다. 지문 3의 ETN은 상장지수증권으로 기초자산의 성과에 수익이 연동되는 파생결합증권이다. 지문 4의 Call옵션은 일정 시점 후에 특정 가격으로 기초자산을 매입할 수 있는 권리이다. 스톡 옵션과 경제적 실질이 같다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (406, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "한국거래소의 공시제도에 대해 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["재무구조나 회사의 중대한 경영 관련 사항은 일반투자자에게 충분하게 알려야 한다", "핵심기술의 특허가 만료된 경우 시간이 걸리더라도 관련 영향을 충분히 검토한 후에 공시해야 한다", "기존 공시내용을 번복하는 경우 거래소가 불성실공시로 제재할 수 있다", "회사 경영과 재무상황에 중대한 영향을 미치는 사항의 경우 지체없이 공시해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "공시제도는 상장법인의 경영관련 내용을 투자자 등 이해관계자들에게 공개하여 증권시장 내 투자자간 정보의 불균형을 해소하고 거래의 원활함과 공정성을 확보하기 위한 정보전달제도이다. \n공시제도는 정보의 정확성과 완전성, 신속성과 적시성을 확보해야 하며, 공시내용의 공평한 전달이 이루어져야 한다. 지문 ②의 경우 핵심기술의 특허만료는 회사 경영에 상당한 영향을 미칠 수 있기 때문에 신속하게 투자자에게 알려함에도 불구하고, 공시제도의 원칙에 위배되는 행위이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/19 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공시제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (407, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "4개 기업의 주가와 각 사의 재무제표에서 산출한 기업가치(EV)와 EBITDA를 비교한 것이다. 각 기업에 대한 평가로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업의 기업가치는 영업활동으로 벌어들인 이익의 3.6배에 달한다", "B기업을 인수한다면 약 4년이면 투자원금을 회수할 수 있을 것으로 전망된다", "C기업의 EV/EBITDA가 가장 낮기 때문에 4개 회사 중에서 가장 비싼 기업이라고 평가할 수 있다", "D기업은 주가는 낮지만 4개 기업 중에서 영업이익에 비해 상대적으로 기업가치를 비싸게 평가받는 기업이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "기업가치(EV)와 세전/이익지급전/감가상감차가전 영업이이(EBITDA)를 비교하는 것은 기업의 가치가 영업이익에 비해 얼마나 고평가/저평가 받는 지를 확인할 수 있는 주가배수이다. 특히 해당 기업을 인수하려는 투자자 입장에서는 기업의 영업활동으로 벌어들이는 수익으로 언제쯤 투자원금을 회수할 수 있는 지를 파악할 수 있는 지표이다. 지문 ①은 EV/EBITDA의 개념을 설명한다. 지문 ②는 EV/EBITDA의 활용방안인 인수 후 투자원금 회수기간을 의미한다. 지문 ③에서 C기업은 4개 기업 중 EV/EBITDA가 가장 낮기 때문에, 4개 기업 중에서는 상대적으로 저평가된 기업이라고 볼 수 있다. 지문 ④에서 D기업은 주가는 가장 낮지만 EV/EBITDA가 4개사 중 가장 높기 때문에, 사장 고평가받는 기업이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/20 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '이익을 활용한 기업가치 평가', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (409, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "A기업과 B기업의 주가 및 순이익, 그리고 주가이익비율에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A기업은 현재의 주가로 투자할 경우 10년간의 순이익으로 투자원금을 회수 할 수 있는 기업이다", "B기업은 A기업보다 상대적으로 저평가되었다고 평가할 수 있다", "A기업의 주가가 B기업의 주가보다 절대적으로 비싸다", "주식시장에서 A기업이 B기업보다 향후 성장성을 높게 평가받고 있다고 해석할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "주가이익비율(PER)은 주가를 주당순이익(EPS)으로 나눈 값이다. PER은 기업의 이익을 주식시장에서 얼마만큼 주가로 평가하고 있는 지를 판단할 수 있는 지표이다.\n지문 ①은 PER의 개념을 설명하는 것으로, PER 10.0배는 현재 주가로 투자할 경우 10년간의 순이익으로 투자원금을 회수할 수 있다는 의미이다.\n지문 ②에서 B기업의 PER는 4.0배(2만원/5,000원)이므로, A기업에 비해 상대적으로 저평가되어 있다.\n지문 ③에서 A기업의 PER가 10배, B기업의 PER가 4배이지만, A기업이 B기업보다 절대적으로 비싼 기업으로 평가하기는 단정짓기 어렵다. A기업의 성장성에 비해 실제 주가가 낮게 거래되고 있을 수도 있다.\n지문 ④에서 A기업의 PER가 B기업보다 높기 때문에, 성장성 측면에서 A기업을 주식시장에서는 높을 것으로 평가할 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'PER 적용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (410, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 3개기업의 재무비율에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["C기업은 수익성이나 안정성 측면에서 가장 취약한 기업이다", "A기업은 타인부채에 대한 상환능력에서 가장 불안정한 기업이다", "B기업은 이자비용을 지불하고 나면 실제 수익은 거의 없다고 해도 과언이 아니다", "C기업의 상황이 수 년간 지속된다면 한계기업으로 분류될 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "매출액 대비 영업이익률은 수익성지표이며, 이자보상비율은 안정성지표이다. 지문 ①에서 C기업은 수익성과 안정성 측면에서 가장 열위의 기업이다. 지문 ②에서 A기업은 타인부채에 대한 상환능력 측면에서 가장 안정적이다. 지문 ③에서 B기업의 이자보상비율이 1이기 때문에, 영업이익과 이자비용이 같다는 의미이다. 지문 ④에서 수익대비 이자부담비용이 높은 기업을 한계기업으로 표현하고 수년간 이같은 상황이 지속되면, 소위 ‘좀비’기업이라고 부른다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무비율의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (411, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "C기업의 Call옵션을 매수하려고 한다. C기업의 주가는 현재 10만원이며, 매수하려는 Call옵션은 만기 3개월 상품이다. 투자자가 선택하는 C기업 Call옵션에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매수한 Call옵션의 행사가격이 15만원이며(옵션프리미엄 5천원) 만기일까지 보유하였는데, 만기일 주가가 14만원이라면 Call옵션 매수자는 4만원의 차익을 얻을 수 있다", "매수한 Call옵션의 행사가격이 8만원이라면, 현재 주가 10만원보다 행사가격이 낮기 때문에 내가격 옵션에 해당한다", "매수한 Call옵션의 행사가격이 10만원이며(옵션프리미엄 1만원) 만기일까지 보유하였는데, 만기일 주가가 12만원이라면 Call옵션 매수자는 수익을 얻는다", "매수한 Call옵션의 행사가격이 12만원이면, 현재 주가 10만원보다 행사가격이 높기 때문에 외가격 옵션에 해당한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문 ①에서 행사가격 15만원을 매입하였는데 만기에 기초자산 가격이 14만원으로 마무리되었으므로, Call옵션 매수자의 수익은 Max{(14만원-15만원), 0}이므로 차익이 발생하지 않는다.\n지문 ②에서 행사가격이 기초자산 가격보다 낮은 Call옵션이므로 내가격 옵션이다\n지문 ③에서 행사가격 10만원을 매입하였는데 만기에 기초자산 가격이 8만원으로 마무리되었으므로, Call옵션 매수자의 수익은 Max{(12만원-10만원, 0)이므로, 2만원의 차익을 얻으며, 지불한 옵션 프리미엄 1만원을 제하더라도 1만원의 순수익을 얻는다.\n지문 ④에서 행사가격이 기초자산 가격보다 높은 Call옵션이므로 외가격 옵션이다\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (412, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "옵션가격에 영향을 미치는 변수들에 대한 설명으로 가장 거리가 먼 것은? (단, 제시한 변수 이외는 모두 동일하다고 가정)", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기초자산의 가격 변동성이 앞으로 만기까지 확대될 것으로 예상된다면 Call옵션의 가격은 상승한다", "행사가격이 기초자산의 가격보다 높아질수록 Call옵션의 가격은 상승한다", "만기까지 남은 시간이 길수록 옵션의 가격은 상승한다", "기초자산의 가격이 현재보다 올라갈 것이 확실하다면 Put옵션의 가격은 하락한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "옵션가격에 영향을 미치는 주요 변수들(기초자산의 가격, 행사가격, 기초자산의 가격변동성, 만기일까지 남은 시간 등)의 상황에 따라 옵션가격의 변화를 종합적으로 파악해야 하는 문제이다. 다만 제시한 변수 이외의 다른 조건은 모두 동일하다는 전제가 필요하다.\n지문 ①에서 기초자산 가격변동성이 높아지면, Call과 Put옵션의 가격은 상승하게 된다\n지문 ②에서 행사가격이 기초자산 가격보다 높아질수록 Call옵션은 외가격이 되므로 옵션가격은 하락한다.\n지문 ③에서 만기까지 남은 시간이 길수록, Call과 Put옵션의 가격은 상승하게 된다.\n지문 ④에서 기초자산 가격이 올라간다면, Put옵션 가격은 하락한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (413, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A는 Y자동차 주식을 매입하려고 한다. 그런데 최근에 Y 자동차 회사의 판매가 부진하고 주요 수출지역에서도 경쟁업체가 시장을 잠식하고 있다는 뉴스를 들었다. 투자자 A가 취할 수 있는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["Y자동차의 주식선물을 매도하면서 Y자동차의 주식현물을 동시에 매입하면, 악재로 인한 주가 손실을 회피할 수 있다", "Y자동차의 주식현물을 사는 대신 주식선물을 매수하더라도, Y자동차의 주식현물을 매수한 것과 유사한 수익을 얻을 수 있다", "Y자동차의 주식선물을 매수하면, Y자동차의 중간배당도 수령할 수 있는 권리를 보유한다", "Y자동차의 주식선물을 매수하고 동시에 주식현물을 매수하면, Y자동차 주식에 레버리지 투자를 한 것과 같은 효과를 얻는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문 ①에서 주식현물 매수와 주식선물 매도로 가격변동의 위험을 회피할 수 있다.\n지문 ②에서 주식선물 매수는 주식현물을 보유한 것과 동일한 효과를 얻을 수 있다.\n지문 ③에서 주식선물은 현물을 보유하지 않기 때문에, 배당을 받을 수 있는 권리가 없다\n지문 ④에서 주식현물과 주식선물을 모두 매수하면, 주가 움직임보다 수배의 손익을 얻을 수 있기 때문에 레버리지 투자를 하는 효과를 얻을 수 있다\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '현물과 선물 구분', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (414, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 회사가 파산신청을 하고 청산절차를 거치는 과정에서 가장 늦게 잔여재산을 받게 되는 자산으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["보통주", "은행대출", "선순위채권", "기업어음"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "주식은 회사의 자산 중 자기자본의 지분권을 표시한 유가증권이다. 주식을 보유한 주주는 잔여재산 청구권을 갖지만, 청산과정에서 타인자본이 자산에 대한 우선권을 행사한 다음에 남아있는 재산에 대해 청구권을 행사할 수 있다. 만일 타인자본이 모든 재산을 가져간다면, 주주 입장에서는 청구할 수 있는 잔여재산이 하나도 남지 않을 수 있다.\n따라서 주주 입장에서는 주가장부가치비율(PBR)이 중요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (415, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식가치의 적정성을 평가할때 활용하는 지표로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["PER", "PBR", "PSR", "ELW"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문 1은 주가이익비율(또는 주가수익비율)으로, 주가를 주당 순이익으로 나눈 값이다.\n지문 2는 주가장부가치비율(또는 주가순자산비율)으로, 주가를 주당 순자산으로 나눈 값이다.\n지문 3은 주가매출비율으로 주가를 주당 매출액으로 나눈 값이다.\n지문 4는 파생결합증권의 하나인 주가워런트증권(Equity Linked Warrant)으로, 주식가치 평가지표와는 무관한 금융투자상품의 하나이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/15 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주가배율의 정확한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (416, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식회사의 주주에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주는 타인자본의 소유자이다", "주주는 원칙적으로 보유 지분보다 많은 주주총회 투표권을 갖는다", "주주는 회사 경영성과에 대해 무한책임을 진다", "주주는 주식의 가치가 상승할 경우 주식을 매도하여 자본차익을 얻을 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "주주는 주식회사에 자본을 출자하여 지분권을 소유한 투자자로서, 기업 입장에서는 자기자본의 주체이다.\n지문 1에서 주주는 타인자본이 아닌 자기자본의 지분 소유자이다.\n지문 2에서 주주는 보유 지분(주식)만큼만 의결권을 행사할 수 있다.\n지문 3은 주주의 유한책임권을 잘못 설명하고 있다.\n지문 4에서 주주는 보유 주식을 매각하여 자본차익을 얻을 수 있는 권리를 갖고 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주 지위 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (417, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "특정 상품을 특정 시점에서 특정 가격으로 매도할 수 있는 권리를 의미하는 용어로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["Call옵션 매수", "Put옵션 매수", "선물 매수", "선물 매도"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "파생상품 중 옵션의 개념에 대한 설명이다. 옵션은 특정 기초자산을 미리 정한 가격에 사거나 팔 수 있는 권리이다.\n지문 1은 특정 상품을 특정 시점에서 특정 가격으로 매수할 수 있는 권리이다\n지문 2는 특정 상품을 특정 시점에서 특정 가격으로 매도할 수 있는 권리이다\n지문 3은 특정 상품을 특정 시점에서 매수하는 선물 계약이다\n지문 4는 특정 상품을 특정 시점에서 매도하는 선물 계약이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (418, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기업의 재무제표에서 확인할 수 있는 항목으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["재무상태표", "현금흐름표", "주석", "산업연관표"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "재무제표는 기업의 모든 경영활동을 회계처리 방식으로 정리한 경영성적표이다. 재무제표는 크게 재무상태표와 손익계산서, 현금흐름표와 자본변동표, 그리고 주석으로 이루어진다.\n지문 1의 재무상태표는 자산/부채/자본으로 구성된 기업의 재무상태를 보여준다.\n지문 2의 현금흐름표는 일정 기간의 현금성 자산의 조달과 지출내역을 보여준다\n지문 3의 주석은 재무상태표 등의 보완설명 등 회계정책과 근거, 질적 정보 등 보충설명을 제공하는 참고사항이다.\n지문 4의 산업연관표는 한국은행이 작성하는 국민계정 분석도표로, 생산과정에 포함된 재화와 서비스의 생산과 처분을 경제활동별로 파악할 수 있는 표이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무제표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (419, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기업의 재무비율 중 안정성 비율에 해당하는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기자본이익률", "유동비율", "부채비율", "이자보상배율"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "재무비율은 기업의 재무제표 항목을 활용하여 기업의 경영성과와 안정성, 활동성 등을 평가할 수 있는 지표이다. 해당 기업의 경영성과를 직관적으로 이해할 수 있으며, 기업간 비교도 가능하다.\n지문 1의 자기자본이익률은 수익성 지표의 일부로, 자기자본 대비 순이익의 비율을 파악하여 투입된 자기자본 대비 이익을 얼마나 벌어들였는지를 파악하는 재무비율이다\n지문 2의 유동비율은 단기부채의 상환능력을 파악할 수 있는 안정성 지표의 하나이다.\n지문 3의 부채비율은 자기자본 대비 부채를 얼마나 부담하고 있는 지를 파악하는 안정성 지표이다.\n지문 4의 이자보상배율은 부채에 대한 이자를 영업이익 내에서 어느 정도 감당하고 있는 지를 파악하는 안정성 지표이다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '안정성비율 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (420, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기업의 재무비율 중 수익성을 판단할 수 있는 지표로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유동비율", "이자보상배율", "자기자본이익률", "옵션가치"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "기업의 재무제표를 활용하여 수익성이나 안정성을 파악할 수 있는 지표가 재무비율이다. 재무 비율에는 수익성/안정성/성장성 등을 파악할 수 있는 지표들이 있다.\n지문 1의 산식은 단기부채의 상환능력을 파악할 수 있는 유동비율을 의미한다.\n지문 2의 산식은 이자비용을 영업이익으로 어느 정도 감당할 수 있는 보여주는 이자보상배율이다.\n지문 3은 투입한 자기자본 대비 순이익을 얼마나 발생하였는지를 파악하는 자기자본이익률으로서, 수익성 지표에 해당한다.\n지문 4는 옵션의 가치를 권리행사 실현여부(내재가치)와 실현가능성(시간가치)로 구분한 산식이다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '수익성비율 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (421, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "A 기업의 한해 경영성과를 다음과 같은 표로 정리하였다. A 기업의 1주당 배당수익률로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["5%", "10%", "20%", "50%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "배당은 기업의 경영성과를 주주와 공유하는 이익배분의 일환이다. 기업의 이익잉여금에서 배당금을 지급하기 때문에, 배당의 유무와 규모는 기업의 재무건전성을 확인할 수 있는 지표 중 하나이다. 주당 배당수익률은 1주당 배당금을 현재 주가로 나누어 산출한다. 현재 주가가 10,000원이고 주당 배당금이 500원이므로, 배당수익률은 5%이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '배당수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (422, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "비상장기업의 주식을 증권시장에서 신규로 거래 가능하도록 하기 위해 해당 기업의 주식을 모집 또는 매출의 방식으로 발행하거나 매각하는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주총회", "유상증자", "기업공개(IPO)", "액면분할"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "신규공모(Initial Public Offering) 또는 기업공개는 해당 기업의 주식을 (신주)모집하거나 (구주)매출하여 해당 기업을 주식시장에 공개하는 것을 의미한다. 구주매출은 기존 주주가 보유한 주식을 매각하는 방식이며, 신주모집은 기존 주주 이외 신주를 발행하여 투자자를 확보하는 방식이다.\n지문 1의 주주총회는 주식회사의 최고 의사결정기구로 주주가 참석하여 회사의 기본조직과 경영에 관한 중요한 사항을 의결하는 기관이다.\n지문 2의 유상감자는 지본금을 축소하면서 기존 주주에게 보상하는 방식이다.\n지문 4의 액면분할은 주식의 액면가를 일정 배율로 나누는 것을 말한다. 예를 들어 액면가 5,000원 주식을 액면가 1,000원으로 나누면, 기존보다 주식수가 5배 늘어난다.\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'IPO 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (423, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "상장지수증권(ETN)에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["특정 주권을 미리 정해진 가격으로 구입할 수 있는 권리가 부여된 유가증권이다", "자산운용회사가 발행하는 지수연동형 상장펀드이다", "금융투자회사(증권회사)가 발행하며 추적지수의 수익률에 연동된다", "주식시장(거래소)에 상장하지 않아도 거래가 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "상장지수증권(ETN: Exchange Traded Notes)는 투자의 손익이 추적지수의 변동에 연동하도록 만들어진 파생결합증권으로, 발행주체는 금융투자회사(증권회사)이다. 상장지수증권은 거래소에 상장되어 거래되는 것을 원칙으로 한다. \n지문 1은 주식워런트증권(ELW)를 의미한다\n지문 2는 상장지수집합투자기구(ETF)를 의미한다\n지문 3은 ETN을 설명하는 지문이다\n지문 4는 상장지수증권의 원칙에 위배되는 문구이다 "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (424, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 설명하는 금융투자상품은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["개별 주식이나 주가지수의 가격변동에 연동하여 사전에 약정된 조건에 따라 투자손익이 결정되는 파생결합증권이다. 파생상품을 내재하여 위험과 기대수익을 다양하게 설계할 수 있어, 해외에서는 구조화상품이라고 부르기도 한다. 약정한 조건을 기초자산의 가격이 상회/하회하면 약속한 수익을 지급하는 상품도 있으며, 주기적으로 기초자산의 가격을 평가하여 평가가격이 약정한 조건을 밑돌지 않으면 이자를 지급하고 투자원금을 조기 지급하는 상품도 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["우선주", "채권", "주가연계증권(ELS)", "선도거래"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "주가연계파생결합증권(ELS)는 개별 주식이나 주가지수의 주가변동에 연동하여, 약정한 조건에 따라 투자수익이 결정되는 파생결합증권이다. 원금보전형 Digital ELS는 약정한 조건을 기초자산 가격이 상회/하회하면 이자와 원금을 지급하고 그렇지 못하면 원금만 지급하는 상품이다. 조기상환 Step-Dow형은 주기적으로 기초자산의 가격을 평가하여 약정한 조건을 하회하지 않으면 만기이전이라도 이자와 원금을 지급하는 상품이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (425, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "장내주식시장에 대한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시간에 관계없이 거래가 가능하다", "결제와 청산의 과정이 장외시장보다 투명하지 않을 수 있다", "거래할 수 있는 상품이 표준화되어 있다", "매매규정이 불분명하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "주식 유통시장의 장내시장은 공인된 거래소에서 거래가 이루어지는 시장이다. 법적, 제도적으로 공인된 거래소이며, 불특정 다수가 참여하여 거래하기 때문에 거래할 수 있는 상품은 표준화, 규격화되어 있다. 거래의 공공성과 결제의 안정성을 담보해야 하기 때문에, 결제와 청산과정이 투명하게 관리된다. 거래는 거래소에서 규정한 거래시간 내에서만 이루어지며, 매매시스템과 거래규칙이 명확하게 제정되어 있다. \n지문 1과 2, 4는 ‘장외시장’의 특징을 언급하고 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (426, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주가가 상승할 때 투자수익을 볼 수 있는 파생상품 거래로 올바른 것만을 모두 묶은 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["Put옵션을 매입한다", "Call옵션을 매입한다", "선물 매도거래를 체결한다", "선물 매수거래를 체결한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ", "ㄴ, ㄷ", "ㄴ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "Call옵션은 특정 가격에 기초자산을 매입할 수 있는 권리이며, Put옵션은 특정 가격에 기초자산을 매도할 수 있는 권리이다. Call옵션을 매입하면 주가 상승시 수익을 얻을 수 있으며, Put옵션 매입은 주가 하락시 수익을 얻을 수 있다. \n선물 매수거래는 가격 상승시 수익을 얻을 수 있으며, 선물 매도거래는 가격 하락시 수익을 얻을 수 있다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (427, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식회사의 주주에 관한 설명으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["회사 이사회 결의에 참여하여 의결권을 행사할 수 있다", "회사가 청산될 때 잔여재산에 대해 청구권을 행사할 수 있다", "회사가 유상증자를 할 때 원천적으로 배제된다", "회사가 유상감자할 때 보상을 받지 못한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "주주는 자기자본의 지분권을 소유한 투자자로서, 경영성과에 따른 이익배당을 청구할 수 있으며, 회사가 청산될 때 잔여재산에 대해 청구권을 행사할 수 있다.\n지문 1에서 주주는 주주총회에서 의결권을 행사할 수 있으며, 이사회는 주주총회에서 선임된 이사로 구성되어 회사의 중요한 업무집행을 결정한다.\n지문 2에서 주주는 회사청산시 잔여재산에 대한 청구권을 행사할 수 있다\n지문 3에서 회사가 유상증자를 주주배정/제3자배정/일반공모 방식으로 진행하며, 주주배정은 기존 주주에게 신주를 인수받을 수 있는 권리를 배정한다. 제3자배정은 기존 주주가 아닌 제3자에게 신주인수권을 배정하는 것이며, 일반 공모는 불특정 다수에게 공개적으로 유상증자를 진행한다. 원천적으로 기존 주주가 유상증자에서 배제되는 것은 아니다.\n지문 4에서 회사가 유상감자를 하면 주주에게 보상하면서 주식을 매입하여 소각한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (428, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "파생상품 거래 중 선물거래의 특징으로 가장 거리가 먼 것은?\n\n", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선물거래는 특정 시점에 기초자산을 인수(도)하는 계약이다", "선물매수자의 경우 계약 만기에 계약 체결가격에 비해 기초자산 가격이 상승하면 수익을 얻는다", "선물매도 거래는 특정 시점에 약정한 가격으로 기초자산을 인도하고 대금을 받는다", "권리행사 가능성이 없다면 계약의 권리를 포기하면 된다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "선물거래는 특정 시점에 기초자산을 인수(도)하는 계약으로, 선물계약의 이행은 의무적으로 이루어진다. 선물 매수자의 경우 만기에 기초자산 가격이 선물계약 체결가격보다 상승하면 수익을 얻지만 하락하면 손해를 입는다. 선물 매도자의 경우 만기에 기초자산 가격이 선물계약 체결가격보다 하락하면 수익을 얻지만 상승하면 손해를 입는다. 따라서 선물거래의 계약당사자 사이의 손익관계는 ‘제로섬’이다\n지문 4의 설명은 옵션(특정 기초자산을 특정 가격에 사고나 팔 수 있는 권리)을 설명한 것으로, 옵션 매수자는 권리행사 가능성이 없다면 권리행사를 포기하면 된다\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '현물과 선물 구분', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (429, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주주총회는 주식회사의 최고 의사결정기관으로, 주주가 참석하여 회사의 핵심 사안을 결정한다.주주총회의 의사결정사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합병의 승인", "정관의 수정", "감사의 선임", "대표이사의 선임"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "주주는 보유한 주식수만큼 주주총회에서 의결권을 행사할 수 있다. 주주총회는 이사회가 소집하여, 주주총회 결의 사항으로는 이사와 감사의 선임과 해임, 정관의 수정, 합병의 승인, 자본의 감소 등이다. 대표이사 선임은 상법에 따르면 이사회에서 선임한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (430, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>와 관련된 금융투자상품으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["코스피는 10월 들어 미 국채금리 급등, 미중 무역분쟁, 3~4분기 실적 우려 등으로 8.4%나 하락하였다. 그 영향으로 OO종목은 가격이 폭락하였다. ‘XXD950KSOPI200콜’은 이달초 가격이 695원이었으나 15일 종가는 10원으로 하락률이 98.5%에 달했다. 자산가치가 큰 폭으로 쪼그라들었다. 반면 주가하락에 베팅한 OO풋(Put)종목이 수익률이 급증하며 대박을 터뜨렸다. YY증권이 발행한 ‘YYDG66코스피200풋’이 2100% 올라 수익률 1위를 기록했다. OO은 옵션을 주식처럼 거래할 수 있게 만든 상품이다. 기초자산의 가격이 내렬갈 때 풋 종목의 가치는 오르지만 콜 종목은 하락한다. 증권업계 관계자는 “최근 시장 변동성이 커지면서 일부 OO종목이 높은 수익을 내고 있지만, OO는 그만큼 투자태도도 크시 때문에 투자에 신중할 필요가 있다”고 전했다. <서울경제 2018-10-17 기사 수정인용>"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식워런트증권(ELW)", "ETF", "채권", "주가이익비율(PER)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "ELW는 금융투자회사(증권회사)가 발행하는 파생결합증권으로, 특정 주식이나 주가지수의 변동에 연계하여 미리 정해진 가격으로 매수하거나 매도할 수 있는 권리가 표시된 증권이다. 옵션형 상품이며, 기초자산의 가격등락에 따라 ELW의 가격 변동폭이 매우 크게 발생할 수 있다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (431, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "성장성이 높은 유망 스타트업이 자본확충을 위해 IPO를 통한 한국거래소 상장을 고민 중이다. 해당 기업이 선택할 수 있는 기업공개 형태에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기존 주주들이 보유하고 있는 주식을 일반투자자에게 매각하여 자본을 조달할 수 있다", "신주를 발행하여 일반투자자에게 매각하는 신주모집 방식만 가능하다", "기업공개 과정에서 회사의 일반적인 경영상황과 재무제표 등은 상장 이후 공개할 수 있기 때문에 미리 제공하지 않아도 된다", "주주발행 방식의 기업공개는 회사의 자본금을 늘리는 것과는 무관하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "IPO방식으로는 신주모집, 구주매각 등 여러가지 방식이 있다. 기업공개 과정에서 상장 전 투자자 모집을 원활히 하기 위해 회사의 일반적 경영상황과 재무제표 등을 공개하고 있다. 기업공개는 필연적으로 주식 수가 늘어나기 때문에 자본금이 늘어난다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'IPO 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (432, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주가배수에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주가가 동일하더라도 주당순이익이 작을수록 PER은 커진다", "PBR은 주당순자산을 주가로 나눈 값이다", "PER이 높다면, 주당순이익은 평균인데 주가가 높거나 주가는 평균인데 주당순이익이낮은 경우로 나누어 볼 수 있다", "PBR이 1보다 작다면 기업의 청산가치에 비해 주가가 저평가되어 있음을 의미한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "주가이익비율(PER, 또는 주가수익비율)은 주가를 주당순이익(EPS)으로 나눈 값이다. PER이 높다면 분자인 주가가 높거나 분모인 주당순이익이 낮은 경우이다.\n주가장부가치비율(PBR, 또는 주가순자산비율)은 주가를 주당순자산으로 나눈 값이다. PBR이 1보다 낮다면, 기업의 청산가치가 주가보다 높다는 것을 의미한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주가배율의 정확한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (433, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식 발행시장의 기능에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["원활한 자금조달에 기여한다", "모험자본의 참여를 통한 자본시장 활성화에 기여한다", "성장기업에 투자할 수 있는 투자수단을 제공한다", "발행을 통해 특정인에 대한 자본집중을 심화시킨다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "주식 발행시장은 기업입장에서 경영활동에 필요한 자본을 조달할 수 있으며, 투자자 입장에서는 기업에 투자할 수 있는 투자수단(주식)을 확보할 수 있는 기회이다. 유휴자본을 자본시장에 참여할 수 있도록 도와 자본시장을 활성화시킨다. \n발행을 하게 되면, 기존 주주의 지분율은 줄어들고 신규 주주의 참여가 이루어지기 때문에 특정인에 대한 자본집중을 심화시키기 보다는 자본의 분산을 도모한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '발행시장 기능 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (434, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "A기업이 주식을 새로이 발행하는 방식으로 자금조달을 계획하고 있다. A기업이 선택할 수 있는 발행방식에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["재무적 협력사가 자금지원을 약속함에 따라, 해당 투자자를 대상으로 사모발행 방식의 주식 발행을 추진할 수 있다", "주주배정 방식으로 기존 주주에게 공개적으로 신주를 투자할 수 있는 기회를 제공할수 있다", "비상장 기업일 경우, 일반 공모방식으로 불특정 다수에게 공개적으로 신주를 투자할 수 있는 기회를 제공하여 자본을 조달할 수 있다", "기존 주주에게 무상증자를 하면, 계획하였던 자본 조달에 성공할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "기업이 주식을 발행하는 방식은 크게 사모방식과 공모방식이 있다. 사모 발행은 특정 또는 소수의 투자자에 한정하여 주식을 발행하고 자본을 조달하는 방식이다. 공모 발행은 크게 기존 주주에게 신주를 투자할 수 있는 기회를 제공하는 주주배정방식과 불특정 다수에게 신주를 투자할 수 있도록 하는 일반공모 방식이 있다.\n한편 주식발행은 주주에게 유상으로 주식을 발행하는 유상증자와 무상으로 주식을 발행하는 무상증자로도 구분할 수 있으며, 무상증자는 자본의 새로운 유입이 없이 잉여자본으로 기존 주주에게 신주를 배분한다.\n무상증자로는 새로운 자본을 조달할 수 없다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자금조달 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (435, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기술적 분석 중 이동평균선에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["이동평균을 구하는 방식은 단순산술평균부터 지수이동평균이나 기하이동평균 등 다양한 편이다", "이동평균선은 해당 기간에 투자자들이 거래한 평균단가로 이해할 수 있다", "이동평균선을 주가가 상향돌파하는 상황을 골든크로스(Golden cross)라고 하며 중요한 주식매도 신호로 인식한다", "이동평균선보다 주가가 아래에 위치한 경우 기간 평균에 비해 주가가 낮게 형성되고 있음을 의미한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "이동평균선은 기술적 분석에서 중요한 보조지표이다. 일정 기간의 주가 평균치를 선으로 연결하여 현재의 주가와 비교하거나 이동평균선 자체의 흐름을 분석할 수 있다. \n이동평균선을 주가가 상향돌파하는 상황을 골드크로스로 부르며, 중요한 주식매수 신호로 인식된다. 반대로 이동평균선을 주가가 하향돌파하는 상황을 데드크로스(Dead Cross)라고 부르며, 중요한 주식매도 신호로 인식된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/27 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '이동평균선 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (436, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기술적 분석에서 거래량에 대한 설명으로 올바른 것만을 <보기>에서 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["거래량이 많아졌다는 것은 매매의사를 가진 투자자들이 몰려들었다는 것을 의미하며 주가에 중요한 변화가 발생하였음을 반영하는 현상이다", "거래량이 이전에 비해 줄어들었다면 해당 주식의 추세가 반전될 여지가 높아졌다고 평가할 수 있다", "주가 상승구간에서 거래량이 동반하여 늘어나면 해당 주식의 상승추세가 강화될 가능성이 높아졌다고 평가할 수 있다", "거래량은 매수자와 매도자가 체결한 수량이며 투자자의 의사가 반영된 결과이기 때문에 거래량으로 시장 참여자들의 에너지(힘) 수준을 파악할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ", "ㄱ, ㄴ", "ㄱ, ㄴ, ㄷ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문에 서술한 모든 내용이 기술적 분석에서 ‘거래량’의 활용방안을 설명하고 있다. \n거래량은 시장참여자들의 의사가 반영된 결과물이기 때문에, 시장참여자의 에너지 정도를 파악할 수 있는 보조지표이다. 가격의 추세가 강화되는 구간에서 거래량도 동반하여 증가하는 경향이 많다. 추세가 약해지는 구간에서는 거래량도 감소한다. 따라서 거래량이 줄어드는 상황은 이전 추세가 반전될 여지가 높은 상황이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/25 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거래량 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (437, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식분석방법 중 기술적 분석에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주가의 흐름이나 여타 보조지표의 추세에서 나타나는 특징을 분석하여 매매시점을 포착하는 방식이다", "주가 변동이 투자심리와 수급상황의 변화 등에 의해 주로 결정된다고 가정하기 때문에 기본적 분석과는 전혀 분리된 주식 분석 방법이다", "예전에 없던 이벤트가 생기더라도 효과적으로 분석할 수 있다", "일반적으로 활용되는 것은 주가를 표시한 챠트이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "기술적 분석은 과거의 가격흐름에서 나타나는 특징적인 현상들을 파악하고 현재의 시장상황과 유사한 국면에서의 주가 흐름을 유추하여 매매시점을 포착하는 방식이다. 주가는 기본적으로 기업의 내재가치를 중심으로 형성되지만, 투자심리 등 다양한 변수에 의해 진폭을 형성할 수 있기 때문에, 기본적 분석과 병행해야만 주가의 적절한 가치평가와 매매에 대한 의사결정을 내릴 수 있다. 기술적 분석은 주로 주가 챠트를 통해 이루어지며, 주가 이외의 다양한 보조지표를 활용한다. 기술적 분석은 과거 형성되었던 주가의 흐름이 미래에도 반복된다는 가정을 바탕으로 하므로, 시장환경이나 제도가 바뀌면 분석의 효용이 반감될 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/26 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기술적 분석 기본적 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (438, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 2개의 주식에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A주식 : 주가 15,000원 액면가 5,000원", "B주식 : 주가 2,000원 액면가 1,000원 (6개월 전 액면분할 실시 5000원→1000원)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["액면가 대비 주가가 3배인 A주식이 2배인 B주식보다 비싸다고 볼 수 있다", "B주식의 발행주식수는 액면분할 이전에 비해 5배 늘어났다", "B주식의 액면분할 당시 분할이전의 주가보다 1/5 수준으로 하락하였을 가능성이 높다", "A주식과 B주식의 주가수익비율(PER)가 5배로 동일하다면 주가의 차이에도 불구하고 내재가치 측면에서는 비슷하다고 평가할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "액면분할은 기존 액면가격을 나누어 비율만큼 발행주식수를 늘리는 것이다. 기존 액면가 5000원에서 1000원으로 액면분할을 한다면, 발행주식수는 기존 1주에서 5주로 늘어난다.\n주가를 비교 평가할 때, 액면가 대비 주가는 참고지표일 뿐 주가의 가치척도로 활용하지 않는다. 지문 ②와 지문 ③은 액면분할로 인해 통상적으로 발생할 수 있는 현상이다.\n지문 ④에서 두 기업의 PER가 5배로 동일하다면, 내재가치 측면에서 비슷하다고 평가할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/24 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '액면가와 시가 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (439, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금유감독원에서 제공하는 <보기>의 유의사항이 적용되는 금융투자상품으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["1. 기초자산의 가격흐름에 따라 원금손실이 발생할 수 있다", "2. 증권회사가 자기신용으로 발행한 무보증 증권으로 예금자보호대상이 아니다", "3. 은행이나 보험사에서 판매하는 상품도 신탁과 펀드에 해당 상품을 편입한 것이다", "4. 기초자산의 가격흐름에 따라 손익이 결정되므로, 손익발생조건을 확실히 이해하고 투자해야 한다", "5. 손실발생조건에 따라 기초자산이 많을수록 수익으로 상환되는 달성확률이 낮아져 손실위험이 높아질 수 있습니다", "6. 손실이 발생하면 손실규모가 커지는 ‘꼬리위험’이 있는 상품입니다", "7. 중도상환을 요청하면 중도상환가격에 따라 원금손실이 발생할 수 있습니다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ETF", "DLS", "우선주", "선도거래"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "해당 지문은 금융감독원에서 제공하는 금융꿀팁 중 “파생결합증권 투자시 유의사항”에 해당하는 내용이다. ELS(주가연계증권) 또는 DLS(비주권연계증권) 등 파생결합증권에 대한 내용이므로, DLS도 포함된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (440, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기업의 재무제표 중 주석에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기업의 회계정책이나 재무제표 작성근거, 본문에 표시되지 않는 질적 정보 등 재무제표 이해에 필요한 보충 정보를 제공하는 제무제표의 항목이다", "지급보증이나 우발채무 등 재무상태표 등에서 찾기 어려운 내용을 제공한다", "일정 기간 발생한 수익과 비용의 경영성과 내용을 표시하며, 매출과 순이익을 파악할 수 있다", "관계기업의 현황과 요약 재무정보도 포함되어 있어, 관련기업으로부터 얻을 수 있는 수익/손실 여부 등을 파악할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "재무제표에는 재무상태표와 손익계산서, 자본변동표와 현금흐름표, 그리고 주석이 포함된다. 주석은 기업의 회계정책과 재무제표 작성근거, 우발채무나 관계기업의 현황 등 기업의 재무상황을 파악할 수 있는 보충자료가 자세하게 서술되어 있다. 따라서 주석은 재무제표의 본문을 보면서 반드시 참조해야 할 참고서이다. 지문 ③은 현금흐름표를 설명한 내용이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/23 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '재무제표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (441, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "\"삼성전자 우\"에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["삼성전자 주식 중 우선주를 의미한다", "해당 주주는 주주총회에 참석하여 안건에 대해 투표할 수 있다", "해당 주주는 보통주 주주보다 이익배당에서 우선 순위에 있다", "일반적으로 보통주보다 배당률이 높은 편이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "주권 중 보통주와 우선주의 구분은 무엇보다 의결권의 유무이다. 주주총회에서 안건에 대해 투표를 통해 표결에 참여할 수 있는 주주는 보통주를 보유한 주주이며, 우선주는 의결권은 없는 대신 이익배당에서 우선 순위에 있다. 일반적으로 국내에서 우선주는 보통주보다 배당률이 높은 편이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/21 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '우선주 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (442, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "캔들챠트에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시가보다 종가가 높다면, 캔들챠트의 몸통은 빨간색으로 표시된다", "시가보다 종가가 높다면, 결과적으로 주가가 장중에 상승세를 보인 것이다", "장중 고가와 저가를 캔들챠트의 몸통 위아래에 표시하여, 장중 가격 등락의 범위를 보여준다", "캔들챠트의 위에서 아래까지의 길이가 길어질수록, 장중 가격 등락은 매우 적은 편으로 해석한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "국내 주식시장에서 활용하는 챠트는 대부분 캔들챠트이다. 일중에 관찰되는 시가와 고가, 저가와 종가를 한 개의 몸통에서 모두 표시할 수 있다. 국내에서는 시가보다 종가가 높다면 몸통을 빨간색으로 칠하고, 시가보다 종가가 낮다면 몸통을 파란색으로 표시한다. 장중 고가와 저가는 몸통 위아래에 선으로 표시한다. \n따라서 캔들챠트의 위에서 아래까지의 길이가 장중 가격 등락의 폭을 보여준다. 위에서 아래까지의 길이기 길어질수록, 장중 가격등락이 큰 편임을 알 수 있다. 캔들챠트는 일중 주가의 흐름과 변동성을 동시에 보여준다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/22 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '캔들챠트 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (443, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "옵션거래의 손익에 대해 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["콜옵션을 매수하기 위해서는 옵션 프리미엄을 풋옵션 매도자에게 지급한다", "풋옵션을 매도한 투자자는 기초자산 가격이 행사가격 이하로 하락하여 풋옵션 매수자가 권리행사를 주장한 경우 풋옵션 매수자의 옵션 프리미엄만 돌려주면 된다", "콜옵션을 매수한 투자자는 행사가격 이상으로 기초자산 가격이 상승하지 않은 경우 지급한 옵션프리미엄 만큼 손해를 본다", "풋옵션을 매수한 투자자는 행사가격 이하로 기초자산 가격이 하락한 경우 행사가격과 기초자산의 차이보다 지급하였던 옵션프리미엄이 크면 순수익을 얻는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문 ①에서 Call옵션 매수자의 옵션프리미엄은 Call옵션 매도자에게 지급된다\n지문 ②에서 Put옵션 매도자는 Put옵션 매수자의 수익을 모두 지급해야 한다\n지문 ③에서 옵션의 권리행사가 이루어지지 않으면 지급하였던 옵션프리미엄 만큼 손해이다.\n지문 ④에서 Put옵션 매수자의 최종수익은 Max{(행사가격-기초자산가격), 0} – 옵션프리미엄이므로, 지급하였던 옵션프리미엄이 행사가격과 기초자산가격의 차이보다 크면 순손실이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '옵션 손익구조 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (444, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "기초자산이 동일한 만기 3개월짜리 선물과 만기 6개월짜리 선물 간의 관계에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["만기 3개월짜리 선물의 이론가격은 현물을 보유할 때 발생하는 보유비용을 현물가격에 더하여 산출한다", "만기 3개월짜리 선물 이론가격이 만기 6개월짜리 선물 이론가격보다 낮게 형성되는 것은 두 선물의 보유비용에서 차이가 발생하기 때문이다", "만기 3개월짜리 선물가격이 만기 6개월짜리 선물가격보다 낮게 형성되는 현상을 통상 백워데이션이라고 부른다", "만기 6개월짜리 선물가격이 계절적인 변수나 수요공급의 불일치로 만기 3개월짜리 선물가격보다 낮게 형성될 수도 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문 ①에서 선물의 이론가격은 현물가격+보유비용으로 구성된다\n지문 ②에서 만기가 서로 다른 선물의 이론가격이 차이가 나는 것은 각 선물에 반영된 이론가격의 차이, 결과적으로 각 선물에 반영된 보유비용의 차이 때문이다.\n지문 ③에서 근월물이 차월물보다 낮게 형성된다면 콘탱고 현상이라고 부른다. 콘탱고/백워데이션 현상은 선물의 기간구조를 설명할 때도 쓰이지만, 선물과 현물가격을 비교할 때도 활용된다.\n지문 ④에서 수급적인 요인이나 계절적인 변수 때문에 일시적으로 차월물 가격이 근월물 가격보다 낮게 형성될 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '파생상품 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (445, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자의 투자자금을 자산운용회사가 운용하여 운용결과를 투자자에게 돌려준다", "투자대상자산으로 주식, 채권은 물론 부동산이나 실물자산도 가능하다", "자산운용회사가 펀드투자 수익을 보장해주기 때문에 원금손실위험이 없는 금융상품이다", "투자자의 투자자금은 자산운용회사가 아닌 신탁회사가 보관한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "펀드는 실적배당형 상품이다. 운용회사는 운용의사결정을 하며, 각종 비용을 차감한 이후 운용성과를 투자자에게 제공하는 실적배당형 상품이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (446, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "인덱스 펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식형 인덱스펀드는 주가지수 수익률을 추종하는 금융투자상품이다", "상장지수펀드(ETF)는 주로 인덱스펀드를 상장시켜 거래하는 펀드이다", "주식형 인버스 인덱스 펀드는 주가지수가 하락할 때 수익이 발생한다.", "레버리지 인덱스펀드는 매일 포지션을 조정하며, 주가지수의 등락과 관계없이 동일한 수익률을 기대할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "레버지리 인덱스펀드는 매일 포지션을 조정하기 때문에 주가지수의 등락에 따라 투자성과가 달라진다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 기본개념 이해', null, null);
