insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (451, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드 투자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산운용회사는 투자자들과 정기적으로 만나 투자자들의 의견을 투자에 반영해야 한다", "펀드판매회사에서 펀드를 매수할 때 실시간가격으로 매입할 수 없다", "상장지수펀드(ETF)는 투자자가 실시간가격으로 투자가 가능하다", "펀드를 통해 직접투자가 어려운 자산에도 투자할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "자산운용회사는 원칙적으로 투자자 정보를 가지고 있지 않는다. 투자자는 펀드판매회사에서 관리하며 자산운용회사는 설정된 펀드에 대한 운용만을 담당한다.\n\n"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 기본개념 이해', null, null);
