insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (530, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (     ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(    )은 사람들이 경제적 또는 재무적 의사결정을 할 때 인간은 합리적이라는 가정에서 모든 경제 주체는 이성적인 선택을 한다고 전제한다. 그러나 (    )은 인간의 선택이 온전히 합리적이진 않으며, 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번함을 실험을 통해 확인했다.  현재는 금융 시장을 이해하고 올바른 선택을 하기 위해서는 (    )이 얘기하는 인간의 ‘행동편향’을 이해하고 극복하고자 노력하는 것이 필요하다는 인식이 점점 더 확산되고 있다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["행동경제학, 전통경제학, 전통경제학", "행동경제학, 전통경제학, 행동경제학", "전통경제학, 행동경제학, 전통경제학", "전통경제학, 행동경제학, 행동경제학"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "전통경제학은 우리가 경제적 또는 재무적 의사결정을 할 때 ‘인간은 합리적’이라는 가정에서 출발한다. 모든 경제주체는 다양한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다는 것이다.  행동경제학은 그 간의 전통 경제학과 달리 인간의 선택이 온전히 합리적이진 않다고 주장한다. 더욱이 돈과 관련된 선택과 행동에서는 이런 합리적이지 않은 모습이 더욱 두드러진다. 행동경제학은 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번한 것을 실험을 통해 확인했다. 불확실한 상황에서 인간의 의사결정은 어림짐작이나 주먹구구식으로 여러 심리적 편향에 흔들린다는 것을 밝혀냈다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (531, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (     ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 먼저 빨리 생각하는 (    )인 시스템 1(자발적인 통제 없이 저절로 빠르게 작동)와 그 다음 느리고 (    )인 시스템 2(노력이 필요한 정신 활동이 요구되는 때에만 작동)이다. 선택의 순간, 시스템 1과 시스템 2가 충돌한다. 대부분 자신이 (    )로 판단한다고 여긴다. 자신이 논리적이며 합리적이고 이성적으로 결정할 수 있다고 믿는 것이다. 그러나 실제로는 (    )이 작동하여 처리하는 경우가 많고 또 반복적이기까지 하다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기본적인 사고체계, 노력이 필요한 사고 체계, 시스템2, 시스템1", "기본적인 사고체계, 노력이 필요한 사고 체계, 시스템1, 시스템2", "노력이 필요한 사고 체계, 기본적인 사고체계, 시스템2, 시스템1", "노력이 필요한 사고 체계, 기본적인 사고체계, 시스템1,  시스템2"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 먼저 빨리 생각하는 기본적 사고체계인 시스템 1(자발적인 통제 없이 저절로 빠르게 작동)와 그 다음 느리고 노력이 필요한 사고체계인 시스템 2(노력이 필요한 정신 활동이 요구되는 때에만 작동)이다. 선택의 순간, 시스템 1과 시스템 2가 충돌한다. 대부분 자신이 시스템 2로 판단한다고 여긴다. 자신이 논리적이며 합리적이고 이성적으로 결정할 수 있다고 믿는 것이다. 그러나 실제로는 시스템 1이 작동하여 처리하는 경우가 많고 또 반복적이기까지 하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (532, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "합리적 투자의사 결정을 위하여 행동편향을 대하는 태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 인간은 행동편향에 빠지기 쉬운 존재임을 인정한다", "오랜기간 형성된 행동편향일수록 단기에 고칠 수 있다", "행동편향으로 인해 투자 의사결정시 합리적이지 않은 선택을 할 수 있음을 인지한다", "금융투자에 대한 기대만이 아니라 변동성 등 투자의 위험을 충분히 인지하고 대비하는 투자 태도를 갖는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문 2의 경우, 오랜 기간 동안 형성된 자신이 행동편향을 바로 잡는 것 역시 오랜 시간이 소요될 수 있다.일상생활하는 가운데 의사결정시마다 자신의 행동편향들을 의식하고 점검하려는 관심과 노력을 지속적으로 기울여 자신이 행동편향들을 극복해 나가려는 마음가짐과 자기성찰이 필요하다.\n우리가 편향에 빠지기 쉬운 존재임을 인정하고 받아들이는 것이 중요하다. 사람들의 의사결정은 계획적이고 신중하기보다 직관적이고 자동적으로 이루어지는데, 그런 행동이 믿음처럼 좋은 결과를 만들어 내지 못하고 손해로 이어진다. 이런 편향에 빠지는 것은 인간이기에 당연한 것임을 받아들인다. 자신이 편향에 얼마나 취약하고 또 그 결과가 자신에게 손해를 준다는 것을 인지하는 겸손한 태도를 취하는 것이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
