insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (463, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권과 주식 투자에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식투자자와 채권투자자 모두 기업에 대한 의결권을 가진다", "채권투자자는 주로 기업의 이익에 대한 배당권을 청구함으로써 이자를 지급받는다", "주식투자자는 투자 원금에 대한 상환청구권을 가진다", "채권은 주식과 달리 일반적으로 원리금의 현금흐름 스케줄이 사전에 정해져 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "채권은 보통 의결권을 가지지 못한다. 그리고 채권은 보통 주식과 달리 이익배당권이 없는 대신 원리금상환청구권을 가진다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '주식과 채권 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (464, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "발행주체에 따른 채권의 종류에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["국채는 재정을 위해 정부가 발행하는 채권으로, 신용위험이 없는 것으로 간주된다", "지방채는 지방자치단체가 특수목적의 자금을 조달하기 위해 발행하는 채권이다", "특수채는 대부분 국가의 신용보강을 수반하기 때문에 신용위험이 없는 것으로 간주된다", "회사채는 상법상의 주식회사가 발행하는 채권으로 회사의 채무이행능력에 따라 다양한 신용등급을 부여받는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "특수채는 국가의 보증이나 신용보강을 수반함으로써 대부분 AAA등급을 부여받으며, 채무이행능력에 따라 하위 등급을 부여받기도 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 분류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (465, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권이 은행예금과 비교하여 다른 점으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권은 매입 후 만기 전에 언제든지 시장에서 사고 팔 수 있다", "채권을 사고팔 수 있다는 것은 매일매일의 가격이 존재한다는 뜻이다", "채권은 은행뿐 아니라 정부, 공기업, 주식회사 등 발행처가 다양하다", "모든 채권은 만기 전에 중간중간 정기적으로 이자를 지급한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "채권에는 중간중간 이자를 지급하는 이표채 뿐만 아니라 이자를 복리로 모아서 만기일에 한꺼번에 지급하는 복리채가 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 기본개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (466, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 원리금의 현금흐름 스케줄이 미리 정해진다", "일반적으로 주식과 달리 기업의 이익을 배당받을 권리가 없다", "시장거래가 가능하기 때문에 만기 이전에도 아무런 손실 없이 투자원금 회수가 가능하다", "발행자가 자금을 조달하기 위해 발행하는 채무증서 성격의 유가증권이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "채권은 만기일 이전에도 유가증권시장에서 자유롭게 거래할 수 있다. 그러나 거래 시점의 시중금리와 발행자의 채무상환능력 따라 거래가격이 변동되며 결정되기 때문에, 투자 원금의 손실이 발생할 수도 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 특징 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (467, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 설명하는 채권 종류를 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["한국은행이 시중의 통화량 조절을 위해 발행하는 채권이다", "특별법에 의해 설립된 금융기관들이 발행하는 채권으로, 발행기업의 신용도에 따라 신용등급을 부여된다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["한은채, 회사채", "한은채, 특수채", "통안채, 특수채", "통안채, 금융채"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "통안채는 한국은행이 시중의 통화량 조절을 위해 발행하는 채권으로, 통안증권 또는 통화안정증권이라고도 한다. 특별법에 의해 설립된 금융기관들이 발행하는 채권을 금융채라 하고, 금융채는 국책은행채, 시중은행채, 기타금융채 등으로 세분된다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 분류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (468, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권에 투자하는 이유로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["다양한 발행자와 만가의 채권이 존재하여 투자자의 상황에 맞는 적절한 상품을 선택할 수 있다", "은행 예금에 투자하는 것보다 높은 수익률을 추구할 수 있다", "중장기 채권의 경우 경제상황이 나빠질 때 가격이 오르는 거의 유일한 투자자산이다", "주식보다 안전하면서도 더 높은 기대수익률을 추구할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "일반적인 채권자산의 위험이 주식보다 낮으며 기대수익률도 주식에 비해 낮은 편이다. 일반적으로 그리고 이론적으로 투자자산의 위험이 클수록 기대수익률도 높다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 특징 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (469, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "형식적으로는 나중에 이자를 붙여 다시 매입하는 조건으로 채권을 매각하는 것이지만 실질적으로는 보유중인 채권을 담보로 자금을 빌리는 단기금융거래를 의미하는 용어로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["콜(call)", "환매조건부채권(RP)", "양도성예금증서(CD)", "기업어음증권(CP)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "① 콜은 금융기관(주로 은행) 사이에서 1일 또는 단기자금을 융통할 때 쓰이는 거래를 뜻한다. 과거 기관들 사이에 전화(call)를 통하여 금리가 결정되어 거래되었기에 붙여진 이름이다. \n③  CD는 양도성예금증서, 즉 증서로 거래될 수 있는 정기예금이다.\n④ CP는 기업에서 발행하는 기업어음을 뜻한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 분류 이해', null, null);
