insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (475, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권의 수익률곡선에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 잔존만기가 길수록 수익률도 높아진다", "같은 만기에는 신용등급이 낮아 신용위험이 클수록 채권의 수익률이 높다", "채권의 종류별로 여러 수익률곡선이 존재한다", "정책 금리의 영향을 직접적으로 받는 단기채권의 수익률이 장기채권의 수익률보다 높은 것이 일반적이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "일반적으로 장기채권이 단기채권에 비해 시중금리의 변동에 따른 가격변동 위험이 크다. 위험이 높을수록 기대수익률이 높기 때문에 장기채권이 단기채권에 비해 대체로 수익률이 높다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 손익구조 이해', null, null);
