insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (483, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "듀레이션이 2년인 국채를 2억원 보유한 투자자 A와 듀레이션이 4년인 국채를 1억원치 보유한 투자자 B가 있다. 어느 날 채권시장에서 2년물 국채의 금리가 1% 상승하고 4년물 국채의 금리가 2% 상승했을 때 투자자 A와 B의 하루동안의 손익에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A는 B의 약 2배 평가이익을 얻었다", "A와 B의 손실은 각각 4백만원으로 동일하다", "A와 B의 손익은 동일하다", "A는 B에비해 절반정도의 평가손실만 입었다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "해당일 투자자 A는 2년 × 2억원 × 1% = 약 400만원의 평가손실이 발생되고 투자자 B는 4년 × 1억원 × 2% = 약 800만원의 평가손실이 발생한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 가치평가 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (484, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권의 가격과 수익률의 관계가 <보기>의 그림과 같이 나타나는 것을 일컫는 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["컨벡시티 효과", "듀레이션 효과", "일드커브 효과", "크레딧 효과"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "정확한 계산으로 구해지는 채권수익률 변동에 따른 채권가격의 변동 모습은 그림과 같이 볼록한 곡선 모습을 보이는데 수익률이 오를 때는 조금씩 가격 하락폭이 둔화되고 수익률이 내릴 때는 조금씩 상승폭이 점증한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 가치평가 이해', null, null);
