insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (487, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권의 수익률곡선에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권의 위험과 수익률의 관계를 나타낸다", "수익률곡선에서 같은 종류의 채권은 일반적으로 잔존만기가 길수록 수익률이 높아지는 우상향 모습을 보인다", "한 면의 수익률곡선 그래프에서는 여러 종류의 채권을 한꺼번에 그려낼 수 없다", "신용등급별 수익률곡선을 살펴보면 신용등급이 낮아 신용위험이 클수록 채권의 수익률이 높아져 수익률곡선의 위치가 높아진다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "한 면의 그래프에서 신용등급별 여러 종류 채권의 수익률곡선들을 한꺼번에 그려낼 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권 가치평가 이해', null, null);
