insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (489, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "이자소득에 대한 총 세율이 20%라고 할 때, 다음 중 세후 실효수익률이 가장 높은 채권은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["발행금리가 0%인 시장금리 5.5%의 1년 만기 채권", "발행금리가 3%인 시장금리 6%의 1년 만기 채권", "발행금리가 6.5%인 시장금리 6.5%의 1년 만기 채권", "발행금리가 8%인 시장금리 7%의 1년 만기 채권"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "① 세후 실효수익률 = 5.5%-(0%×0.2) = 5.5%\n② 세후 실효수익률 = 6%-(3%×0.2) = 5.4%\n③ 세후 실효수익률 = 6.5%-(6.5%×0.2) = 5.2%\n④ 세후 실효수익률 = 7%-(8%×0.2) = 5.4%"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '채권의 수익률 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (490, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "중앙은행이 금리인하를 통해 시중금리의 하락을 유도할 때 기대되는 영향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["저축의 증가를 통한 경제활동 활성화", "예금 등 금리상품 대비 주식, 부동산 등의 상대적 매력 증가", "대출 등 조달금리 하락으로 투자와 생산의 증가", "생산활동, 투자활동으로 시중의 유동성 이동"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "중앙은행의 금리인하는 시중 유동성을 저축 등 이자상품에서 실물경제 분야로 이동시켜 경제를 활성화시키려는 노력의 일환이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준금리 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (491, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["일반적으로 경기가 좋아지면 주가는 상승하나 채권 가격이 하락하고, 반대로 경기가 나빠지면 주가는 하락하나 채권 가격이 상승한다. 이는 경기라는 변수에 대해서 주식과 채권 가격이 (         )이기 때문이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상관계수가 1", "상관계수가 0", "상관계수가 마이너스", "상관계수가 플러스"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "상관계수는 -1부터 +1까지 산출되며, +1은 완전히 같은 방향으로 움직이는 것을, -1은 완전히 반대 방향으로 움직이는 것을 의미한다. 상관계수가 마이너스 라는 것은 대체로 반대방향으로 움직인다는 것을 의미한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상관계수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (492, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "경제지표에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["GDP(국내총생산)와 경기종합지수는 일반적으로 금융시장 참여자가 중요하게 생각하는 경제지표이다", "분기별로 발표되는 GDP수치는 금리나 주가에 즉각적인 영향을 미친다", "경기종합지수는 경제의 각 부문을 대표하는 경제지표들을 가공, 종합하여 하나의 지표로 나타난 것이며 선행지수, 동행지수, 후행지수로 나누어진다", "실업률, 전경련BSI(기업경기실사지수), 통계청CSI(소비자전망지수) 등도 금융시장에서 관심을 가지는 경제지표이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "금리나 주가에 직접적인 영향을 미치는 것은 GDP 지표 발표치 자체가 아니라 한국은행과 각 금융 기관이 전망하는 향후 GDP 추정치와 이에 대한 시장의 컨센서스 변화인 경우가 많다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '경제지표 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (493, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "물가와 채권 금리의 관계에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["향후 물가가 크게 오를 것으로 예상되면 돈에 대한 수요가 증가해 시중 금리는 오르게 된다", "물가가 기대치보다 크게 오르게 되면 물가 안정을 제1의 목표로 삼는 한국은행은 기준금리를 올리는 등 시중 자금을 흡수해 돈의 공급을 줄이는데, 이는 시장 금리를 상승시키는 원인이 된다", "물가 상승으로 한국은행의 기준금리 인상 등 통화긴축이 예상될 경우 단기적으로 주가에 긍정적인 영향을 끼친다", "소비자 물가 지수는 한국은행의 통화 정책 결정 시 중요한 참고가 되기에 금융시장에 미치는 영향이 큰 편이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "물가 지수의 변화가 채권시장에 끼치는 영향은 직접적인 데 반해 주식시장에 끼치는 영향은 다소 간접적이다. 물가 상승으로 한국은행의 기준금리 인상 등 긴축 통화가 예상될 경우 단기적으로 주가에는 좋지 않은 영향을 끼칠 수 있다. 하지만 장기적으로 물가 수준의 상승은 곧 기업이 보유하고 있는 자산 가격의 상승, 기업이 창출하는 명목 이익의 상승으로 연결되므로 주가에 긍정적으로 작용한다는 분석이 많다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '물가의 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (494, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 상황들이 시중금리에 어떤 영향을 미치게 되는지 금리의 방향을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금리 하락, 상승, 상승, 상승", "금리 하락, 하락, 상승, 상승", "금리 하락, 상승, 하락, 상승", "금리 하락, 상승, 하락, 하락"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "기준금리 상향 등을 통한 통화 긴축정책은 시중 유동성을 흡수하며 금리상승을유발하며 확장적 재정정책 (경기부양 재정정책)은 국채발행과 경기부양 가능성 확대로 금리상승을 유발한다. 주식시장 상승은 경기지표의 호전과 채권시장 자금의 주식시장 이동을 통해 금리상승을 유발한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금리변동요인 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (495, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "증시주변 자금에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자들이 가장 관심 깊게 지켜보는 증시주변자금 지표는 고객예탁금과 신용잔고의 추이인데 일부 전문가는 고객예탁금과 신용잔고를 합한 총계 금액을 종합 증시주변자금으로 계측한다", "고객예탁금은 언제든지 주식매입 자금으로 동원될 수 있는 대기자금 성격이 강하기에 증가할수록 주식시장에 호재로 해석하는 전문가들이 많다", "신용잔고는 언젠가 주식의 매도 등을 통해 상환해야 하기에 일정 기간내 주식 매도가 발생할 가능성이 매우 높은 일종의 매도대기물량으로 계산된다", "주식 공매도는 과열된 주가를 정상화시키기도 하며 증권시장의 유동성을 높이기도 하는 순기능이 있지만 불법적인 매매를 위해 주식 공매도가 실행되는 역기능도 존재한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "일부 전문가는 고객예탁금에서 신용잔고를 차감한 금액을 종합 증시주변자금으로 계측하기도 한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '증시주변자금 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (496, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "환율과 경제에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A국 상품의 경쟁력에 비해 A국의 통화가치가 하락하면 무역수지를 포함한 경상수지나 국제수지의 흑자 증가로 A국의 경제성장에 플러스 요인이 된다", "다른 나라 통화들에 비해 A국의 통화가치가 하락한다는 것은 다른 나라들의 입장에서는 A국에서 생산되는 상품 또는 주식 등 A국 자산들의 가격이 떨어진다는 것과 같다", "A국 상품의 경쟁력에 비해 A국의 통화가치가 상승하면 국부의 가치 증가로 경제성장에 플러스 요인이 된다", "1997년 IMF 외환위기 전까지만 하더라도, 미국과 일본의 플라자 합의로 한국이 수혜를 입었던 3년(1986~1988년)을 제외하고는, 한국은 수십년간 고질적으로 무역수지 적자를 지속하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "수 많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다. 이른바 전 세계적으로 확산되어 있는 소리없는 전쟁, 환율전쟁이 그것이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '환율의 영향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (497, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자본시장과 거시경제 변수에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시중금리 수준이 높아지면 더 많은 대출이 일어나 시중에 통화량(유동성)이 더욱 풍부해진다", "한국은행은 기준금리 결정, 시중 은행의 지급준비율 결정, 통안채 발행량 조정의 3가지 주요 방법으로 시중 통화량을 조절한다", "실업률은 한 나라의 경제 활동을 포괄적으로 나타내며 1년에 1번 연말에 발표된다", "수많은 국가들이 자국의 통화가치를 강하게 하여 국가의 달러 환산 부(富)가 높아지도록 유도하고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "시중 금리 수준이 높아지면 대출이 줄어들어 시중 유동성이 줄어들게 된다. 실업률은 매월 발표된다. 수많은 국가들이 자국의 통화가치를 약하게 하여 외국 통화의 유입을 유도하는 물밑 정책을 펼치고 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '거시경제변수 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (498, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "행동편향을 통한 실수가 금융투자상품과 서비스를 선택할 때 빈번히 발생하는 이유에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자상품에 대한 경험과 기회가 적다", "많은 금융투자 의사결정이 감정적으로 이루어진다", "투자의사결정의 실수가 있었다면 이를 반복하지 않기 위해 노력한다", "금융투자자들이 금융상품을 복잡하게 여겨 제대로 알고 의사결정을 하려 하지 않는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 금융상품에 대한 학습경험의 기회가 적기 때문에 행동편향에 따라 행동한다.\n지문 2의 경우, 많은 금융투자 의사결정이 비용 대비 효용을 따지기보다는 스트레스, 걱정, 손실이나 후회에 대한 두려움에 떠밀려 의사결정을 한다.\n지문 3의 경우, 금융소비자가 행동편향에 따라 투자의사를 결정하면 혹독한 대가를 치룬 후 후회하는 모습을 반복할 수 있다. 이런 투자의사결정의 실수를 극복하기 위해서 내가 가진 행동편향을 이해할 필요가 있다.\n지문 4의 경우, 대부분의 금융투자자들은 금융투자상품을 복잡하게 여겨서 제대로 알고 의사결정을 하려는 시간과 노력을 기울이지 않고 행동편향에 따라 선택한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 발생이유 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (499, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "다음 신문 기사의 내용을 통해 유추해 볼 수 있는 김씨의 행동편향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["<기사> 투자 경험이 없는 김씨는 어느 날 친구로부터 주식으로 돈을 벌었다는 얘기에 귀가 솔깃했다. 좋은 종목 있으면 추천해달라고 친구에게 요청했고, 친구는 증권 브로커 B씨를 소개해주었다. 이씨는 전문가처럼 주식에 관해 유창하게 설명해 주었고 바이오 종목 1개를 추천했다. 김씨는 1천만원을 투자했고, 이익이 났다. 김씨는 투자에 대해 자신감이 붙었고 금액도 1억원으로 늘리고 투자종목도 늘렸다. 그러나 몇 개 종목에서 손해가 나기 시작했고 원금까지 손실을 입기 시작했다. 덜컥 겁이나 이제라도 그만해야 싶었지만 손실이 커져 그만할 수 없었다. 급기야 원금을 반이나 날린 후에 진작 그만두었어야 한다고 후회했다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향", "사후확증 편향", "손실회피 편향", "자기과신 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 투자 경험이 없는 김씨가 친구가 수익이 났다는 얘기만을 듣고 귀가 솔깃해 추천을 권유한 것은 군중심리 편향에 가깝다.\n지문 2의 경우, 사후확증 편향은 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이미 그렇게 될 줄 알고 있었던 것처럼 믿게 되는 오류로 상기 기사내용과는 거리가 멀다.\n지문 3의 경우, 원금까지 손실을 입기 시작한 이후 그만하지 못한 것은 손실을 회피하려는 편향 때문이다.\n지문 4의 경우, 투자에 대한 경험이 없었으나 초기 투자 성공으로 투자에 대한 자신감이 붙어 투자 금액을 쉽게 늘린 것은 자기과신 편향의 영향으로 볼 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (500, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "한 설문조사 결과 운전자의 88%가 자신이 평균보다 운전을 잘한다고 생각하며 그 중 80% 이상이 자신의 운전실력이 상위 30% 안에 든다고 생각한다고 한다. 이렇듯 사람들은 본인에 대한 과도한 믿음, 즉 자기과신을 하는 경향이 강하다. 이러한 자기과신 편향이 투자의사결정에 영향을 주었을 때의 반응으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그 팀장 설명도 그럴싸한데 다가 지난 번에도 그 말대로 되더라니까. 이번에도 맞을 거야", "처음에는 조금 불안하기도 했는데 매달 꼬박 꼬박 수익금이 들어오더라. 그래서 더 돈을 넣으려고. 너도 해봐", "낮은 금리의 퇴직연금 포트폴리오를 조정해야지 조정해야지 생각만하고 계속 그대로 놨둬 기회를 놓쳐버렸어", "내가 1년 간 지켜봤는데 이 주식은 계속 오르지 않고 올랐다가 내려갔다 반복만 해. 이 정도 내려가면 다시 올라가니까 지금 사야해"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 자기자신 뿐만 아니라 타인의 말을 그대로 믿고 행동하는 것도 그런 대상을 내가 잘 골랐다는 자기과신 편향의 영향이다. \n지문 2의 경우, 폰지 사기의 전형적인 수법으로 몇번의 수익금 수령이 초기 불안을 없애고 자기과신을 통해 더 많은 돈을 투자한다.\n지문 3의 경우, 현상유지 편향이 영향을 주었을 때의 행동으로 조정할 의사가 있거나 조정해야 할 상품을 그대로 유지하게 한다.\n지문 4의 경우, 자기과신 편향은 이렇다 할 대표성이 없는 몇차례의 경험을 통해 더욱 단단해져 이번에도 지난번처럼 될 거라 믿고 행동하게 만든다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (501, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "다음 투자 격언은 어떤 행동편향에 대한 주의를 주고 있는가?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["우리가 위험에 빠지는 이유는 무언가를 몰라서가 아니다. 확실히 안다고 착각해서다. - 마크 트웨인"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "손실회피 편향", "자기과신 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급하는 것을 말한다.\n지문2의 경우, 사람들이 합리적이거나 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는 편향이다.\n지문3의 경우, 사람들이 이득보다 손실이 더 크게 느껴, 이익 증가보다 손실 증가에 민감하게 느끼는 것을 말한다.\n지문4의 경우, 사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이 믿고 행동한다. 이러한 자기과신 편향은 자신이 가지고 있는 능력이나 정보, 지식 수준을 실제보다 더 높거나 정확하다고 믿게 만들어 섣불리 위험한 투자를 할 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (559, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인전문투자자에 대한 설명으로 <보기>의 (   ) 안에 들어갈 단어로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["개인전문투자자는 최근 5년 중 1년 이상 금융투자상품 잔고가 (      ) 이상일 것을 필수요건으로 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["1천만원", "3천만원", "5천만원", "1억원"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문3 자격요건은 필수요건과 선택요건이 있다. 필수요건은 금융투자상품(주식, 펀드 등)잔고 5천 만원(최근 5년 중 1년 이상 금융투자상품잔고) 이상이다. 금융투자회사별 잔고 합산도 가능하다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (502, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "군중심리는 주식시장이 급격히 오르거나 떨어질 때 자주 작동하여 시장을 과열시키거나 쉽게 얼어붙게 한다. 이러한 군중심리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["남들이 주식으로 돈을 벌었다는 소식이 들리면 소외감이 커져 군중심리가 발동한다", "경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 현혹되지 않는다", "군중심리에 빠지는 것은 다른 이들과 다른 결정을 내리는 것보다, 그들과 함께하는 것이 더 마음이 편하고 쉽기 때문이다", "사회에 순응하고 싶어하는 인간의 본능 때문에 대다수와 다르게 행동함으로써 군중에서 소외되고 싶지 않아 군중심리가 생긴다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "군중심리의 기저에는 사회에 순응하고 군중에서 소외되고 싶지 않은 인간의 본능이 깔려 있다. 이에 따라 남들이 투자를 통해 돈을 벌었다거나 하면 거기서 소외되지 않게 투자에 뛰어든다. 경험이 적은 금융투자자이거나, 자신감이 없는 금융투자자일수록 군중심리에 더 취약하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (503, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "심리학적 분석에 따르면 인간의 사고 체계는 두가지 시스템에 의해 작동한다. 빠르게 직관적으로 생각하는 시스템1과 느리고 노력이 필요한 시스템2가 그것이다. 사람들은 금융투자 의사결정 시 후자를 사용한다고 착각하지만 전자를 사용해 결정하는 경우가 많다. 금융투자 시 시스템1이 이익과 손실상황에서 보이는 성향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["이익상황에서는 위험회피성향을, 손실상황에서는 위험회피성향을 보인다", "이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보인다", "이익상황에서는 위험보유성향을, 손실상황에서는 위험보유성향을 보인다", "이익상황에서는 위험보유성향을, 손실상황에서는 위험회피성향을 보인다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "향후 주식시장에 대한 긍정적인 전망이 우세할 때 이미 수익이 난 계좌를 보유한 투자자는 긍정적인 전망에도 불구하고 이익을 실현하고 싶어한다. 미래의 불확실성이 싫기 때문에 당장의 이익을 챙겨야 할 것 같아서이다. 반대로 주식시장에 대한 부정적인 전망이 우세 할 때 이미 손실이 난 계좌를 보유한 투자자는 시장상황이 부정적이어서 지금의 손실규모가 더 커질 수 있음에도 사람들은 손실을 확정 짓지 않고 위험을 보유하려고 한다. 이익상황에서는 위험회피성향을, 손실상황에서는 위험보유성향을 보이고 있는 것이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '손실회피성향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (504, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "합리적인 투자자라면 시장 상황이나 미래 가치 등을 판단하여 보유하고 있는 주식의 매도 여부를 결정한다. 그러나 일반적으로 그 종목의 적정가치를 따져보려 하지않고 자신이 구입한 가격만을 최우선 결정요인으로 판단하는 경우가 많다. 이러한 편향을 일컫는 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향", "기준점 편향", "시기추론 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "사람들은 객관적인 판단이 아닌 사실이나 숫자를 기준으로 삼고, 이를 바탕으로 결정을 내리는데 이를 기준점 편향이라한다. 기준점 편향을 앵커링효과라고 도 한다.  앵커링효과에서 앵커(Anchor)는 배를 정박시킬 때 배가 떠내려가지 않도록 하는 닻을 의미하며 앵커링효과는 배를 정박시키는 닻을 내리듯 사람의 생각이나 판단이 그 기준점을 벗어나지 않는 것을 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '기준점편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (505, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융상품 투자 시 ''과거의 운용실적이 미래의 수익을 보장하지 않습니다''라는 문구는 다음 중 어떤 행동편향을 방지하기 위한 것인가?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["손실회피 편향", "사후확증 편향", "매몰비용의 오류", "과거추세지속가정 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "과거의 운용실적이 미래의 수익을 보장하지 않는다는 것은 투자의사 결정을 할 때, 과거의 수익만을 보고 앞으로도 그런 수익이 지속될 것이라고 판단하는 것은 위험하다는 것을 알리는 것이다. 과거의 수익은 미래의 수익을 담보하는 대표성이 있는 것이 아닌데 앞으로도 과거처럼 수익이 날거라고 믿고 행동하는 과거추세지속가정 편향을 막기위한 것이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '과거추세지속가정편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (565, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자회사가 위탁계좌 거래 관련규정에 따라 정기적으로 발송하는 거래내역 등을 수신하는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["우편", "전자우편", "방문설명", "HTS/MTS 직접조회"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "방문설명은 거래내역 등의 통보방법이 아님"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자관리 방법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (506, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자를 하기 전에 빠지기 쉬운 대표적인 행동편향 만을 짝지어 놓은 것으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향, 심적회계, 현재중시 편향", "군중심리 편향, 확증편향, 손실회피 편향", "사후확증 편향, 심적회계, 현상유지 편향", "사후확증 편향, 손실회피 편향, 현재중시 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자를 하기 전에 빠지기 쉬운 대표적인 행동편향은 군중심리 편향(주위에서 투자를 통해 돈을 벌었다는 소식을 들었을 때 투자를 하지않은 자신만 바보가 된 거 같은 기분이 들어 투자시장에 뛰어든다), 심적회계(예상치 못했던 성과급이나 연말정산 후 세금 환급액이 들어오면 없던 돈인 셈치고 평상시 위험하다고 생각했던 주식에 몰빵 투자한다), 현재중시 편향(자신의 미래 재무목표에 맞는 투자가 아니라, 즉각적인 욕망을 충족하려는 충동에 따라 단기 고수익만을 목표로 투자한다)이다.\n손실회피 편향이나 확증편향, 사후확증 편향은 투자 후 빠지기 쉬운 대표적인 행동편향이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (507, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서는 방어형 투자자들이 빠지기 쉬운 대표적인 행동편향 중 하나를 설명하고 있다. 이에 해당하는 행동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["저금리시대가 지속되면서 합리적인 금융투자를 통해 미래 자산을 만들어 가야하지만 위험 추구 성향이 낮은 방어형 투자자들은 기본적으로 손실을 두려워해 투자를 꺼려한다. 이들도 저금리 기조가 고착화되어 안정형 상품으로는 미래에 자신이 쓸 자산을 만들기 어려운 것을 인정하면서도 투자에는 여전히 소극적이다. 이는 변화를 좋아하지 않고 불확실한 것을 꺼리기 때문이다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "도박사의 오류", "현상유지 편향", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "현상유지 편향의 주된 원인은 변화에 대한 거부감이다. 이런 편향은 금융투자 관성으로 이어지는데, 저금리 시대에도 과거의 관행을 그대로 유지하며 안정형 상품을 유지하고 있는 것이 대표적인 사례이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (508, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "형동경제학에서 기존 전통경제학과 거리를 두고 있는 내용에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인간의 선택이 온전히 합리적이지 않다고 주장한다", "불확실한 상황에서 인간의 의사결정은 여러 심리적 편향에 흔들린다", "인간은 돈과 관련된 선택과 행동에서는 합리적이지 않은 모습이 더욱 두드러진다", "모든 경제주체는 처한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "전통적인 주류 경제학은 우리가 경제적 또는 재무적 의사결정을 할 때 ‘인간은 합리적’이라는 가정에서 출발한다. 모든 경제주체는 다양한 환경과 자신이 처한 상황을 이해한 후 이성적인 선택을 내린다는 것이다. 반면 행동경제학은 그 간의 전통 경제학과 달리 인간의 선택이 온전히 합리적이진 않다고 주장한다. 더욱이 돈과 관련된 선택과 행동에서는 이런 합리적이지 않은 모습이 더욱 두드러진다. 행동경제학은 사람들의 선택이 앞뒤가 맞지 않고 목적에도 부합하지 않는 등 일관성이 없는 경우가 빈번한 것을 실험을 통해 확인했다. 불확실한 상황에서 인간의 의사결정은 어림짐작이나 주먹구구식으로 여러 심리적 편향에 흔들린다는 것을 밝혀냈다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동경제학 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (509, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "행동경제학의 대표적 이론으로, 주식시장에 대한 긍정적인 전망이 우세하여도 이미 수익이 난 계좌의 이익을 실현하고 싶어하거나, 반대로 주식시장에 대한 부정적인 전망이 우세하여도 이미 손실이 난 계좌를 그대로 보유하고자 하는 편향을 설명하는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전망이론", "게임이론", "기대효용이론", "기대-가치이론"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 전망이론은 행동경제학자 대니얼 카너먼과 아모스 트버스키가 주장한 이론으로 사람들이 이익보다 손실에 더 민감하여 이익상황에서는 안전한 선택을, 손실상황에서는 위험한 선택을 하는 것을 밝혀냈다. \n지문2의 경우, 게임이론은 여러 경제주체가 서로를 이기기 위해 경쟁하는 상황에서 그 결론이 어떻게 도출되는지를 설명하기 위한 이론이다. ''게임''이란 이름이 붙은 것은 경쟁자들이 서로를 이기기 위해 전략을 구사하는 상황이 승부를 내는 게임을 연상시키기 때문이다.\n지문3의 경우, 기대효용이론은 행동이 초래하는 결과가 불확실한 상황에서 합리적인 경제주체의 판단은 결과에 관한 효용의 기대치에 입각하여 이루어진다는 이론이다.\n지문4의 경우, 기대-가치이론은 기대와 가치가 동기의 결정요인이라고 가정한다. 어떤 행동을 하는 동기가 생기는 것은 기대요인과 가치요인이 함께 있어야 한다는 것이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전망이론 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (510, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자를 하다보면 큰 경제 이슈가 발생했을 때, 미디어에 수많은 소위 전문가라는 사람들이 나와 이런 일이 일어날 줄 이미 오래전부터 알고 있었다는 듯이 이렇게 된 원인을 아주 논리적으로 해석하고 설명하는 것을 볼 수 있다. 이처럼 어떤 일이 발행하였을 때 이미 발생한 일의 결과를 알고 나면 그 일이 벌어질 수 밖에 없었고 이전부터 그렇게 될 줄 알고 있었던 것처럼 믿게 된다는 것을 설명하는 행동편향은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "군중심리 편향", "현재중시 편향", "사후확증 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 기준점 편향은 앵커링 효과라고도 하는데, 배가 떠내려가지 않도록 닻을 내리듯이 생각이나 판단이 기준점을 벗어나지 않는 말한다.\n지문2의 경우, 군중심리 편향은 군중심리에 빠져 다른 금융투자자들의 행동이 합리적인지 따져보지도 않고 막연히 그 행동을 따라하는 것을 말한다.\n지문3의 경우, 현재중시 편향이란 미래에 비해 현재를 중시하는 것으로 즉각적인 욕망을 충족하려는 충동에 따라 행동하는 것을 말한다.\n지문4의 경우, 사후확증 편향은 투자 선택에 있어서 마치 내가 이럴 줄 알았다는 착각을 불러 일으킨다. 그러나 합리적인 투자를 위해서는 그것이 사전이 아닌 사후 진단이라는 것을 기억해야할 필요가 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (511, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자기과신 편향을 가지고 있는 사람들이 이에 따른 투자 실패 경험이 있을 때에도 같은 실수를 반복하는 이유를 설명하는 편향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["내 잘못이 아니라 시장 탓으로 돌린다", "내 잘못이 아니라 상황 탓으로 책임을 회피한다", "투자 실패한 경험은 잊고 성공한 경험만 기억한다", "위험요소를 상대적으로 높게 평가한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "사람들은 자신의 능력 또는 판단이 스스로에게 유리한 결과를 가져다 줄 것이라고 믿고 행동한다. 이를 ‘자기과신 편향’이라고 한다. 특정 금융투자상품 또는 투자 정보에 대한 자신의 판단이 다른 사람들의 평가나 판단보다 더 정확하다고 생각하고 과몰입할 뿐 아니라 위험요소는 상대적으로 낮게 평가한다. 투자 위험을 가중시키는 일이다. 사람들이 투자 실패 경험을 반복하는 이유는 지나친 자기과신으로 실수를 외부 탓으로 돌리거나 기억 자체를 잊어버리려고 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자기과신편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (512, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 공통으로 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["2001년 바버와 오딘 교수는 주식투자에 있어서 남녀간 차이가 뚜렷이 존재한다는 연구 결과를 발표했다. 이 연구를 통해 남자가 여자보다 (     )편향이 높고, 주식투자에 있어서 남자는 (     )편향으로 인한 과다한 거래로 여자보다 수익률이 낮다고 밝혔다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기과신", "시기추론", "매몰비용", "현재중시"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "UC-Davis 바버(Brad Barber) 교수와 UC-Berkeley의 오딘(Terrance Odean) 교수는 1991년부터 1997년까지 6년간 무려 35,000 가구의 주식계좌를 살펴본 후 주식투자에 있어서 남녀간 성(性)적 차이가 뚜렷이 존재한다는 연구 결과를 발표했다.\n남자가 여자보다 자기과신 편향이 높아 주가의 상승과 하락을 예상할 수 있다는 확신으로 빈번한 매매를 했을 확률이 높다. 이로 인한 결과는 거래비용 증가와 수익률 악화로 이어질 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (513, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "군중심리 편향은 투자 뿐만 아니라 소비 등 다양한 분야의 인간 행동에서 일어난다. 군중심리에서 비롯된 행동을 설명하는 용어로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["모방소비", "편승효과", "스노브 효과", "밴드왜건 효과"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1의 경우, 모방소비란 다른 사람의 소비 행동을 그대로 따라하는 소비를 말하는 것으로 군중심리에 영향을 받는다.\n지문2의 경우, 편승효과란 소비자가 어떤 재화를 소비할 때, 다른 소비자들이 많이 소비하는 소비 형태를 따라감으로써 수요가 증가하는 현상을 뜻하는 경제학 이론으로 군중심리의 한 형태이다. 지문3의 경우, 스노브 효과(snob effect)는 다른 사람들이 어떤 상품을 많이 소비하고 있기 때문에, 자신은 그 재화의 소비를 중단하거나 줄이는 것을 말하는 것으로 군중심리에 반대되는 개념이다. 지문4의 경우, 밴드왜건 효과는 서커스나 퍼레이드 행렬의 선두에서 요란한 퍼포먼스를 펼치며 행렬을 인도하는 마차(밴드왜건, Bandwagon)의 뒤를 따라 사람들의 긴 행렬이 줄줄이 이어지는 모습을 빗대어 표현한 것으로 군중심리를 설명한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (514, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "사후확증 편향은 인간이 기억을 재구성하는 특성 때문에 발생한다. 사람들의 기억을 되돌아 볼 때 불완전한 부분을 자신이 믿고 싶은 것으로 메꾸는 경향이 있는 것이다. 금융투자시 이러한 사후확증 편향에 빠지지 않기 위한 태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자 과정에서 자신이 저지른 실수를 인정한다", "금융투자에서 필요한 것은 자신감이라는 것을 명심한다", "투자 과정에서 있었던 실수를 통해 배우려는 태도를 취한다", "금융투자시 나의 선택이 틀릴 수 있음을 받아들이고 대비책을 세워 놓는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "사후확증 편향에서 벗어나기 위해서는 투자 과정에서 자신이 저지른 실수를 인정하고 실수를 통해 배우려는 태도가 필요하다. 또한 나의 선택이 틀릴 수 있음을 받아들이고 대비책을 세워 놓을 필요가 있다. 금융투자자들의 지나친 자신감은 반복된 실수를 가져올 수 있다는 것을 명심하고 자신의 장점과 단점을 현실적으로 직시하는 노력이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '사후확증 편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (515, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "각 행동편향 및 이에 빠지지 않기 위한 행동을 짝지은 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["손실회피 편향: 이익과 손실을 똑같은 가치로 평가하려고 지속적으로 노력한다", "군중심리 편향: 지나친 자신감은 반복된 실수를 가져올 수 있다는 것을 명심한다", "심적회계: 돈에 관한 행위를 할 때는 사용처에 상관없이 동일한 잣대를 가지려고 노력한다", "기준점 편향: 처음 산 가격이나 초기에 알게 된 정보에만 의존하지 않고 다양한 기준으로 투자 대상을 살펴보려 노력한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문2의 경우, 사후확증 편향에 빠지지 않기 위한 행동이다. 군중심리에 빠지지 않기 위해서는 군중에서 한걸음 떨어져 다른 금융투자자들이 왜 그런 행동을 보이는지 충분히 따져 본 후 선택하는 태도가 필요하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (516, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "각 행동편향 및 이에 빠지지 않기 위한 행동을 짝지은 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향 : 늘 반대 의견을 구하려고 노력한다", "시기추론 편향 : 보유한 포트폴리오의 점검을 통해 부적한 부분이 있는지 파악하고 이를 방치하고 있었는지 판단한다", "매몰비용의 오류 : 투자가 잘못되어 가고 있다는 것을 발견했다면 과거에 들어간 비용에서 벗어나 합리적인 결정을 내리려고 노력한다", "도박사의 오류 : 독립적인 확률적 사건에 섣부른 기대를 반영하여 인과관계가 있을 것으로 보고 의사결정을 하는 행동을 삼가해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문2의 경우, 현상유지 편향에 빠지지 않기 위한 행동이다. 시기추론 편향에 빠지지 않기 위해서는 앞으로의 10년을 생각하면 아주 멀게 느껴지지만, 지난 10년을 생각해보면 똑같은 10년이지만 꽤 빨리 흘렀다고 느낀다. 이렇듯 자신의 미래를 역방향으로 예측하면 그 시간을 짧게 느끼고 미래에 대비하여 장기 투자해야 한다는 필요성도 더 절실히 느낄 수 있다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (517, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 설명하는 행동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["원금 손실의 위험 때문에 공격적인 투자를 못하던 보수적인 투자자도 투자를 통해 얻은 수익금만을 투자할 때는 태도가 달라진다. 수익금은 어차피 내 돈이 아니었으니 날려도 된다는 태도로 매우 공격적인 투자를 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "손실회피 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "심적회계에 대한 설명이다.  사람들은 돈에 대한 기준이나 가치를 모두 같은 것으로 생각하지 않고 각각의 용도에 따라 다르게 생각하며 취급한다. 이를 ‘심적 회계(Mental Accounting)’라고 하는데, 사람 마음 속에서 돈에 대한 기준이 항목에 따라 다르다는 것이다. 어디에 쓰는 돈이건 본질적으로 가치가 같다는 점에서 편향이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '심적회계편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (546, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "증권회사의 비대면 계좌를 개설하기 위해 필요한 것으로 가장 거리가 먼것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["본인 명의의 스마트폰", "주민등록증 또는 운전면허증", "실명확인이 가능한 본인명의 금융기관 계좌", "증권회사 HTS(Home Trading System)가 설치된 PC"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 본인명의의 스마트 폰이 필요하다.\n지문2 신분증은 실명확인을 위한 신분증 촬영을 위해 필요하다.\n지문3 실명확인을 위해 필요하다.\n지문4 HTS는 개설된 계좌를 사용하여 거래에 필요한 프로그램이다. 비대면계좌를 개설하기 위해 반드시 필요한 것은 아니다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '계좌개설절차 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (518, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 A에게 예상되는 행동편향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 올해 유망 종목이라고 해서 샀는데 오르긴 커녕 아직도 20%나 손해가 나있어.", "B : 이제라도 정리해야 되는 거 아냐?", "A : 아 몰라~. 어여튼 원금 회복되기 전에는 못팔아. 지금도 돈생기면 조금씩 사고 있는데 언젠가는 오르겠지. 아우 내가 정말 미쳐!"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기준점 편향", "현재중시 편향", "손실회피 편향", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 투입 원금이 기준점으로 잡히는 기준점 편향 때문에 주식을 매도하려고 할 때 현재 상황이나 미래 가치 보다 구입했던 가격이 최우선 기준점이 되어 매도 가격에 영향을 미친다.\n지문 2의 경우, 현재중시 편향이란 미래에 비해 현재를 중시하는 것으로 즉각적인 욕망을 충족하려는 충동에 따라 행동하는 것을 말하는 것으로 해당 대화 내용과는  거리가 멀다.\n지문 3의 경우, 사람들은 이익보다 손실이 더 크게 느끼는데 이에 따라 손실 난 투자를 그대로 유지하려고 하는 경향이 있다.\n지문 4의 경우, 매몰비용의 오류는 가지고 있는 주식을 보유할지, 매각할지를 결정할 때 주식의 가격이 앞으로 어떻게 될 지를 고려하는 것이 아니라 들어간 돈 즉 매입가를 가장 중요시 여기는 편향을 말한다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현재중시편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (519, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 투자자들에게 영향을 미치는 행동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 내가 최근에 소개 받은 단톡 주식카페가 있는데 거기서 추천한 종목이 하나 있는데 같이 투자할래?", "B : 정말? 핫한 주식 맞아?", "A : 어 카페 회원들 다 극찬하고 있어! 지금도 좀 올랐는데 앞으로 더 좋을 거래!", "B : 그래? 내가 아는 사람도 대박 종목 하나 잡아서 크게 벌었다던데 그런 종목인가 보다. 하자! 하자!", "A : 넌 친구 잘 둔지 알아"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["심적회계", "기준점 편향", "군중심리 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "군중심리에 빠져 다른 투자자들의 행동이 합리적인지 따져보지도 않고 막연히 그 행동을 따라하는 것을 말한다. 투자 초보자들이 겪는 가장 일반적인 행동편향 중 하나이며, 집단적인 관심이 커질 경우 버블을 만들기도 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '군중심리편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (520, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 B씨에게 예상되는 행동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 그 때 샀던 바이오주 지금도 잘나가? 그 대표라는 사람 말대로 신약 허가도 받았고?", "B : 아니 지금은 좀 떨어졌어.", "A : 왜? 신약 허가 못 받았어?", "B : 응. 그래도 괜찮아 조만간 다시 진행할꺼래.", "A : 그 대표 말과 달랐구나. 큰소리 치더만.", "B : 야 니가 뭘 안다고 그래~ 원래 다 그런 거거든 그리고 이 회사는 정말 믿을 만한 회사야. 알지도 못하면서."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증 편향", "시기추론 편향", "현상유지 편향", "도박사의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "확증편향에 빠진 금융투자자는 기존의 믿음에 부합하는 정보만 찾고 그 정보를 더 중요하게 여기고, 모순된 정보의 중요도는 무시하거나 임의로 낮춘다. 또 정보를 자기 믿음에 맞게끔 재해석하기도 하는데, 심지어 모순된 정보 마저도 아전인수식으로 자기 믿음에 유리하게 해석한다. 자기 믿음에 부합하는 정보만 기억하고, 모순된 정보는 잊어버리거나 자기 믿음에 부합한다고 기억해 버리는 것이다. 이는 상황을 직시하지 못하게 하여 그릇된 선택을 하게 만들 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '확증편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (521, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 B씨에게 예상되는 행동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["A : 주식투자는 장기로 해야 한다는데 10년, 20년 후 장기적으로 괜찮을 분야가 뭐가 있을까?", "B : 야 장기는 무슨~, 당장 돈 쓸 일이 태산인데 그래 가지고 어느 세월에 돈벌겠냐? 빨리 오를 종목을 찾아야지! 크게 오르는 종목들이 즐비하고 당장 돈 쓸 일이 태산인데 말이야.", "A : 그런가? 장기투자를 해야 안정적이라고 해서 말이야.", "B : 야 환매를 해서 이익을 얻지 못하려면 뭐 하러 투자를 하냐!"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증 편향", "현재중시 편향", "현상유지 편향", "사후확증 편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "현재중시 편향에 빠진 투자자들은 미래에 비해 현재를 중시하여, 즉각적인 욕망을 충족하려는 충동에 따라 행동하려고 한다. 이에 따라 금융투자에서도 당장의 이익만을 추구하는 단기 투자에 그치고 노후 같은 장기 목표에 대한 투자를 등한시 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현재중시편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (522, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 오버나이트 테스트와 관련된 행동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["당신이 보유한 종목들이 투자 손해를 보고 있다. 만약 누군가가 오늘 당신이 보유한 모든 종목을 팔아 현금화해준다면 다음날 그 현금으로 당신이 가지고 있던 그 종목을 다시 매수할 것인가? 만약 다시 매수하지 않을 거라면 그런 종목을 지금은 왜 그대로 가지고 있는가? 오버나이트 테스트는 내가 보유하고 있는 종목들의 가치가 오르거나 내린 주식이 아닌 현금일때도 그 주식을 매수할 것인가를 생각해보고 계속 보유여부를 합리적으로 따져 보는 것이다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["군중심리 편향", "현재중시 편향", "시기추론 편향", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "매몰비용의 오류에서 벗어나는 방법은 투자가 잘못되어 가고 있다는 것을 발견했다면 재빠르게 정신을 차리고 되돌아서는 것뿐이다. 매몰비용을 포기하고 합리적인 결정을 내려야 하는데 투자에서는 오버나이트 테스트를 해보는 것도 좋은 방법 중 하나이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '매몰비용편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (523, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 D씨에게 예상되는 핸동편향으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["내가 몇 달간 쭉 지켜 지켜봤는데 이 주식은 3일 연속 하락하면 그 다음에는 꼭 오르더라고 그래서 지금 사야해"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["현상유지 편향", "손실회피 편향", "도박사의 오류", "매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "도박사의 오류란 서로 독립적인 확률적 사건이 서로 확률에 영향을 미친다는 착각에 기인한 오류이다. 주가가 오르거나 내릴 확률은 반반일 뿐인데 며칠 연속 떨어졌으니 오를 때가 되었다거나 반대로 며칠 연속 올랐으니 오늘은 떨어질거라 판단하는 것이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '도박사의오류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (524, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "현상유지 편향이 영향을 미친 사례로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["퇴직연금(DC)의 경우 최초 운용지시를 한 상품으로 그대로 두거나 방치하는 경우가 많다", "퇴직을 앞둔 직장인들의 가장 큰 후회 중 하나는 노후 준비를 소홀하게 했다는 것이다", "손실이 난 투자 포트폴리오를 그대로 가져가면서, 그 손실이 늘어나도 팔지 않는 경향이 있다", "저금리시대가 지속되고 있는데도 가계금융 자산의 70% 이상이 원리금보장형으로 그대로 운용되고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "현상유지 편향은 변화를 좋아하지 않고 불확실 것과 마주하는 것을 꺼려하는 행동 상태를 말한다. 금융회사가 추천한 상품을 가입한 후 재평가하지 않고 처음 그대로 방치하거나, 손실난 포트폴리오를 귀찮아서 그대로 가져가기도 한다. \n지문 2의 경우, 시기추론 편향의 영향으로 사람들은 보통 먼 미래의 일은 추상적이고 낙관적으로 생각하는데 이로 인해 노후 준비도, 장기투자도 쉽게 하지 못한다.\n"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '현상유지편향 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (525, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자를 하고 난 후, 수익이 났을 때 빠지기 쉬운 대표적인 행동편향 만을 짝지어 놓은 것으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자기과신 편향, 현상유지 편향, 확증편향", "자기과신 편향, 손실회피 편향, 확증편향", "손실회피 편향, 현상유지 편향, 확증편향", "자기과신 편향, 사후확증 편향, 확증편향"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자가 성공적이어서 수익이 났다면 앞으로 더 큰 성과를 낼 수 있다는 자신감을 얻는다. 이런 경우 보통 투자 금액이 이전보다 훨씬 더 커진다.(자기과신 편향)\n또, 이런 성과가 나올 수밖에 없었던 이유를 갖다 붙인다. 결과가 이렇게 될 줄 알고 있었다고 느끼며 행동에 확신이 붙는다. 주변 사람들에게 자신의 성공기를 확신에 찬 모습으로 떠벌리고 다닌다.(사후확증 편향) 만일 자신의 선택이 아니라 주변 사람의 추천을 통해 얻은 수익이라면 그 대상에 대한 신뢰가 매우 커져 그 사람 말만 계속 믿고 투자하려 한다.(확증편향)"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (553, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A는 월요일에 OO전자 주식을 매수 하였다. 주식이 결제되어 투자자 A의 계좌로 입고되는 시점으로 가장 적절한 것은? (단, 월요일~금요일 사이에 공휴일은 없는 것으로 가정)", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["월요일", "화요일", "수요일", "목요일"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 매수당일 평가금액이 표시되지만, 결제되어 입고 되지는 않는다.\n지문3 매매가 체결된 주식의 결제시점은 체결일(월요일, D일)로부터 3영업일(D+2일)로 규정되어 있어 수요일이 정답이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (526, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자시 자신의 투자성향을 확인하고 그에 맞게 포트폴리오를 구성하는 것은 매우 중요하다. 공격형 투자자들이 빠지기 쉬운 대표적인 행동편향 만을 짝지어 놓은 것으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["확증편향, 자기과신 편향, 도박사의 오류", "확증편향, 현상유지 편향, 매몰비용의 오류", "손실회피 편향, 도박사의 오류, 기준점 편향", "현상유지 편향, 자기과신 편향, 매몰비용의 오류"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "공격형 투자자들은 투자성향상 주식 같은 위험자산에 큰 고민없이 선뜻 투자한다. 그리고 자신의 선택에 대해 확신을 가지며 자신의 생각과 다른 의견에는 잘 귀기울이지 않는다.(확증편향) 이런 행동이 수익으로 이어졌다면 자신의 투자 감각을 과신하며 모든 게 자신의 능력 때문이라고 확신한다.(자기과신 편향) \n주식투자를 쉽게 생각하며 분산하지 않고 몰빵 투자를 하는 등 더 용감한 투자를 하기도 한다. 다른 사람들이 수익을 내고 있다는 소리를 전해 들으면 그 상품에 대해서도 자산의 능력을 믿고 쉽게 투자를 고려한다.(군중심리 편향) \n만약 성과를 내지 못하고 손실을 본다 해도 손실의 원인을 분석하기보다는 손해를 만회하고자 하는 욕구가 더 강해지며 다음 번에는 실패하지 않을 거라 믿는다.(도박사의 오류)"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (527, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "행동편향을 극복하기 위한 행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 자신이 이성적인 존재임을 믿고 선택한다", "금융투자시 자신이 겪었던 행동편향이 무엇인지 확인한다", "금융투자시 투자 원칙과 기준을 세우고 투자 과정을 객관적으로 기록한다", "금융상품과 서비스에 대한 의사결정을 해야 할 때 행동편향의 영향을 받고 있는지 스스로 점검해 본다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 행동경제학에서는 인간의 선택이 온전히 이성적이지 않고 앞뒤가 맞지 않거나 목적에도 부합하지 않는 등 일관성이 없다고 보았다.\n행동편향을 극복하기 위해서는 행동편향이 무엇인지 인지하고 자신이 겪었던 행동편향을 확인하며, 행동편향에 흔들리지 않도록 투자 원칙과 기준을 세우고 투자 과정을 기록하며, 의사결정시 행동편향을 영향을 받고 있는지 스스로 확인해야 한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (528, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "행동편향 극복을 위한 올바른 자세로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["하이투자태도-하이리턴을 명심한다", "투자과정을 기록하고 모니터링 한다", "과거의 실패는 잊고 새로운 마음으로 투자한다", "자산배분과 리밸런싱 등 투자원칙을 세우고 기록한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문 1의 경우, 기대수익률에 대한 투자 위험을 인지함으로서 자기과신이나 확증편향에 빠진 맹목적인 투자를 지양하게 할 수 있다. \n지문 2의 경우, 투자과정을 기록함으로서 자신의 감정적인 선택의 결과를 확인할 수 있고 이를 통해 반복된 실수를 피할 수 있다.\n지문 3의 경우, 투자는 과거의 실수를 반복하지 않기 위한 리뷰가 필요하다. 새로운 기대만을 가지고 임하는 투자는 실패를 반복할 수 있다.\n지문 4의 경우, 자산배분의 원칙을 세우고 리밸런싱 기준을 통해 투자를 관리하는 것은 행동편향의 영향에서 벗어나 초심을 유지할 수 있게 도와준다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (529, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "행동편향 극복을 위한 ''투자과정 기록 및 모니터링''에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기억은 주관적이어서 왜곡되기도 하지만 기록은 객관적이다", "어떤 생각으로 투자 대상을 선택하였고 어떻게 관리할 계획인지 기록한다", "투자결과 중 성공한 투자만을 골라 이유를 평가하고 기록하여 성공전략에 집중한다", "기록내용을 모니터링 하여 다음 투자시 자신의 행동편향에 휘둘리지 않고 합리적인 의사 결정을 하는데 이용한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "자신의 투자 경험을 기록하는 것은 반복된 실수를 피하는 가장 효과적인 방법이다. 기억은 지극히 주관적이어서 왜곡되기도 하지만, 이에 비해 기록은 객관적이다. 자신이 어떤 생각으로 투자 대상을 선택하였고 어떻게 관리를 할 계획인지를 기록한다. 성공한 투자의 성공 이유는 물론 실패한 투자의 실패 원인이 무엇이었는지 냉정하게 평가하고 기록해 둔다. 이는 다음 투자시 자신의 행동편향에 휘둘리지 않고 합리적인 의사 결정을 하는데 지속적으로 도움을 준다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '행동편향 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (602, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 주식 포트폴리오 운용을 구성하는 목적 및 방법에 관한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 이유는 주식이 가진 높은 내재 변동성에 따른 투자태도를 줄이기 위해서이다", "개인 투자자가 주식 포트폴리오를 구성할 때 단기적인 시장의 움직임보다는 장기적인 관점에서 구성하고 포트폴리오의 변경은 최소화해야 한다", "개인 투자자가 주식 포트폴리오를 구성하는 경우 시장상황이나 대내외 변수와 같은 거시적인 요인들을 배제하고 투자 대상 개별주식의 내재 가치와 시장가격에 의존하는 전략이 필요하다", "개인 투자자가 주식을 활용하여 포트폴리오를 구성할 때 가장 중요한 것은 투자태도 감소 보다는 수익 극대화에 있다고 할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "주식은 대표적인 위험자산으로 높은 수익을 기대할 수 있는 대신 단기적으로 변동성이 매우 크고 가격을 예측하기 어렵다는 특징이 있다. 따라서 투자자는 주식 포트폴리오를 구성할 때 투자태도 관리에 최우선 순위를 두고 단기적으로 시장의 변화에 적극적으로 대응하는 것이 필요하며, 기대 수익률 뿐만 아니라 기업이 가진 내재가치와 함께 다양한 대내외 변수들을 함께 고려하는 것이 필요하다. 결국 개인 투자자가 주식을 활용하여 포트폴리오를 구성하는 이유는 주식이 가진 고유의 내재 변동성과 개별주식 투자에 따른 투자태도를 줄이는데 목적이 있다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (603, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식 포트폴리오를 구성하기 위해 투자자가 수행해야 하는 사전적 분석에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식 포트폴리오를 구성하기 위해 개별주식에 대한 분석이 필요하며, 그 분석 방법은 크게 기본적 분석과 기술적 분석으로 구분한다", "기본적 분석은 주식의 본질가치와 시장가격 간에 괴리가 발생할 수 있다는 것을 전제로 주식의 가치를 분석하는 방법이다", "기술적 분석은 시장 참여자의 패턴을 분석하는 방법으로 과거 주식가격 움직임의 양상이 미래에도 반복된다는 가정에 따라 가치를 분석하는 방법이다", "기본적, 기술적 분석 등 다양한 가치분석을 통해 지속적으로 초과수익을 얻을 수 있으므로 주식 포트폴리오 구성시 최우선적으로 고려해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "일반적으로 개인 투자자의 입장에서 주식분석을 통해 지속적으로 초과수익을 얻는 다는 것은 매우 어려운 일이며, 이는 주식 고유의 높은 변동성과 함께 주식의 가격을 예측한다는 것이 현실적으로 불가능하기 때문이다. 따라서 투자자는 주식포트폴리오를 구성할 때 이러한 기본적, 기술적 분석을 보조적인 활용수단으로 가져가는 것이 필요하다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (604, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자의 주식 포트폴리오 구성 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공격적 투자성향을 가진 투자자라면 성장주에 대한 투자비중이 높은 주식 포트폴리오를 구축하는 것이 적합하다", "안정적 투자성향을 가진 투자자라면 가치주나 배당주에 대한 투자비중이 높은 주식 포트폴리오를 구축하는 것이 적합하다", "공격적 투자성향을 가진 투자자라면 베타전략을 추종하는 주식 포트폴리오를 구축하는 것이 적합하다", "안정적 투자성향을 가진 투자자라면 시장수익률을 추종하는 주식 포트폴리오를 구축하는 것이 적합하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "베타전략은 주가지수 혹은 특정지수를 추종하여 수익을 내고자 하는 운용방식으로 안정적 투자성향을 가지고 있는 투자자가 주식포트폴리오 구축시 활용하기에 적합한 전략인 반면에 알파전략은 벤치마크지수 대비 적극적으로 초과수익을 내고자 하는 운용방식으로 공격적 투자성향을 가지고 있는 투자자가 주식포트폴리오 구축시 활용하기에 적합한 전략이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (605, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "스마트베타전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["로우볼 전략은 주가변동성이 낮은 주식에 집중 투자하여 수익률을 높이고자 한다", "밸류 전략은 기업가치 대비 주가가 낮게 형성되어 있는 기업에 집중 투자하여 수익률을 높이고자 한다", "퀄리티 전략은 재무적으로 우량한 기업에 집중 투자하여 수익률을 높이고자 하는 한다", "모멘텀 전략은 주가가 많이 오른 주식은 팔고, 주가가 많이 떨어진 주식을 집중투자하여 수익률을 높이고자 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "모멘텀 전략은 주가상승세가 강하게 나타나는 주식에 집중 투자하여 수익률을 높이고자 하는 전략을 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (533, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자회사에 해당하는 것으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["증권회사", "선물회사", "자산운용회사", "상호저축은행"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "우리나라 금융회사는 은행, 비은행 금융회사, 보험회사, 금융투자회사, 기타 금융회사로 분류된다. 이중에 금융투자회사는 증권회사, 선물회사, 자산운용회사, 투자자문회사, 부동산신탁회사, 종합금융회사가 있다. 상호저축은행은 비은행 금융회사다.\n지문1 증권회사는 대표적인 금융투자회사다.\n지문2 선물회사는 금융투자업의 투자중개업(주로 선물)을 주로 하는 금융투자회사다.\n지문3 자산운용회사는 금융투자업 중 집합투자업을 주로 하는 금융투자회사다.\n지문4 상호저축은행은 비은행 금융회사로 비은행 금융회사에는 상호저축은행, 여신금융전문회사, 대부업자, 상호금융이 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (534, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자회사의 6가지 기본업무 중 ‘개인투자자가 주식과 채권 등을 사고 팔수 있도록 역할’을 하는 업무로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자매매업", "투자중개업", "신탁업", "투자자문업"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 투자매매업은 금융투자회사가 ‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문2 개인투자자가 투자하기를 원하는 주식, 채권 등을 단순하게 사고 팔 수 있도록 ‘중개’하는 역할이다. 중개는 시장에서 사자와 팔자를 연결하는 기능만 한다는 것이다. 따라서 중개기능을 수행한 금융투자회사에 가격체결 수준을 이유로 문제를 제기 할 수 없다. \n지문3 신탁업은 위탁자가 법률행위에 의하여 자기재산을 수탁자에게 이전하고, 수탁자는 그 재산을 신탁목적에 따라 관리 또는 처분하며, 그 이익을 일정한 수익자에게 귀속시키는 제도다.\n지문4 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자업 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (535, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "집합투자업에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["집합투자업은 펀드운용을 주된 업무로 한다", "자산운용사 중에는 특정 종류의 자산을 사모로만 운용할 수 있는 회사도  있다", "집합투자업을 영위하는 회사들은 회사명에 OO자산운용 또는 OO투자신탁운용 등을 주로 사용한다", "개인투자자 입장에서 볼 때 가입중인 펀드를 어느 자산운용사가 운용하는지는 전혀 중요하지 않다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 집합투자업은 펀드 운용이 주요 업무다.\n지문2 사모전문집합투자기구를 운용할 수 있는 사모전문운용사를 말한다.\n지문3 자산운용사가 주로 사용하는 명칭은 OO자산운용, OO투자신탁운용이다. \n지문4 모든 투자자는 자신이 투자하고 있는 펀드를 운용하는 자산운용사가  어디인지 알고 자산운용사에 대한 평가를 펀드의 평과와 동시에 해야 한다.  펀드투자의 첫걸음은 투자한 펀드를 운용하는 자산운용사에 대한 판단이다.  따라서 전혀 중요하지 않은 것이 아니라, 정말 중요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '집합투자업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (536, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(          )은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자일임업", "투자자문업", "투자매매업", "집합투자업"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자일임업은 특정 투자자로부터 금융투자상품에 대한 투자판단 전부 또는 일부를 일임 받아 투자자별로 구분하여 금융투자상품을 운용하는 업(業)이다.\n지문2 투자자문업은 특정인을 위하여 금융투자상품의 가치 또는 투자판단에 대한 자문을 제공하는 업(業)을 말한다.\n지문3 투자매매업은 금융투자회사가‘회사의 고유재산으로 투자’하는 것을 말한다. \n지문4 집합투자업은 우리가 일반적으로 알고 있는 펀드운용을 하는 것이다. 정확하게는 ‘2인 이상의 투자자로부터 모은 금전 등을 투자자로부터 일상적인 운용지시를 받지 아니하면서 재산적 가치가 있는 투자대상자산을 취득, 처분 그 밖의 방법으로 운용하고 그 결과를 투자자에게 배분하여 귀속하는 것’을 말한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (571, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "포트폴리오 매니저에게는 운용의 목표와 기준이 되는 지표이고, 투자자의 입장에서는 해당 펀드의 운용결과가 기준대비 얼마나 효율적이었는지 판단하는 기준이 되는 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["위험", "수익률", "벤치마크", "미실현이익"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문3 포트폴리오 매니저가 포트폴리오를 운용할 때는 운용의 목표와 기준을 정해서 운용한다. 이때 이 운용의 목표와 기준이 되는 기준지표가 우리가 흔히 이야기하는 벤치마크다. 포트폴리오 매니저에게는 운용의 목표와 기준이 되는 기준지표지만, 투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단 하는 기준이 된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (537, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["금융투자회사가 투자일임업을 수행할 때 사용하는 계좌를 일반적으로 (       )라고 부른다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["DB(Defined Benefit)", "DC(Defined Contribution)", "Wrap Account", "IRP(Individual Retirement Pension)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 확정급여형 퇴직연금(Defined Benefit)은 기존의 퇴직금제도와 유사하게근속연수에 평균임금을 곱하여 퇴직금이 산정되는 구조다. \n지문2 확정기여형 퇴직연금(Defined Contribution)은 퇴직금을 직원의 퇴직연금계좌로 지급하는 구조로 운용수익률에 따라 퇴직금의 규모가 변동한다.\n지문3 Wrap Account = Wrap(감싸다) + Account(계좌) 고객의 자산현황, 투자목표, 위험허용도 등을 반영하여 가장 적합한 포트폴리오를 제공하고 그에 따른 고객의 의사결정에 따라 일임받은 투자권한 내에서 주문집행, 결제 등의 업무를 처리하고 사전에 정한 일정비율의 수수료를 받는 종합자산관리서비스\n지문4 개인형 퇴직연금(Individual Retirement Pension)은 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (538, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "생애 처음으로 투자를 시작하는 개인투자자 A의 행동 중 현행 제도와 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["우체국을 방문해서 주식매매 계좌를 신규개설 하였다", "증권회사에 가서 펀드매매 계좌를 개설하고 펀드를 매수하였다", "증권회사 앱을 다운받아 비대면 계좌를 개설하고 주식을 매수하였다", "은행을 방문해서 개설한 주식매매 계좌에 100만원을 입금하고, 은행직원에게 주식 매수 주문을 하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 우체국에서 주식매매를 위한 위탁계좌를 개설할 수 있다.\n지문2 증권회사는 펀드 판매사로서 펀드 매매를 위한 계좌개설과 펀드 매수를 중개할 수 있다.\n지문3 증권회사의 비대면 채널을 통한 주식 매매 거래의 전형이다.\n지문4 은행은 증권회사를 대신해서 주식매매 계좌의 개설을 대행해 줄 수 있다. 그러나 주식매수 주문은 은행에서는 할 수 없고, 해당 계좌의 원 소속인 증권사를 통해서 매매 주문을 할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융투자회사 종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (539, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A가 온라인에서 본 유사투자자문업자의 행위 중 현행 자본시장법에 비추어 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유사투자자문업자는 모두 불법으로 법적 근거가 없다", "안내문에는 불특정 다수인을 대상으로 발행되는 간행물, 전자우편 등에 의한 조언이 가능하다고 한다", "금융감독원에 신고된 유사투자자문업자는 월 30만원 이하로 일대일 투자자문을 하는 것은 합법적이라고 한다", "자신에게 돈을 맡기면 매월 5%씩 안정적인 수익률을 보장한다는 광고와 함께 자신의 계좌수익률 사진을 보내왔다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 유사투자자문업은 자본시장법 101조(유사투자자문업의 신고)에 따라 금융위원회에 신고한 자는 합법적인 유사투자자문업자다. 관련 규정을 어긴 자만 불법이다.\n지문2 자본시장법 101조(유사투자자문업의 신고)의 규정으로 맞는 말이다.\n지문3 1대1 투자자문은 불법이며 불특정 다수를 상대로 조언을 해야 합법이다.\n지문4 유사투자자문업자의 일임계약은 불법이고, 수익률 약정 등도 과장광고로 금지대상이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '유사투자자문 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (540, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "공모주와 스팩(SPAC)투자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["공모주 청약은 증권회사 계좌없이 은행에서도 할 수 있다", "공모주 청약은 IPO(Initial Public Offering)를 주관하는 주관사나 공동주관사를 통해서만 청약이 가능하다", "스팩이 만기 시점까지 합병하지 못하는 경우에는 투자자에게 투자금의 원금과 이자를 반환하는 수익구조를 가진다", "스팩은 공모를 통해 M&A자금을 먼저 마련하며 상장한 후 다 른회사와 합병하는 것을 유일한 사업목적으로 하는 Paper Company다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 공모주 청약을 위해서는 금융투자회사를 통해서 청약을 하고, 배정받는 주식을 보유하거나 매매를 통해 이익을 얻을 수 있다. 따라서 증권회사의 계좌는 반드시 있어야 청약이 가능하다.\n지문2 주관사나 공동주관사를 통해서만 해당 공모주의 청약을 할 수 있기 때문에 해당 공모주를 주관(또는 공동주관)하는 증권사의 계좌가 필요하다.   \n* IPO(Initial Public Offering) 또는 상장(Listing) : 기업이 발행한 주권이 한국거래소 (KRX)가 정하는 요건에 따른 증권시장에서 거래가 될 수 있는 자격을 부여 받는 것\n지문3 스팩의 큰 특징중의 하나로 합병이 불발될 때는 원금과 이익금을 돌려받는 구조다.\n지문4 스팩은 합병을 목적으로 하는 명목상의 회사다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '공모주 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (541, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권 투자를 원하는 개인투자자 A가 하는 행동으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["B은행 채권을 직접 매수하기 위해 B은행에 예금계좌를 개설했다", "증권회사 영업점을 방문해서 만기가 2년 남은 B은행 채권을 매수했다", "장내 국채시장에 들어가서 30년 만기 국채를 500만원씩 두 번 분할 매수했다", "증권회사 HTS를 통해 장내 채권시장에서 장외채권인 사모사채 1억원을 매수하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 채권매매를 직접하기 위해서는 증권회사의 위탁계좌를 개설해야 한다. 은행을 방문해서 신탁을 통해 매매를 지시할 수는 있으나, 이때도 예금계좌로 활용할 수는 없다.\n지문2 증권회사 영업점을 활용한 장외시장을 통한 채권매매의 전형적인 경우다.\n지문3 장내 국채시장은 국채전문딜러로 지정된 증권회사, 기관만이 참여 가능한 시장이다. 개인이 참여할 수도 없고 거래 단위로 주로 100억 단위\n지문4 증권사 HTS로 거래하는 장내 채권시장은 상장채권만을 거래할 수 있고 사모사채인 장외채권은 매매할 수 없다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권거래 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (542, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드에 투자를 원하는 투자자 A가 알고 있는 펀드에 대한 설명으로 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드 기준가를 산출하는 등 신탁 회계를 담당하는 곳이 사무수탁회사다", "집합투자기구 평가회사를 통해 펀드 평가정보를 얻을 수 있다", "펀드는 판매하는 곳에서 직접 운용하기 때문에 어느 금융회사에서 가입하는지가 아주 중요하다", "펀드는 운용재산을 보관하는 수탁회사가 별도로 있어 자산운용과 자산보관이 분리되어 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 사무수탁회사는 펀드회계를 담당하는 회사다.\n지문2 펀드 평가사를 통해 객관적인 펀드 정보를 얻을 수 있다.\n지문3 펀드는 가입을 하는 판매사와 운용을 하는 운용회사가 분리되어 있다. 가입하는 곳에서 운용한다는 것은 틀린 말이다.\n지문4 수탁회사의 기능에 대한 올바른 설명이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (543, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(       )는 Wrap Account의 일종으로 하나의 계좌에서 다양한 일임운용서비스를 통합적으로 제공하는 금융투자상품이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["UMA(Unified Managed Accounts)", "CMA(Cash management account)", "ELS(Equity Linked Securities)", "DLS(Derivative Linked Securities)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 UMA(Unified Managed Accounts)에 대한 설명이다.\n지문2 CMA(cash management account) 어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD), 국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품이다. \n지문3 ELS(Equity Linked Securities) 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자일임업 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (544, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A의 거래형태에 대한 설명으로 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["증권회사 MTS로 위탁계좌에 있는 돈으로 ETF를 매수하였다", "ETF 매수를 위해 은행을 방문하여 특정금전신탁에 가입하여 운용지시를 하였다", "은행에서 특정금전신탁으로 매수하는 ETF나 증권회사를 통해 매수하는 ETF는 동일한 것으로 매매비용도 동일하다", "증권회사를 통해 ETF를 매매할때는 실시간 매매가 가능하지만 은행의 특정금전신탁을 통한 ETF매매는 계좌별 실시간 매매는 어렵다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 증권회사 위탁계좌와 MTS를 활용한 직접매매형태다.\n지문2 ETF에 투자하는 특정금전신탁에 가입하는 형태로 은행의 신탁을 통해서 ETF를 거래할 수 있는 방법이다.\n지문3 특정금전신탁을 통해서 매매하던지 직접 매매하던지 매매하는 ETF는 동일하다. 그러나 투자비용에서는 위탁계좌를 통한 매매와 특정금전신탁을 통한 매매는 큰 차이가 있다. 특정금전신탁이 수취하는 신탁보수가 위탁계좌에는 없고, 매매수수료도 차이가 난다.\n지문4 증권회사를 통한 ETF매매는 한국거래소에 직접 주문을 내지만, 특정금전신탁을 통한 매매는 고객의 운용지시와 특정금전신탁의 주문이 증권사를 통해서 이루어 지기 때문에 아무리 빨라도 실시간 매매라고 할 수는 없다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (545, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용으로 적합한 것만을 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉠, ㉣", "㉠, ㉡, ㉤", "㉡, ㉢, ㉣, ㉤", "㉠, ㉡, ㉢, ㉣, ㉤"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1, 지문2, 지문3, 지문4 ‘금융상품 한눈에’ 에서는 예금과 대출, 연금저축, 보험, 펀드 모두를 한 번에 비교할 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '비교공시 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (547, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식매매를 위한 대기자금도 단기 실세금리 수준의 이자율을 받을 수 있도록 비대면 주식계좌 개설시 함께 계좌를 개설하는 단기 금융투자상품은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ELS(Equity Linked Securities)", "CMA(Cash management account)", "DLS(Derivative Linked Securities)", "IRP(Individual Retirement Pension)"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 ELS(Equity Linked Securities)는 주가지수 또는 특정주식가격의 변동과 연계되어 수익률이 결정되는 증권\n지문2 CMA(cash management account)어음관리계좌 또는 종합자산관리계좌라 불린다. 고객이 예치한 자금을 CP나 양도성예금증서(CD)·국공채 등의 채권에 투자하여 그 수익을 고객에게 돌려주는 금융상품\n지문3 DLS(Derivative Linked Securities) 주가 외 기초자산(금리, 통화, 상품, 신용위험 등) 가격의 변동과 연계되어 수익률이 결정되는 증권\n지문4 개인형 퇴직연금 IRP (Individual Retirement Pension)개인별로 퇴직금 및 여유자금을 운용할 수 있는 퇴직금 전용계좌"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', 'CMA 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (548, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "MTS(Mobile Trading System)을 활용한 주식매매에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["MTS 로그인을 위해서는 공동인증서만을 사용할 수 있다", "비밀번호 대신 지문으로 거래가 가능한 증권회사도 있다", "비대면계좌 개설시 모바일 OTP를 발급 받는 경우 OTP기기를 따로 가지고 다닐 필요가 없다", "한국거래소가 부여한 코드를 입력하지 않아도 종목명 입력이나 음성 검색이 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 MTS 로그인은 공동인증서 뿐만 아니라 개별금융회사의 인증서( 예:카카오페이인증, KB모바일 인증 등)나 간편인증, 생체인증으로 로그인이 가능하다.\n지문2 MTS 거래시에도 비밀번호 대신 지문으로 거래가 가능하다.\n지문3 모바일 OTP 서비스를 이용하는 경우 별도의 OTP기기는 필요하지 않다.\n지문4 주식종목의 검색방법은 직접입력 또는 음성입력을 통해 검색이 가능하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (549, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "국내주식 매매에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["국내주식 매매화면은 호가 매수가격과 호가 매도가격을 보여준다", "매수잔량 또는 매도잔량은 해당 가격에 주문을 한 주식 수를 의미한다", "종목마다 한국거래소가 부여한 코드가 있는데 그 코드를 기억하고 있어야 매매가 가능하다", "주식매매시 통상 한 화면에서 매수와 매도 메뉴가 함께 위치하고 있지만 색깔과 화면 제목으로 구분이 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 매수/매도 호가에 대한 설명이다.\n지문2 매수/매도잔량에 대한 설명이다.\n지문3 코드를 기억할 필요는 없다. 종목명 검색을 텍스트 또는 음성으로 할 수 있다.\n지문4 국내주식 매매 화면은 색으로 구분이 가능하고, 메뉴명칭으로도 손쉽게 구분할 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '실전주식거래 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (550, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식 매매 시 가격을 지정해서 주문하는 것을 (   )주문이라고 하고 가격은 정하지 않고 주문을 내면 접수된 시점에 가장 유리한 가격으로 매매가 체결되는 주문을 (   )주문이라 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["지정가, 시장가", "시장가, 대용가", "지정가, 대용가", "시장가, 조건부지정가"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 ‘ 지정가주문’과 ‘시장가주문’에 대한 설명\n지문2 증권을 매매할 때 위탁증거금으로 현금 대신 유가증권을 사용할 수 있다. 이때 대용 유가증권의 가격을 ‘대용가’라고 한다.\n지문3 & 지문4 ‘조건부 지정가’는 지정가로 거래를 시도하다가 거래가 체결되지 않으면 장 마감 시점에 시장가도 전환하며 매매 체결을 하는 주문거래 방식이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (551, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "시장가 주문에 대한 설명으로 <보기>의 (    ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["시장가 주문이란 투자자가 종목과 (    )은 지정하되 (    )은 지정하지 않고 주문을 내면 주문이 접수된 시점에서 가장 유리한 가격으로 매매가 성립되는 주문을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["수량, 비율", "수량, 가격", "가격, 수량", "가격, 비율"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1~4 시장가 주문이란 투자자가 (종목), (수량)은 지정하되 (가격)은 지정하지 않고 주문을 내면 주문이 접수된 시점에서 가장 유리한 가격으로 매매가 성립되는 주문을 말한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (552, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "해외주식 주문에 대한 설명으로 <보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["해외주식 매매를 할 때 해외 주식시장의 주요 매매시간이 한국시간으로 심야인 경우에  해외주식시장이 열리기 전에 미리 (        )주문을 할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장가", "지정가", "대용가", "지정가 예약"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문4 해외주식시장이 열리기 전에 지정가 예약주문을 통해 주식매매를 하는것에 대한 설명이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주문종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (554, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드 매매의 결제에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드를 매수할 때와 매도할 때 결제일과 기준가 적용일이 다르다", "펀드는 주식매매와 달리 펀드별로 결제일과 기준가 적용일이 다를 수 있다", "펀드는 매수를 청구한 시간에 따라 적용하는 기준가격이 다르게 적용될 수 있다", "펀드는 원칙적으로 주식의 편입비율과 투자자산의 종류가 같으면 펀드 기준가를 적용하는 기준시간이 모두 동일하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 매수의 경우에는 D+1일, D+2일 등 펀드마다 다르게 적용되고, 매도의 경우에도 기준가 적용일과 결제일이 다를 수 있다.\n지문2 주식매매는 동일한 날짜에 매매된 경우에는 동일한 날짜에 결제되지만, 펀드의 경우에는 다르게 적용되는 것이 일반적이다.\n지문3 펀드의 투자설명서에 명시된 시간을 기준으로 기준가격이 다르게 적용된다.\n지문4 펀드는 주식의 편입비율과 투자자산의 종류가 같다고 해도 펀드 기준가를 적용하는 기준시간이 다를 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (555, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드매수에 있어 일반적으로 적용하는 매수시간(15시 30분 또는 17시)을 적용하지 않는 펀드는?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["레버리지 펀드", "주식형 펀드", "채권형 펀드", "MMF"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 레버리지 펀드는 당일의 레버리지를 위한 선물포지션 결정을 위해 통상의 15시 30분보다 2시간 정도 빠른 시간을 지정한다.\n지문2 일반적으로 15시 30분을 기준으로 한다.\n지문3 일반적으로 17시를 기준으로 한다.\n지문4 일반적으로 17시를 기준으로 한다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '레버리지투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (556, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드매매와 관련한 설명으로 <보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["펀드의 매수와 매도시에 적용하는 기준가 적용일과 기준시간 등은 펀드를 가입하는 시점에 법적으로 반드시 교부받아야 하는 (            )를 통해 확인하는 것이 가장 정확하다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자확인서", "투자설명서", "자산운용보고서", "자산보관관리보고서"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 투자확인서는 가입시점에 펀드에 대한 설명을 정확하게 듣고 투자설명서를 교부 받았다고 확인하는 확인서에 불과하다.\n지문2 투자설명서는 가입시점에 반드시 설명을 듣고, 교부받아야 하는 필수서류이며 펀드 매매에 따른 기준가 적용과 관련 사항이 기재되어 있다.\n지문3 운용보고서에도 같은 내용이 기재되어 있기도 하다. 그러나 가입시점에 법적 교부서류인 투자설명서와는 다르다.\n지문4 자산보관관리보고서는 기 가입하고 있는 펀드를 대상으로 발송된다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자보호 서류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (557, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자의 종류에 대한 설명으로 <보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(            )는 금융투자상품 투자에 있어서 일정수준 이상의 자산을 보유하고, 전문성을 갖추고 있어서 투자에 따른 위험감수 능력이 있다고 인정되는 개인투자자를 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전업투자자", "단기투자자", "장기투자자", "개인전문투자자"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 투자를 본업으로 하는 개인투자자를 말하는 명칭이다.\n지문2 주로 단기 시세차익을 목적으로 투자하는 투자자를 이르는 명칭이다. \n지문3 장기 보유에 따른 시세차익과 배당수익등을 목적으로 장기보유하는 투자자를 이르는 명칭이다.\n지문4 ‘개인전문투자자’는 금융투자상품 투자에 있어서 일정 수준이상의 자산을 보유하고, 전문성을 갖추고 있어서 투자에 따른 위험감수 능력이 있다고 인정되는 개인 투자자를 말한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (558, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인전문투자자에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["코넥스 상장기업에 금액규제 없이 투자할 수 있다", "파생상품투자자가 의무적으로 이수하는 사전교육, 모의투자가 면제된다", "사모펀드에서 규제하는 최저 가입금액에 대한 제한없이 투자할 수 있다", "주식에 투자하는 사람은 차액결제제도(CFD)를 이용해서 기존에 활용하던 신용, 미수, 주식담보대출보다 더 큰 레버리지를 사용할 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ, ㄴ", "ㄱ, ㄷ, ㄹ", "ㄴ, ㄷ, ㄹ", "ㄱ, ㄴ, ㄷ, ㄹ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문 4가지가 다 맞는 내용임"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (560, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인전문투자자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인전문투자자가 직접 금융투자협회에 신청해야 한다", "개인전문투자자는 필수요건을 갖추고 선택요건 중 한 가지 이상을 충족하면 된다", "자산요건은 부채를 차감한 순재산이 5억원 이상일 것이다", "소득요건은 본인의 직전연도 소득금액이 1억원 이상이거나 부부 합산 직전연도 소득합계액이 1.5억원 이상일 것이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 개인전문투자자 신청 서류를 금융투자회사(주로 증권회사)에 제출한다. \n각 금융투자회사는 자체심사를 통해 자격부여 여부를 판단한다. \n지문2 필수요건과 선택요건이 있다. 선택요건은 소득요건, 전문가요건, 자산요건이 있다.\n지문3 자산요건은 순재산 5억원 이상(부부합산 필수, 거주 부동산관련 금액 제외)으로 부채를 차감한 순재산 5억 이상이다.\n지문4 소득요건은 본인의 직전연도 소득금액이 1억 원 이상이거나 부부가 합산해서 직전연도 소득합계액이 1.5억 원 이상이면 충족된다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자자종류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (561, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(     )이란 가치평가방법 등에 대한 투자자의 이해가 어려운 상품으로서 최대 원금손실 가능성이 일정수준(20%~30%)이상이며 투자자보호가 필요한 상품을 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["저난도 금융투자상품", "고난도 금융투자상품", "고수익 금융투자상품", "중위험 금융투자상품"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문2 2019년 11월에 금융감독원이 발표한 ‘고위험 금융상품 투자자 보호 강화를 위한 종합개선방안’에 따르면, 고난도 금융투자상품은 다음과 같이 규정된다. ‘고난도 금융투자상품’은 파생상품 내재 등으로 가치평가방법 등에 대한 투자자의 이해가 어려운 상품으로서, 최대 원금손실 가능성이 일정수준 (20%~30%)이상인 상품이다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (562, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "고난도 금융투자상품에 대한 설명으로 <보기>의 (     ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 (     )된다는 사실을 알아야 한다. 물론 해당 금융투자회사는 이러한 사항에 대한 통지가 (     ) 사항이다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["철회, 의무", "확정, 의무", "철회, 선택", "확정, 선택"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1  투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 (철회)된다는 사실을 알아야 한다. 물론 해당 금융투자회사는 이러한 사항에 대한 통지가 (의무화)되어 있다. 그리고 숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유 종용하는 행위가 금지되어 있어 \n오로지 투자자의 의지로 청약의 승낙 여부를 판단해야 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (563, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A가 금융투자회사에서 고난도 금융투자상품를 가입할 때 경험하는 상황으로 사실과 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["숙려기간 내에 청약 승낙 의사표시를 해야 가입할 수 있다", "만 65세 미만의 일반투자자에게는 녹취숙려제도를 선택적으로 적용할 수 있다", "숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유·종용하는 행위가 금지되어 있다", "투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 철회된다는 사실을 알아야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 숙려기간 내에 청약 승낙 의사표시를 해야 가입할 수 있다. \n지문2 고난도 금융투자상품에 대해서는 만 65세 미만의 일반투자자에게도 녹취 · 숙려제도를 의무화 하고 있다. \n지문3 숙려기간 중에는 투자권유를 한 금융투자회사는 청약의 승낙을 권유·종용하는 행위가 금지되어 있다. \n지문4 투자자는 숙려기간 내에 별도 청약 승낙 의사표시가 없을 경우 자동적으로 청약이 철회된다는 사실을 알아야 한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '고난도상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (564, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "일반투자자인 투자자 A가 처음으로 레버리지ETF 거래를 하기 위해 거쳐야 하는 상황으로 가장 거리가 먼 것은 ?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["사전교육 수료는 금융투자교육원 홈페이지에서 신청가능 하다", "모바일 수강은 가능하지만 신청과 결제는 웹(web)에서만 할 수 있다", "교육완료 후에는 교육이수번호를 거래하는 증권회사에 제출하면 된다", "의무교육 과정으로 진도율이 70% 이상이면 수료된다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 사전교육 수료는 금융투자교육원 홈페이지에서 신청가능 하다. \n지문2 모바일 수강은 가능하지만 신청과 결제는 웹에서만 할 수 있다.\n지문3 교육완료 후에는 ‘교육이수번호’를 거래를 원하는 증권회사에 제출하면 된다.\n지문4 과정은 해당거래를 하기 위한 의무교육 과정으로 진도율이 100%인 경우에 수료된다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자제한 제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (566, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["자산운용회사는 자산운용보고서를 작성하여 신탁업자의 확인을 받아 (    )마다 1회 이상 투자자에게 교부하여야 한다. 최근에는 주로 이메일을 통한 교부가 주로 이루어지고 있다. 고객이 거부의사를 표시하거나, 평가금액이 (    ) 이하인 경우 교부하지 않아도 된다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["3개월, 10만원", "3개월, 100만원", "6개월, 10만원", "6개월, 100만원"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 자산운용회사는 자산운용보고서를 작성하여 신탁업자의 확인을 받아 3개월마다 1회 이상 투자자에게 교부하여야 한다. 최근에는 주로 이메일을 통한 교부가 주로 이루어지고 있다. 고객이 거부의사를 표시하거나, 평가금액이 10만원 이하인 경우 교부하지 않아도 된다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련 법정서류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (567, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자산운용보고서를 구성하는 내용 중 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["운용경과 및 수익률 현황", "회계감사인의 선임ㆍ해임에 관한 사항", "총보수비용 비율, 보수 및 비용 지급현황 등 비용현황", "자산종류별 비중(통화 별), 자산구성 변화 등 자산구성 현황"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 운용경과 및 수익률 현황 : 비교지수 대비 운용성과 및 누적수익률 연도별 운용수익률, 보유자산 별 손익현황 등 \n지문2 회계감사인의 선임ㆍ해임에 관한 사항은 자산보관관리보고서의 기재내용이다.\n지문3 총 보수 비용 비율, 보수 및 비용 지급현황 등 비용현황\n지문4 자산구성 현황 : 자산종류별 비중(통화 별), 자산구성 변화, 환 헤지를 위한 파생상품 현황, 국가별/업종별 투자비중, 환 헤지, 주요자산보유 현황, 투자대상 상위 10종목 "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련 법정서류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (568, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(        )는 신탁업자가 펀드의 자산이 적정하게 운용되었는지 여부 등에 관하여 확인한 내용을 투자자에게 제공하기 위한 목적으로 자본시장법에 따라 작성된다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래내역서", "투자설명서", "자산운용보고서", "자산보관관리보고서"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문3 자산운용보고서는 펀드운용결과를 설명하는 보고서로서 자산운용회사는 신탁업자의 확인을 받아 판매회사를 통하여 3개월마다 1회 이상 투자자에게 제공하여야 한다. \n지문4 자산보관관리보고서는 신탁업자가 펀드의 자산이 적정하게 운용되었는지 여부 등에 관하여 확인한 내용을 투자자에게 제공하기 위한 목적으로 자본시장과 금융투자업에 관한 법률에 따라 작성된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련 법정서류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (569, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A가 새내기 펀드투자자로서 바람직한 행동으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["요즘 유행하는 펀드가 무엇인지 검색하고 가입했다", "최근 한달 간 수익률이 제일 높은 펀드를 알아보고 가입했다", "신문에 나오는 펀드이름을 보고 스마트폰을 이용해서 가입했다", "금융투자회사 창구에서 나의 투자성향에 맞는 펀드를 추천받고 투자설명서로 자세한 설명을 듣고 난 후 투자여부를 결정했다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 유행하는 펀드라고 해서 모든 투자자에게 적합한 것은 아니다.\n지문2 단기 수익률이 높은 펀드를 무작정 따라가는 투자선택보다는 위험 대비 수익률을 확인하는 절차가 필요하다.\n지문3 언론에 자주 소개되는 이름난 펀드가 꼭 좋은 펀드일 수는 없다.\n지문4 초보 투자자 일수록 창구에서 자신의 투자스타일과 위험성향에 맞는 펀드를 추천받아 자세한 설명을 듣고 난 후에 투자여부를 결정하는 것이 바람직하다. 이러한 경험을 한 후에 온라인 가입을 통해 투자비용을 아끼는 방향으로 발전하는 것이 좋다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드 매매 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (570, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자에 있어서 투자자가 수익을 내기 위해 부담한 위험에 대비해서 어느 정도 수준의 수익을 달성했는지를 나타내는 수익률을 (         )이라고 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["절대수익률", "상대수익률", "중장기수익률", "위험조정수익률"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 절대수익률은 위험이나 기간에 대한한 고려없이 산출한 수익률이다.\n지문2 상대수익률은 비교대상이 되는 다른 투자대안과 비교하는 상대적인 개념의 수익률이다.\n지문3 중장기 수익률은 수익률을 측정하는 기간에 대한 고려를 통해 단기(주로 1년 이하)대비 더 긴 기간을 측정한 수익률이다.\n지문4 보기는 위험 조정 수익률에 대한 설명이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '위험조정수익률 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (572, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자실적 평가에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["벤치마크는 포트폴리오 매니저에게 운용의 목표와 기준이 되는 기준지표다", "투자자의 입장에서는 해당 펀드의 운용결과가 기준지표 대비 얼마나 효율적이었는지를 판단하는 기준이 된다", "벤치마크는 ‘투자전략을 효과적으로 실행했는지 여부를 판단하는 기준이 되는 독립적인 수익률’로 정의할 수 있다", "펀드를 기준으로 벤치마크는 각 펀드의 투자설명서에 내용이 기재되어 있고, 상황에 따라 유연하게 변경 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 벤치마크는 포트폴리오 매니저에게 운용의 목표와 기준이 되는 기준지표가 맞다. \n지문2 투자자의 입장에서는 해당펀드의 운용결과가 이 기준지표 대비 얼마나 효율적이었는지를 판단하는 기준으로 활용된다.\n지문3 벤치마크는 ‘투자전략을 효과적으로 실행했는지 여부를 판단하는 기준이 되는 독립적인 수익률’로 정의할 수 있다. \n지문4 펀드를 기준으로 벤치마크는 각 펀드의 투자설명서에 내용이 기재되어 있고, 임의로 변경할 수 없다. 이는 투자설명서 정정사항에 해당하여 정당한 절차를 거친 이후에 효력을 갖는다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (573, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "벤치마크(기준지표) 중에 섹터/스타일 지수의 성격의 지수로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["대형주지수", "중소형지수", "가치주지수", "종합주가지수"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "벤치마크의 종류인 시장지수와 섹터/스타일지수의 구분에 대한 문제이다.\n지문1 섹터/스타일 지수\n지문2 섹터/스타일 지수\n지문3 섹터/스타일 지수\n지문4 종합주가지수는 대표적인 시장지수의 일종이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (574, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자실적을 평가하는 지표중에 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 (         ) 라고 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["수익률", "벤치마크", "위험조정 성과지표", "절대수익 추구 성과지표"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 단순수익률은 위험요소를 고려하지 않는 수익률 기준이다.\n지문2 벤치마크는 수익률을 비교평가하는 기준으로 위험요소인 변동성을 별도록 고려하지는 않는다.\n지문3 수익률과 위험요소를 동시에 고려하여 성과를 측정하는 지표를 위험조정 성과지표(RAPM : Risk –Adjuisted Performance)라 한다. 선택하는 수익률과 위험지표와 위험을 조정하는 방식에 따라 단위 위험당 초과수익률과 위험조정 수익률로 나눌 수 있다. \n지문4 절대수익 추구 성과지표라는 용어는 없으나, 투자자가 목표로 하는 절대수익률을 기준으로 할 수는 있다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (575, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "샤프비율(Sharpe Ratio)에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["샤프비율이 낮을수록 성과가 양호하다고 본다", "샤프비율은 투자수익률 대비 변동성 비율로 투자성과를 측정한다", "포트폴리오가 부담한 위험 1단위당 초과수익의 크기를 나타낸다", "비교대상 포트폴리오의 샤프비율이 둘 다 (-)값이 나온다면 어느 한쪽이 더 우수하다고 판단하기 어렵다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "지문1 샤프비율이 높을수록 성과가 양호하다고 본다.\n지문2 샤프비율 = (포트폴리오 기대수익률 - 무위험자산수익률) / 포트폴리오 수익률의 표준편차\n지문3 포트폴리오가 부담한 위험 한 단위당 초과수익의 크기를 나타낸다. \n지문4 예를 들어 두 포트폴리오를 비교할 때 두 포트폴리오의 샤프비율이 둘 다 (-)의 값을 보인다면 어느 쪽이 더 우수하다는 판단은 무의미하다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (576, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(      )은 포트폴리오 수익률을 위험으로 나누어(벤치마크 대비 초과 수익률을 추적오차의 표준편차로 나눈 값) 산출하는 지표다. 벤치마크 대비 안정적 수익을 보는 지표인데 이 지표가 높을수록 성과가 우수한 포트폴리오로 볼 수 있다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["정보비율", "투자비율", "위험비율", "수익비율"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "정보비율은 포트폴리오 수익률을 위험으로 나누어 산출하는 지표이다(BM 대비 초과 수익률을 추적오차의 표준편차로 나눈 값)\n\n정보비율(IR)=초과 수익(value add)/위험(risk)\n\n벤치마크 대비 안정적 수익이 얼마나 되는지 보는 지표로, 정보비율이 높을수록 성과가 우수한 포트폴리오로 볼 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (577, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A는 동일한 위험을 부담하고 얻은 초과수익이 높은 펀드를 선호한다고 할 때 선택하는 상품으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["A펀드", "B펀드", "C펀드", "D펀드"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자자 A는 정보비율이 높은 펀드를 선호한다. 이는 동일한 위험을 부담하고 얻은 초과수익이 높은 펀드에 선호가 있고 이런 펀드는 벤치마크 대비 안정적인 수익률을 내는 상품이라고 할 수 있다.\n\n\n\n지문1 정보비율이 제일 높은(1.67) 펀드 ㉠이 정답이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (578, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 내용을 순서대로 적절하게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(      )는 부담한 위험수준에 대해 요구되는 수익률보다 펀드가 얼마나 더 높은 수익률을 달성했는가를 나타내는 지표다. 우수한 포트폴리오의 경우에는 지속적으로 (      )을 보이고 반대로 성과가 열등한 포트폴리오의 경우에는 지속적으로 (     )을 보인다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["젠센의 알파, 음(-)의 알파값, 양(+)의 알파값", "젠센의 알파, 양(+)의 알파값, 음(-)의 알파값", "인덱스 알파, 음(-)의 알파값, 양(+)의 알파값", "인덱스 알파, 양(+)의 알파값, 음(-)의 알파값"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "젠센의 알파는 부담한 위험수준에 대해 요구되는 수익률보다 펀드가 얼마나 더 높은 수익률을 달성했는가를 나타내는 지표다. 우수한 포트폴리오의 경우에는 지속적으로 양(+)의 알파값을 보인다. 반대로 성과가 열등한 포트폴리오의 경우엔 지속적으로 음(-)의 알파값을 보인다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '벤치마크 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (579, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드 실질투자 수익률 보고서에 대한 설명으로 적절한 것만을 <보기>에서 모두 고른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["비용차감 전 수익률과 세부내역을 확인할 수 있다", "입금과 출금이 고려된 자산의 투자수익률을 각종 발생비용 내역을 고려한 수익률을 확인할 수 있다", "투자원금에 대한 정보는 확인할 수 없다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ㄱ", "ㄴ, ㄷ", "ㄱ, ㄴ", "ㄱ, ㄴ, ㄷ"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "펀드 실질 투자수익률 보고서를 통해 비용차감 전 수익률과 세부내역을 확인 가능하다. 입금과 출금이 고려된 자산의 투자수익률을 각종 발생비용 내역을 고려한 수익률을 확인 가능하다. 펀드 실질 투자수익률 보고서는 펀드에 대한 기본정보와 함께 원금과 입출금 내역 및 보수/수수료를 감안한 누적수익율과 연환산 수익율을 제공한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '투자관리 방법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (580, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "상장기업의 주식을 취득해서 보유하는 경우에 발생하는 주주의 권리로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주주총회에 참석할 권리", "배당가능이익에 대해 배당을 받을 권리", "매년 정기적으로 보유하고 있는 주식에 비례해서 이자를 받을 권리", "회사의 경영에 중대한 영향을 미치는 행위에 대한 다수 주주의 결정에 반대해서 주식매수청구권을 행사할 권리"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "지문1 주총에 참석해서 주요 의사결정에 참여하는 것은 주주의 기본적인 권리에 속한다.\n지문2 주주로서 배당가능이익에 대해 주총의 의결에 따라 배당을 받을권리가 있다.\n지문3 이자를 받을 권리는 채권을 보유한 채권자의 권리이다. 주주의 권리는 아니다.\n지문4 주식매수청구권에 대한 권리도 주주의 권리에 속한다. 주로 소액주주에게 해당한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주주의 권리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (581, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주주로서 행사할 수 있는 권리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["합병승인의 특별결의에 반대하면 주식매수청구권을 청구할 수 있다", "주식 매수가격은 주주와 해당 법인간의 협의에 의해서 결정하는 것이 원칙이다", "주식매수청구권은 소액주주의 경우 의사표시가 없어도 적용을 해주기 때문에 그냥 기다리기만 하면 된다", "주식매수 청구를 받은 법인은 매수의 청구기간이 종료하는 날부터 1월 이내에 해당 주식을 매수하여야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "상법상 주식매수청구권이 인정되는 경우는\n① 회사의 영업양도 등을 함에 있어 필요한 특별결의\n② 합병승인의 특별결의\n③ 분할 합병승인의 특별결의\n④ 주식의 포괄적 교환승인의 특별결의\n⑤ 주식의 포괄적 이전승인의 특별결의의 경우이다. 이런 경우에 반대하는 주주는 회사에 대하여 주식매수청구권을 행사 할 수 있도록 상법에 규정하고 있다. 이런 규정은 다수가 찬성하는 중대한 사안이라도 반대하는 주주에게 보유주식을 회사에게 적정가격으로 팔 수 있는 권리를 부여한 것이다. \n지문3 주식매수청구권은 주주의 일방적인 의사표시에 의하여 성립한다. 의사표시가 없으면 주식매수청구권을 행사하지 않는 것으로 간주된다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주총 특별결의 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (582, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 (    ) 안에 들어갈 말로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["(     )은 펀드투자자에게 중대한 영향을 미치는 사항을 투자자가 직접 결정하기 위해  하는 회의로 투자설명서 중 기타 투자자 보호를 위해 필요한 사항에 내용이 기재되어 있는 회의를 말한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자회의", "임원회의", "수익자총회", "대의원 대회"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "펀드의 수익자 총회 관련 사항은 투자설명서 제 5부 기타 투자자 보호를 위해 필요한 사항에 내용이 기재되어 있으며 수익자 총회는 수익자 전체로 구성된다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (583, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 설명하고 있는 수익자총회 관련 제도로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["수익자는 수익자총회에 출석하지 아니하고 서면에 의하여 의결권을 행사 할 수 있다. 이 경우 수익자는 서면에 의결권 행사의 내용을 기재하여 수익자총회일 전날까지 자산운용회사에 제출해야 한다. 다만, 제도에서 규정하는 요건을 모두 충족하는 경우에는 수익자총회에 출석한 수익자가 소유한 수익증권의 총 좌수의 결의 내용에 영향을 미치지 아니하도록 의결권을 행사한 것으로 본다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["간주의결권 행사", "유사의결권 행사", "주식매수청구권 행사", "주식매도청구권 행사"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "간주의결권 행사를 위한 요건\n① 수익자에게 법 시행령 제221조 제6항에 따라 집합투자규약에 적힌 내용을 알리는 서면, 전화·전신·팩스, 전자우편 또는 이와 비슷한 전자통신의 방법으로 의결권행사에 관한 통지가 있었으나 의결권이 행사되지 아니하였을 것\n② 간주 의결권행사의 방법이 집합투자규약에 기재되어 있을 것 \n③ 수익자총회에서 의결권을 행사한 수익증권의 총 좌수가 발행된 수익증권의 총 좌수의10분의 1 이상 일 것\n④ 그밖에 수익자의 이익보호와 수익자 총회결의의 공정성 등을 위하여 간주의결권 행사의 결과를 금융위원회가 정하여 고시하는 바에 따라 수익자에게 제공하는 것을 따를 것\n\n지문3 주식매수청구권제도는 회사의 해산이나 합병, 영업양수/양도 둥의 결의 등 회사의 경영에 중대한 영향을 미치는 행위에 대한 다수주주의 결정에 반대하는 소수 주주를 보호하기 위하여 회사가 반대주주의 보유주식매수를 청구할 수 있는 권리를 부여한 제도다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/10/02 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (584, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "상장회사의 상장폐지 사유로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["파산신청", "주가급등", "고의중과실로 공시의무 위반", "외부감사인의 감사의견 부적정"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문1 상장법인이 파산신청을 하면 관리종목으로 지정된다. 그리고 법원의 파산선고 결정이 나면 즉시 상장폐지 된다.\n지문2 단순 주가급등으로는 상장폐지 대상이 될 수 없다.\n지문3 상장법인이 최근 1년간 공시의무 위반 누계 벌점이 15점 이상인 경에는 관리종목으로 지정된다. 관리종목 지정 후 최근 1년간 누계벌점이 15점 이상 추가되었을 경우와 고의, 중과실로 공시의무를 위반했을 경우에는 상장폐지 조건에 해당된다.\n지문4 반기 검토보고서 상 감사의견이 ‘부적정’ 또는 ‘의견거절’인 경우 관리종목으로 지정된다. 2년 연속으로 감사보고서 상 감사의견이 감사범위 제한 한정인 경우에는 상장폐지 절차가 개시된다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장폐지 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (585, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>에서 설명하고 있는 제도로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식시장에서 상장폐지가 결정 된 종목은 일반적으로 투자자에게 최종 매매 기회를 주기 위해 일정기간 동안 제한된 범위 내에서 매매를 할 수 있도록 허용한 후에 상장폐지 한다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자동매매", "정리매매", "시장가매매", "조건부 지정가매매"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "지문2 정리매매는 상장폐지 되는 주식을 보유하고 있는 투자자들에게 마지막으로 매매를 통해 유동성을 부여하는 시한부 매매라고 볼 수 있다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '상장폐지 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (586, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A가 투자 시작시점부터 투자관리까지의 과정에서 바람직한 태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["계좌개설 사실과 관련정보를 SMS를 통해 통보받고 확인 하였다", "자신의 거래내역을 매월 전자우편으로 통보받아 확인하고 거래횟수를 줄여야겠다고 생각했다", "투자 중인 펀드의 자산운용보고서를 받아서 위험부담수준 및 투자목표의 달성여부를 확인했다", "공모펀드 가입시점에 한 장짜리 리플릿으로 설명을 듣고 그 리플릿을 교부받아서 공모펀드에 가입하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 투자하고 있는 계좌의 개설에 대한 현황을 통보받아 정확한 정보를 확보한다.\n지문2 관련 거래내역을 통보받아 확인하고 적절한 조치를 취한다.\n지문3 현재 투자하고 있는 사항에 대해 정확한 정보에 따른 적절한 조치를 위할 수 있는 동기를 갖는다.\n지문4 공모펀드 가입을 할 때에는 리플릿이 아니라 투자설명서로 설명하고 교부하여야 한다. 이를 확인하는 서류에 설명 듣고, 교부받았음을 확인해야 한다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '금융거래 일반 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (587, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A의 바람직한 투자행동으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자회사에서 추천상품 상품에 대해 이해가 되지 않아 가입을 연기하고 다시 방문하기로 했다", "펀드 가입시 고객 보관용으로 교부받은 서류를 보관하고 있으면서 자산운용보고서가 오면 변화된 내용을 확인해 본다", "담당 PB인 B의 도움을 받아 자신의 계좌를 잘 관리하고 있으며, 혼자서도 잘 관리할 수 있는 능력을 갖추려고 노력 중이다", "금융회사에게 투자상품, 조언 또는 권고사항, 거래에 대해 어떻게 보상을 받는지에 대해 알 방법이 없어서 그냥 넘어가기로 했다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "지문1 상품이 이해되지 않는다면 가입절차를 중단하고 이해한 이후에 가입하는 것이 바람직하다.\n지문2 고객 보관용 서류를 잘 보관하고, 이후 수령하는 서류와 확인하여 투자를 이어가는 것이 바람직하다.\n지문3 관리자의 도움과 투자자 자신의 노력으로 혼자 관리할 수 있는 능력과 자신감을 갖는 것이 중요하다.\n지문4 불이익이 발생할 때 보상받을 수 있는 방법을 문의하고 도움을 받는 것이 중요하고 그냥 넘어가는 것은 바람직한 투자방법이 아니다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '피해방지요령 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (588, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "금융투자시 발생할 수 있는 투자 위험 및 시장 투자태도에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 일반적으로 통제와 관리가 불가능한 시장위험에 대해서는 투자비중을 줄이거나 피하는 것이 유효한 전략이다", "투자자는 다양한 투자태도에 직면할 수 있으므로 발생가능한 투자태도를 가정하고 이에 대한 관리와 대비책을 통해 투자태도 이후 발생할 수 있는 투자기회를 잡는 것이 필요하다", "직접적 시장위험은 시장의 직접적인 움직임에 따라 발생할 수 있는 원금손실위험을 말하며 이는 투자자가 통제하기 어려운 시장의 변화에 따라 발생한다", "금융투자시 발생하는 다양한 위험 중 투자자가 통제할 수 있는 위험을 비체계적 위험이라고 하며 자산배분과 분산투자를 통해 통제 가능하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "금융투자시 발생할 수 있는 직접적 시장위험은 투자한 자산 가격의 부정적인 움직임으로 인한 손실위험을 말하며 개별자산위험으로도 불리운다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '위험과 투자태도 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (589, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "적절한 투자태도 관리방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자 A는 연간 5%의 목표수익을 안정적으로 달성하기 위해 최근 3년간 연평균 7% 이상의 수익을 거둔 주식에 전액 투자하기로 하였다", "투자자 B는 연간 5%의 목표수익을 안정적으로 달성하기 위해 연 3%의 이자가 발생하는 채권과 함께 최근 3년간 연평균 7% 이상의 수익을 거둔 주식에 나누어 투자하기로 하였다", "투자자 C는 증권사 직원 B씨가 제시한 유망한 주식 종목 대신 다양한 성장주에 투자하는 펀드상품에 가입하여 투자태도 관리를 도모하였다", "투자자 D는 투자에 따르는 다양한 투자태도 요인에 대해 사전점검 후 직접적 시장위험을 통제하기 위해 10개의 유망주식으로 포트폴리오를 짜고 원금을 나눠서 투자하기로 하였다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "통제 가능한 직접적 시장위험에 대해서는 분산투자를 통해 투자태도를 관리할 수 있다. 집중투자 방식으로는 직접적 시장위험을 통제하기 어렵다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도 관리방법 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (590, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자과정에서 직면할 수 있는 유동성 투자태도에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유동성 투자태도는 투자한 금융상품의 구조적 특징으로 인해 발생할 수 있으며 특히 시장조정 시 발생할 가능성이 크다", "좋은 투자기회를 발굴한 경우 즉각적인 투자를 통해 수익기회를 창출할 수 있도록 유동성 관리가 필요하다", "유동성 투자태도는 투자시 필연적으로 발생하는 투자태도이므로 이를 대비하여 투자자는 투자한 금액에 해당하는 현금을 보유하는 것이 필요하다", "유동성 투자태도를 관리하기 위해 투자시점을 분산하여 투자하는 것은 매우 효과적인 방법이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "유동성 투자태도에 대한 관리는 투자한 이후 투자자가 원하는 시점에 현금화 할 수 있는지의 여부와 함께 투자시점을 분산함으로써 투자기회 발굴 시 즉시 투자할 수 있도록 유동성을 관리하는 것을 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도 종류 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (591, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자과정에서 직면할 수 있는 다양한 위험과 투자태도에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금융투자시 통제 불가능한 시장위험과 통제 가능한 투자태도 모두에 노출될 수 있으며 투자태도는 적극적인 관리를 통해 투자기회로 만들 수 있다", "투자대상 자산가격 자체의 변동성에서 발생할 수 있는 직접적 시장위험을 적극적인 분산투자와 자산배분을 통해 통제할 수 있다", "유동성 투자태도 관리를 위해 분산투자를 활용하는 경우 평균매입단가 인하효과로 이어져 수익률 개선에도 좋은 영향을 미칠 수 있다", "투자경험이 많지 않은 개인투자자는 다른 투자자들의 동향을 면밀히 분석하고 이들의 투자트렌드에 맞춰 투자하는 전략이 유효하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자자는 군중심리 투자태도를 경계해야 하며 투자자에게 가장 좋은 군중심리 투자태도 관리 방법은 자신만의 투자원칙과 방식을 세우고 철저히 실천하는 것이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도 종류 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (592, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 투자자 A의 실제 투자사례에서 발생 가능한 투자태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["2021년 1월 투자자 A는 증권사 직원을 찾아가 보유하고 있던 원금 5천만원에 대한 투자상담을 받고 다음과 같은 투자 포트폴리오를 구성하였다. (참고로 원금 5천만원 중 2천만원은 1년 후 전세 보증금을 올려줄 때 사용될 자금이다)", "<투자자 A의 투자내역>", "2천만원 – 향후 실적 기대감이 높은 IT섹터 B기업 주식", "2천만원 – 부동산 펀드 (만기 3년)", "1천만원 –  최근 지인을 통해 소개 받은 주식 종목(합병 기대감으로 최근 주가가 30% 상승중인 종목)"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["유동성 투자태도", "군중심리 투자태도", "직접적 시장위험", "원금초과손실위험"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자자 A의 투자 포트폴리오를 분석해 보면, 먼저 시장의 움직임에 따른 통제 불가능한 간접적 시장위험 뿐만 아니라, 전체 자산 중 60% 이상을 특정 주식종목에 분산하지 않고 집중투자함에 따른 직접적 시장위험에 함께 노출되어 있다. 또한 전체 자금 중 2천만원이 만기 3년짜리 부동산 펀드에 묶임으로써 1년 후 자금 필요시점에서의 가용 자금이 3천만원으로 줄어 들었고, 3천만원이 모두 주식자산에 집중됨으로써 증시 조정에 따른 수익률 하락 시 유동성 투자태도에 직면할 가능성도 있다. 마지막으로 철저한 분석없이 지인의 소개에 따른 투자로 인해서 군중심리 투자태도에도 노출되었다고 볼 수 있다.\n"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자태도 종류 숙지', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (593, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자산배분전략에 대한 설명으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["가장 기본적인 원칙은 최대한 많은 수의 자산에 투자하는 것이다", "가장 중요한 기준은 단기적인 시장의 변화에 어떻게 효율적으로 대응하느냐에 있다", "단기적인 시장의 움직임은 철저히 배제하고 장기적인 시장 트렌드에 초점을 맞춰야 한다", "마켓타이밍에 전적으로 의존하는 투자로 인해 발생하는 포트폴리오 변동성 확대 가능성을 줄일 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "자산배분전략을 수립할 때는 단순히 많은 자산에 투자하는 것보다는 자산간 상관관계가 낮은 다양한 자산에 투자함으로써 분산투자효과를 극대화하는 것이 좋다. 아울러 장기적인 시각의 전략적자산배분과 단기적 시장대응을 위한 전술적자산배분을 함께 고려해야 한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (594, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자산배분전략과 포트폴리오 운용에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분전략과 포트폴리오 운용은 투자자가 목표로 하는 투자수익을 달성하고 투자에 따른 위험을 적절히 통제하는 가장 중요한 과정이다", "포트폴리오 운용은 다양한 투자자산 중에서도 양의 상관관계를 가진 여러 자산을 조합할 때 가장 좋은 효과가 발생한다", "기대수익이 유사하면서 안정적인 자산, 또는 안정성이 유사하면서 기대수익이 높은 자산에 투자하는 것이 포트폴리오 운용의 기본이다", "자산배분전략과 포트폴리오 운용은 세부적인 방법론의 차이일 뿐 투자효율성을 극대화하려는 목표는 같다고 할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "포트폴리오 운용은 다양한 투자자산 중에서도 음의 상관관계를 가진 자산을 조합할 때 가장 좋은 효과가 발생한다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (595, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자산배분전략과 포트폴리오 운용 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분전략과 포트폴리오 운용의 첫번째 과정은 투자목표 설정단계로서 투자기간 및 세금, 자금의 성격 등에 대한 검토가 이루어진다", "자산배분전략과 포트폴리오 운용의 두번째 과정은 투자실행단계로서 실질적인 자산배분 및 포트폴리오 구성이 이루어진다", "자산배분전략과 포트폴리오 운용단계 중 투자실행단계에서는 투자목적 및 세금에 기반하여 구체적인 투자수단을 찾는 과정이 진행된다", "자산배분전략과 포트폴리오 운용단계 중 사후관리 단계는 시장의 변화를 모니터링 하고 구축된 포트폴리오가 목표한 방향으로 잘 작동하는지 관찰하는 과정이 진행된다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "자산배분전략과 포트폴리오 운용 과정 중 투자목적 및 세금, 자금의 성격 등 투자자의 세부제약조건을 고려하는 단계는 첫번째 단계인 투자목표 설정단계에서 이루어 진다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (596, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "전략적 자산배분에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["단기적인 시장변화에 적극적으로 대응하는 의사결정 과정이다", "투자자의 투자성향이 중요한 판단기준이 된다", "위험자산과 안전자산 간 투자비중을 결정하는 과정이다", "내재가치에 근거한 자산배분이 이루어진다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "전략적 자산배분은 장기적 투자목표에 따른 의사결정 과정으로 투자자의 위험감내 수준 등 투자성향에 근거한 투자목표가 중요한 판단기준이며, 이러한 투자성향에 따른 위험자산과 안전자산간 비중의 결정과 내재가치에 근거한 자산배분이 이루어지는 과정이다. 반면 전술적 자산배분은 단기적인 시장변화에 적극적으로 대응하는 의사결정 과정으로 시장변화에 대한 투자자의 투자타이밍 포착 등이 중요한 판단 기준이며 시장 변화에 따라 적극적으로 투자자산을 교체하거나 비중을 조절함으로써 역투자전략을 수행하는 과정이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전략적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (597, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "전략적 자산배분 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["전략적 자산배분 첫 단계에서 투자자는 투자성향, 투자자금의 성격, 세금 등과 같은 투자제약조건에 기반한 투자목표를 수립해야 한다", "전략적 자산배분을 위해 투자자는 투자대상 자산집단 및 각각의 자산집단별 세분화된 투자가능 자산을 선별하는 것이 필요하다", "전략적 자산배분을 위해 투자자는 미래 기대수익률과 미래 위험에 기반한 포트폴리오의 산출이 필요하다", "전략적 자산배분의 목적은 단기적인 자산배분에 근거하기 때문에 투자자는 기대수익률이나 내재위험 등 단기적인 내재가치에 기반하여 자산을 선별하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "전략적 자산배분은 장기적 투자목표에 따른 의사결정 과정으로 투자자의투자목표에 장기적으로 부합할 수 있는 투자자산 집단 및 세부 자산 포트폴리오를 구성하는 것이 필요하고 이를 위해서는 미래 기대수익과 미래 위험을 추정하여 이에 기반하는 것이 중요하다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전략적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (598, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "전술적 자산배분 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자산의 미래 기대수익 등 본질가치에 기반한 단기 투자전략을 구성한다", "단기적으로 시장의 변화방향을 예상하여 사전적으로 자산구성을 변화시켜 나가는 매우 적극적인 자산배분 전략이다", "본질적으로 역투자전략이라고 할 수 있으며 이는 자산의 본질가치와 시장가격 간의 비교를 통해 매수-매도를 수행하는 전략이다", "전술적 자산배분전략을 수립할 때 투자자는 자산집단의 가격이 평균반전과정을 따른다는 가정 하에 구성하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "전술적 자산배분전략은 단기적으로 시장의 변화 방향을 예상하고 사전적으로 자산구성을 변화시켜 나가는 자산배분전략으로 자산의 본질가치 보다는 마켓타이밍에 따라 적극적으로 자산을 교체하는 전략이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전술적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (599, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자의 자산배분 실행 과정에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자는 단기적인 시장에 적극적으로 대응하는 전술적 자산배분 보다는 장기적인 투자목표에 기반한 전략적 자산배분에 더 쉽게 접근하기 때문에 전략적 자산배분에 편중되는 경향이 있다", "개인투자자의 비합리적 투자는 전략적 자산배분의 결여와 전술적 자산배분에 편중되는 데에 그 원인이 있다고 할 수 있다", "개인투자자가 자산배분전략을 실행함에 있어 가장 중요한 것은 위험과 관계없이 높은 기대수익률을 가진 자산을 선별하여 포트폴리오를 구성하는 것이라고 할 수 있다", "개인투자자의 자산배분전략은 기본적인 투자전략의 목표나 기간이 기관투자자와 다를 수 밖에 없으므로 기관투자자의 자산배분전략은 전혀 도움이 되지 않는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "개인 투자자의 경우 기본적으로 마켓타이밍에 기반하여 투자하는 경향이 많아 전술적 자산배분에 대한 의존이 높은 편이고 이로 인해 비합리적 투자가 이루어지는 경우가 많다. 개인 투자자가 포트폴리오를 구성할 때는 수익률에만 의존하는 것이 아니라 위험 대비 기대수익률을 평가하는 것이 필요하고, 이를 위해서는 벤치마킹할 수 있는 기관 투자자나 전문가의 자산배분전략이나 포트폴리오를 참고하는 것이 좋다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (600, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 증권사 시장전망에 동의한 투자자가 취할 수 있는 자산배분전략으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "- 2020년 3월부터 시작된 코로나 바이러스의 영향으로 글로벌 경기는 극심한 침체국면에 빠짐", "- 경기 회복을 위해 미국을 비롯한 주요국들은 금리인하 등 통화완화정책과 함께 막대한 유동성 공급을 통한 재정정책을 펼침", "- 이로 인해 글로벌 경기는 점진적인 경기회복 추이를 보이고 있으나 그 부작용으로 인플레이션(물가상승)이 발생할 가능성이 높아짐"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["그동안 시장에 공급된 막대한 유동성으로 인해 향후 인플레이션 가능성이 매우 커진 상황이기 때문에 인플레이션 국면에 유리한 원자재 투자 비중을 늘린다", "완전히 해결되지 않은 코로나 바이러스 국면에서 안전자산이면서도 물가상승에 대비가 가능한 물가연동채권(TIPS)의 투자 비중을 늘린다", "완전히 해결되지 않은 코로나 바이러스 국면과 높은 시장변동성에 대응하기 위해 안전자산인 국채 투자비중을 늘린다", "그동안 시장에 공급된 유동성이 향후 경기 회복을 위한 다양한 사회간접시설 확충 사업 등에 쓰일 것으로 예상하여 인프라펀드에 대한 투자비중을 늘린다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "경기 둔화 국면에서 중앙은행은 금리인하를 통한 통화정책을 사용하고, 아울러 정부는 적극적인 재정정책을 통해 유동성을 공급하여 경기를 회복시키게 된다. 이로 인해 다양한 자산가격은 상승하게 되며 경기도 점진적으로 회복하는 효과가 있으나, 시장에 풀린 유동성으로 인해 인플레이션 문제가 부각되며 시간이 지나면 금리인상이나 유동성 축소와 같은 국면이 전개될 가능성이 높다. 따라서 경기둔화국면이 완화되면서 인플레이션 가능성이 높은 시장상황에서는 금리상승에 취약한 채권이나 주식의 비중을 줄이고 인플레이션 헤지 기능이 있는 원자재, 인프라, 물가연동채권 등의 비중을 확대하는 전략이 필요하다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 올바른 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (601, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "경기순환 국면과 그에 따른 전술적 자산배분전략으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 경기둔화국면에서 유동성 공급으로 인해 물가상승이 예상되면 인플레이션 헤지를 위한 자산인 원자재와 회사채에 대한 투자비중 확대가 필요하다", "일반적으로 경기호조가 지속되고 이로 인한 물가상승이 예상되면 인플레이션에 대비할 수 있는 자산인 원자재와 주식 등 위험자산에 대한 투자비중 확대가 필요하다", "일반적으로 경기둔화가 심화되고 물가가 하락하면 안전자산 성격의 국고채 및 현금에 대한 투자비중 확대가 필요하다", "일반적으로 경기호조가 지속되는 동시에 물가가 하락하면 위험자산 성격의 주식이나 회사채에 대한 투자비중 확대가 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "일반적으로 경기둔화국면에서 유동성 공급으로 인해 물가상승이 예상되는 국면에서는 인플레이션 헤지를 위한 자산인 원자재나 인프라에 대한 투자 비중을 확대해야 하며, 향후 물가상승으로 인한 부작용을 억제하기 위해 금리인상 등의 정책이 예상되기 때문에 채권 등의 자산비중을 줄이는 것이 필요하다. 다만 채권 중에서도 인프레이션 헤지 기능이 있는 물가연동채권(TIPS)의 경우는 예외이다.  "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '전술적 자산배분 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (606, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자의 주식 포트폴리오 전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["적극적 포트폴리오 관리전략은 시장지수 대비 높은 성과를 내기 위해 다양한 유형의 주식에 적극적으로 최대한 분산하여 포트폴리오를 구축한다", "적극적 포트폴리오 관리전략은 특정 트렌드나 테마를 좇아 포트폴리오를 구성하는 경향이 많아 포트폴리오의 변동성이 높아질 수 있다", "소극적 포트폴리오 관리전략은 안정적인 주식 포트폴리오를 유지하고자 하는 투자자에게 적합한 전략으로 시장지수를 추종하는 포트폴리오를 구축하고 이를 추종하는 방식이다", "소극적 포트폴리오 관리전략은 주식시장의 특정 지수를 초과하는 수익을 내는 것이 목적이 아니라 주식시장 자체의 성장에 따른 수익을 추종하는 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "적극적 포트폴리오 관리전략은 투자하고자 하는 시장지수를 선정하고 지수의 수익률을 상회하는 성과를 목표로 포트폴리오를 관리하게 되며, 따라서 특정 섹터나 종목에 대한 자산배분이나 투자비중 유지 보다는 시장의 트랜드나 테마를 좇아 포트폴리오를 구성하여 투자하는 전략이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (607, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "핵심-위성 포트폴리오 관리전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["소극적 포트폴리오 관리전략과 적극적 포트폴리오 관리전략의 장점을 섞어서 투자효과를 높이는 목적으로 사용한다", "적극적 포트폴리오 관리전략의 비중은 70~80%, 소극적 포트폴리오 관리전략의 비중을 20~30% 의 비중으로 섞어서 수익률을 적극적으로 개선시키는 전략이다", "핵심-위성 포트폴리오 관리전략은 변동성을 적절한 수준에서 관리하면서도 시장지수보다 높은 수익률을 내고자 하는 목적으로 많이 사용된다", "투자자는 핵심-위성 포트폴리오 관리전략을 수행함에 있어 초기 설정한 투자 비중을 꾸준히 유지하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "핵심-위성 포트폴리오 관리전략은 시장지수를 안정적으로 추종할 수 있는 대표종목으로 전체 포트폴리오 중 70~80%를 구성하고, 나머지 20~30%의 비중은 시장의 트렌드를 추종하여 단기적으로 시장을 주도하는 테마 섹터나 종목으로 포트폴리오를 관리하는 방법이다. 즉 소극적 포트폴리오 관리전략의 비중을 70~80%로, 적극적 포트폴리오 관리전략의 비중을 20~30%로 유지하는 전략이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (608, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 증권사 시장전망에 동의한 투자자가 핵심-위성 포트폴리오 관리전략에 따라 구성해 볼 수 있는 포트폴리오로 가장 적합한 것은? (단, 투자자는 안정적 투자성향으로 가정)", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["주식시장 전망 :", "코로나 발생 이후 글로벌 경기침체가 심화되었고 이에 대한 각국 중앙은행 및 행정부의 금리인하 및 양적완화 정책이 실시됨. 이에 따른 대규모 유동성 공급은 글로벌 경제를 회복국면으로 이끌고 있고, 이에 따라 기업들의 실적도 반등 추세를 이어감. 특히 코로나로 인한 재택근무 확대 및 비대면 비즈니스 활동의 증가로 인해 반도체 및 언텍트 테마 관련업종의 실적 향상이 두드러질 것으로 기대됨. 다만 코로나 이슈가 여전히 진행 중이고, 변이 바이러스의 등장 등 시장에 변수가 여전히 많아 신중한 투자가 필요함"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장 대표지수 성과추종형 인덱스펀드 투자 70% + 반도체업종 관련주식 투자 30%", "반도체업종 관련주식투자 70% + 시장 대표지수 성과추종형 인덱스펀드 투자 30%", "언택트테마 관련주식 투자 70% + 반도체업종 관련주식 투자 30%", "반도체업종 관련주식 투자 70% + 언택트테마 관련주식 투자 30%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "핵심-위성 포트폴리오 관리전략은 시장지수를 안정적으로 추종할 수 있는 대표종목으로 전체 포트폴리오 중 70~80%를 구성하고, 나머지 20~30%의 비중은 시장의 트렌드를 추종하여 단기적으로 시장을 주도하는 테마 섹터나 종목으로 포트폴리오를 관리하는 방법이다. 투자자 A는 기존 주식투자 경험이 없는 투자자로서 전체 자산 중 일부 자산을 주식 포트폴리오에 투자하는 만큼 소극적 포트폴리오 관리전략의 비중을 70~80%로, 적극적 포트폴리오 관리전략의 비중을 20~30%로 유지하는 핵심-위성 전략이 바람직하다. 따라서 시장 대표지수를 추종하는 인덱스펀드에 대한 투자를 70% 수준으로 하고, 향후 시장 전망이 우호적인 반도체 또는 언텍트 관련 종목에 대한 투자를 30% 수준으로 하는 것이 바람직하다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '주식 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (609, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 채권 포트폴리오를 구성하는 목적 및 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권은 대표적인 안전자산의 성격을 갖추고 있으므로 채권 포트폴리오를 통해 전체 포트폴리오의 변동성을 낮출 수 있다", "채권은 유동성이 풍부하고 종류가 다양해서 개인 투자자가 채권 실물을 통해 포트폴리오를 구성하기에 매우 용이한 자산이다", "채권은 발행 주체 및 구조에 따라 매우 다양한 종류가 발행되고 있기 때문에 포트폴리오의 효용성이 높다", "개인 투자자가 채권을 통해 포트폴리오를 구성할 때 여러가지 방법이 있을 수 있으나 그중 간접투자상품을 활용하는 것이 효율적이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "채권은 유동성이 풍부한 자산이긴 하지만 다른 자산 대비 시장에서 거래되는 규모가 크고, 주식과 같이 대중화된 거래소를 통한 거래보다는 기관들 간에 1:1로 장외시장에서 주로 거래가 이루어지는 형식이어서 개인 투자자가 직접적으로 실물에 투자하기에 어려움이 적지 않은 자산이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (610, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 채권 포트폴리오를 구성할 때 활용할 수 있는 다양한 채권과 그 특징에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["한국 국채: 이자는 낮은 반면 안정적, 포트폴리오 변동성 축소", "브라질 국채: 높은 이자율과 변동성, 포트폴리오 수익률 제고", "하이일드 채권: 높은 이자율과 변동성, 포트폴리오 수익률 제고", "우량등급 회사채: 국채 대비 높은 이자율과 낮은 변동성, 포트폴리오 효율성 제고"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "우량등급 회사채는 일반적으로 국채 대비 높은 이자율을 얻을 수 있으나 변동성은 국채 대비 높은 경우가 대부분이기 때문에 포트폴리오 구성 시 포트폴리오 효율성을 높일 수 있지만 투자태도 관리가 필요하다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (611, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권 포트폴리오를 구성하기 위해 사전적으로 검토해야 할 사항에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["신용등급: 신용등급이 높은 채권은 금리가 낮은 반면, 신용등급이 낮은 채권은 금리가 높다", "만기: 만기가 긴 채권일수록 높은 금리를 제시하면서 동시에 위험도가 낮아지는 효과가 있다", "듀레이션: 일반적으로 듀레이션이 긴 채권은 시장금리에 대한 민감도가 높기 때문에 금리 하락시기에는 듀레이션이 긴 채권의 비중을 높여야 유리하다", "발행주체: 일반적으로 채권의 발행주체가 국가나 정부기관일수록 금리는 낮다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "채권의 만기가 길어질수록 시장 금리 변화에 노출될 가능성이 높아지기 때문에 위험도가 높아지게 된다. 따라서 장기 채권일수록 제시금리는 높아진다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (612, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권의 특징과 그에 따른 포트폴리오 구성방법으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["채권은 다른 자산 대비 거래 단위가 크고, 장외시장에서 거래되는 비중이 높아 개인 투자자는 채권에 투자하는 펀드나 ETF와 같은 간접투자상품을 통해 포트폴리오를 구성하는 것이 효율적이다", "채권은 발행 주체나 조건에 따라 매우 다양한 금리와 수익률 구조를 가지고 있기 때문에 포트폴리오를 구성하기에 효율적인 자산이다", "모든 채권은 안전자산의 성격을 갖고 있기 때문에 포트폴리오의 안정성을 높이는 특징이 있으나 수익률 측면에서는 다른 자산대비 매력도가 떨어지는 단점이 있다", "채권은 종류에 따라 매우 다양한 수익구조와 변동성이 있을 수있으므로 다양한 종류의 채권에 분산투자함으로써 자산배분 효과를 높일 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "채권은 발행 주체나 조건에 따라 매우 다양한 수익구조를 가지고 있으며 신용등급이 낮은 하이일드 채권이나 이머징국가에서 발행된 국채 등은 주식이나 부동산 등 다른 자산과 비교해서도 높은 금리와 매력적인 수익률을 보유하고 있다고 할 수 있다. 다만 이러한 채권은 상대적으로 높은 변동성을 가지고 있기 때문에 안전자산으로 분류하기는 어렵다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (613, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "채권 포트폴리오의 수익률 및 변동성에 가장 큰 영향을 미치는 금리의 움직임과 이에 따른 포트폴리오 구성방법에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["향후 시장금리 상승으로 채권가격 하락이 예상되면 듀레이션이 짧은 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 하락으로 채권가격 하락이 예상되면 듀레이션이 긴 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 상승으로 채권가격 상승이 예상되면 듀레이션이 긴 채권으로 포트폴리오를 구성하는 것이 필요하다", "향후 시장금리 하락으로 채권가격 상승이 예상되면 듀레이션이 짧은 채권으로 포트폴리오를 구성하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "채권 포트폴리오는 시장금리와 매우 높은 상관관계를 갖고 있기 때문에 투자자는 시장금리의 움직임에 따라 채권 포트폴리오의 듀레이션을 조절해야 한다. 일반적으로 듀레이션이 긴 채권은 금리민감도가 높고 변동성이 큰 반면 듀레이션이 짧은 채권은 상대적으로 변동성이 낮다. 따라서 투자자는 시장금리 상승으로 인해 채권가격이 하락하는 구간에서는 듀레이션이 짧은 채권으로 포트폴리오를 구성하고, 반대로 시장금리 하락으로 인해 채권가격이 상승하는 구간에서는 듀레이션이 긴 채권 포트폴리오로 구성해야 한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (614, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 증권사 시장전망에 동의한 투자자가 구성해 볼 수 있는 채권 포트폴리오의 내용으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "2020년 3월부터 시작된 코로나 바이러스의 영향으로 글로벌 경기는 극심한 침체국면에 빠짐. 경기 회복을 위해 미국을 비롯한 주요국들은 금리인하 등 통화완화정책과 함께 막대한 유동성 공급을 통한 재정확대정책을 펼침.", "이로 인해 글로벌 경기는 점진적인 경기회복 추이를 보이고 있으나, 그 부작용으로 인플레이션(물가상승)이 발생할 가능성이 높아진 상황임"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인플레이션이 예상되는 국면에서 향후 중앙은행의 금리인상이 예상되기 때문에 듀레이션이 짧은 채권의 비중을 늘린다", "금리의 변동성이 높아질 가능성이 증가하는 상황이므로 듀레이션이 짧은 채권의 비중을 늘린다", "글로벌 경기가 점진적인 회복추이를 보일 가능성이 높기 때문에 듀레이션이 짧은 고금리 채권의 비중을 늘린다", "글로벌 경기가 점진적인 회복추이를 보일 가능성이 높기 때문에 만기가 긴 채권의 비중을 늘려서 수익률을 높인다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "경기침체구간에서는 경기 회복을 위해 금리인하 등의 통화완화정책과 함께 유동성공급을 늘리는 등 적극적인 재정확대정책이 나오게 된다. 이에 따라 경기는 점차 회복세를 보이지만 이후 시중에 풀린 유동성에 따른 부작용으로 시장은 인플레이션 투자태도에 직면하게 되고, 이에 대한 억제책으로 금리인상과 함께 유동성 축소가 진행되게 된다. 문제에서 제시된 시장상황은 경기회복과 함께 향후 인플레이션 억제를 위한 금리인상 및 유동성 축소가 예상되는 국면이므로 이러한 경우 투자자는 만기와 듀레이션이 짧은 채권의 비중을 늘려 변동성을 축소해야 하고, 경기 회복에 따른 수혜를 받을 수 있는 고금리 채권의 비중을 늘리는 방향으로 채권 포트폴리오 전략을 가져가는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (615, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "글로벌 경기의 흐름과 채권 가격의 영향에 따른 채권 포트폴리오 관리방향으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["글로벌 시장에 위기 상황이 발생하면 시장에서는 안전자산에 대한 선호현상이 강해지고, 이에 따라 선진국 국채의 가격이 상승할 가능성이 높으므로 포트폴리오에서 비중을 늘려야 한다", "글로벌 경기가 호조세를 지속하며 안정세가 지속되는 구간에서는 안전자산 성격의 국채 보다는 금리가 높은 하이일드 채권의 비중을 늘리는 전략이 필요하다", "글로벌 경기가 호조세를 보이며 물가상승이 출현하는 구간에서는 금리인하 가능성이 높으므로 듀레이션이 긴 채권의 비중을 늘리는 전략이 필요하다", "글로벌 경기가 침체국면을 지속하는 구간에서는 금리인하 가능성이 높으므로 듀레이션이 긴 채권의 비중을 늘리는 전략이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "다양한 채권의 종류 중에서 특히 안전자산으로서의 성격이 강한 선진국 국채나 우량회사채 등은 글로벌 시장에 위기상황이 발생하거나, 글로벌 경기침체가 지속되는 구간에서 수요가 많아지며 가격이 상승하는 특징이 있기 때문에 이러한 구간에서 비중을 늘리는 전략이 필요하다. 반면 글로벌 경기가 호조세를 지속하는 구간에서는 상대적으로 금리가 높은 위험자산 성격의 하이일드 채권의 비중을 높이는 전략이 필요하다. 아울러 경기가 침체국면을 지속하는 구간에서는 향후 금리인하 등의 정책이 나올 가능성이 높기 때문에 이에 따른 롱듀레이션 채권 비중을 늘리는 전략이 필요한 반면, 글로벌 경기의 호조세가 지속되며 인플레이션 우려가 가시화 되는 구간에서는 향후 금리 상승 가능성이 높기 때문에 듀레이션이 짧은 채권의 비중을 늘리는 전략이 필요하다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (616, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 주식과 채권을 활용하여 포트폴리오를 구성하는 전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식과 채권은 전통적으로 낮은 상관관계를 가지고 있는 자산이기 때문에 두 자산을 적절한 비중으로 혼합하여 투자하면 포트폴리오의 분산투자효과를 높일 수 있다", "상관관계가 낮은 주식과 채권에 분산하여 투자하면 경제환경변수에 따른 간접적 시장투자태도를 줄일 수 있다", "상관관계가 낮은 주식과 채권에 분산하여 투자하면 각각의 자산이 보유한 고유의 투자태도인 직접적 시장투자태도를 줄일 수 있다", "너무 낮은 금리의 채권으로 주식이 보유한 변동성을 상쇄시키기 힘든 국면에서는 채권보다는 고배당주로 관심을 돌려는 것도 좋은 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "주식과 채권은 대표적인 위험자산과 안전자산으로 두 자산간 상관관계는 마이너스를 보이거나 매우 낮다. 따라서 두 자산간 분산투자를 통해 투자자는 포트폴리오의 변동성을 낮추고 분산투자 효과를 높일 수 있다. 두 자산간 분산투자를 통해 투자자는 포트폴리오의 대외적인 시장변수, 즉 간접적 시장투자태도를 감소시키는 효과를 얻을 수 있다. 그러나 이러한 주식과 채권에 대한 분산투자가 각각의 자산이 보유하고 있는 직접적 시장투자태도를 줄이는 데 영향을 미칠 수는 없다. 주식과 채권 각각의 직접적 시장투자태도를 줄이기 위해서는 주식과 채권자산을 각각 분산투자하여 해결할 수 있다.  "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '채권 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (617, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "파생상품을 활용하여 포트폴리오를 구성하는 목적 및 활용방법으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선물이나 옵션과 같은 파생상품의 증거금 제도를 활용하여 레버리지 투자효과를 높일 수 있다", "선물이나 옵션과 같은 파생상품을 활용하여 투자자산 가격하락 위험을 헤지할 수 있다", "파생상품을 활용한 레버리지 투자를 통해 투자자산의 변동성을 줄이고 투자위험을 낮출 수 있다", "다양한 파생상품 중에서도 특히 장내에서 거래되는 선물, 옵션과 더불어 이들 상품을 기초로 발행되는 파생결합상품이 활용하기 적합하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "개인 투자자가 파생상품을 활용하는 목적은 레버리지 활용, 투자자산가격의 하락위험 헤지, 차익거래 등이 대표적이다. 파생상품은 개인 투자자들이 주로 활용하는 장내파생상품과 기관들간 계약에 의한 장외파생상품으로 구분된다. 개인 투자자들은 장내거래소를 통한 선물, 옵션 그리고 이러한 파생상품을 기초자산으로 발행된 파생결합상품을 주로 활용할 수 있다. 파생상품은 증거금제도를 활용한 레버리지 투자가 가능하다는 특징이 있으며 이는 투자수익을 높일 수 있다는 장점이 있는 반면 원금을 초과하여 손실이 발생할 수 있기 때문에 변동성을 키우고 투자위험이 높아질 수 있다는 단점이 있다.   "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (618, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "파생상품 포트폴리오를 구성하는 경우 발생할 수 있는 투자태도로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["파생상품을 활용하여 포트폴리오를 구성할 때 과도한 레버리지 투자로 인해 투자원금을 초과하여 손실이 발생할 수 있다", "파생상품은 다른 금융상품 대비 유동성이 낮기 때문에 파생상품을 판매하는 금융투자회사의 헤지거래는 이러한 낮은 유동성을 더욱 악화시킬 가능성이 있다", "파생상품은 다양한 수익구조를 만들기 위해 주로 선물, 옵션 등을 기초로 금융공학기법을 활용하는 경우가 많아 상품구조가 복잡하다는 단점이 있다", "파생상품은 역외거래가 거의 없고, 대부분 역내거래 형태로 이뤄지고 있으므로 상품성이 제한될 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "파생상품을 거래하는 거래소는 국내외 매우 다양하며, 최근에는 역외 파생상품을 기초로 하여 다양한 구조의 상품이 만들어 지고 거래되고 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (619, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "파생상품을 활용하여 포트폴리오의 위험을 헤지하는 전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자가 주식선물을 활용하는 경우 편입할 주식 포트폴리오에 대해 주식선물 매수포지션을 취할 수 있기 때문에 주식 포트폴리오의 가격하락 위험을 헤지할 수 있다", "투자자는 KRX에 상장된 지수, 통화, 금리, 주식 등 다양한 자산을 기초로 하는 파생상품을 활용하여 포트폴리오의 가격하락위험을 헤지할 수 있다", "금리형 상품에 투자하는 포트폴리오의 경우 현물의 방향과 반대방향의 선물 포지션을 취함으로써 금리 변동시 포트폴리오의 손실을 헤지할 수 있다", "옵션을 활용하는 경우 풋옵션을 매수하여 미래 투자자산 가격 하락으로 인한 손실을 방어할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "주식선물의 경우 현재 편입하고 있는 주식 포트폴리오에 대해 매도 포지션을 취함으로써 주식포트폴리오의 현물가격이 하락하더라도 선물매도에 따른 차익으로 상쇄하는 헤지효과를 가져올 수 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (620, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "파생상품을 활용한 포트폴리오 운용전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자자가 선물을 활용하여 포트폴리오의 위험을 상쇄시킬수는 있으나 추가적인 수익창출 기회는 없다", "투자자가 옵션을 활용하여 포트폴리오의 위험을 헤지하는 것 뿐만 아니라 포트폴리오 수익을 개선시키는 것도 가능하다", "투자자는 보유한 주식을 매개로 콜옵션을 발행하여 상대방에게 매도함으로써 옵션 프리미엄을 통한 추가수익을 얻을 수 있다", "투자자는 보유한 주식을 매개로 풋옵션을 발행하여 상대방에게 매도함으로써 옵션 프리미엄을 통한 추가수익을 얻을 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "투자자는 보유한 주식을 매개로 콜옵션을 발행하여 거래 상대방에게 매도하는 대신 옵션프리미엄을 수취할 수 있으며, 이러한 전략을 커버드콜 전략이라고 한다. 이 전략에서의 핵심은 콜옵션 매도시 얻게 되는 프리미엄 수입이며 이는 단지 주식을 보유하는 것에서 옵션을 활용함으로써 얻게 되는 추가수익이다. 반면에 풋옵션을 매도함으로써 옵션프리미엄을 수취하는 전략을 커버드풋 전략이라고 하며 이 전략은 현재 현물을 보유한 것이 아니라, 매도 포지션을 취하고 있거나, 미래에 현물을 매입할 예정인 경우 사용하는 전략이다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (621, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "파생결합상품을 활용한 포트폴리오 운용전략에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["선물, 옵션과 같은 파생상품을 활용하여 특정 수익구조로 만든 금융상품으로 다양한 수익구조를 제공하는 장점이 있으나 구조가 복잡하여 개인투자자가 포트폴리오에 활용하기에는 어려움이 매우 많다", "주식이나 환율, 금리와 같은 기초자산과 선물, 옵션 등의 파생상품을 결합하여 기초자산의 움직임과 별도의 수익구조를 제공함으로써 포트폴리오 효과를 높일 수 있다", "포트폴리오에 활용하면 시장의 움직임이 급격한 변동성을 보이는 경우 손실폭이 커질 수 있다는 단점이 있다", "투자되는 기초자산가격의 움직임을 예측하기 어려운 국면에 기초자산 가격의 움직임과 상관관계가 낮게 구조화된 수익구조를 제공할 수 있는 장점이 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "파생결합상품은 주식이나 환율, 금리와 같은 기초자산과 선물, 옵션 등의파생상품을 결합하여 기초자산의 움직임과 별도의 수익구조를 제공하는 상품으로 포트폴리오 효과를 높일 수 있다는 장점이 있다. 아울러 개인 투자자가 포트폴리오에 편입하기 어려운 파생상품을 활용하여 구조화된 수익구조를 제공하는 금융투자상품으로 보다 용이한 투자가 가능하다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생결합상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (622, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주가연계 파생결합상품의 종류와 그에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["ELD(주가연계예금): 주가지수의 변화와 연계되어 지급이자율이 달라지는 예금상품으로 원금이 보장되고 예금자보호를 받는다", "ELF(주가연계펀드): 주가지수의 변화와 연계된 구조의 ELS(주가연계증권)에 투자하는 펀드로 투자성과는 편입한 ELS의 성과에 연동된다", "ELT(주가연계신탁): 주가지수의 변화와 연계된 구조의 ELS에 투자하는 특정금전신탁으로 증권회사나 은행 등 신탁업자가 취급하는 상품이다", "ELS(주가연계증권): 주가지수의 변화와 연계된 구조의 증권형 상품으로 원금이 보장되지는 않지만 예금자보호를 받는다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "ELS(주가연계증권), ELF(주가연계펀드), ELF(주가연계신탁)와 같은 상품은 투자결과에 따라 투자자의 수익이 달라지는 상품으로 원금이 보장되지 않고, 예금자보호에서 제외되는 상품이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생결합상품 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (623, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 A는 현재 보유하고 있는 주식 포트폴리오의 가격이 상승할 것으로 예상하고 있지만, 향후 1개월간 5% 이상의 수익은 거두기 어려울 것으로 판단하고 있다. 이러한 시나리오에 입각해서 투자자 A가 파생상품을 활용하여 해당 포트폴리오 운용을 최적화 할 수 있는 전략으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["보유 중인 주식포트폴리오의 가치에 해당하는 만큼 주식선물을 매도", "보유 중인 주식을 매개로 한 콜옵션을 매도하여 프리미엄을 수취", "보유 중인 주식포트폴리오의 가치에 해당하는 만큼 주식선물을 매수", "보유 중인 주식을 매개로 한 풋옵션을 매수하여 프리미엄을 수취"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "투자자 A는 현재 보유중인 주식포트폴리오의 가격이 제한된 범위 내에서 상승할 것으로 예상하고 있다. 따라서 이 경우 현재 보유 중인 주식 포트폴리오는 그대로 유지한 상황에서 옵션거래를 통해 추가적인 수익기회를 얻는 전략이 유효하며 이때 사용하는 전략이 보유 주식 포트폴리오를 매개로 한 콜옵션을 거래상대방에게 매도하고 그 대가로 옵션 프리미엄을 수취하는 전략이다. 이러한 전략을 커버드콜 전략이라고 한다. 커버드 콜 전략은 주식포트폴리오의 가격이 향후 상승할 것으로 예상되지만 제한된 범위내에서 상승할 것으로 판단되는 상황에서 유효한 전략이며, 실제 옵션 만기시점에 예상한 것보다 가격 상승폭이 큰 경우에는 옵션의 조건에 따라 수익이 제한될 수 있는 단점이 있다.  "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '파생결합상품 활용한 헤지 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (624, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드를 활용하여 포트폴리오를 구성하는 목적 및 장단점으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드는 전문가에 의한 운용이 이루어지기 때문에 다양한 자료와 시장데이터에 근거한 체계적인 포트폴리오 운용이 가능하다", "펀드는 다수의 투자자로부터 자금을 받아 집합투자형태로 운용하기 때문에 투자자가 직접 포트폴리오를 운용하는 방식에 비해서 비용이 절감되는 효과가 크다", "펀드는 다수의 투자자로부터 자금을 받아 집합투자형태로 운용되기 때문에 적은 금액으로도 포트폴리오 분산투자효과가 높다", "펀드를 활용하여 포트폴리오를 운용하는 경우 원금보장이 되지 않을 뿐만 아니라 예금자보호법의 대상도 되지 않으므로 주의해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "펀드는 전문가에 의한 체계적인 운용과 집합투자 운용방식에 따른 분산투자효과의 극대화, 풍부한 유동성 등 많은 장점을 가지고 있지만, 직접투자 대비 위탁운용 방식으로 인한 불가피한 투자비용 증가라는 단점도 동시에 가지고 있다. 따라서 투자자는 펀드를 활용하여 포트폴리오를 운용하고자 하는 경우 비용 대비 효율성이 높은 상품을 선별하여 투자하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (625, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드를 활용하여 포트폴리오를 구성하는 경우에 필요한 프로세스에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["다양한 조건들을 검토해야 하지만, 특히 최근 1년간 실현수익률을 평가하여 상위권에 있는 펀드를 중심으로 포트폴리오를 구성한다", "투자대상 자산이나 지역, 국가, 섹터 등에 대한 검토 뿐만 아니라 어떠한 스타일과 전략을 갖고 있는지에 대한 세분류도 매우 중요하다", "해당 운용사와 매니저의 투자철학이나 운용스타일을 확인하는 것이 필요하다", "펀드평가 사이트를 방문하여 펀드의 위험대비 성과지표 등을 확인하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "펀드를 활용하여 포트폴리오를 구성하는 경우 투자자는 과거 수익률 추이도 중요하지만 단순히 과거수익률에 의존하는 것 보다는 미래전망에 기초한 펀드투자가 더욱 중요하다. 이를 위해서 투자자는 사전적으로 펀드를 운용하는 자산운용사의 투자철학이나 전담 펀드매니저의 운용전략 등을 꼼꼼히 점검하는 것이 필요하고, 이를 위해서 해당 자산운용사의 홈페이지나 펀드평가사이트 등을 방문하여 리서치하는 것이 필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 이해', null, null);
