insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (628, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드 포트폴리오를 구성하는 과정에서 활용할 수 있는 다양한 전략에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["다양한 투자자산에 투자하는 펀드의 위험대비 기대수익을 확인하고 상관관계가 낮은 펀드에 분산투자하여 펀드 포트폴리오를 구성한다", "포트폴리오의 효율을 높일 수 있도록 상관관계가 높은 펀드에 대한 집중투자전략으로 포트폴리오를 구성한다", "펀드 포트폴리오의 효율성을 높이기 위해 위험대비 기대수익률을 체크하여 그 비율이 낮은 펀드에 대한 집중투자전략으로 포트폴리오를 구성한다", "다양한 수익-위험 구조를 가진 펀드유형 중 고수익-고위험 구조를 가진 펀드에 집중투자하는 포트폴리오 전략을 추구한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자자는 펀드 포트폴리오를 구성할 때 다양한 위험대비 기대수익을 가진 펀드에 분산투자하여 포트폴리오의 분산투자 효과를 높일 필요가 있다. 이를 위해서는 고위험-고수익유형의 펀드나 저위험-저수익유형의 펀드 등 특정 유형에 쏠리지 않고 다양한 수익-위험 구조의 펀드에 분산하여 투자하는 것이 필요하다. 투자자는 수익과 위험은 필연적으로 같은 방향으로 움직인다는 점을 인지해야 한다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (629, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드 포트폴리오를 관리하는 과정과 리밸런싱 프로세스에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개별 펀드에서 수행되는 자산 재배분과는 별도로 지속적인 보유 펀드 포트폴리오의 리밸런싱을 통해 전체 펀드 포트폴리오의 수익률 관리를 해야 한다", "펀드 포트폴리오의 효율을 높일 수 있도록 지속적인 펀드 리밸런싱을 수행해야 하며 최대한 그 주기를 단축할 필요가 있다", "리밸런싱에 수반되는 비용을 감안하여 가장 적합한 리밸런싱 주기를 설정하고 주기에 따라 리밸런싱을 수행하는 것이 효율적이다", "최초 포트폴리오 구성 시 정한 각 유형별 비중에서 일정 비율 이상으로 펀드 비중이 증감하는 경우에 최초 비율로 다시 조절해 주는 주기적 교체매매 전략을 활용할 필요가 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "펀드 리밸런싱을 위해서는 필연적으로 비용이 수반되며 이는 펀드 가입 시 지불하는 선취수수료 등과 같이 다양하다. 따라서 펀드의 리밸런싱 주기가 너무 잦으면 비용 대비 효율성이 떨어질 수 있다.  따라서 투자자는 펀드 리밸런싱을 위한 각자의 원칙을 정하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '펀드 포트폴리오 관리전략 이해', null, null);
