insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (631, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "대체투자상품 포트폴리오를 구성하는 목적에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["최근 대체투자상품의 급성장과 대중화로 인해 개인투자자들도 대체투자상품을 활용한 포트폴리오 구성이 점차 많아지고 있다", "대체투자상품은 일반 전통자산과의 낮은 상관관계로 인해 포트폴리오 투자의 효율성을 높일 수 있다", "대체투자상품 포트폴리오는 주식, 채권 등 전통자산 포트폴리오 대비 항상 높은 기대수익을 제공한다", "대체투자상품 포트폴리오는 시장에 연동되는 전통자산 대비 투자시점에 제시하는 목표 기대수익률의 달성 확률이 상대적으로 높다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "대체투자상품 포트폴리오는 전통자산 포트폴리오와의 낮은 상관관계 등포트폴리오 활용도 측면에서 매우 유용하다. 아울러 주식이나 채권과 같이 시장의 움직임에 깊이 연동되는 전통자산과 달리 투자시점에 제시하는 기대수익률의 달성 확률이 높은 경향이 있다. 그러나 대체투자상품 포트폴리오가 전통자산 포트폴리오 대비 일방적으로 수익률이 높거나 위험대비 기대수익이 좋은 것은 아니다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (632, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "대체투자상품 포트폴리오 구성과정 및 유의사항에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["대체투자자산은 일반적으로 투자규모가 크고, 직접 조달하기 쉬우며 장내시장에서 거래되는 형태가 많아서 개인 투자자가 대체투자상품 포트폴리오를 구성하는데 효율적이다", "대체투자상품은 일반적으로 전통자산 대비 투자기간이 장기이고 자산의 유동성이 낮기 때문에 유동성 관리가 중요하다", "대체투자상품은 일반적으로 공모형태보다는 사모형태로 모집되는 경우가 많기 때문에 대체투자상품 포트폴리오 구성과정이 전통자산 포트폴리오 대비 어려움이 있을 수 있다", "대체투자상품은 내재된 투자태도나 환매제약 조건 등 사전에 점검해야 할 사항들이 많기 때문에 전문가를 통해 꼼꼼히 확인하는 작업이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "대체투자자산은 일반적으로 투자규모가 크고, 직접 조달하기 어려우며 장외시장에서 거래되는 형태가 많아서 개인 투자자가 대체투자상품 포트폴리오를 구성하기 위해서는 직접투자보다는 펀드나 신탁 등 간접투자상품을 활용하는 것이 효율적이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (633, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "헤지펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["시장상황과 상관관계가 낮은 다양한 전략을 통해 보다 효율적인 포트폴리오 투자효과를 제공할 수 있다", "멀티전략형 헤지펀드는 주식, 채권 등의 전통자산과 부동산, 인프라 등 다수의 대체전략을 자산배분의 형태로 운용하는 전략으로 포트폴리오 투자효과를 높일 수 있다", "개인투자자가 직접적으로 투자하기 어려운 다양한 자산에 손쉽게 투자할 수 있는 수단을 제공하고 있으며 불특정다수의 투자자로부터 공모 형태로 펀드를 조성하여 높은 유동성을 제공하고 있다", "개인투자자가 헤지펀드에 가입할 때는 높은 최소가입규모와 함께 일정 기간 환매제한과 같은 까다로운 입출입 규정 등을 잘 살펴봐야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "헤지펀드는 전통자산 뿐만 아니라 다양한 대체전략을 레버리지를 일으켜 투자하는 사모펀드의 형태이므로 투자기간이 장기일 수 있고, 환매 제한기간과 높은 최소가입금액 등 개인 투자자 입장에서 제한이 있을 수 있다는 점은 감안해야 한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (634, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 투자자산 포트폴리오를 구성할 때, 대체투자상품과 전통자산투자상품의 비중을 조절하는 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 개인 투자자는 과거 투자 경험에 대한 의존도가 높아 포트폴리오가 특정자산에 편중되는 경향이 많아 시장 변동성에 그대로 노출되어 있는 전통자산과 시장 변동성과 연관성이 낮은 대체투자상품을 함께 섞어서 포트폴리오를 구성할 필요가 있다", "대체투자상품을 포트폴리오에 활용할 때는 전통자산에서 커버하지 못하는 투자태도-리턴 프로파일 상의 영역을 채워 투자하는 것이 좋다", "전통자산과 대체투자자산의 가격간 상관관계를 인지하고 시장 국면에 따라 유기적으로 비중을 조절하는 것이 필요하다", "전통자산과 대체투자자산은 상관관계가 낮아 포트폴리오 효과가 크기 때문에 초기에 비중을 균등하게 배분하여 투자할 필요가 있고, 이러한 비중을 절대적으로 유지하는 것이 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "일반적으로 개인 투자자는 과거의 투자경험에 대한 의존도가 높아 포트폴리오가 특정 자산에 편중되는 경향이 많이 나타나기 때문에 시장의 변동성에 그대로 노출되는 경우가 많다. 따라서 개인 투자자가 투자 포트폴리오에 대체투자상품을 활용하는 목적은 전통자산에서 커버하지 못하는 투자태도-리턴 프로파일 상의 영역을 채워, 보다 안정적인 자산관리를 하는데 있다. 따라서 투자자는 전통자산과 대체투자자산간의 상관관계를 파악하고, 시장국면에 따라 비중을 유기적으로 조절하는 것이 필요하다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (635, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "<보기>의 증권사 시장전망에 동의한 투자자가 취해볼 수 있는 전통자산과 대체투자상품 간 리밸런싱 내용으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["현재 시장 판단 및 향후 전망 :", "현재 시장은 풍부한 유동성으로 인해 경기 호황국면을 이어가고 있고, 이에 맞춰 기업실적은 호조세를 이어가고 있음. 반면 물가는 상승세를 이어가며 인플레이션에 대한 우려가 제기되는 국면임"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식비중 확대, 채권비중 축소, 부동산비중 축소, 원자재 비중 확대", "주식비중 축소, 채권비중 확대, 부동산비중 축소, 원자재 비중 확대", "주식비중 확대, 채권비중 확대, 부동산비중 확대, 원자재 비중 확대", "주식비중 축소, 채권비중 축소, 부동산비중 축소, 원자재 비중 축소"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "현재 시장국면은 경기호황국면이 이어지면서 기업실적의 호조세가 지속되고 이로 인한 주식시장의 활황이 지속될 수 있는 환경이다. 따라서 주식의 비중은 확대하는 것이 필요하다. 반면에 인플레이션에 대한 우려로 인해 향후 중앙은행의 정책금리 인상과 이로 인한 시장금리 상승이 예상되는 국면이기 때문에 채권의 비중은 축소하는 것이 필요하다. 대체투자상품 중 금리와 역의 상관관계에 있는 부동산 역시 비중을 축소하는 것이 바람직하며, 반면 인플레이션 헤지기능을 갖고 있는 원자재의 비중은 확대함으로써 인플레이션에 따른 부작용을 완화시킬 수 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (636, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "대체투자상품와 그에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["금은 대표적인 안전자산으로 인식되는 대체투자상품으로 글로벌 경기침체나 경제위기 국면에서 가격이 상승하는 특징이 있다", "부동산은 저금리가 이어지는 국면에서 가격이 상승하는 특징이 있으며 물가동향과 가격이 정비례하여 움직이는 특징이 있다", "원자재는 대표적인 인플레이션 헤지용 대체투자상품으로 주식이나 채권과 같은 전통자산과의 낮은 상관관계로 분산투자효과가 높다", "경기악화 국면에서 정부의 경기진착책 등 정책 이슈가 기대되는 국면에서는 인프라와 같은 대체투자상품에 대한 비중확대가 필요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "부동산은 저금리가 이어지는 국면에서 가격이 상승하는 특징이 있으며 물가의 움직임과 부동산 가격은 반대로 움직이는 경향이 있다. 따라서 인플레이션 국면에서 부동산 투자비중은 축소하는 전략이 필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '대체투자상품 포트폴리오 관리전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (637, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "상장지수펀드(ETF)에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["상장지수펀드는 낮은 비용, 높은 유동성, 투명한 운용 등의 특징을 갖추고 있어 개인 투자자가 포트폴리오 운용을 하는데 있어 효율을 높일 수 있는 대표적인 상품이다", "상장지수펀드는 주식, 채권 등과 같은 전통자산 뿐만 아니라 부동산, 원자재, 인프라 등 대체투자상품 등을 기초로 하여 만들 수 있어 활용도가 높은 상품이다", "상장지수펀드는 특정지수를 벤치마크로 선정하고, 이 지수를 추종하여 수익을 내는 패시브 형태의 상품만 존재한다", "최근에는 다양한 상장지수펀드를 편입하여 자산배분효과를 높이는 EMP (ETF Managed Portfolio)와 같은 상품도 등장하며 더욱 활용도를 높여주고 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "상장지수펀드는 특정지수를 벤치마크로 선정하고 이 지수를 추종하여 수익을 내는 패시브 펀드로 처음 등장하였으나, 최근에는 액티브 전략으로 운용되는 상품이 등장하며 빠르게 성장하고 있는 추세이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (638, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 자산배분전략과 포트폴리오를 구성하는데 활용할 수 있는 간접투자수단에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 자산배분전략과 포트폴리오를 구성하는데 있어 비용 측면과 효율성 측면에서 비교하면 직접 자산을 편입하는 것이 가장 유리하다", "펀드나 신탁과 같은 간접투자상품은 동일한 자산에 투자하더라도 운용주체의 성향에 따라 운용전략에 차이가 많을 수 있기 때문에 신중한 선택이 필요하다", "로보어드바이저 상품은 개인 투자자의 성향을 분석하여 이에 맞는 자산배분전략과 포트폴리오를 구성하고 관리하는 상품으로 최근 활용도가 증가하고 있다", "로보어드바이저와 같은 투자자 맞춤형 상품은 자산배분전략과 포트폴리오 운용에 있어 활용도가 높으나 고비용이나 성과의 불확실성 등의 단점도 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "개인 투자자가 간접투자상품을 활용하여 포트폴리오를 구성하는 것은 시장에서 직접 자산을 편입하여 포트폴리오를 구축하는 것 보다 훨씬 편리하고 효율적이다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (639, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "자산배분전략과 포트폴리오 구성에 앞서 수행되어야 하는 투자목표설정에 대한 설명으로 가장 거리가 먼 것은? ", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 자산배분전략과 포트폴리오를 구성함에 있어 수익실현 기준 및 로스컷 기준 등 명확한 목표를 수립하는 것이 매우 중요하다", "자산배분의 안정성과 투자 수익성을 함께 추구하는 것을 목표로 하는 전략을 베타-알파 투자전략이라 하고, 이 전략은 장기적이고 안정적 운용을 목표로 하는 베타 자산에 70~80%를 투자하고, 나머지 자산을 추가수익 가능성이 높은 자산에 투자하는 전략이다", "자산배분전략을 충실하게 반영하여 안정적인 성과유지를 목표로 하는 전략을 베타 투자전략이라고 하고, 이 전략은 최초 설정한 자산군별 비중을 큰 변화 없이 유지하는 전략이다", "시장상황에 따른 적극적인 투자수익 추구를 목표로 하는 전략을 알파 투자전략이라고 하고, 이 전략은 전체 자산을 추가수익 가능성이 높은 고위험-고수익 자산에 투자하는 전략이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "자산배분전략과 포트폴리오 운용을 수행하는 목적은 특정 자산에 몰빵투자를 하거나 단기적인 시장의 움직임만을 추종하여 투자함으로써 발생할 수 있는 위험을 줄이고 안정적인 자산관리를 도모하기 위함이다. 따라서 다양한 자산배분전략과 포트폴리오 구성의 목표 중 특정 자산이나 전략에 전체 자산을 투자하는 것은 성립되지 않는다. 알파투자전략의 경우에는 베타투자자산의 비중을 50% 수준 이내에서 유지하고, 나머지 비중은 시장의 단기변화를 효과적으로 포착할 수 있는 알파투자 전략에 투자하는 전략을 말한다"}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (640, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자가 투자목표를 정하고 포트폴리오를 관리할 때의 유의사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 투자목표를 수립할 때는 가급적 목표수익률을 높게 설정하고 시장상황에 따라 목표수익률을 조절해 가는 것이 필요하다", "포트폴리오를 관리함에 있어 기준이 되는 수익률은 전 운용기간에 걸친 평균수익률을 사용하여야 한다", "포트폴리오를 관리함에 있어 유동성 투자태도에 대한 점검이나 긴급상황에 대비한 유동성 관리는 필수적이다", "포트폴리오의 수익률은 특정시점에만 관리하는 것이 아니라 시장환경의 변화에 따라 지속적으로 관리하는 것이 중요하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "투자에 대한 목표 수익률은 적정한 수준으로 설정하는 것이 좋다. 목표수익률이 과도할수록 투자자는 특정 자산에 집중해서 투자하는 경향이 생기게 되고 이로 인해 안정적인 자산관리가 어려워 질 수밖에 없다. 자산배분전략과 포트폴리오 운용은 일시적인 높은 수익이 아니라 꾸준하고 안정적인 수익을 지속적으로 가져감으로써 안정적인 자산관리를 할 수 있는데 목적이 있다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (641, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인별 목표에 기반하여 자산배분전략과 포트폴리오 운용을 수행하는 방법에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인투자자가 기관투자자 및 투자전문기관의 자산배분전략을 참고할 수는 있지만, 투자자의 개별 상황을 감안하지 않은 전략이므로 현실적으로 맞지 않을 수 있다", "투자자 목표기반 자산배분전략과 포트폴리오를 구성함에 있어 가장 중요한 것은 시장성과 대비 꾸준히 초과성과를 달성하는 것에 있다", "투자자 목표기반 자산배분전략과 포트폴리오를 구성하는 것은 나의 목표와 상황에 맞는 가장 효율적인 전략을 찾는 것이다", "투자자 목표기반 자산배분전략과 포트폴리오 구성을 통해 개인투자자가 가장 노출되기 쉬운 군중심리 투자태도를 제어할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "투자자 목표기반 자산배분전략과 포트폴리오를 구성하는데 있어 중요한 것은 시장의 성과 대비 몇 %의 수익을 얻겠다거나, 절대수익으로 몇 %의 수익을 내겠다는 것이 아니라 내가 수립한 목표를 달성하기 위한 자산배분과 포트폴리오 전략을 구성하는 것에 있다."}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (642, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "투자자 목표기반 자산배분전략 및 포트폴리오 구성 단계에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["첫번째 단계: 구체적이고 현실적인 투자 목표를 수립해야 하며 구체적으로 명확한 투자기간과 미래에 필요한 자금 규모 등과 같은 현실적인 목표를 수립한다", "두번째 단계: 세금제도에 기반한 세후 수익률과 함께 투자시 소요되는 투자비용을 고려하여 포트폴리오에 넣을 수 있는 투자 후보군을 구축한다", "세번째 단계: 투자자산의 위험 대비 기대수익을 감안하여 목표에 맞는 투자군을 선택해야 하며 이때 투자자는 자산군의 최근 성과를 기준으로 투자군을 선택한다", "네번째 단계: 투자자의 제약 조건을 고려해서 시나리오 분석을 진행하고 세번째 단계에서 선택된 투자군 내에 있는 자산 중에서 실제 투자자산을 선택하여 투자 포트폴리오를 구성한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "투자자 목표기반 자산배분전략과 포트폴리오를 구성 절차 중 세번째 단계에서는 투자자산의 위험 대비 기대수익을 감안하여 목표에 맞는 투자군을 선택하게 되며 투자자는 자산군의 최근 성과만을 기준으로 판단하는 것이 아니라 장기간의 평균수익률을 감안하는 것이 중요하다. 따라서 투자자는 장기간의 역사적 성과에 기반해서 다양한 자산들의 위험대비 기대수익을 참고하여 투자하는 것이 필요하다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/09/06 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '개인투자자 자산배분전략 이해', null, null);
