insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (660, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "가치주와 성장주에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 성장주가 가치주보다 투자위험성이 크다고 할 수 있다", "가치주는 현재의 주가가 현재의 기업의 가치에 비해 저평가된 주식이다", "성장주는 기업의 미래 가치의 기대치가 높은 주식이다", "개인투자자가 성장주에 투자할 경우 간접투자보다는 직접투자가 바람직하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "성장주 투자는 가치주 투자에 비해 투자위험도가 크므로 개인투자자가 성장주에 투자하고자 하는 경우 직접투자보다는 간접투자 방식을 택하는 것이 바람직하다\n\n지문1의 경우, 가치주의 특징을 설명한 것으로 맞는 답이다\n지문2의 경우, 가치주의 특징을 설명한 것으로 맞는 답이다.\n지문3의 경우, 성장주의 특징을 설명한 것으로 맞는 답이다.\n지문4의 경우, 특히 성장주투자는 많은 투자지식과 투자경험이 필수적이므로 개인투자자들에게는 직접투자방식은 바람직하지 않기 때문에 오답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '주식 분류 이해', null, null);
