insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (662, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "국내 주식시장에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["국내에는 코스피시장, 코스닥시장, 코넥스시장이 있다", "시가총액 기준으로는 코스피시장이 규모가 가장 크고 상장종목 수도 가장 많다", "대형우량주는 대부분 코스피시장에 상장되어 있다", "상장요건이 가장 완화되어 있는 시장은 코넥스시장이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "3개의 시장 중에서 시가총액 기준으로는 코스피시장의 규모가 가장 크지만 상장종목의 수가 가장 많은 시장은 코스닥시장이다\n\n지문1의 경우, 국내에는 코스피시장, 코드닥시장 그리고 코넥스시장이 있으므로 맞는 답이다\n지문2의 경우, 3개의 시장 중에서 시가총액 기준으로는 코스피시장의 규모가 가장 크지만 상장 종목 수 기준으로는 코스닥시장이 최대의 시장이므로 오답이다\n지문3의 경우, 코스피시장의 특징이므로 정답이다.\n지문4의 경우, 코넥스시장의 특징이므로 정답이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '유통시장 제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (663, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "시가총액에 대한 설명으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기업이 발행한 주식 수에 매년말 기준 주가를 곱해서 산출한다", "중형주는 시가총액 기준 상위 101위에서 500위까지 400종목을 말한다", "시가총액은 매일 변하는 것이 일반적이다", "회사의 규모와 아무런 관계가 없다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "시가총액은 기업이 발행한 주식의 수에 현재의 주가를 곱해서 계산되므로, 기업의 주가가 변하거나 상장주식의 수가 변하면 시가총액도 변한다\n\n지문1의 경우, 시가총액은 기업이 발행한 주식의 수에 현재의 주가를 곱해서 계산되므로 오답이다\n지문2의 경우, 중형주는 시가총액 기준 상위 101위에서 300위까지 200개 기업의 주식을 말하는 것이므로 오답이다\n지문3의 경우, 기업의 주가가 변하거나 상장주식의 수가 변하면 시가총액도 변하므로  맞는 답이다.\n지문4의 경우, 기본적으로 시가총액이 큰 기업을 대기업이라고 부르기도 하기 때문에 틀린 답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '유통시장 제도 이해', null, null);
