insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (665, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "고배당주에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["고배당주로 구분된 기업은 일정한 배당금을 매분기 지급하고 있다", "고배당 종목은 배당금을 지급하지 않는 일반 종목에 비해 상대적으로 안정된 기업이라고 볼 수 있다", "저금리 시대에서는 고배당주 투자의 장점이 더욱 부각될 수 있다", "대기업이라고 해도 반드시 정기적인 배당금을 지급하는 것은 아니다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "아무리 고배당주라고 하더라도 배당금 지급 여부와 배당금의 지급 규모는 기업의 경영 방침에 따라 가변적이다.\n\n지문1의 경우, 은행이자와는 달리 배당금 지급 여부와 배당금의 지급 규모는 기업의 경영 방침에 따라 가변적이므로 오답이다.\n지문2의 경우, 고배당주에 대한 설명으로 맞는 답이다\n지문3의 경우, 고배당주 투자전략에 대한 설명으로 맞는 답이다.\n지문4의 경우, 고배당주에 대한 설명으로 맞는 답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '주식 분류 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (666, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식투자 시 기본적 분석과 기술적 분석에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["기본적 분석의 핵심은 기업 재무제표에 근거한 분석이다", "기술적 분석의 활용가치는 장기투자에서 더욱 유용하다", "기술적 분석의 근거 중의 하나는 주가 움직임이 투자자의 군중심리 영향을 받기도 하기 때문이다", "개인투자자들은 기본적분 석을 우선적으로 활용하는 것이 바람직하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "기술적분석의 활용가치는 단기투자에서 더 높다고 볼 수 있다.\n\n지문1의 경우, 기본적분석에서 가장 중요한 자료 중의 하나는 기업의 재무제표이으로 맞는 답이다.\n지문2의 경우, 단기적인 주가의 변동은 기업의 기본적 가치와는 별도로 시장에서 투자자의 심리적 요인이 죄우할 수 있기 때문에 틀린 답이다\n지문3의 경우, 기술적분석의 개요로서 정답이다.\n지문4의 경우, 개인투자자들은 기본적분석의 결과를 더욱 중시해야 하므로 정답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '기본적 분석과 기술적 분석 구별', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (667, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "액티브형과 패시브형 펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["액티브형 펀드는 시장수익률을 능가하는 초과수익 달성에 목표를 두고 있다", "패시브형 펀드는 기본적으로 시장평균 수익 달성을 목표로 한다", "인덱스펀드는 대표적인 액티브형 펀드이다", "펀드시장에서는 여전히 액티브형 펀드의 시장점유율이 높은 편이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "인덱스펀드는 대표적인 패시브형 펀드이다\n\n지문1의 경우, 액티브형 펀드에 대한 설명으로 맞는 답이다\n지문2의 경우, 패시브형 펀드에 대한 설명으로 맞는 답이다\n지문3의 경우, 패시브형 펀드는 일반적으로 인덱스펀드로 부르기도 한다.\n지문4의 경우, 액티브형 펀드와 패시브형 펀드는 각각 장단점이 있으며 국내 펀드시장을 포함하여 전 세계 펀드시장에서 여전히 액티브형 펀드의 시장점유율이 높다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '액티브운용 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (668, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "역내(On-shore)펀드와 역외(Off-shore)펀드에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["역내펀드는 국내에서 설정된 펀드로서 원화로 투자를 할 수 있다", "역외펀드는 해외에서 설정되어 운용되고 있지만 국내에서 판매되는 펀드이다", "역외펀드에 투자하기 위해서는 달러, 유로 등의 외화로 투자해야 한다", "역내펀드에 투자하기 위해서는 환헤지가 필수적이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "환헤지는 투자자가 스스로 금융기관과 별도의 계약을 체결해야 한다\n\n지문1의 경우, 역내펀드에 대한 설명으로 맞는 답이다\n지문2의 경우, 역외펀드에 대한 설명으로 맞는 답이다\n지문3의 경우, 역외펀드에 대한 설명으로 맞는 답이다\n지문4의 경우, 역외펀드는 외화로 투자하기 때문에 투자자는 환율 변동의 위험에 노출되게 된다. 따라서 환위험 요인을 제거하기 위해서는 환헤지가 필요하다. 역내펀드는 환헤지가 불필요하다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '해외투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (669, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "객관적 펀드평가에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드평가회사가 국내 판매되는 모든 펀드를 평가하는 것은 아니다", "펀드평가의 기준은 매우 다양하다", "펀드평가회사는 펀드평가를 전문으로 하는 비영리법인이다", "펀드 선택 시 펀드평가회사의 평가결과를 참작하면 유용하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "펀드평가회사는 제3의 독자적인 펀드평가를 전문으로 하는 회사이지만 국가기관이나 공기업이 아닌 일반 회사일 뿐이다\n\n지문1의 경우, 펀드평가 정보에 대한 설명으로 맞는 답이다\n지문2의 경우, 펀드에 대한 평가 기준은 매우 다양하다. 펀드의 수익률만 보더라도 평가 기간이 단기 또는 장기인가에 따라 평가의 결과가 완전히 달라질 수 있다\n지문3의 경우, 펀드평가회사는 자산운용회사나 은행 또는 증권회사에 속하지 않고 완전히 독립적인 회사이지만 국가기관은 아니다\n지문4의 경우, 동일한 펀드에 대한 펀드평가회사, 자산운용회사 또는 은행이나 증권회사의 평가결과 다를 경우 펀드평가회사의 결과가 상대적으로 객관적이라고 할 수 있다. 따라서 투자자는 펀드평가회사의 평가 결과도 항상 관심을 가져야 한다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (670, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "펀드평가 정보에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["펀드평가회사에서 제공하는 펀드평가는 투자수익률 뿐만 아니라 투자운용 상의 위험 요인 등 다수의 요소를 종합적으로 고려하여 평가하고 있다", "펀드평가회사는 국내에서 운용되고 있는 대부분의 펀드를 평가하고 있다", "펀드평가회사는 운용자산의 규모, 설립기간 등을 고려하여 일정 요건을 갖춘 펀드만 평가하고 있다", "펀드평가회사 간의 평가 또는 자산운용회사, 은행이나 증권회사 자체의 평가 결과는 다를 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "펀드평가회사에서 평가하는 펀드는 일정한 자격 요건을 갖춘 펀드만을 대상으로 하고 있기 때문에 펀드평가 등급을 받는 펀드의 숫자는 제한적이다\n\n지문1의 경우, 펀드평가 방법에 대한 설명으로 맞는 답이다\n지문2의 경우, 일정 요건을 갖춘 펀드 만을 대상으로 하고 있기 때문에 대부분의 펀드를 평가하는 것은 아니다\n지문3의 경우, 펀드평가 대상에 대한 설명으로 맞는 답이다\n지문4의 경우, 펀드평가 결과에 대한 설명으로 맞는 답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '펀드관련제도 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (671, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "인덱스펀드에 관한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["인덱스펀드는 기본적으로 시장평균 수익률 달성을 목표로 하기 때문에 모든 인덱스펀드의 투자실적은 동일할 수 밖에 없다", "인덱스펀드도 차별화 전략을 통해서 운용스타일 등이 다양해지고 있다", "인덱스펀드의 특징 중의 하나는 저렴한 운용보수에 있다", "인덱스펀드와 일반 액티브형 펀드 간 수익률 우위는 시장 상황에 따라 달라질 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 1}, {"type": "SOLUTION_TEXT", "data": "인덱스펀드라고 하더라도 다양한 투자전략이 구사되고 있으며 결과적으로 투자수익률도 달라지게 된다. 다만 일반 액티브펀드 간의 수익률의 차이보다는 인덱스펀드 간의 수익률의 차이는 그 진폭이 소규모인 경우가 많다\n\n지문1의 경우, 인덱스펀드도 운용스타일이 각각 다르기 때문에 아무리 인덱스펀드라고 하더라도 모든 인덱스펀드의 투자수익률이 동일할 수는 없다\n지문2의 경우, 다양한 인덱스펀드가 등장화면서 인덱스펀드 간의 차별화전략도 필요해지고 있다\n지문3의 경우, 인덱스펀드의 장점에 대한 설명으로 맞는 답이다\n지문4의 경우, 인덱스펀드와 액티브펀드의 수익률 특징에 대한 설명으로 맞는 답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '인덱스펀드 개념 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (650, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식투자 시 발생하는 체계적 위험을 줄일 수 있는 방법으로 가장 적절한 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분전략에 의한 분산투자", "국내주식에 집중투자", "국내채권에 집중투자", "대형주에 집중투자"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "체계적 위험은 주식 이외의 다양한 형태의 자산이나 해외시장에 자산을 분산하여 투자함으로써 줄일 수 있다.\n지문1의 경우, ‘자산배분전략’이란 다양한 형태의 자산이나 국내외 시장에 분산하여 투자하는 전략을 말한다.\n지문2의 경우, 국내주식투자에 전념할 경우 국내시장에서 발생하는 투자위험을 피하기에는 한계가 있다.\n지문3의 경우, 주식투자 시에 발생하는 체계적 위험을 줄이기 위해 주식 이외의 금융투자상품에 전념하는 것도 한계가 있다.\n지문4의 경우, 주식투자 시에 발생하는 체계적 위험을 줄이기 위해 부동산 자산에 투자를 전념하는 것도 바람직한 대안이 될 수는 없다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '기본적 투자태도관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (651, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "주식투자 시 발생하는 비체계적 위험에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["특정 산업에 집중투자하는 경우 발생하기 쉽다", "특정 기업에 집중투자하는 경우 발생하기 쉽다", "다양한 산업에 투자 종목을 늘림으로써 줄일 수 있다", "투자종목 수을 늘린다고 해도 줄이기가 쉽지 않다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 4}, {"type": "SOLUTION_TEXT", "data": "비체계적 위험은 특정 기업이나 특정 산업의 주식에 집중투자하는 경우에 발생하기 때문에 투자종목을 늘릴 경우 줄어들게 된다.\n지문1의 경우, 성장가능성이 높다고 해서 특정 산업에 집중해서 투자하는 경우 비체계적 위험이 증가된다.\n지문2의 경우, 성장가능성이 높다고 해서 특정 기업에 집중해서 투자하는 경우 비체계적 위험이 증가된다.\n지문3의 경우, 비체계적 위험을 줄일 수 있는 가장 좋은 방법 중 하나는 다양한 산업에 걸쳐 투자종목을 늘림으로써 가능해진다.\n지문4의 경우, 투자종목을 늘릴 경우 투자위험, 특히 ‘비체계적 위험’을 줄일 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '기본적 투자태도관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (652, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자의 재무상태를 파악하기 위해 재무상태표를 준비하는 방법에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["개인자산의 정리 및 기록은 유동성이 높은 순서대로 하는 것이 적합하다", "개인부채는 단기부채와 장기부채로 구분하여 정리하고 기록하는 것이 적합하다", "단기부채란 일반적으로 상환기간이 2년 이내인 부채를 말한다", "유동성이 높다는 것은 현금화를 비교적 빨리 시킬 수 있다는 것을 말한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 3}, {"type": "SOLUTION_TEXT", "data": "단기부채란 일반적으로 상환기간이 1년 이내인 부채를 말한다\n\n지문1의 경우, 개인자산의 정리 및 기록은 유동성이 높은 순서대로 하는 것이 적합하기 때문에 맞는 설명이다.\n지문2의 경우, 개인부채는 단기부채와 장기부채로 구분하여 정리하고 기록하는 것이 적합하기 때문에 맞는 설명이다.\n지문3의 경우, 단기부채란 일반적으로 상환기간이 1년 이내인 부채를 말하기 때문에 틀린 설명이다.\n지문4의 경우, 유동성이란 현금화를 시키는 과정에서 신속하면서 동시에 자산가치의 훼손을 최소화 정도를 의미한는 것으로서 유동성이 높다는 것은 현금화를 빨리 시킬 수 있다는 의미이기 때문에 맞는 설명이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인자산관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (653, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인의 현금흐름표에 포함되는 비상자금에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["일반적으로 향후 6개월 동안 소요될 것으로 예상되는 자금이다", "일반적으로 주택마련자금이나 자녀교육비를 의미한다", "주로 기본생활비를 말한다", "기타 예비자금도 포함시키는 것이 적절하다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "주택마련자금이나 자녀교육비는 비상자금의 성격과는 거리가 멀다\n\n지문1의 경우, 비상자금은 예상치 않은 사태에 대비하기 위한 자금을 말하는 것으로 일반적으로 6개월 간의 생활비를 말하기 때문에 적합한 설명이다.\n지문2의 경우, 주택마련자금이나 자녀교육비 등은 예상치 않은 사태라고 말하는 것은 적절하지 않기 때문에 틀린 설명이다.\n지문3의 경우, 비상자금은 예상치 않은 사태에 대비하기 위한 향후 6개월 정도 소요될 기본생활비를 말하기 때문에 맞는 설명이다.\n지문4의 경우, 비상자금은 예상치 않은 사태에 대비하기 위한 향후 6개월 정도 소요될 기타 예비자금도 포함시키는 것이 적절하기 때문에 맞는 설명이다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '개인자산관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (672, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "개인투자자의 바람직한 해외투자 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["해외투자는 국내투자 일변도에서 벗어나 투자수익을 높이거나 투자위험을 줄이기 위해 효과적이고 필요한 투자전략이다", "수익률 증대를 위해 이머징마켓 투자비중을 극대화할 필요가 있다", "투자위험 관리를 위해 해외투자는 금융투자상품의 종류나 투자지역을 다양화해야 한다", "최근 들어 해외투자 비중을 늘려나가고 있는 국민연금 사례를 참고할 필요가 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "이머징마켓에 집중투자하여 고위험-고수익을 노리는 해외투자전략은 바람직한 투자가 아니다\n\n지문1의 경우, 해외투자의 필요성에 대한 설명으로 맞는 답이다\n지문2의 경우, 이머징마켓에 대한 집중투자는 투자위험을 오히려 높일 수가 있기 때문에 해외투자의 효과와는 거리가 먼 설명이다\n지문3의 경우, 해외투자의 방법과 전략에 대한 설명으로 맞는 답이다\n지문4의 경우, 해외투자의 필요성에 대한 설명으로 맞는 답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '해외투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (673, 'MULTIPLE_CHOICE_CORRECT_ANSWER', '[{"data": "해외투자에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["국내 주식시장의 규모는 전 세계시장에서 2%도 채 안 되는 소규모 시장이다", "해외주식투자는 세계 최대의 시장인 미국시장에만 집중해서 투자하는 것이 가장 효과적이라고 할 수 있다", "정보부족의 문제가 있기 때문에 간접투자가 직접투자보다 효과적이라고 할 수 있다", "성장잠재력 측면에서는 이머징마켓이 선진국시장보다 매력적이다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": 2}, {"type": "SOLUTION_TEXT", "data": "아무리 미국시장이 최대의 시장이기는 하지만 해외투자의 효과를 보기 위해서는 미국시장 일변도 보다는 다른 외국시장에 대해서도 일정 부분 분산투자가 필요하다\n\n지문1의 경우, 국내주식시장의 규모에 대한 설명으로 맞는 답이다\n지문2의 경우, 주식시장의 규모로 본다면 미국시장에 대한 투자비중이 높아야 되는 건 적절한 전략이지만 미국시장에 올인하는 투자는 지양할 필요가 있다\n지문3의 경우, 해외투자의 방법과 전략에 대한 설명으로 맞는 답이다\n지문4의 경우, 이머징마켓에 대한 설명으로 맞는 답이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/10/03 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, null, '해외투자 이해', null, null);
