insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (1, 'MULTIPLE_CHOICE', '[{"data": "다음 조건 아래에서 적절한 자산관리 방법으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["정기예금금리(무위험수익률): 1%", "위험보상율: 3%"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["여유자금을 정기예금에 투자한다", "여유자금을 기대수익률이 3%인 금융상품에 투자한다", "여유자금을 기대수익률이 5%인 금융상품에 투자한다", "여유자금을 기대수익률이 7%인 금융상품에 투자한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "투자할 자산으로부터 예상되는 수익률과 투자자가 만족할 만한 수익률을 비교하면 비교적 쉽게 투자 의사를 결정할 수 있다. 투자자는 특정한 투자 대상에서 ''기대되는 수익률(expected return)''이 일정 수준 이상이어야 투자한다. 이때 투자자를 만족시킬 수 있는 최소한의 수익률을 ‘요구 수익률(required return)’이라고 한다. 투자자들이 기대하는 최소한의 요구 수익률은 정기예금(무위험자산)의 이자율과 위험보상율을 합한 것이다. \n\n요구 수익률 = 이자율(무위험자산 수익률)+위험 보상률\n\n위 경우 무위험수익률(1%)과 위험보상률(3%)을 합한 요구수익률은 4%이다. 따라서 기대수익률이 4% 이상인 금융상품에 투자하거나, 아니면 무위험자산에 맡겨 두는 것이 낫다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '요구수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (2, 'MULTIPLE_CHOICE', '[{"data": "3년 간 주식투자로 다음과 같은 현금흐름이 발생했을 때, 보유기간 동안 수익률로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_IMAGE", "preface": "null", "dataType": "image", "data": ["img_1.png"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["20%", "25%", "30%", "35%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "투자에서 발생하는 수익률은 다음과 같이 계산한다. 먼저 주식과 채권과 같은 투자 자산을 매도한 가격에서 이를 매입할 때 들어간 금액을 뺀 시세 차익과 채권 이자와 주식 배당처럼 투자 자산에서 발생한 현금흐름을 더한다. 그리로 수수료 등 비용을 차감한다. 이렇게 계산한 금액을 매입 금액으로 나눈 다음 100을 곱해 퍼센트(%)로 환산하면 수익률을 계산할 수 있다. \n\n수익률=(매도 금액-매입 금액±보유 기간 중 현금흐름-비용)/(매입 금액)  ×100\n          =  (120만원-100만원+10만원-5만원)/100만원  ×100 = 25% "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자수익률 계산', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (3, 'MULTIPLE_CHOICE', '[{"data": "1000만원을 연 복리 6%로 투자해서 2,000만원으로 만드는데 걸리는 시간으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["6년", "8년", "10년", "12년"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "72의 법칙은 복리를 전제로 자산이 2배로 늘어나는 데 걸리는 시간을 손쉽게 계산하는 방식이다. 72를 해당 수익률로 나누면 대략적으로 원금의 2배가 되는 기간을 산출할 수 있다.  \n\n72÷금리= 원금이 2배가 되는 시기(연)\n\n따라서 1000만원의 돈을 연 6%의 복리 상품에 투자하면 원금이 2배가 되는 데 걸리는 시간은 12년(72÷6=12)이다. "}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '72의 법칙 활용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (4, 'MULTIPLE_CHOICE', '[{"data": "복리효과를 감안했을 때 수익률이 가장 높은 사람으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["강감찬씨는 1년을 투자해서 6% 수익을 냈다", "김유신씨는 2년을 투자해서 12% 수익을 냈다", "곽재우씨는 3년을 투자해서 18% 수익을 냈다", "이순신씨는 4년 동안 투자해서 24% 수익을 냈다"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["강감찬", "김유신", "곽재우", "이순신"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "투자 대상 기간이 서로 다를 때는 서로 비교할 수 없다. 이때는 수익률을 1년을 기준으로 표준화 해서 비교하는 것이 일반적이다. 그리고 연율화 과정에서 재투자를 가정해 복리를 적용해 계산하는 것이 원칙이다. \n② 김유신씨가 2년 동안 낸 12% 수익을 연으로 환산하면 5.8%이다.\n③ 곽재우씨가 3년 동안 낸 18% 수익을 연으로 환산하면 5.8%이다.\n④ 이순신씨가 4년 동안 낸 24% 수익을 연으로 환산하면 5.5%이다. "}]', '상', '미니테스트', null, 35, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '복리효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (5, 'MULTIPLE_CHOICE', '[{"data": "투자 수익률을 계산할 때 고려해야 할 사항으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["거래가 빈번하면 수수료 부담이 커진다", "비과세 금융상품을 선택하면 수익률을 높일 수 있다", "기회비용은 명시적으로 계산할 수 없으므로 고려하지 않는다", "투자대상을 평가할 때는 수익과 함께 위험도 고려해야 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "수수료와 세금이 눈에 드러나는 비용이라면 그렇지 않은 것도 있다. 대표적인 예로 기회비용이 있다. 기회비용이란 어떤 행위를 하기 위해 포기해야 하는 다른 기회의 가치를 말하는데, 투자에도 그대로 적용된다. 예를 들어, 주식 투자를 하면서 많은 시간과 노력을 기울여 종목을 발굴할 것이다. 이런 시간과 노력이 얼마만큼 가치를 가질 것인지 명시적으로 계산하기는 어렵지만, 기회비용이 발생한 것만큼은 분명하다. 시간과 노력을 다른 곳에 기울일 수도 있기 때문이다. 투자 활동에 수반되는 기회비용을 줄이기 위한 방편으로 직접투자 대신 펀드와 위탁 판매와 같은 간접투자를 이용하기도 하는데, 이때는 투자 결정을 대신해 주는 대가로 운용 보수를 지불한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '수익률 결정요소로서 비용', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (6, 'MULTIPLE_CHOICE', '[{"data": "투자수익과 리스크에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["리스크를 감수하지 않으면 손실을 보지도 않지만 수익을 얻을 수도 없다", "기대수익이 10%라는 말은 항상 10% 수익을 얻는다는 뜻이다", "수익의 변동폭이 크면 리스크가 크다고 할 수 있다", "투자에서 위험은 분산이나 표준편차로 측정할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "기대 수익률이 연 10%라고 해서 그 주식에 투자하면 무조건 연 10%의 수익을 얻는다는 뜻은 아니다. 기대한 수익이 실현되지 않을 가능성, 투자 결과가 기대했던 수익과 다를 가능성을 리스크라고 한다. 투자 위험은 투자에서 예상되는 미래 수익률의 분산이나 표준편차로 측정한다. 즉, 예상 수익률이 기대 수익률에서 크게 벗어나지 않으면 투자 위험이 적거나 없다고 하고, 예상되는 수익이 들쑥날쑥하면 리스크가 크다고 말한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '리스크에 대한 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (7, 'MULTIPLE_CHOICE', '[{"data": "다음 중 가장 유리한 투자대안으로 올바른 것은? (단, 모든 조건은 동일하다고 가정)", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자위험(표준편차) 10%, 기대수익률 5%", "투자위험(표준편차) 10%, 기대수익률 7%", "투자위험(표준편차) 10%, 기대수익률 10%", "투자위험(표준편차) 10%, 기대수익률 15%"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자위험이 동일하다면 수익률이 큰 자산을 선택한다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (8, 'MULTIPLE_CHOICE', '[{"data": "다음과 같은 투자대안이 있을 때, 합리적 선택으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자대안 A: 기대수익률 5%, 투자위험 10%", "투자대안 B: 기대수익률 10%, 투자위험 10%", "투자대안 C: 기대수익률 10%, 투자위험 7%", "투자대안 D: 기대수익률 5%, 투자위험 7%"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["투자대안 A를 매수하고 B를 매도한다", "투자대안 B를 매도하고 C를 매수한다", "투자대안 C를 매수하고 D를 매도한다", "투자대안 A를 매도하고 D를 매수한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "기대수익이 동일하다면 투자위험이 적은 투자대안을 선택하고, 투자위험이 동일하다면 수익률이 큰 대안을 선택하는 것이 유리하다. A와 B는 투자위험이 동일하기 때문에 기대수익률이 큰 B를 매수하고 A를 매도하는 것이 유리하다.   "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '합리적 투자결정', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (9, 'MULTIPLE_CHOICE', '[{"data": "투자계획을 수립할 때 고려해야 할 사항을 올바르게 모두 묶은 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자자의 위험 감내 수준", "투자기간", "자산배분", "제약조건"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["가, 나, 다", "나, 다, 라", "가, 나, 라", "가, 나, 다, 라"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "투자 관리는 크게 투자 계획(Plan)을 수립하고 실행하고(do) 통제(see)하는 3단계를 거친다. 투자 계획 수립은 먼저 투자 목표를 수립하는 것에서 시작하는데, 이때 투자자의 위험 감내 수준, 투자 기간, 제약 조건을 함께 고려해야 한다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자계획 수립 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (10, 'MULTIPLE_CHOICE', '[{"data": "통합적인 투자관리가 이루어지는 과정을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["투자계획", "자산배분", "포트폴리오 평가", "종목선정"]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["가-나-다-라", "가-다-나-라", "가-나-라-다", "가-라-나-다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [3]}, {"type": "SOLUTION_TEXT", "data": "Plan(투자계획)-Do(자산배분 및 종목선정)-See(포트폴리오 평가)의 순서로 투자관리가 이루어진다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자관리 프로세스 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (11, 'MULTIPLE_CHOICE', '[{"data": "장기 투자성과에 가장 큰 영향을 미치는 것으로 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["자산배분", "종목선정", "투자타이밍", "리밸런싱"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "투자 전략을 실행하는 과정에서 투자자는 자산 배분, 종목 선정, 투자 시기 포착이라는 3가지 문제에 직면한다. 이중 투자성과에 가장 큰 영향을 미치는 것은 자산배분이다. 1974~1983년 91개의 대규모 연금 플랜을 연구한 결과, 자산 배분이 포트폴리오 운용 성과에 91.5%의 영향을 미치는 것으로 나타났고, 증권 선택(2.7%)과 시장 예측(1.8%)이 미치는 영향은 미미했다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/06/28 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '자산배분 중요성 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (12, 'MULTIPLE_CHOICE', '[{"data": "투자전술을 수립하는 과정에 대한 설명으로 괄호 안에 들어갈 말을 순서대로 올바르게 나열한 것은?", "type": "QUESTION_TEXT"}, {"type": "EXAMPLE_BOX_TEXT", "preface": "null", "dataType": "text", "data": ["먼저 주식과 채권 등 자산에 대한 투자 비율을 정하고 구체적인 종목을 선정하는 것을 ( ㉮ )이라고 하고, 반대로 투자 종목을 먼저 선정하고 자산 배분을 나중에 하는 것을 ( ㉯ )이라고 한다."]}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["㉮ 상향식 ㉯ 하향식", "㉮ 하향식 ㉯ 상향식", "㉮ 적극적 ㉯ 소극적", "㉮ 소극적 ㉯ 적극적"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [2]}, {"type": "SOLUTION_TEXT", "data": "자산 배분이 전략을 수립하는 것이라면, 종목 선정과 투자 타이밍을 선정하는 것은 구체적인 전술이다. 먼저 주식과 채권 등 자산에 대한 투자 비율을 정하고 구체적인 종목을 선정하는 것을 하향식(Top Down)이라고 하고, 반대로 투자 종목을 먼저 선정하고 자산 배분을 나중에 하는 것을 상향식(Bottom Up)이라고 한다. "}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '투자전술 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (13, 'MULTIPLE_CHOICE', '[{"data": "소극적 투자관리에 대한 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["매수와 매도를 최소화해서 거래 비용을 줄인다", "증권시장에는 이미 이용할 수 있는 모든 정보가 반영되어 있다", "증권시장에서 과대평가 또는 과소평가된 증권을 찾기 어렵다", "다소 위험을 감수하더라도 초과 수익을 획득할 수 있다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이라고 여긴다. 증권시장에는 이용할 수 있는 모든 정보가 반영되어 있으므로 과대 또는 과소 평가된 증권을 찾기 어렵다. 그래서 투자 관리의 초점을 초과 수익을 획득하는 것보다는 투자 위험을 최소화하는 데 두고 시장수익률을 추구한다."}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (14, 'MULTIPLE_CHOICE', '[{"data": "증권시장이 효율적이라고 전제하는 소극적 투자 관리 전술에 설명으로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식을 매수한 다음 장기보유한다", "인덱스펀드에 투자한다", "시장대표지수를 추종하는 상장지수펀드(ETF)에 투자한다", "미래 성장이 예상되는 일부 종목에 집중투자한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "전략적 차원에서 결정된 자산 배분의 범위 내에서, 최적의 투자 대안을 선정하는 전술적 차원에서의 대응은 적극적 투자 관리와 소극적 투자 관리로 나눌 수 있다. 이중 소극적 투자 관리(Passive Investment Style)는 증권시장이 효율적이기기 때문에 과대평가 또는 과소평가 된 증권을 찾기 어렵다고 생각한다. 구체적인 종목을 분석하거나 독자적으로 판단하지 않고, 시장 전체의 일반적인 예측을 그대로 받아들여 정보 비용을 줄이고 매수와 매도를 최소화해 거래 비용을 줄인다. 단순 매수 후 보유하는 전략, 인덱스펀드와 ETF를 활용한 투자, 정액 분할 투자가 여기에 해당된다. 일부 종목에 집중투자하는 것은 적극적 투자관리 전략에 해당한다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '소극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (15, 'MULTIPLE_CHOICE', '[{"data": "홍길동씨는 주식시장에 이용할 수 있는 모든 정보가 반영되어 있다고 생각하지 않는 투자자이다. 홍길동씨의 생각에 부합하는 투자 전술로 가장 거리가 먼 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["주식시장의 이례적인 현상을 활용해 투자한다", "적정한 투자타이밍을 찾기 위해 노력한다", "특정 산업이나 소수 종목에 집중 투자한다", "주식을 매수한 다음 장기보유 한다"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "적극적 투자 관리(Active Investment Style)는 다소 위험을 감수하더라도 시장의 평균을 넘어서는 초과 수익을 추구한다. 증권시장이 비효율적이라고 전제하고, 위험이 동일한 증권들 중에서 수익이 높은 증권을 선택하거나, 기대 수익이 동일한 증권 중에서 위험이 낮은 증권을 선택하는 방법이다. 적극적 투자 관리 방법으로는 소주종목이나 특정 산업에 집중적으로 투자하거나, 투자 타이밍을 포착하거나, 시장의 이례적인 현상을 이용해 종목을 선정하는 것이 대표적이다"}]', '중', '미니테스트', null, 25, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '적극적 투자관리 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (16, 'MULTIPLE_CHOICE', '[{"data": "계란을 한 바구니에 담지 말라는 말과 관련 있는 투자 방법으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["분산투자", "집중투자", "거치식투자", "마케타이밍 전략"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "분산투자라의 중요성을 표현한 대표적인 말이다. 분산투자는 계란을 옮길 때 많은 바구니를 사용한다는 뜻 이상이다. 다른 것들과 분명히 구별되는 바구니를 찾는 것이 중요해졌다. 각 바구니의 독특한 수익률 형태가 다른 바구니의 손익을 부분적으로 상쇄해서 전체 포트폴리오의 변동성을 낮추기 때문이다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (17, 'MULTIPLE_CHOICE', '[{"data": "한 해의 절반은 건기이고, 나머지 절반은 우기인 섬나라에서 당신이 우산을 판매하고 있다고 가정해 보자. 날씨에 따른 매출액 변동을 줄이려면 어떤 제품을 추가해서 판매하는 것이 좋을까?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["아이스크림", "커피", "비옷", "장화"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [1]}, {"type": "SOLUTION_TEXT", "data": "분산투자효과를 이해하고 있는지 묻는 질문이다. 날씨가 제품 판매량에 영향을 미친다. 우기에는 우산, 우의, 장화 판매가 늘어나겠지만, 건기에는 판매가 급감한다. 대신 아이스크림 판매가 늘어날 수 있다. 커피 판매량은 날씨에 크게 영향을 받지 않는다."}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
insert into "ETEST"."PROBLEM" ("PROB_ID", "ANSWER_TYPE", "QUESTION", "SOLUTION", "DIFFICULTY", "CATEGORY", "IMG_SRC", "TIME_RECOMMENDATION", "CREATOR_ID", "CREATE_DATE", "VALIDATOR_ID", "VALIDATE_DATE", "EDITOR_ID", "EDIT_DATE", "SOURCE", "INTENTION", "QUESTION_INITIAL", "SOLUTION_INITIAL" ) values (18, 'MULTIPLE_CHOICE', '[{"data": "분산투자를 통해서 줄일 수 있는 위험으로 가장 올바른 것은?", "type": "QUESTION_TEXT"}, {"type": "MULTIPLE_CHOICE_TEXT", "dataType": "text", "data": ["글로벌 경제위기", "천재지변", "전쟁", "분식회계"]}]', '[{"type": "MULTIPLE_CHOICE_CORRECT_ANSWER", "data": [4]}, {"type": "SOLUTION_TEXT", "data": "투자 위험은 크게 체계적 위험과 비체계적 위험으로 나뉜다. 체계적 위험은 세계적인 경제 위기나 천재지변, 전쟁 등과 같이 모든 투자 자산의 가치에 동시에 영향을 미치는 위험을 말한다. 따라서 체계적 위험은 분산투자를 해도 사라지지 않는다. 비체계적 위험이란 개별 기업의 독특한 요소 때문에 발생하는 위험으로, 성격이 다른 기업이나 자산에 투자하면 비체계적 위험을 회피하거나 그 손실을 상쇄할 수 있다"}]', '하', '미니테스트', null, 15, '이장영', TO_DATE('2021/07/01 00:00:00', 'YYYY/MM/DD HH24:MI:SS'), null, null, null, null, '집필진 출제', '분산투자효과 이해', null, null);
